INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 3, 2135, "お出かけですか？\nわたくしもお供\nいたします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 4, 2136, "ひゃん！\n主さま…\nいじわるです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 101, 2137, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 102, 2138, "主さまの優しさに\nわたくしはいつも\n癒されています…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 1, 1, 2139, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 2, 2, 2140, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 3, 3, 2141, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 1, 2142, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 2, 2143, "このふかふかの優しさ\n主さまのようです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 3, 2144, "かわいい♪\n主さまにそっくり♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 4, 2145, "この心地よさ…\n主さまのようです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 5, 2146, "ここで洗濯物を\n乾かせるでしょうか　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 6, 2147, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 7, 2148, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105902, 0, 0, 8, 2149, "都会のことを\n学ばないと…\nふむふむ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 0, 1, 2151, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 0, 2, 2152, "ギルドハウスの\n使い心地は\nどうですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 0, 3, 2153, "ご用があれば\nなんでも言って\nくださいね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 0, 4, 2154, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 1, 1, 2155, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 2, 2, 2156, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190801, 0, 3, 3, 2157, "ここは夜でも\nにぎやかですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 1, 2158, "…\n少しうとうと\nしてしまった", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 2, 2159, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 3, 2160, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 4, 2161, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 5, 2162, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 101, 2163, "少年のことを考えると\n顔がにやけてしまう", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 102, 2164, "少年は今頃\n平和に過ごせて\nいるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 1, 2165, "ん？　悪いが\n兜は脱がないぞ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 2, 2166, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 3, 2167, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 4, 2168, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 101, 2169, "君といると、兜の中が\nいつもより暑いんだ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 102, 2170, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 1, 1, 2171, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 2, 2, 2172, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 3, 3, 2173, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 2, 2175, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 3, 2176, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 4, 2177, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 5, 2178, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 7, 2180, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 8, 2181, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 1, 2183, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 2, 2184, "ドングリの木\n近くに生えてない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 3, 2185, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 4, 2186, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 5, 2187, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 101, 2188, "あんパン買ったし\nあいつにも\n分けてあげよう", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 102, 2189, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 1, 2190, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 2, 2191, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 3, 2192, "ドングリあげるから\nあんパン買ってきて～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 4, 2193, "う～\n疲れちゃったよ～\nおんぶして運んで～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 101, 2194, "あんたといると\nリア充って\n感じがするよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 102, 2195, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 1, 1, 2196, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 2, 2, 2197, "そろそろお昼寝でも\nしたい気分だねぇ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 3, 3, 2198, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 2, 2200, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 3, 2201, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 4, 2202, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 5, 2203, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 7, 2205, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 8, 2206, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 1, 2208, "ランドソル…\nおっきな街だなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 2, 2209, "ロザリア…\nきっと、いるよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 3, 2210, "最近、弓の練習\nサボっちゃってるな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 4, 2211, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 5, 2212, "ちょっとだけ\n慣れてきたかも？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 101, 2213, "いつか、\nあの人と一緒に…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 102, 2214, "…なんか最近、\nあの人のこと\nばっかり…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 1, 2215, "もしよかったら\n街に出かけない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 2, 2216, "この部屋、\n妖精さんも\n気に入るかも！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 3, 2217, "今日は\nなににしよっか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 4, 2218, "今日はどこ行く？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 101, 2219, "たまには二人で…\nな、なんちゃって！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 102, 2220, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 1, 1, 2221, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 2, 2, 2222, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 3, 3, 2223, "ランドソルは\n夜でも賑やかだね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 2, 2225, "わぁっ！\nすっごく\nふわふわだよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 3, 2226, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 4, 2227, "くんくん\nなんか…いい匂い？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 5, 2228, "すっごいなー\nあったかいなー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 7, 2230, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 8, 2231, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 1, 2233, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 2, 2234, "カップケーキが\n食べたいな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 3, 2235, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 4, 2236, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 5, 2237, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 101, 2238, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 102, 2239, "早く大人になりたいな\n…あの人と同じくらい", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 1, 2240, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 2, 2241, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 3, 2242, "これ宿題です\nやっておいて\nくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 4, 2243, "あ、遅刻ですよ\n待っていたんですから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 101, 2244, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 102, 2245, "あの…何歳まで\n子供だと思いますか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 1, 1, 2246, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 2, 2, 2247, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 3, 3, 2248, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 2, 2250, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 3, 2251, "つ、つぶらな瞳には\n弱いんです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 4, 2252, "リラックスできますね\n集中力も\n上がりそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 5, 2253, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 7, 2255, "ピーマンもこれくらい\nおいしければ\nいいのに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 8, 2256, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 1, 2258, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 2, 2259, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 3, 2260, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 4, 2261, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 5, 2262, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 101, 2263, "彼は優しいから、\nきっと良い\nお父さんになるわね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 102, 2264, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 1, 2265, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 2, 2266, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 3, 2267, "よかったら一緒に\n絵本を作らない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 4, 2268, "あなたはいつも元気ね\n先生も見習わないと♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 101, 2269, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 102, 2270, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 1, 1, 2271, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 2, 2, 2272, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 3, 3, 2273, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 2, 2275, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 3, 2276, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 4, 2277, "いい香りね～\n保育園にも\n置こうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 5, 2278, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 7, 2280, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 8, 2281, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 1, 2283, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 2, 2284, "拠点とはやはり\n斯くあるべきじゃの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 3, 2285, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 4, 2286, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 5, 2287, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 101, 2288, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 102, 2289, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 1, 2290, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 2, 2291, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 3, 2292, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 4, 2293, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 101, 2294, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 102, 2295, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 1, 1, 2296, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 2, 2, 2297, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 3, 3, 2298, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 2, 2300, "よい座り心地じゃ\nわらわの玉座としよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 3, 2301, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 4, 2302, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 5, 2303, "あたたかい…\n身も心も\n浄化されるようじゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 7, 2305, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 8, 2306, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 1, 2308, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 2, 2309, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 3, 2310, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 4, 2311, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 5, 2312, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 101, 2313, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 102, 2314, "まさか私が\nアイドルになるなんて\n思ってなかったです", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 1, 2315, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 2, 2316, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 3, 2317, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 4, 2318, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 101, 2319, "どうしました？\n新しい服が\n欲しくなりましたか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 102, 2320, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 1, 1, 2321, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 2, 2, 2322, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 3, 3, 2323, "夜の方が\n作業が捗るのは\n私だけですかね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 2, 2325, "このふかふか具合\nいい素材を\n使ってますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 3, 2326, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 4, 2327, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 5, 2328, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 7, 2330, "何をするにも\n食事は大切！\n体力が資本です！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 8, 2331, "私もいつか\nデザインを\nまとめて本に…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 1, 2333, "またお魚を\n捕りにいこうかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 2, 2334, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 3, 2335, "ここなら、\n日焼けの心配は\nありませんね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 4, 2336, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 5, 2337, "泳ぐのも食べるのも\n大得意です！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 101, 2338, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 102, 2339, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 1, 2340, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 2, 2341, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 3, 2342, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 4, 2343, "あなたの水着、\n私が選んであげます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 101, 2344, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 102, 2345, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 1, 1, 2346, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 2, 2, 2347, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 3, 3, 2348, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 2, 2350, "泳ぎ疲れたので\nちょっと休憩です～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 3, 2351, "あなたにも、\n可愛い水着を\n着せてあげたいです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 4, 2352, "なんだか、\nリゾート気分です…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 5, 2353, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 7, 2355, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 8, 2356, "南国のフルーツ図鑑\n…じゅるり…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 10, 2358, "わ～♪　おいしそ…\nか、かわいいですね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 11, 2359, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 1, 2360, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 2, 2361, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 3, 2362, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 4, 2363, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 5, 2364, "クロールの練習なら、\n部屋でもできそうです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 101, 2365, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 102, 2366, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 1, 2367, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 2, 2368, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 3, 2369, "海の美しさは、\n森とはちがう\n魅力がありました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 4, 2370, "この髪飾りですか？\n造花ですから\n水の中も平気です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 101, 2371, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 102, 2372, "はじめての海が、\n主さまと一緒で\n幸せです♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 1, 1, 2373, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 2, 2, 2374, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 3, 3, 2375, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 2, 2377, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 3, 2378, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 4, 2379, "この香り…\nきっと主さまも\n気に入ります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 5, 2380, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 7, 2382, "泳いだ後の食事は\n一段と美味しく\n感じます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 8, 2383, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 10, 2385, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 11, 2386, "ふかふかですね\n主さまのようです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 1, 2387, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 2, 2388, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 3, 2389, "今日はドジを\nしませんように！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 4, 2390, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 5, 2391, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 101, 2392, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 102, 2393, "水着でご奉仕したら、\nあの人は喜ぶかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 1, 2394, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 2, 2395, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 3, 2396, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 4, 2397, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 101, 2398, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 102, 2399, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 1, 1, 2400, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 2, 2, 2401, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 3, 3, 2402, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 2, 2404, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 3, 2405, "きれいにすれば\nかわいさも倍増\nですよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 4, 2406, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 5, 2407, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 7, 2409, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 8, 2410, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 10, 2412, "えへへ♪\n南の島まで\n乗せてください！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107701, 0, 0, 11, 2413, "なんだか、\n人魚姫に\nなった気分です♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 1, 2414, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 2, 2415, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 3, 2416, "ビーチにいると\n日焼けが心配だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 4, 2417, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 5, 2418, "お、泳ぎの練習なんて\nしないからね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 101, 2419, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 102, 2420, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 1, 2421, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 2, 2422, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 3, 2423, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 4, 2424, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 101, 2425, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 102, 2426, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 1, 1, 2427, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 2, 2, 2428, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 3, 3, 2429, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 2, 2431, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 3, 2432, "こんな魔物がいたら\n操るのも楽しいかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 4, 2433, "にゃう～♪\nまるで南国にいる\n気分ね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 5, 2434, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 7, 2436, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 8, 2437, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 10, 2439, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 11, 2440, "リゾートリゾートっ\nのんびりしましょ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 1, 2441, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 2, 2442, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 3, 2443, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 4, 2444, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 5, 2445, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 101, 2446, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 102, 2447, "キミだけね…\n私のことをここまで\n理解してくれるのは", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 1, 2448, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 2, 2449, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 3, 2450, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 4, 2451, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 101, 2452, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 102, 2453, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 1, 1, 2454, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 2, 2, 2455, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 3, 3, 2456, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 2, 2458, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 3, 2459, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 4, 2460, "いい香りね…\n働く意欲が\n湧いてくるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 5, 2461, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 7, 2463, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 8, 2464, "座学も大切ね\nもっと知識を\n身につけないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 10, 2466, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108001, 0, 0, 11, 2467, "落ち着くわ…\n海とひとつになった\n気分ね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 1, 2468, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 2, 2469, "猫グッズで\n海の家を飾りたいにゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 3, 2470, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 4, 2471, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 5, 2472, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 101, 2473, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 102, 2474, "あたしの宝は\nここにあるにゃ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 1, 2475, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 2, 2476, "今からキミを\nあたしの相棒に\n任命するにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 3, 2477, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 4, 2478, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 101, 2479, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 102, 2480, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 1, 1, 2481, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 2, 2, 2482, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 3, 3, 2483, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 2, 2485, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 3, 2486, "にゃにゃ～\nなんてかわいさにゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 4, 2487, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 5, 2488, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 7, 2490, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 8, 2491, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 10, 2493, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107901, 0, 0, 11, 2494, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 1, 2495, "人の家って、道場とは\n違った香りがするね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 2, 2496, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 3, 2497, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 4, 2498, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 5, 2499, "もっと\n上手く立ち回って\n出世したいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 101, 2500, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 102, 2501, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 1, 2502, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 2, 2503, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 3, 2504, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 4, 2505, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 101, 2506, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 102, 2507, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 1, 1, 2508, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 2, 2, 2509, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 3, 3, 2510, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 2, 2512, "いざという時のため\nにも、休憩は大事だね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 3, 2513, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 4, 2514, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 5, 2515, "あったかいなぁ。\nたまにはのんびり\nしようか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 7, 2517, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 8, 2518, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 1, 2520, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 2, 2521, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 3, 2522, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 4, 2523, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 5, 2524, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 101, 2525, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 102, 2526, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 1, 2527, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 2, 2528, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 3, 2529, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 4, 2530, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 101, 2531, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 102, 2532, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 1, 1, 2533, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 2, 2, 2534, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 3, 3, 2535, "夜のパトロールで\n一緒に平和を\n守るッス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 2, 2537, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 3, 2538, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 4, 2539, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 5, 2540, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 7, 2542, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 8, 2543, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 1, 2545, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 2, 2546, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 3, 2547, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 4, 2548, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 5, 2549, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 101, 2550, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 102, 2551, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 1, 2552, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 2, 2553, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 3, 2554, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 4, 2555, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 101, 2556, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 102, 2557, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 1, 1, 2558, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 2, 2, 2559, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 3, 3, 2560, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 2, 2562, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 3, 2563, "つん、つん。ふふっ♪\n癒やされます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 4, 2564, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 5, 2565, "あったかいね\nお父さん…\nえ？　分からない？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 7, 2567, "霊のみなさんは\n食べられないので…\n私がいただきます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 8, 2568, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108101, 0, 0, 12, 2570, "わぁ、お菓子が沢山…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 1, 2572, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 2, 2573, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 3, 2574, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 4, 2575, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 5, 2576, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 101, 2577, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 102, 2578, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 1, 2579, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 2, 2580, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 3, 2581, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 4, 2582, "プリンの可能性は\n無限大なの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 101, 2583, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 102, 2584, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 1, 1, 2585, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 2, 2, 2586, "ランチは豪華に\nプリンアラモードなの", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 3, 3, 2587, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 2, 2589, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 3, 2590, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 4, 2591, "癒されるの～\nまるで天国なの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 5, 2592, "ここでなら、\n焼きプリンが\n作れるの！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 7, 2594, "腹が減っては\nハロウィンは\nできぬなの！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 8, 2595, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108201, 0, 0, 12, 2597, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 1, 2599, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 2, 2600, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 3, 2601, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 4, 2602, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 5, 2603, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 101, 2604, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 102, 2605, "アイツを魅了する\n魔法とかないかしら？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 1, 2606, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 2, 2607, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 3, 2608, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 4, 2609, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 101, 2610, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 102, 2611, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 1, 1, 2612, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 2, 2, 2613, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 3, 3, 2614, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 2, 2616, "あう～、お菓子の\n食べすぎで\n動けな～い", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 3, 2617, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 4, 2618, "大人な香りに\n包まれてる～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 5, 2619, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 7, 2621, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 8, 2622, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108301, 0, 0, 12, 2624, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 1, 2626, "次はどんな策で団長を\nからかってみようか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 2, 2627, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 3, 2628, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 4, 2629, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 5, 2630, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 101, 2631, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 102, 2632, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 1, 2633, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 2, 2634, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 3, 2635, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 4, 2636, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 101, 2637, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 102, 2638, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 1, 1, 2639, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 2, 2, 2640, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 3, 3, 2641, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 2, 2643, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 3, 2644, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 4, 2645, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 5, 2646, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 7, 2648, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 8, 2649, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 1, 2651, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 2, 2652, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 3, 2653, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 4, 2654, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 5, 2655, "ふあぁ～…\n昼寝でもするか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 101, 2656, "ふふっ…\nあの子、見た目より\n根性あるんだね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 102, 2657, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 1, 2658, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 2, 2659, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 3, 2660, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 4, 2661, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 101, 2662, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 102, 2663, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 1, 1, 2664, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 2, 2, 2665, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 3, 3, 2666, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 2, 2668, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 3, 2669, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 4, 2670, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 5, 2671, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 7, 2673, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 8, 2674, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 1, 2676, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 2, 2677, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 3, 2678, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 4, 2679, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 5, 2680, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 101, 2681, "次はどの漫画を\n貸そうかなー♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 102, 2682, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 1, 2683, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 2, 2684, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 3, 2685, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 4, 2686, "新魔法思いついた！\n私って天才！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 101, 2687, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 102, 2688, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 1, 1, 2689, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 2, 2, 2690, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 3, 3, 2691, "夜からが、\nオタ活本番でしょ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 2, 2693, "ふいー！\nくつろいで本を読む！\n最高だねー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 3, 2694, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 4, 2695, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 5, 2696, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 7, 2698, "ウマ―！\nこれの詳細きぼんぬ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 8, 2699, "こ、古代語の本！？\n現代語でおk…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 1, 2701, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 2, 2702, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 3, 2703, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 4, 2704, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 5, 2705, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 1, 2706, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 2, 2707, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 3, 2708, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 4, 2709, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 1, 1, 2710, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 2, 2, 2711, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 3, 3, 2712, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 2, 2714, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 3, 2715, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 4, 2716, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 5, 2717, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 7, 2719, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (190901, 0, 0, 8, 2720, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 1, 2722, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 2, 2723, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 3, 2724, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 4, 2725, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 5, 2726, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 101, 2727, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 102, 2728, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 1, 2729, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 2, 2730, "歌を届ける\nサンタさんに\nなりたいな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 3, 2731, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 4, 2732, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 101, 2733, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 102, 2734, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 1, 1, 2735, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 2, 2, 2736, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 3, 3, 2737, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 2, 2739, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 3, 2740, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 4, 2741, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 5, 2742, "この光みたいに\n灯火になるような\n歌を歌いたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 7, 2744, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 8, 2745, "物語には\n曲作りのヒントが\n詰まっているんです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 14, 2747, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108401, 0, 0, 15, 2748, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 1, 2749, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 2, 2750, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 3, 2751, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 4, 2752, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 5, 2753, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 101, 2754, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 102, 2755, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 1, 2756, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 2, 2757, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 3, 2758, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 4, 2759, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 101, 2760, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 102, 2761, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 1, 1, 2762, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 2, 2, 2763, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 3, 3, 2764, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 2, 2766, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 3, 2767, "この子と一緒に\nおままごとしたいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 4, 2768, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 5, 2769, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 7, 2771, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 8, 2772, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 14, 2774, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108501, 0, 0, 15, 2775, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 1, 2776, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 2, 2777, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 3, 2778, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 4, 2779, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 5, 2780, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 101, 2781, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 102, 2782, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 1, 2783, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 2, 2784, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 3, 2785, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 4, 2786, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 101, 2787, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 102, 2788, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 1, 1, 2789, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 2, 2, 2790, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 3, 3, 2791, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 2, 2793, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 3, 2794, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 4, 2795, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 5, 2796, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 7, 2798, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 8, 2799, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 14, 2801, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108601, 0, 0, 15, 2802, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 1, 2803, "着物も慣れると\n動きやすいものさ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 2, 2804, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 3, 2805, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 4, 2806, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 5, 2807, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 101, 2808, "これからもずっと\n仲間と、彼と\n一緒に…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 102, 2809, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 1, 2810, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 2, 2811, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 3, 2812, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 4, 2813, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 101, 2814, "キミといると\n気持ちが段々と\n安らいでいくよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 102, 2815, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 1, 1, 2816, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 2, 2, 2817, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 3, 3, 2818, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 2, 2820, "…疲れていたのかな\nもう少しで眠る\nところだった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 3, 2821, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 4, 2822, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 5, 2823, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 7, 2825, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 8, 2826, "外を出歩くのも\nいいけど、中で\n読書もありだな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 1, 2828, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 2, 2829, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 3, 2830, "今年は\nどんな福袋を\n買おうかな…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 4, 2831, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 5, 2832, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 101, 2833, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 102, 2834, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 1, 2835, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 2, 2836, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 3, 2837, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 4, 2838, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 101, 2839, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 102, 2840, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 1, 1, 2841, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 2, 2, 2842, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 3, 3, 2843, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 2, 2845, "ふぅ…\n帯も少し\nゆるめちゃおう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 3, 2846, "お正月用に\n着物を縫って\nあげたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 4, 2847, "これでお肌が\n保湿出来たら\nいいのになぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 5, 2848, "ん～…\nぬくぬくしてて\n動きたくないよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 7, 2850, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 8, 2851, "年末年始は\n告白のチャンス…\nむ、無理無理！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 1, 2853, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 2, 2854, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 3, 2855, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 4, 2856, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 5, 2857, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 101, 2858, "うーん…\n騎士くんはお正月\n何してるんだろ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 102, 2859, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 1, 2860, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 2, 2861, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 3, 2862, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 4, 2863, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 101, 2864, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 102, 2865, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 1, 1, 2866, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 2, 2, 2867, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 3, 3, 2868, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 2, 2870, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 3, 2871, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 4, 2872, "はぁー♪\nいい香りー…\n気分スッキリ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 5, 2873, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 7, 2875, "ど、どうしよう…！\n手が、止まんないよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 8, 2876, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 1, 2878, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 2, 2879, "ここを私のチョコで\n埋め尽くしますわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 3, 2880, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 4, 2881, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 5, 2882, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 101, 2883, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 102, 2884, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 1, 2885, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 2, 2886, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 3, 2887, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 4, 2888, "解毒薬はどこに\nいれたかしら…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 101, 2889, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 102, 2890, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 1, 1, 2891, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 2, 2, 2892, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 3, 3, 2893, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 2, 2895, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 3, 2896, "この中、何か\n入りそうですわね\n…チョコレート、とか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 4, 2897, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 5, 2898, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 7, 2900, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109001, 0, 0, 8, 2901, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 1, 2903, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 2, 2904, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 3, 2905, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 4, 2906, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 5, 2907, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 101, 2908, "お姉ちゃんのチョコ\n食べさせてあげたい…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 102, 2909, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 1, 2910, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 2, 2911, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 3, 2912, "ナデナデするのは\nお姉ちゃんの仕事！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 4, 2913, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 101, 2914, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 102, 2915, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 1, 1, 2916, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 2, 2, 2917, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 3, 3, 2918, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 2, 2920, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 3, 2921, "このかわいさは\nお姉ちゃんも大満足♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 4, 2922, "この香り、チョコに\n使えるかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 5, 2923, "弟くんと一緒に\nぬくぬくしたいな…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 7, 2925, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109101, 0, 0, 8, 2926, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 1, 2928, "アタシって…\nここにいても\nいいのか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 2, 2929, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 3, 2930, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 4, 2931, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 5, 2932, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 101, 2933, "みんなが早く\nアタシのこと\n思い出さないかな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 102, 2934, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 1, 2935, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 2, 2936, "どうした？\n戦うのか！？\n…なんだ、違うのか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 3, 2937, "身長のことは\nあんまり言うな！\n気にしてるからな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 4, 2938, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 101, 2939, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 102, 2940, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 1, 1, 2941, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 2, 2, 2942, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 3, 3, 2943, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 2, 2945, "ふぃ～…\n極楽極楽…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 3, 2946, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 4, 2947, "くんくん…\nたまにはこういうのも\n悪くないかもな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 5, 2948, "あー…\nもっと前にコイツと\n出会いたかった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 7, 2950, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 8, 2951, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 1, 2953, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 2, 2954, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 3, 2955, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 4, 2956, "この前の\n捜査資料…\nどこにあったかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 5, 2957, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 101, 2958, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 102, 2959, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 1, 2960, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 2, 2961, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 3, 2962, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 4, 2963, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 101, 2964, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 102, 2965, "助手くんは\n私にとって\n最高の相棒だよ☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 1, 1, 2966, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 2, 2, 2967, "一休みしたら\n推理の答え合わせを\nしてみようか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 3, 3, 2968, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 2, 2970, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 3, 2971, "この愛らしさ…\n要捜査対象だね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 4, 2972, "思考が活性化される\n素敵な香りだね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 5, 2973, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 7, 2975, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 8, 2976, "探偵だからね\nミステリには\n一家言あるんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 1, 2978, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 2, 2979, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 3, 2980, "先輩を観察しやすい\n場所はあるかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 4, 2981, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 5, 2982, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 101, 2983, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 102, 2984, "せ、先輩になら\n観察されてもいいなぁ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 1, 2985, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 2, 2986, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 3, 2987, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 4, 2988, "ま、また…\n先輩と一緒に\nバイトしたい…です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 101, 2989, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 102, 2990, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 1, 1, 2991, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 2, 2, 2992, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 3, 3, 2993, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 2, 2995, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 3, 2996, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 4, 2997, "ここから先輩の\n香りがすればなあ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 5, 2998, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 7, 3000, "おいし～\nこれは\n先輩も好きな味かな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 8, 3001, "す、好きな人に\n近づく方法…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 1, 3003, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 2, 3004, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 3, 3005, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 4, 3006, "わー！\nあれもこれも\n気になっちゃうな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 5, 3007, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 101, 3008, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 102, 3009, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 1, 3010, "歩き方がきれい？\nえへへ、そうかな…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 2, 3011, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 3, 3012, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 4, 3013, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 101, 3014, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 102, 3015, "お茶の時間に\nしたいな～…\n一緒にどう？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 1, 1, 3016, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 2, 2, 3017, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 3, 3, 3018, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 2, 3020, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 3, 3021, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 4, 3022, "いい香り～！\nなんだか気持ちが\n落ち着くね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 5, 3023, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 7, 3025, "こ、これ美味し～！\n止められないよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109201, 0, 0, 8, 3026, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 1, 3028, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 2, 3029, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 3, 3030, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 4, 3031, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 5, 3032, "なにか面白そうなもの\nないですかね～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 101, 3033, "ここのみんなとも\nもうお友だちですぅ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 102, 3034, "おなかが減ったです…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 1, 3035, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 2, 3036, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 3, 3037, "あなたもお勉強\n苦手ですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 4, 3038, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 101, 3039, "お友だちいっぱいで、\nルゥはうれしいです♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 102, 3040, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 1, 1, 3041, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 2, 2, 3042, "午後もいっぱい\n遊ぶですよ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 3, 3, 3043, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 2, 3045, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 3, 3046, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 4, 3047, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 5, 3048, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 7, 3050, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109301, 0, 0, 8, 3051, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 1, 3053, "ピアノ…\nどこかにないかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 2, 3054, "ふぅ…\n落ち着くな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 3, 3055, "マナリアはいつも\n賑やかなんだ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 4, 3056, "みんな…\n今頃何してるかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 5, 3057, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 101, 3058, "人と話すのは\n得意じゃないな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 102, 3059, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 1, 3060, "やっぱり、私の翼や\n尻尾…気になる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 2, 3061, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 3, 3062, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 4, 3063, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 101, 3064, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 102, 3065, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 1, 1, 3066, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 2, 2, 3067, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 3, 3, 3068, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 2, 3070, "羽を伸ばしたく\nなっちゃうね…ふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 3, 3071, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 4, 3072, "いい香り…\nこれ、\nアンも好きそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 5, 3073, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 7, 3075, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109401, 0, 0, 8, 3076, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 1, 3078, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 2, 3079, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 3, 3080, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 4, 3081, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 5, 3082, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 101, 3083, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 102, 3084, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 1, 3085, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 2, 3086, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 3, 3087, "こんな場所で\nなんというプレイを！\n…妄想でした", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 4, 3088, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 101, 3089, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 102, 3090, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 1, 1, 3091, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 2, 2, 3092, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 3, 3, 3093, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 2, 3095, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 3, 3096, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 4, 3097, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 5, 3098, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 7, 3100, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109501, 0, 0, 8, 3101, "クウカの探している\nジャンルはどこに\nありますかね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 1, 3103, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 2, 3104, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 3, 3105, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 4, 3106, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 5, 3107, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 101, 3108, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 102, 3109, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 1, 3110, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 2, 3111, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 3, 3112, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 4, 3113, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 101, 3114, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 102, 3115, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 1, 1, 3116, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 2, 2, 3117, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 3, 3, 3118, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 2, 3120, "天下分け目の\n戦いに備えて…\nマッタリデース", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 3, 3121, "口寄せの術で\n召喚したいくらい\nかわいいデース", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 4, 3122, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 5, 3123, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 7, 3125, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109601, 0, 0, 8, 3126, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 1, 3128, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 2, 3129, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 3, 3130, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 4, 3131, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 5, 3132, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 101, 3133, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 102, 3134, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 1, 3135, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 2, 3136, "レムにできること\nでしたら、なんでも\nお申し付けください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 3, 3137, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 4, 3138, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 101, 3139, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 102, 3140, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 1, 1, 3141, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 2, 2, 3142, "昼食の時間ですね\n何か食べたいものは\nありますか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 3, 3, 3143, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 2, 3145, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 3, 3146, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 4, 3147, "よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 5, 3148, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 7, 3150, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 8, 3151, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 1, 3153, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 2, 3154, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 3, 3155, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 4, 3156, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 5, 3157, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 101, 3158, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 102, 3159, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 1, 3160, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 2, 3161, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 3, 3162, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 4, 3163, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 101, 3164, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 102, 3165, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 1, 1, 3166, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 2, 2, 3167, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 3, 3, 3168, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 2, 3170, "…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 3, 3171, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 4, 3172, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 5, 3173, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 7, 3175, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 8, 3176, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 1, 3178, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 2, 3179, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 3, 3180, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 4, 3181, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 5, 3182, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 101, 3183, "また、みんなで\n一緒に買い物に\n行きたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 102, 3184, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 1, 3185, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 2, 3186, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 3, 3187, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 4, 3188, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 101, 3189, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 102, 3190, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 1, 1, 3191, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 2, 2, 3192, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 3, 3, 3193, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 2, 3195, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 3, 3196, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 4, 3197, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 5, 3198, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 7, 3200, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 8, 3201, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 1, 3203, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 2, 3204, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 3, 3205, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 4, 3206, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 5, 3207, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 101, 3208, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 102, 3209, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 1, 3210, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 2, 3211, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 3, 3212, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 4, 3213, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 101, 3214, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 102, 3215, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 1, 1, 3216, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 2, 2, 3217, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 3, 3, 3218, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 2, 3220, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 3, 3221, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 4, 3222, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 5, 3223, "あったか～い♪\n夏の太陽みたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 7, 3225, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 8, 3226, "夏のために\n最新ファッションを\n勉強ちう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 10, 3228, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110001, 0, 0, 11, 3229, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 1, 3230, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 2, 3231, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 3, 3232, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 4, 3233, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 5, 3234, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 101, 3235, "海が楽しいって\n思えたのも\nあの子のおかげね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 102, 3236, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 1, 3237, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 2, 3238, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 3, 3239, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 4, 3240, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 101, 3241, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 102, 3242, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 1, 1, 3243, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 2, 2, 3244, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 3, 3, 3245, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 2, 3247, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 3, 3248, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 4, 3249, "この匂い…\nなんだか夏の海を\n思い出すわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 5, 3250, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 7, 3252, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 8, 3253, "夏休み中でも\nしっかりお勉強\nしないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 10, 3255, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110101, 0, 0, 11, 3256, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 1, 3257, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 2, 3258, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 3, 3259, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 4, 3260, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 5, 3261, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 101, 3262, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 102, 3263, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 1, 3264, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 2, 3265, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 3, 3266, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 4, 3267, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 101, 3268, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 102, 3269, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 1, 1, 3270, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 2, 2, 3271, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 3, 3, 3272, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 2, 3274, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 3, 3275, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 4, 3276, "夏らしい\nいい香りね…\nなんの香りかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 5, 3277, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 7, 3279, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 8, 3280, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 10, 3282, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110301, 0, 0, 11, 3283, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 1, 3284, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 2, 3285, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 3, 3286, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 4, 3287, "夏ならではの\n料理…か\n何がいいかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 5, 3288, "やっぱりここは\n涼しくて快適だな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 101, 3289, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 102, 3290, "水着で部屋の中に\nいるのって\n変な感じだな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 1, 3291, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 2, 3292, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 3, 3293, "夏バテしてないか？\nここで休んでけよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 4, 3294, "…そう見られると\n照れるっていうか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 101, 3295, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 102, 3296, "おまえと\n夏の思い出ができて\nよかったよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 1, 1, 3297, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 2, 2, 3298, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 3, 3, 3299, "今日もお疲れ\n明日のパトロールも\nよろしくな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 2, 3301, "泳ぎ疲れたからな\n少し休憩だ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 3, 3302, "か、かわいい…\nいや、なんでもねぇ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 4, 3303, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 5, 3304, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 7, 3306, "海で食べると\nまた一味違うよな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 8, 3307, "夏のサバイバル特集…\nちょっと読んでみるか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 10, 3309, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110401, 0, 0, 11, 3310, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 1, 3311, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 2, 3312, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 3, 3313, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 4, 3314, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 5, 3315, "クロールも\n犬かきも\n大得意さ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 101, 3316, "なんだか歌いたい\n気分さ～\nキミもどう～？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 102, 3317, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 1, 3318, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 2, 3319, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 3, 3320, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 4, 3321, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 101, 3322, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 102, 3323, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 1, 1, 3324, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 2, 2, 3325, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 3, 3, 3326, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 2, 3328, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 3, 3329, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 4, 3330, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 5, 3331, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 7, 3333, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 8, 3334, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 10, 3336, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110501, 0, 0, 11, 3337, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 103, 3338, "あの人が\nわたしの騎士だったら\nいいのにな…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 103, 3339, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105901, 0, 0, 103, 3340, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105901, 0, 0, 103, 3341, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106001, 0, 0, 103, 3342, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106001, 0, 0, 103, 3343, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 1, 3344, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 2, 3345, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 3, 3346, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 4, 3347, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 5, 3348, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 101, 3349, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 102, 3350, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 1, 3351, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 2, 3352, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 3, 3353, "日が落ちたら\n線香花火\nやりたいわぁ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 4, 3354, "木陰でのんびり\nお昼寝…\n最高やね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 101, 3355, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 102, 3356, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 1, 1, 3357, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 2, 2, 3358, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 3, 3, 3359, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 2, 3361, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 3, 3362, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 4, 3363, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 5, 3364, "これで\n冷えた身体も\nポカポカやねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 7, 3366, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 8, 3367, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 10, 3369, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110601, 0, 0, 11, 3370, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 1, 3371, "真似したくなる\n人がここには\n大勢いますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 2, 3372, "偽物を一人\n紛れこませました\n…冗談です", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 3, 3373, "動物に変身して\nその辺りを\n散歩してきます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 4, 3374, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 5, 3375, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 101, 3376, "次は彼に\nどんなことを\n教えましょうか…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 102, 3377, "知識はともかく\n私の力はまだ\n必要なようですね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 1, 3378, "どうしました？\n…何でもない？\n…そうですか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 2, 3379, "退屈しています\n芸でもして\n楽しませなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 3, 3380, "研究のためです\n少し話し相手に\nなりなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 4, 3381, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 101, 3382, "貴方にはまだ色々と\n教えるべきことが\nありますね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 102, 3383, "随分多くの女性と\n知り合いなのですね\n驚きました", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 1, 1, 3384, "…\nおはようございます\n…まだ朝ですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 2, 2, 3385, "食事にします\n…どうしました？\n早く持ってきなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 3, 3, 3386, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 2, 3388, "マサキより\n座り心地は\nいいですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 3, 3389, "意外に複雑な\n構造ですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 4, 3390, "真似るなら\n香りまで\n再現しなくては", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 5, 3391, "発火現象の\n模倣…なかなか\n難しいものです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 7, 3393, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107001, 0, 0, 8, 3394, "暇つぶしには\nちょうどよさそうです\n…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 1, 3396, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 2, 3397, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 3, 3398, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 4, 3399, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 5, 3400, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 101, 3401, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 102, 3402, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 1, 3403, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 2, 3404, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 3, 3405, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 4, 3406, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 101, 3407, "学院でも、\nBB団の活動を\nしていきましょうね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 102, 3408, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 1, 1, 3409, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 2, 2, 3410, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 3, 3, 3411, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 2, 3413, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 3, 3414, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 4, 3415, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 5, 3416, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 7, 3418, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110701, 0, 0, 8, 3419, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 1, 3421, "あんた、何してんの。\nいいけど、なんでも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 2, 3422, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 3, 3423, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 4, 3424, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 5, 3425, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 101, 3426, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 102, 3427, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 1, 3428, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 2, 3429, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 3, 3430, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 4, 3431, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 101, 3432, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 102, 3433, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 1, 1, 3434, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 2, 2, 3435, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 3, 3, 3436, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 2, 3438, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 3, 3439, "なんだこれ\nかわいいかよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 4, 3440, "ふーん。\nこの匂い…\nなんかいいじゃん", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 5, 3441, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 7, 3443, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110801, 0, 0, 8, 3444, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 1, 3446, "イタズラってなにを\nしたらいいのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 2, 3447, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 3, 3448, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 4, 3449, "オバケ…いないよね？\n怖くなんてないけど！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 5, 3450, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 101, 3451, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 102, 3452, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 1, 3453, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 2, 3454, "触りましたね？\nイタズラしましたね？\nへんた～い！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 3, 3455, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 4, 3456, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 101, 3457, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 102, 3458, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 1, 1, 3459, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 2, 2, 3460, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 3, 3, 3461, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 2, 3463, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 3, 3464, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 4, 3465, "甘い匂いがいっぱい…\nお菓子の香りかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 5, 3466, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 7, 3468, "ニンジンもこれくらい\nおいしければ\nいいのに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 8, 3469, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111101, 0, 0, 12, 3471, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 1, 3473, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 2, 3474, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 3, 3475, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 4, 3476, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 5, 3477, "どうすればみんなを\nびっくりさせられる\nかな～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 101, 3478, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 102, 3479, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 1, 3480, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 2, 3481, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 3, 3482, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 4, 3483, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 101, 3484, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 102, 3485, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 1, 1, 3486, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 2, 2, 3487, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 3, 3, 3488, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 2, 3490, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 3, 3491, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 4, 3492, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 5, 3493, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 7, 3495, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 8, 3496, "本のなかに\nいたずらのヒント\nないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111201, 0, 0, 12, 3498, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 103, 3500, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 103, 3501, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 1, 3502, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 2, 3503, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 3, 3504, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 4, 3505, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 5, 3506, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 101, 3507, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 102, 3508, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 1, 3509, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 2, 3510, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 3, 3511, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 4, 3512, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 101, 3513, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 102, 3514, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 1, 1, 3515, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 2, 2, 3516, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 3, 3, 3517, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 2, 3519, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 3, 3520, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 4, 3521, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 5, 3522, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 7, 3524, "おいしい～♪\nおにいちゃんも\n食べようよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 8, 3525, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111301, 0, 0, 12, 3527, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 1, 3529, "ここ、好きだよ。\nみんな優しいから…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 2, 3530, "なんかさみしいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 3, 3531, "知らない人怖いな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 4, 3532, "お部屋おっきいねー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 5, 3533, "ルナね…\nお昼寝したいかも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 101, 3534, "おにいさん！\n今日は何して遊ぶの？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 102, 3535, "あのね、\nお料理教えてほしい！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 1, 3536, "あっ、おにいさんだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 2, 3537, "ルナは元気だよ！\nいいこだもん！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 3, 3538, "今日は何しようかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 4, 3539, "ルナと遊んでよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 101, 3540, "ねーねー、\nルナとお出かけして！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 102, 3541, "おにいさんと、\n遊びたいなー！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 1, 1, 3542, "ふふっ！\nなんかまぶしいね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 2, 2, 3543, "ルナ、お腹空いたな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 3, 3, 3544, "どこに行こうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 2, 3546, "ルナ、これ好きだな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 3, 3547, "かわいい！　好き！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 4, 3548, "なんかいい匂い！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 5, 3549, "あったかいねー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 7, 3551, "ふふっ\nおいしいね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111401, 0, 0, 8, 3552, "ルナ、\n眠くなっちゃう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 103, 3554, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 103, 3555, "あなたとなら\nどんなことだって\nできる気がするわ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 1, 3556, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 2, 3557, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 3, 3558, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 4, 3559, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 5, 3560, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 101, 3561, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 102, 3562, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 1, 3563, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 2, 3564, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 3, 3565, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 4, 3566, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 101, 3567, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 102, 3568, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 1, 1, 3569, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 2, 2, 3570, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 3, 3, 3571, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 2, 3573, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 3, 3574, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 4, 3575, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 5, 3576, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 7, 3578, "いつでも全力で\nケンカするために\n力を蓄えないとな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 8, 3579, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 1, 3581, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 2, 3582, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 3, 3583, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 4, 3584, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 5, 3585, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 101, 3586, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 102, 3587, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 1, 3588, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 2, 3589, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 3, 3590, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 4, 3591, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 101, 3592, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 102, 3593, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 1, 1, 3594, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 2, 2, 3595, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 3, 3, 3596, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 2, 3598, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 3, 3599, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 4, 3600, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 5, 3601, "体が温まってくると\nひと暴れしたくなるな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 7, 3603, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 8, 3604, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 14, 3606, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111501, 0, 0, 15, 3607, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 1, 3608, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 2, 3609, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 3, 3610, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 4, 3611, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 5, 3612, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 101, 3613, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 102, 3614, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 1, 3615, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 2, 3616, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 3, 3617, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 4, 3618, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 101, 3619, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 102, 3620, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 1, 1, 3621, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 2, 2, 3622, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 3, 3, 3623, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 2, 3625, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 3, 3626, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 4, 3627, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 5, 3628, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 7, 3630, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 8, 3631, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 14, 3633, "面白ーい！\nこの演出、ライブでも\nできないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111601, 0, 0, 15, 3634, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 103, 3635, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 103, 3636, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 1, 3637, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 2, 3638, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 3, 3639, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 4, 3640, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 5, 3641, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 101, 3642, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 102, 3643, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 1, 3644, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 2, 3645, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 3, 3646, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 4, 3647, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 101, 3648, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 102, 3649, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 1, 1, 3650, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 2, 2, 3651, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 3, 3, 3652, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 2, 3654, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 3, 3655, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 4, 3656, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 5, 3657, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 7, 3659, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 8, 3660, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 14, 3662, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111701, 0, 0, 15, 3663, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 1, 3664, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 2, 3665, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 3, 3666, "初日の出って\n特別な感じがして\nいいわよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 4, 3667, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 5, 3668, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 101, 3669, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 102, 3670, "今年もあいつと\n居られること…\n結構うれしいわ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 1, 3671, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 2, 3672, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 3, 3673, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 4, 3674, "門松飾るから\nちょっと手伝って\nくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 101, 3675, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 102, 3676, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 1, 1, 3677, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 2, 2, 3678, "おせちに飽きたら\n次は何を食べよう\nかしら…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 3, 3, 3679, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 2, 3681, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 3, 3682, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 4, 3683, "いい匂いね\n落ち着いて\n眠れそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 5, 3684, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 7, 3686, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112001, 0, 0, 8, 3687, "魔物を使わない\nおせちを\n調べておかなきゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 1, 3689, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 2, 3690, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 3, 3691, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 4, 3692, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 5, 3693, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 101, 3694, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 102, 3695, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 1, 3696, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 2, 3697, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 3, 3698, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 4, 3699, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 101, 3700, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 102, 3701, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 1, 1, 3702, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 2, 2, 3703, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 3, 3, 3704, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 2, 3706, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 3, 3707, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 4, 3708, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 5, 3709, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 7, 3711, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112101, 0, 0, 8, 3712, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 103, 3714, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 103, 3715, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 1, 3716, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 2, 3717, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 3, 3718, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 4, 3719, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 5, 3720, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 101, 3721, "主さまの温もりは\n心まで温めてくれる\nようです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 102, 3722, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 1, 3723, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 2, 3724, "お餅はゆっくりと\n召し上がって\nくださいまし", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 3, 3725, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 4, 3726, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 101, 3727, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 102, 3728, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 1, 1, 3729, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 2, 2, 3730, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 3, 3, 3731, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 2, 3733, "ふかふか…\nこのまま\n寝てしまいそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 3, 3734, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 4, 3735, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 5, 3736, "風邪をひきませんよう\n暖かくいたしましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 7, 3738, "食べ過ぎは\n正月太りの\nもとだというのに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111901, 0, 0, 8, 3739, "主さまのために\n参拝の作法を\n予習しておきましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 1, 3741, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 2, 3742, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 3, 3743, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 4, 3744, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 5, 3745, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 101, 3746, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 102, 3747, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 1, 3748, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 2, 3749, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 3, 3750, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 4, 3751, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 101, 3752, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 102, 3753, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 1, 1, 3754, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 2, 2, 3755, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 3, 3, 3756, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 2, 3758, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 3, 3759, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 4, 3760, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 5, 3761, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 7, 3763, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112201, 0, 0, 8, 3764, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 1, 3766, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 2, 3767, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 3, 3768, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 4, 3769, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 5, 3770, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 101, 3771, "あの人がいると\nなんだかホッとする\n不思議…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 102, 3772, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 1, 3773, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 2, 3774, "よかったら魔法の\n練習に付き合って\nくれませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 3, 3775, "あの…\nこの格好どうですか？\n似合ってると…いいな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 4, 3776, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 101, 3777, "私、あなたのそばに\nいられたら、それで…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 102, 3778, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 1, 1, 3779, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 2, 2, 3780, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 3, 3, 3781, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 2, 3783, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 3, 3784, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 4, 3785, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 5, 3786, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 7, 3788, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112301, 0, 0, 8, 3789, "ふふ…\n魔法少女になっても\n読書はやめられないな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 103, 3791, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 103, 3792, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 1, 3793, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 2, 3794, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 3, 3795, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 4, 3796, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 5, 3797, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 101, 3798, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 102, 3799, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 1, 3800, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 2, 3801, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 3, 3802, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 4, 3803, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 101, 3804, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 102, 3805, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 1, 1, 3806, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 2, 2, 3807, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 3, 3, 3808, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 2, 3810, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 3, 3811, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 4, 3812, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 5, 3813, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 7, 3815, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180401, 0, 0, 8, 3816, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 1, 3818, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 2, 3819, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 3, 3820, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 4, 3821, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 5, 3822, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 101, 3823, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 102, 3824, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 1, 3825, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 2, 3826, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 3, 3827, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 4, 3828, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 101, 3829, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 102, 3830, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 1, 1, 3831, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 2, 2, 3832, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 3, 3, 3833, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 2, 3835, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 3, 3836, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 4, 3837, "リラックスできる\nいい香りですね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 5, 3838, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 7, 3840, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112401, 0, 0, 8, 3841, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 1, 3843, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 2, 3844, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 3, 3845, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 4, 3846, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 5, 3847, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 101, 3848, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 102, 3849, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 1, 3850, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 2, 3851, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 3, 3852, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 4, 3853, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 101, 3854, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 102, 3855, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 1, 1, 3856, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 2, 2, 3857, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 3, 3, 3858, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 2, 3860, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 3, 3861, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 4, 3862, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 5, 3863, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 7, 3865, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112601, 0, 0, 8, 3866, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 103, 3868, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 103, 3869, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 103, 3870, "騎士クンの前で\n好きって言えるように\n練習しないと…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 103, 3871, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 103, 3872, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 103, 3873, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 1, 3874, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 2, 3875, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 3, 3876, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 4, 3877, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 5, 3878, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 101, 3879, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 102, 3880, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 1, 3881, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 2, 3882, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 3, 3883, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 4, 3884, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 101, 3885, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 102, 3886, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 1, 1, 3887, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 2, 2, 3888, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 3, 3, 3889, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 2, 3891, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 3, 3892, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 4, 3893, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 5, 3894, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 7, 3896, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112501, 0, 0, 8, 3897, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 1, 3899, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 2, 3900, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 3, 3901, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 4, 3902, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 5, 3903, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 101, 3904, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 102, 3905, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 1, 3906, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 2, 3907, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 3, 3908, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 4, 3909, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 101, 3910, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 102, 3911, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 1, 1, 3912, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 2, 2, 3913, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 3, 3, 3914, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 2, 3916, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 3, 3917, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 4, 3918, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 5, 3919, "炎はいいぞ\nそこには人類発展の\nドラマがある", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 7, 3921, "フオオー！\n次から次へと固形食物\n原理気になるー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111001, 0, 0, 8, 3922, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 103, 3924, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 103, 3925, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192101, 0, 0, 1, 3933, "ちぇるーん☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 1, 3951, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 2, 3952, "どこかに楽しいもの\n落ちてないかにゃー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 3, 3953, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 4, 3954, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 5, 3955, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 101, 3956, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 102, 3957, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 1, 3958, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 2, 3959, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 3, 3960, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 4, 3961, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 101, 3962, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 102, 3963, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 1, 1, 3964, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 2, 2, 3965, "今日のランチは\nどこで\n映えよっかにゃー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 3, 3, 3966, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 2, 3968, "ふかふかチエルン\n略して、ふかルン♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 3, 3969, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 4, 3970, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 5, 3971, "マシュマロは\n焼かない方のが\nかわいくないです？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 7, 3973, "チエル食べても\n太らない体質\nなんでぇ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (110901, 0, 0, 8, 3974, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 1, 3976, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 2, 3977, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 3, 3978, "あたしの代わりに誰か\n働いてくれないかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 4, 3979, "労働の後に食べる\nあんパン最高～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 5, 3980, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 101, 3981, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 102, 3982, "次の休みはあいつと\n一日中だらだら\nしたいな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 1, 3983, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 2, 3984, "お、差し入れ？\n気が利くねぇ\nあんがと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 3, 3985, "新しい依頼？\n今日はもう無理～\n閉店で～す", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 4, 3986, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 101, 3987, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 102, 3988, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 1, 1, 3989, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 2, 2, 3990, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 3, 3, 3991, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 2, 3993, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 3, 3994, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 4, 3995, "あんパンの香りの\nアロマとか\nあればいいのにな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 5, 3996, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 7, 3998, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112701, 0, 0, 8, 3999, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 103, 4001, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 103, 4002, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 1, 4003, "フロンティア\nスピリッツを\nお見せするべ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 2, 4004, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 3, 4005, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 4, 4006, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 5, 4007, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 101, 4008, "みんなオラより\n若ぇのに働いてて\n立派だべな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 102, 4009, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 1, 4010, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 2, 4011, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 3, 4012, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 4, 4013, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 101, 4014, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 102, 4015, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 1, 1, 4016, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 2, 2, 4017, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 3, 3, 4018, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 2, 4020, "牧草みたいに\nふっかふかだべ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 3, 4021, "この子、目の感じが\nエリザベスに似てるべ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 4, 4022, "いい匂いだべ～\n疲れた体に\nしみるべな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 5, 4023, "あったけぇな～\n牧場にも\n置いてみっかな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 7, 4025, "うまいべ！\n生産者のこだわりを\n感じるべさ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112801, 0, 0, 8, 4026, "酪農も戦いも\nまずは知識だべ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 1, 4028, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 2, 4029, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 3, 4030, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 4, 4031, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 5, 4032, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 101, 4033, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 102, 4034, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 1, 4035, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 2, 4036, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 3, 4037, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 4, 4038, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 101, 4039, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 102, 4040, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 1, 1, 4041, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 2, 2, 4042, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 3, 3, 4043, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 2, 4045, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 3, 4046, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 4, 4047, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 5, 4048, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 7, 4050, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113001, 0, 0, 8, 4051, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 1, 4053, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 2, 4054, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 3, 4055, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 4, 4056, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 5, 4057, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 101, 4058, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 102, 4059, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 1, 4060, "おでかけですか？\nではわたくしも\nお供いたします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 2, 4061, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 3, 4062, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 4, 4063, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 101, 4064, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 102, 4065, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 1, 1, 4066, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 2, 2, 4067, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 3, 3, 4068, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 2, 4070, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 3, 4071, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 4, 4072, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 5, 4073, "主さまの心と\n繋がったときのような\n温もりを感じます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 7, 4075, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180501, 0, 0, 8, 4076, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 103, 4078, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 103, 4079, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 1, 4080, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 2, 4081, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 3, 4082, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 4, 4083, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 5, 4084, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 101, 4085, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 102, 4086, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 1, 4087, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 2, 4088, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 3, 4089, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 4, 4090, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 101, 4091, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 102, 4092, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 1, 1, 4093, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 2, 2, 4094, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 3, 3, 4095, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 2, 4097, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 3, 4098, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 4, 4099, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 5, 4100, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 7, 4102, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (112901, 0, 0, 8, 4103, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 1, 4105, "退屈です…\nなにか楽しいことは\nないですか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 2, 4106, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 3, 4107, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 4, 4108, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 5, 4109, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 101, 4110, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 102, 4111, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 1, 4112, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 2, 4113, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 3, 4114, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 4, 4115, "お兄さんは\nヒマそうですね…\nうらやましいです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 101, 4116, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 102, 4117, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 1, 1, 4118, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 2, 2, 4119, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 3, 3, 4120, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 2, 4122, "はぁ～…\nもうここから一歩も\n動きたくないです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 3, 4123, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 4, 4124, "なんかおしゃれな\n匂いがするですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 5, 4125, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 7, 4127, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 8, 4128, "ふーむ…\nもっと楽しい本は\nないですかね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 1, 4130, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 2, 4131, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 3, 4132, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 4, 4133, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 5, 4134, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 101, 4135, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 102, 4136, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 1, 4137, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 2, 4138, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 3, 4139, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 4, 4140, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 101, 4141, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 102, 4142, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 1, 1, 4143, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 2, 2, 4144, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 3, 3, 4145, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 2, 4147, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 3, 4148, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 4, 4149, "心地よい香りで\n我が精神も安らぐ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 5, 4150, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 7, 4152, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 8, 4153, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 10, 4155, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113201, 0, 0, 11, 4156, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 1, 4157, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 2, 4158, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 3, 4159, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 4, 4160, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 5, 4161, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 101, 4162, "騎士クン…\n今どこにいるの…？\n…会いたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 102, 4163, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 1, 4164, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 2, 4165, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 3, 4166, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 4, 4167, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 101, 4168, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 102, 4169, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 1, 1, 4170, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 2, 2, 4171, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 3, 3, 4172, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 2, 4174, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 3, 4175, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 4, 4176, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 5, 4177, "温かい…\n騎士クンがくれる\n力みたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 7, 4179, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180201, 0, 0, 8, 4180, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 103, 4182, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 103, 4183, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 1, 4184, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 2, 4185, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 3, 4186, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 4, 4187, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 5, 4188, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 101, 4189, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 102, 4190, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 1, 4191, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 2, 4192, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 3, 4193, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 4, 4194, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 101, 4195, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 102, 4196, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 1, 1, 4197, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 2, 2, 4198, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 3, 3, 4199, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 2, 4201, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 3, 4202, "あんたたちも\n海を楽しみたいって\n思っているのかね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 4, 4203, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 5, 4204, "こういう格好だと\n少し冷えちまうんだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 7, 4206, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 8, 4207, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 10, 4209, "へえ…\nなかなかいい\n乗り心地じゃないか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113101, 0, 0, 11, 4210, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 1, 4211, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 2, 4212, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 3, 4213, "今のナナカちゃんは\nたぶん水属性！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 4, 4214, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 5, 4215, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 101, 4216, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 102, 4217, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 1, 4218, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 2, 4219, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 3, 4220, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 4, 4221, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 101, 4222, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 102, 4223, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 1, 1, 4224, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 2, 2, 4225, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 3, 3, 4226, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 2, 4228, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 3, 4229, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 4, 4230, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 5, 4231, "夜になると急に冷えて\n肌寒いんだよね～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 7, 4233, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 8, 4234, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 10, 4236, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113301, 0, 0, 11, 4237, "ナナカちゃんの\n誕生～\nなんちゃって", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 1, 4238, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 2, 4239, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 3, 4240, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 4, 4241, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 5, 4242, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 101, 4243, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 102, 4244, "キミへの応援\nちゃんと\n届いてるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 1, 4245, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 2, 4246, "どうしたの？\n元気がないなら\n私が応援するよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 3, 4247, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 4, 4248, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 101, 4249, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 102, 4250, "キミを応援\nすることができて…\n私、すごく幸せ…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 1, 1, 4251, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 2, 2, 4252, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 3, 3, 4253, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 2, 4255, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 3, 4256, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 4, 4257, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 5, 4258, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 7, 4260, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 8, 4261, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 10, 4263, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113401, 0, 0, 11, 4264, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 1, 4265, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 2, 4266, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 3, 4267, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 4, 4268, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 5, 4269, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 101, 4270, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 102, 4271, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 1, 4272, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 2, 4273, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 3, 4274, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 4, 4275, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 101, 4276, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 102, 4277, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 1, 1, 4278, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 2, 2, 4279, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 3, 3, 4280, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 2, 4282, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 3, 4283, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 4, 4284, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 5, 4285, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 7, 4287, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 8, 4288, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 10, 4290, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113501, 0, 0, 11, 4291, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 103, 4292, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 103, 4293, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 1, 4294, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 2, 4295, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 3, 4296, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 4, 4297, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 5, 4298, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 101, 4299, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 102, 4300, "少年との\n海のバカンス…\nまた行きたい\nものだな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 1, 4301, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 2, 4302, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 3, 4303, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 4, 4304, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 101, 4305, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 102, 4306, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 1, 1, 4307, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 2, 2, 4308, "昼食のお誘いかい？\nいいよ、付き合おう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 3, 3, 4309, "もう夜か…\n一日が経つのは\nはやいものだね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 2, 4311, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 3, 4312, "うん…ふわふわの\n感触が\nよくわかるな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 4, 4313, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 5, 4314, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 7, 4316, "兜を気にせず\n食べられるのは\n楽でいいな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 8, 4317, "ふむ…鎧がないと\nページがめくりやすい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 10, 4319, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113601, 0, 0, 11, 4320, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 1, 4321, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 2, 4322, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 3, 4323, "色んな人を観察できて\n眼福眼福…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 4, 4324, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 5, 4325, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 101, 4326, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 102, 4327, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 1, 4328, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 2, 4329, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 3, 4330, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 4, 4331, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 101, 4332, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 102, 4333, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 1, 1, 4334, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 2, 2, 4335, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 3, 3, 4336, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 2, 4338, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 3, 4339, "…これ\nネネカが変身\nしてないよね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 4, 4340, "いい香りだ…\n解析完了\n今度生成しようっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 5, 4341, "安心するな～…\nアジトに置けないから\n羨ましいよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 7, 4343, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106801, 0, 0, 8, 4344, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 1, 4346, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 2, 4347, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 3, 4348, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 4, 4349, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 5, 4350, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 101, 4351, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 102, 4352, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 1, 4353, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 2, 4354, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 3, 4355, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 4, 4356, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 101, 4357, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 102, 4358, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 1, 1, 4359, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 2, 2, 4360, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 3, 3, 4361, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 2, 4363, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 3, 4364, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 4, 4365, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 5, 4366, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 7, 4368, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113701, 0, 0, 8, 4369, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 103, 4371, "あの方の前では\n常に気高く\nありたいですわね", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 103, 4372, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 103, 4373, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 103, 4374, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 1, 4375, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 2, 4376, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 3, 4377, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 4, 4378, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 5, 4379, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 101, 4380, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 102, 4381, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 1, 4382, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 2, 4383, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 3, 4384, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 4, 4385, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 101, 4386, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 102, 4387, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 1, 1, 4388, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 2, 2, 4389, "ふぅ、いいことした\n後のランチは\nおいしいわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 3, 3, 4390, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 2, 4392, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 3, 4393, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 4, 4394, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 5, 4395, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 7, 4397, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113801, 0, 0, 8, 4398, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 1, 4400, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 2, 4401, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 3, 4402, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 4, 4403, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 5, 4404, "マントは動きに\nメリハリがついて\nいいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 101, 4405, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 102, 4406, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 1, 4407, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 2, 4408, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 3, 4409, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 4, 4410, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 101, 4411, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 102, 4412, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 1, 1, 4413, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 2, 2, 4414, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 3, 3, 4415, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 2, 4417, "このフィット感\nどうにか服作りにも\n活かせれば…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 3, 4418, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 4, 4419, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 5, 4420, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 7, 4422, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 8, 4423, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (113901, 0, 0, 12, 4425, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 1, 4427, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 2, 4428, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 3, 4429, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 4, 4430, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 5, 4431, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 101, 4432, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 102, 4433, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 1, 4434, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 2, 4435, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 3, 4436, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 4, 4437, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 101, 4438, "あんたも一緒に\n新しい技の練習\nどうッスか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 102, 4439, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 1, 1, 4440, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 2, 2, 4441, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 3, 3, 4442, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 2, 4444, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 3, 4445, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 4, 4446, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 5, 4447, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 7, 4449, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 8, 4450, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114101, 0, 0, 12, 4452, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 103, 4454, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 103, 4455, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 1, 4456, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 2, 4457, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 3, 4458, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 4, 4459, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 5, 4460, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 101, 4461, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 102, 4462, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 1, 4463, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 2, 4464, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 3, 4465, "どうしたの？\n出かけるなら\n私も同行しようか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 4, 4466, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 101, 4467, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 102, 4468, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 1, 1, 4469, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 2, 2, 4470, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 3, 3, 4471, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 2, 4473, "ハロウィンはまだ\nこれからだ\n一休みしようか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 3, 4474, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 4, 4475, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 5, 4476, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 7, 4478, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 8, 4479, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114001, 0, 0, 12, 4481, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 1, 4483, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 2, 4484, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 3, 4485, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 4, 4486, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 5, 4487, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 101, 4488, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 102, 4489, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 1, 4490, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 2, 4491, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 3, 4492, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 4, 4493, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 101, 4494, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 102, 4495, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 1, 1, 4496, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 2, 2, 4497, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 3, 3, 4498, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 2, 4500, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 3, 4501, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 4, 4502, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 5, 4503, "この温かさ…\n愛と希望に\n通じるものがある…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 7, 4505, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114201, 0, 0, 8, 4506, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 103, 4508, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 103, 4509, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 1, 4510, "ぴっぷる！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 2, 4511, "ぴっぷるぴっぷる♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 3, 4512, "ぴっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 4, 4513, "ぷっぷー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 5, 4514, "ぴぃ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 1, 4515, "ぴっぷるんるん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 2, 4516, "ぷぅ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 3, 4517, "ぴぴっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 0, 4, 4518, "ぷぅー…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 1, 1, 4519, "ちゅ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 2, 2, 4520, "ぷはー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (192501, 0, 3, 3, 4521, "ぴぇー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 1, 4531, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 2, 4532, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 3, 4533, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 4, 4534, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 5, 4535, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 101, 4536, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 102, 4537, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 1, 4538, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 2, 4539, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 3, 4540, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 4, 4541, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 101, 4542, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 102, 4543, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 1, 1, 4544, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 2, 2, 4545, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 3, 3, 4546, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 2, 4548, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 3, 4549, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 4, 4550, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 5, 4551, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 7, 4553, "こ、これは…\nおいしすぎて手が\n止まらないかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114301, 0, 0, 8, 4554, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 1, 4556, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 2, 4557, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 3, 4558, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 4, 4559, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 5, 4560, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 101, 4561, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 102, 4562, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 1, 4563, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 2, 4564, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 3, 4565, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 4, 4566, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 101, 4567, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 102, 4568, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 1, 1, 4569, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 2, 2, 4570, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 3, 3, 4571, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 2, 4573, "適度な休息は\n成功の秘訣ですわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 3, 4574, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 4, 4575, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 5, 4576, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 7, 4578, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 8, 4579, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 14, 4581, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114401, 0, 0, 15, 4582, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 1, 4583, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 2, 4584, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 3, 4585, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 4, 4586, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 5, 4587, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 101, 4588, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 102, 4589, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 1, 4590, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 2, 4591, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 3, 4592, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 4, 4593, "お姉さんの\n頼れるところ\n見せてあげるわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 101, 4594, "何かあったら\n頼ってくれても\nいいんだからね？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 102, 4595, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 1, 1, 4596, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 2, 2, 4597, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 3, 3, 4598, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 2, 4600, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 3, 4601, "私だって…\nかわいいものは\n好きでいいわよね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 4, 4602, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 5, 4603, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 7, 4605, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 8, 4606, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 14, 4608, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114601, 0, 0, 15, 4609, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 103, 4610, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 103, 4611, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 1, 4612, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 2, 4613, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 3, 4614, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 4, 4615, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 5, 4616, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 101, 4617, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 102, 4618, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 1, 4619, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 2, 4620, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 3, 4621, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 4, 4622, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 101, 4623, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 102, 4624, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 1, 1, 4625, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 2, 2, 4626, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 3, 3, 4627, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 2, 4629, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 3, 4630, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 4, 4631, "華やかな香りね\nクリスマスに\nピッタリだわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 5, 4632, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 7, 4634, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 8, 4635, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 14, 4637, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114501, 0, 0, 15, 4638, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 1, 4639, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 2, 4640, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 3, 4641, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 4, 4642, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 5, 4643, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 101, 4644, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 102, 4645, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 1, 4646, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 2, 4647, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 3, 4648, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 4, 4649, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 101, 4650, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 102, 4651, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 1, 1, 4652, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 2, 2, 4653, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 3, 3, 4654, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 2, 4656, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 3, 4657, "なんだ？\nオマエも初詣に\n行きたいのか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 4, 4658, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 5, 4659, "コイツがあれば\nうまい魚が\n焼けそうだな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 7, 4661, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (114701, 0, 0, 8, 4662, "おみくじって\n書いてあることが\nむずかしいんだよな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 1, 4664, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 2, 4665, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 3, 4666, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 4, 4667, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 5, 4668, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 101, 4669, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 102, 4670, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 1, 4671, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 2, 4672, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 3, 4673, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 4, 4674, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 101, 4675, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 102, 4676, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 1, 1, 4677, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 2, 2, 4678, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 3, 3, 4679, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 2, 4681, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 3, 4682, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 4, 4683, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 5, 4684, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 7, 4686, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180101, 0, 0, 8, 4687, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 1, 4689, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 2, 4690, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 3, 4691, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 4, 4692, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 5, 4693, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 101, 4694, "ずっとこうして\n年の始まりを\n過ごしたいですね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 102, 4695, "いつか二人きりで\n初日の出を\n見たいです", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 1, 4696, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 2, 4697, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 3, 4698, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 4, 4699, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 101, 4700, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 102, 4701, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 1, 1, 4702, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 2, 2, 4703, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 3, 3, 4704, "あたたかくして\n幸せな初夢を\n見ましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 2, 4706, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 3, 4707, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 4, 4708, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 5, 4709, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 7, 4711, "おいしすぎて\n食べる手が\n止まりません～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (111801, 0, 0, 8, 4712, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 103, 4714, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 103, 4715, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 1, 4716, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 2, 4717, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 3, 4718, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 4, 4719, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 5, 4720, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 101, 4721, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 102, 4722, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 1, 4723, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 2, 4724, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 3, 4725, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 4, 4726, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 101, 4727, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 102, 4728, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 1, 1, 4729, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 2, 2, 4730, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 3, 3, 4731, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 2, 4733, "少し休みます…\n時間が来たら\n起こすように", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 3, 4734, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 4, 4735, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 5, 4736, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 7, 4738, "いかなる強者でも\n兵站、補給は\n必要なものです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115001, 0, 0, 8, 4739, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 1, 4741, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 2, 4742, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 3, 4743, "主さまも精霊の召喚を\n試してみますか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 4, 4744, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 5, 4745, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 101, 4746, "進化した精霊に\n負けぬよう\n修行に励みます", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 102, 4747, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 1, 4748, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 2, 4749, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 3, 4750, "里で採れた果物を\n持って参りました\n召し上がってください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 4, 4751, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 101, 4752, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 102, 4753, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 1, 1, 4754, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 2, 2, 4755, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 3, 3, 4756, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 2, 4758, "この癒し…\n主さまにも味わって\nいただきたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 3, 4759, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 4, 4760, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 5, 4761, "瞑想するのに\nちょうどよい暖かさで\nございます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 7, 4763, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115501, 0, 0, 8, 4764, "風の精霊について\nもっと詳しく知って\nおかなければ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 1, 4766, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 2, 4767, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 3, 4768, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 4, 4769, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 5, 4770, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 101, 4771, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 102, 4772, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 1, 4773, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 2, 4774, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 3, 4775, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 4, 4776, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 101, 4777, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 102, 4778, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 1, 1, 4779, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 2, 2, 4780, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 3, 3, 4781, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 2, 4783, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 3, 4784, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 4, 4785, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 5, 4786, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 7, 4788, "自然の恵みに\n感謝して…\nいただきます♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115601, 0, 0, 8, 4789, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 103, 4791, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 103, 4792, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 1, 4793, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 2, 4794, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 3, 4795, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 4, 4796, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 5, 4797, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 101, 4798, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 102, 4799, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 1, 4800, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 2, 4801, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 3, 4802, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 4, 4803, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 101, 4804, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 102, 4805, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 1, 1, 4806, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 2, 2, 4807, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 3, 3, 4808, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 2, 4810, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 3, 4811, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 4, 4812, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 5, 4813, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 7, 4815, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 8, 4816, "ん…なるほどね。\n勉強になるわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 1, 4818, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 2, 4819, "依頼があれば\n海でも山でも\n赴くさ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 3, 4820, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 4, 4821, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 5, 4822, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 101, 4823, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 102, 4824, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 1, 4825, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 2, 4826, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 3, 4827, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 4, 4828, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 101, 4829, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 102, 4830, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 1, 1, 4831, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 2, 2, 4832, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 3, 3, 4833, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 2, 4835, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 3, 4836, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 4, 4837, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 5, 4838, "ポカポカするね…\n助手くん\n君もおいでよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 7, 4840, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 8, 4841, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 10, 4843, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115701, 0, 0, 11, 4844, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 1, 4845, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 2, 4846, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 3, 4847, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 4, 4848, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 5, 4849, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 101, 4850, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 102, 4851, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 1, 4852, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 2, 4853, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 3, 4854, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 4, 4855, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 101, 4856, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 102, 4857, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 1, 1, 4858, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 2, 2, 4859, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 3, 3, 4860, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 2, 4862, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 3, 4863, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 4, 4864, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 5, 4865, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 7, 4867, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180601, 0, 0, 8, 4868, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 1, 4870, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 2, 4871, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 3, 4872, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 4, 4873, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 5, 4874, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 101, 4875, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 102, 4876, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 1, 4877, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 2, 4878, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 3, 4879, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 4, 4880, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 101, 4881, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 102, 4882, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 1, 1, 4883, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 2, 2, 4884, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 3, 3, 4885, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 2, 4887, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 3, 4888, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 4, 4889, "すてきな香り…\nお花かしら？\n森で探してみようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 5, 4890, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 7, 4892, "レッスンのために\n栄養補給して\nおかないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115801, 0, 0, 8, 4893, "教養を身に着ける\nことも大切よね！\nなになに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 1, 4895, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 2, 4896, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 3, 4897, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 4, 4898, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 5, 4899, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 101, 4900, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 102, 4901, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 1, 4902, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 2, 4903, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 3, 4904, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 4, 4905, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 101, 4906, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 102, 4907, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 1, 1, 4908, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 2, 2, 4909, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 3, 3, 4910, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 2, 4912, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 3, 4913, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 4, 4914, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 5, 4915, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 7, 4917, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (115901, 0, 0, 8, 4918, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 103, 4920, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 103, 4921, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 103, 4922, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 103, 4923, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 103, 4924, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103601, 0, 0, 103, 4925, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 1, 4926, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 2, 4927, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 3, 4928, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 4, 4929, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 5, 4930, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 101, 4931, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 102, 4932, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 1, 4933, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 2, 4934, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 3, 4935, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 4, 4936, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 101, 4937, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 102, 4938, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 1, 1, 4939, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 2, 2, 4940, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 3, 3, 4941, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 2, 4943, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 3, 4944, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 4, 4945, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 5, 4946, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 7, 4948, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116001, 0, 0, 8, 4949, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 1, 4951, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 2, 4952, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 3, 4953, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 4, 4954, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 5, 4955, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 101, 4956, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 102, 4957, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 1, 4958, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 2, 4959, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 3, 4960, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 4, 4961, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 101, 4962, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 102, 4963, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 1, 1, 4964, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 2, 2, 4965, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 3, 3, 4966, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 2, 4968, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 3, 4969, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 4, 4970, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 5, 4971, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 7, 4973, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116301, 0, 0, 8, 4974, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 103, 4976, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 103, 4977, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 1, 4978, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 2, 4979, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 3, 4980, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 4, 4981, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 5, 4982, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 101, 4983, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 102, 4984, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 1, 4985, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 2, 4986, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 3, 4987, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 4, 4988, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 101, 4989, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 102, 4990, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 1, 1, 4991, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 2, 2, 4992, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 3, 3, 4993, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 2, 4995, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 3, 4996, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 4, 4997, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 5, 4998, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 7, 5000, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116201, 0, 0, 8, 5001, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 1, 5003, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 2, 5004, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 3, 5005, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 4, 5006, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 5, 5007, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 101, 5008, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 102, 5009, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 1, 5010, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 2, 5011, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 3, 5012, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 4, 5013, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 101, 5014, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 102, 5015, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 1, 1, 5016, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 2, 2, 5017, "お腹が空きました…\nご飯食べにいきますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 3, 3, 5018, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 2, 5020, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 3, 5021, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 4, 5022, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 5, 5023, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 7, 5025, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116501, 0, 0, 8, 5026, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 103, 5028, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 103, 5029, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 1, 5030, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 2, 5031, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 3, 5032, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 4, 5033, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 5, 5034, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 101, 5035, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 102, 5036, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 1, 5037, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 2, 5038, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 3, 5039, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 4, 5040, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 101, 5041, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 102, 5042, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 1, 1, 5043, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 2, 2, 5044, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 3, 3, 5045, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 2, 5047, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 3, 5048, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 4, 5049, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 5, 5050, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 7, 5052, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116601, 0, 0, 8, 5053, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 1, 5055, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 2, 5056, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 3, 5057, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 4, 5058, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 5, 5059, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 101, 5060, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 102, 5061, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 1, 5062, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 2, 5063, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 3, 5064, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 4, 5065, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 101, 5066, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 102, 5067, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 1, 1, 5068, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 2, 2, 5069, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 3, 3, 5070, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 2, 5072, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 3, 5073, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 4, 5074, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 5, 5075, "ぽかぽか…\n幸せなひとときですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 7, 5077, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116701, 0, 0, 8, 5078, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 1, 5080, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 2, 5081, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 3, 5082, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 4, 5083, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 5, 5084, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 101, 5085, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 102, 5086, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 1, 5087, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 2, 5088, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 3, 5089, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 4, 5090, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 101, 5091, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 102, 5092, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 1, 1, 5093, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 2, 2, 5094, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 3, 3, 5095, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 2, 5097, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 3, 5098, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 4, 5099, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 5, 5100, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 7, 5102, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116801, 0, 0, 8, 5103, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 103, 5105, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 103, 5106, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 1, 5107, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 2, 5108, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 3, 5109, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 4, 5110, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 5, 5111, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 101, 5112, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 102, 5113, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 1, 5114, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 2, 5115, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 3, 5116, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 4, 5117, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 101, 5118, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 102, 5119, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 1, 1, 5120, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 2, 2, 5121, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 3, 3, 5122, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 2, 5124, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 3, 5125, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 4, 5126, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 5, 5127, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 7, 5129, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116901, 0, 0, 8, 5130, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 1, 5132, "変身している間に\n色々と試しておこう\nまずは…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 2, 5133, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 3, 5134, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 4, 5135, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 5, 5136, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 101, 5137, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 102, 5138, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 1, 5139, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 2, 5140, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 3, 5141, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 4, 5142, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 101, 5143, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 102, 5144, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 1, 1, 5145, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 2, 2, 5146, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 3, 3, 5147, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 2, 5149, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 3, 5150, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 4, 5151, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 5, 5152, "ありがたいな\n少し足元が\n冷えていたところだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 7, 5154, "これは…！\n持ち運べるように\n改良できないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (180301, 0, 0, 8, 5155, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 1, 5157, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 2, 5158, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 3, 5159, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 4, 5160, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 5, 5161, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 101, 5162, "あのおかたの全てが\n早く私のものに\nなりますように…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 102, 5163, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 1, 5164, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 2, 5165, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 3, 5166, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 4, 5167, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 101, 5168, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 102, 5169, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 1, 1, 5170, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 2, 2, 5171, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 3, 3, 5172, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 2, 5174, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 3, 5175, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 4, 5176, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 5, 5177, "あのおかたの\nぬくもりを\n思い出しますわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 7, 5179, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 8, 5180, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 10, 5182, "二人乗りができると\nいいですわね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117001, 0, 0, 11, 5183, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 103, 5184, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 103, 5185, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 1, 5186, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 2, 5187, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 3, 5188, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 4, 5189, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 5, 5190, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 101, 5191, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 102, 5192, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 1, 5193, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 2, 5194, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 3, 5195, "海で迷子に\nならないように\n手を繋ごうね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 4, 5196, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 101, 5197, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 102, 5198, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 1, 1, 5199, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 2, 2, 5200, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 3, 3, 5201, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 2, 5203, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 3, 5204, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 4, 5205, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 5, 5206, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 7, 5208, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 8, 5209, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 10, 5211, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117101, 0, 0, 11, 5212, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 1, 5213, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 2, 5214, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 3, 5215, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 4, 5216, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 5, 5217, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 101, 5218, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 102, 5219, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 1, 5220, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 2, 5221, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 3, 5222, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 4, 5223, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 101, 5224, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 102, 5225, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 1, 1, 5226, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 2, 2, 5227, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 3, 3, 5228, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 2, 5230, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 3, 5231, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 4, 5232, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 5, 5233, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 7, 5235, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 8, 5236, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 10, 5238, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117201, 0, 0, 11, 5239, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 1, 5240, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 2, 5241, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 3, 5242, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 4, 5243, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 5, 5244, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 101, 5245, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 102, 5246, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 1, 5247, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 2, 5248, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 3, 5249, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 4, 5250, "仕立て屋も\nアイドルも\n諦めません！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 101, 5251, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 102, 5252, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 1, 1, 5253, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 2, 2, 5254, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 3, 3, 5255, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 2, 5257, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 3, 5258, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 4, 5259, "いい香りですね～\n波の音が聞こえて\nきそうです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 5, 5260, "泳いだ後は\n少し寒いので\nちょうどいいですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 7, 5262, "夏バテしないように\n栄養はしっかり\n取らないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 8, 5263, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 10, 5265, "デザインはもちろん\n触り心地も\n最高ですね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117401, 0, 0, 11, 5266, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 103, 5267, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 103, 5268, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 1, 5269, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 2, 5270, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 3, 5271, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 4, 5272, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 5, 5273, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 101, 5274, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 102, 5275, "水着、似合ってるって\n言ってもらえるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 1, 5276, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 2, 5277, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 3, 5278, "なんでしょう？\nもしかして…\nボイトレに興味が？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 4, 5279, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 101, 5280, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 102, 5281, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 1, 1, 5282, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 2, 2, 5283, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 3, 3, 5284, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 2, 5286, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 3, 5287, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 4, 5288, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 5, 5289, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 7, 5291, "アイドルとして、\n食べすぎには\n気をつけないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 8, 5292, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 10, 5294, "この子に乗りながら\nライブをする…\nというのはどうですか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117301, 0, 0, 11, 5295, "まるで人魚になった\n気分…なんて、ふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 1, 5296, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 2, 5297, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 3, 5298, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 4, 5299, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 5, 5300, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 101, 5301, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 102, 5302, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 1, 5303, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 2, 5304, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 3, 5305, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 4, 5306, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 101, 5307, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 102, 5308, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 1, 1, 5309, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 2, 2, 5310, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 3, 3, 5311, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 2, 5313, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 3, 5314, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 4, 5315, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 5, 5316, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 7, 5318, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118301, 0, 0, 8, 5319, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 1, 5322, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 2, 5323, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 3, 5324, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 4, 5325, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 5, 5326, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 101, 5327, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 102, 5328, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 1, 5329, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 2, 5330, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 3, 5331, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 4, 5332, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 101, 5333, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 102, 5334, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 1, 1, 5335, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 2, 2, 5336, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 3, 3, 5337, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 2, 5339, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 3, 5340, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 4, 5341, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 5, 5342, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 7, 5344, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118401, 0, 0, 8, 5345, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 1, 5348, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 2, 5349, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 3, 5350, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 4, 5351, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 5, 5352, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 101, 5353, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 102, 5354, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 1, 5355, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 2, 5356, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 3, 5357, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 4, 5358, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 101, 5359, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 102, 5360, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 1, 1, 5361, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 2, 2, 5362, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 3, 3, 5363, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 2, 5365, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 3, 5366, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 4, 5367, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 5, 5368, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 7, 5370, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117501, 0, 0, 8, 5371, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 1, 5400, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 2, 5401, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 3, 5402, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 4, 5403, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 5, 5404, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 101, 5405, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 102, 5406, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 1, 5407, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 2, 5408, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 3, 5409, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 4, 5410, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 101, 5411, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 102, 5412, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 1, 1, 5413, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 2, 2, 5414, "美は食で作られる\n美容にいいものを\n食べようよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 3, 3, 5415, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 2, 5417, "ふう～…\nリラックスする\nボクも美しい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 3, 5418, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 4, 5419, "美しいボクは\n常にいい香りを\nしていなくちゃね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 5, 5420, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 7, 5422, "バランスのいい食事が\n美しいボクを\n作るのさ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117601, 0, 0, 8, 5423, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 103, 5425, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 103, 5426, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 1, 5427, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 2, 5428, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 3, 5429, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 4, 5430, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 5, 5431, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 101, 5432, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 102, 5433, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 1, 5434, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 2, 5435, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 3, 5436, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 4, 5437, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 101, 5438, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 102, 5439, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 1, 1, 5440, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 2, 2, 5441, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 3, 3, 5442, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 2, 5444, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 3, 5445, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 4, 5446, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 5, 5447, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 7, 5449, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118001, 0, 0, 8, 5450, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 1, 5452, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 2, 5453, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 3, 5454, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 4, 5455, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 5, 5456, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 101, 5457, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 102, 5458, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 1, 5459, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 2, 5460, "この制服、\nハロウィンらしくて\n大好きさ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 3, 5461, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 4, 5462, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 101, 5463, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 102, 5464, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 1, 1, 5465, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 2, 2, 5466, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 3, 3, 5467, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 2, 5469, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 3, 5470, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 4, 5471, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 5, 5472, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 7, 5474, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 8, 5475, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117701, 0, 0, 12, 5477, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 1, 5479, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 2, 5480, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 3, 5481, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 4, 5482, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 5, 5483, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 101, 5484, "幽霊になる前に\n天下統一を\n果たしたいデスね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 102, 5485, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 1, 5486, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 2, 5487, "妖魔を家来に\nしてみるのは\nどうデス？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 3, 5488, "マントを使って\nウツセミの術の精度を\n高めるデス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 4, 5489, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 101, 5490, "あの世でも\nショーグンに\nお仕えしマスよー！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 102, 5491, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 1, 1, 5492, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 2, 2, 5493, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 3, 3, 5494, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 2, 5496, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 3, 5497, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 4, 5498, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 5, 5499, "パレードの熱気に\n負けないくらい\n熱いデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 7, 5501, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 8, 5502, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117801, 0, 0, 12, 5504, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 103, 5531, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101301, 0, 0, 103, 5532, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 1, 5533, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 2, 5534, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 3, 5535, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 4, 5536, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 5, 5537, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 101, 5538, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 102, 5539, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 1, 5540, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 2, 5541, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 3, 5542, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 4, 5543, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 101, 5544, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 102, 5545, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 1, 1, 5546, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 2, 2, 5547, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 3, 3, 5548, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 2, 5550, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 3, 5551, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 4, 5552, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 5, 5553, "やばばば…\nあったかくて\n眠くなっちゃう～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 7, 5555, "これ食べて\nもうひとがんばり\nしよーっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 8, 5556, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (117901, 0, 0, 12, 5558, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 1, 5560, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 2, 5561, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 3, 5562, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 4, 5563, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 5, 5564, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 101, 5565, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 102, 5566, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 1, 5567, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 2, 5568, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 3, 5569, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 4, 5570, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 101, 5571, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 102, 5572, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 1, 1, 5573, "今日も一日\n管理人として\nがんばります♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 2, 2, 5574, "お腹がすきました…\nそろそろお昼に\nしませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 3, 3, 5575, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 2, 5577, "良質なお仕事は\n良質な休憩から\nです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 3, 5578, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 4, 5579, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 5, 5580, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 7, 5582, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118501, 0, 0, 8, 5583, "新条例はこのように\n活用されている、と…\nふむふむ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 1, 5585, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 2, 5586, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 3, 5587, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 4, 5588, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 5, 5589, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 101, 5590, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 102, 5591, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 1, 5592, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 2, 5593, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 3, 5594, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 4, 5595, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 101, 5596, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 102, 5597, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 1, 1, 5598, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 2, 2, 5599, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 3, 3, 5600, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 2, 5602, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 3, 5603, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 4, 5604, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 5, 5605, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 7, 5607, "この味付け…\nあっちでも再現\nできるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106701, 0, 0, 8, 5608, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 1, 5610, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 2, 5611, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 3, 5612, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 4, 5613, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 5, 5614, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 101, 5615, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 102, 5616, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 1, 5617, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 2, 5618, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 3, 5619, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 4, 5620, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 101, 5621, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 102, 5622, "君との秘密が\nまた一つ\n増えちゃったね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 1, 1, 5623, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 2, 2, 5624, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 3, 3, 5625, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 2, 5627, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 3, 5628, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 4, 5629, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 5, 5630, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 7, 5632, "生徒の見本に\nなれるように\n好き嫌いはしないの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119001, 0, 0, 8, 5633, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 103, 5635, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106301, 0, 0, 103, 5636, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 103, 5637, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 103, 5638, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 1, 5639, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 2, 5640, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 3, 5641, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 4, 5642, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 5, 5643, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 101, 5644, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 102, 5645, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 1, 5646, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 2, 5647, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 3, 5648, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 4, 5649, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 101, 5650, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 102, 5651, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 1, 1, 5652, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 2, 2, 5653, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 3, 3, 5654, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 2, 5656, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 3, 5657, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 4, 5658, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 5, 5659, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 7, 5661, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119101, 0, 0, 8, 5662, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 1, 5664, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 2, 5665, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 3, 5666, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 4, 5667, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 5, 5668, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 101, 5669, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 102, 5670, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 1, 5671, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 2, 5672, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 3, 5673, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 4, 5674, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 101, 5675, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 102, 5676, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 1, 1, 5677, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 2, 2, 5678, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 3, 3, 5679, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 2, 5681, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 3, 5682, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 4, 5683, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 5, 5684, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 7, 5686, "これはオマエがくれる\nプリンとは\n別腹なの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 8, 5687, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 14, 5689, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119901, 0, 0, 15, 5690, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 1, 5691, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 2, 5692, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 3, 5693, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 4, 5694, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 5, 5695, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 101, 5696, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 102, 5697, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 1, 5698, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 2, 5699, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 3, 5700, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 4, 5701, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 101, 5702, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 102, 5703, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 1, 1, 5704, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 2, 2, 5705, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 3, 3, 5706, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 2, 5708, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 3, 5709, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 4, 5710, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 5, 5711, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 7, 5713, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 8, 5714, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 14, 5716, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119201, 0, 0, 15, 5717, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 103, 5718, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 103, 5719, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 1, 5720, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 2, 5721, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 3, 5722, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 4, 5723, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 5, 5724, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 101, 5725, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 102, 5726, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 1, 5727, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 2, 5728, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 3, 5729, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 4, 5730, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 101, 5731, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 102, 5732, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 1, 1, 5733, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 2, 2, 5734, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 3, 3, 5735, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 2, 5737, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 3, 5738, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 4, 5739, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 5, 5740, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 7, 5742, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 8, 5743, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 14, 5745, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (119301, 0, 0, 15, 5746, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 1, 5747, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 2, 5748, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 3, 5749, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 4, 5750, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 5, 5751, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 101, 5752, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 102, 5753, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 1, 5754, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 2, 5755, "きゃあっ！？\nへ、変なところを\n触らないでください！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 3, 5756, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 4, 5757, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 101, 5758, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 102, 5759, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 1, 1, 5760, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 2, 2, 5761, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 3, 3, 5762, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 2, 5764, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 3, 5765, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 4, 5766, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 5, 5767, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 7, 5769, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120601, 0, 0, 8, 5770, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 1, 5773, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 2, 5774, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 3, 5775, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 4, 5776, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 5, 5777, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 101, 5778, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 102, 5779, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 1, 5780, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 2, 5781, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 3, 5782, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 4, 5783, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 101, 5784, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 102, 5785, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 1, 1, 5786, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 2, 2, 5787, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 3, 3, 5788, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 2, 5790, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 3, 5791, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 4, 5792, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 5, 5793, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 7, 5795, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120401, 0, 0, 8, 5796, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 1, 5799, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 2, 5800, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 3, 5801, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 4, 5802, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 5, 5803, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 101, 5804, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 102, 5805, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 1, 5806, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 2, 5807, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 3, 5808, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 4, 5809, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 101, 5810, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 102, 5811, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 1, 1, 5812, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 2, 2, 5813, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 3, 3, 5814, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 2, 5816, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 3, 5817, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 4, 5818, "においをうまく使った\nイタズラって…\nなにかないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 5, 5819, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 7, 5821, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120501, 0, 0, 8, 5822, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 1, 5825, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 2, 5826, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 3, 5827, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 4, 5828, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 5, 5829, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 101, 5830, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 102, 5831, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 1, 5832, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 2, 5833, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 3, 5834, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 4, 5835, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 101, 5836, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 102, 5837, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 1, 1, 5838, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 2, 2, 5839, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 3, 3, 5840, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 2, 5842, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 3, 5843, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 4, 5844, "これは冬の花の香り…\n風情があって\nいいじゃないか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 5, 5845, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 7, 5847, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120801, 0, 0, 8, 5848, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 1, 5850, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 2, 5851, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 3, 5852, "この格好も\n慣れてくれば\n動きやすいわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 4, 5853, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 5, 5854, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 101, 5855, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 102, 5856, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 1, 5857, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 2, 5858, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 3, 5859, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 4, 5860, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 101, 5861, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 102, 5862, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 1, 1, 5863, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 2, 2, 5864, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 3, 3, 5865, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 2, 5867, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 3, 5868, "ふふっ\nかわいいものって\n癒されるわね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 4, 5869, "…ん\nこれが梅の香り？\n…いい匂いね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 5, 5870, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 7, 5872, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120701, 0, 0, 8, 5873, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 103, 5875, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 103, 5876, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 1, 5877, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 2, 5878, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 3, 5879, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 4, 5880, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 5, 5881, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 101, 5882, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 102, 5883, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 1, 5884, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 2, 5885, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 3, 5886, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 4, 5887, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 101, 5888, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 102, 5889, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 1, 1, 5890, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 2, 2, 5891, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 3, 3, 5892, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 2, 5894, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 3, 5895, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 4, 5896, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 5, 5897, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 7, 5899, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120901, 0, 0, 8, 5900, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 1, 5902, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 2, 5903, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 3, 5904, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 4, 5905, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 5, 5906, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 101, 5907, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 102, 5908, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 1, 5909, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 2, 5910, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 3, 5911, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 4, 5912, "あなたの想いが\n全力全開を超える\n力をくれました！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 101, 5913, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 102, 5914, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 1, 1, 5915, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 2, 2, 5916, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 3, 3, 5917, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 2, 5919, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 3, 5920, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 4, 5921, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 5, 5922, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 7, 5924, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121001, 0, 0, 8, 5925, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 1, 5927, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 2, 5928, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 3, 5929, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 4, 5930, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 5, 5931, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 101, 5932, "少年が\n目を覚ましてくれて\n本当によかった…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 102, 5933, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 1, 5934, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 2, 5935, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 3, 5936, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 4, 5937, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 101, 5938, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 102, 5939, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 1, 1, 5940, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 2, 2, 5941, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 3, 3, 5942, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 2, 5944, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 3, 5945, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 4, 5946, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 5, 5947, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 7, 5949, "食の大切さは\nいつの食事からも\n感じられるね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121201, 0, 0, 8, 5950, "ふむ…面白い！\n今度全巻\n借りにこようっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 1, 5952, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 2, 5953, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 3, 5954, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 4, 5955, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 5, 5956, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 101, 5957, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 102, 5958, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 1, 5959, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 2, 5960, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 3, 5961, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 4, 5962, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 101, 5963, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 102, 5964, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 1, 1, 5965, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 2, 2, 5966, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 3, 3, 5967, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 2, 5969, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 3, 5970, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 4, 5971, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 5, 5972, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 7, 5974, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (116401, 0, 0, 8, 5975, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 1, 5977, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 2, 5978, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 3, 5979, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 4, 5980, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 5, 5981, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 101, 5982, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 102, 5983, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 1, 5984, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 2, 5985, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 3, 5986, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 4, 5987, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 101, 5988, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 102, 5989, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 1, 1, 5990, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 2, 2, 5991, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 3, 3, 5992, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 2, 5994, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 3, 5995, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 4, 5996, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 5, 5997, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 7, 5999, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121101, 0, 0, 8, 6000, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 1, 6002, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 2, 6003, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 3, 6004, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 4, 6005, "～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 5, 6006, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 101, 6007, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 102, 6008, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 1, 6009, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 2, 6010, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 3, 6011, "お喋り…？\nわたしでいいの…？\nうれしい…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 4, 6012, "あなたの好きな歌…\n教えて…？\n練習、するから…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 101, 6013, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 102, 6014, "安心、して…\nわたしの歌で、\nあなたを守るから…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 1, 1, 6015, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 2, 2, 6016, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 3, 3, 6017, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 2, 6019, "こんな特等席…\n恐縮、です…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 3, 6020, "い、今のうちに、\nこの子でお世話の\n練習を…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 4, 6021, "いい香り…\n…つらい気持ちも\n癒してくれる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 5, 6022, "あったかい…\nいつまでも、\nこうしていられそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 7, 6024, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118101, 0, 0, 8, 6025, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 1, 6027, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 2, 6028, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 3, 6029, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 4, 6030, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 5, 6031, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 101, 6032, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 102, 6033, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 1, 6034, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 2, 6035, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 3, 6036, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 4, 6037, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 101, 6038, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 102, 6039, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 1, 1, 6040, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 2, 2, 6041, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 3, 3, 6042, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 2, 6044, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 3, 6045, "今日も演技の\n練習に付き合ってね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 4, 6046, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 5, 6047, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 7, 6049, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121301, 0, 0, 8, 6050, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 103, 6052, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107501, 0, 0, 103, 6053, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 103, 6054, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107601, 0, 0, 103, 6055, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 103, 6056, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107801, 0, 0, 103, 6057, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 1, 6058, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 2, 6059, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 3, 6060, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 4, 6061, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 5, 6062, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 101, 6063, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 102, 6064, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 1, 6065, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 2, 6066, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 3, 6067, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 4, 6068, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 101, 6069, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 102, 6070, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 1, 1, 6071, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 2, 2, 6072, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 3, 3, 6073, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 2, 6075, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 3, 6076, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 4, 6077, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 5, 6078, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 7, 6080, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121401, 0, 0, 8, 6081, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 1, 6083, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 2, 6084, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 3, 6085, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 4, 6086, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 5, 6087, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 101, 6088, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 102, 6089, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 1, 6090, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 2, 6091, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 3, 6092, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 4, 6093, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 101, 6094, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 102, 6095, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 1, 1, 6096, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 2, 2, 6097, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 3, 3, 6098, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 2, 6100, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 3, 6101, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 4, 6102, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 5, 6103, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 7, 6105, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121501, 0, 0, 8, 6106, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 103, 6108, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 103, 6109, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 1, 6110, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 2, 6111, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 3, 6112, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 4, 6113, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 5, 6114, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 101, 6115, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 102, 6116, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 1, 6117, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 2, 6118, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 3, 6119, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 4, 6120, "しーっ！\n今、尾行中なんです\n話はあとで…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 101, 6121, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 102, 6122, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 1, 1, 6123, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 2, 2, 6124, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 3, 3, 6125, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 2, 6127, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 3, 6128, "くう…！\nこのかわいさは\n盗めないです…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 4, 6129, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 5, 6130, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 7, 6132, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121601, 0, 0, 8, 6133, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 1, 6135, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 2, 6136, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 3, 6137, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 4, 6138, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 5, 6139, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 101, 6140, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 102, 6141, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 1, 6142, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 2, 6143, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 3, 6144, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 4, 6145, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 101, 6146, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 102, 6147, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 1, 1, 6148, "さあ！\n今日も一日\n全力で参りますわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 2, 2, 6149, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 3, 3, 6150, "もう少しだけ…\n明日の準備をして\nおかないと…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 2, 6152, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 3, 6153, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 4, 6154, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 5, 6155, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 7, 6157, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121701, 0, 0, 8, 6158, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 1, 6161, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 2, 6162, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 3, 6163, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 4, 6164, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 5, 6165, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 101, 6166, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 102, 6167, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 1, 6168, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 2, 6169, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 3, 6170, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 4, 6171, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 101, 6172, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 102, 6173, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 1, 1, 6174, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 2, 2, 6175, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 3, 3, 6176, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 2, 6178, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 3, 6179, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 4, 6180, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 5, 6181, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 7, 6183, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121801, 0, 0, 8, 6184, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 1, 6187, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 2, 6188, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 3, 6189, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 4, 6190, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 5, 6191, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 101, 6192, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 102, 6193, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 1, 6194, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 2, 6195, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 3, 6196, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 4, 6197, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 101, 6198, "船の上では食材が\n限られるので料理の\nいい練習になります", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 102, 6199, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 1, 1, 6200, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 2, 2, 6201, "船の上では\n規則正しい生活が\n重要ですから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 3, 3, 6202, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 2, 6204, "海に揺られるような\n心地よさですね\n気が休まります…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 3, 6205, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 4, 6206, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 5, 6207, "温かい…\n潮風で冷えた体に\n沁みますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 7, 6209, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122001, 0, 0, 8, 6210, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 103, 6212, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102601, 0, 0, 103, 6213, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 1, 6214, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 2, 6215, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 3, 6216, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 4, 6217, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 5, 6218, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 101, 6219, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 102, 6220, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 1, 6221, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 2, 6222, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 3, 6223, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 4, 6224, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 101, 6225, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 102, 6226, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 1, 1, 6227, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 2, 2, 6228, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 3, 3, 6229, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 2, 6231, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 3, 6232, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 4, 6233, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 5, 6234, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 7, 6236, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (121901, 0, 0, 8, 6237, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 1, 6239, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 2, 6240, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 3, 6241, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 4, 6242, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 5, 6243, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 101, 6244, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 102, 6245, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 1, 6246, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 2, 6247, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 3, 6248, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 4, 6249, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 101, 6250, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 102, 6251, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 1, 1, 6252, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 2, 2, 6253, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 3, 3, 6254, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 2, 6256, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 3, 6257, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 4, 6258, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 5, 6259, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 7, 6261, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122101, 0, 0, 8, 6262, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 1, 6264, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 2, 6265, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 3, 6266, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 4, 6267, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 5, 6268, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 101, 6269, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 102, 6270, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 1, 6271, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 2, 6272, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 3, 6273, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 4, 6274, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 101, 6275, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 102, 6276, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 1, 1, 6277, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 2, 2, 6278, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 3, 3, 6279, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 2, 6281, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 3, 6282, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 4, 6283, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 5, 6284, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 7, 6286, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122201, 0, 0, 8, 6287, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 103, 6289, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 103, 6290, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 1, 6291, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 2, 6292, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 3, 6293, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 4, 6294, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 5, 6295, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 101, 6296, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 102, 6297, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 1, 6298, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 2, 6299, "よしよし！\n今日のけんぞくも\nいい子だね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 3, 6300, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 4, 6301, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 101, 6302, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 102, 6303, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 1, 1, 6304, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 2, 2, 6305, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 3, 3, 6306, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 2, 6308, "わー！\nふかふかだ！\nきもちぃー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 3, 6309, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 4, 6310, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 5, 6311, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 7, 6313, "はむはむ…\nんー！\nおいしー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122301, 0, 0, 8, 6314, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 1, 6316, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 2, 6317, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 3, 6318, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 4, 6319, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 5, 6320, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 101, 6321, "終わらない夏は\nないけれど…\nつい願ってしまうな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 102, 6322, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 1, 6323, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 2, 6324, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 3, 6325, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 4, 6326, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 101, 6327, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 102, 6328, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 1, 1, 6329, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 2, 2, 6330, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 3, 3, 6331, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 2, 6333, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 3, 6334, "肌触りのよさが\nこの格好だと\nよく分かるよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 4, 6335, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 5, 6336, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 7, 6338, "夏を楽しみきるには\nエネルギーが\n必要だからね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 8, 6339, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 10, 6341, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122501, 0, 0, 11, 6342, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 1, 6343, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 2, 6344, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 3, 6345, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 4, 6346, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 5, 6347, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 101, 6348, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 102, 6349, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 1, 6350, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 2, 6351, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 3, 6352, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 4, 6353, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 101, 6354, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 102, 6355, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 1, 1, 6356, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 2, 2, 6357, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 3, 3, 6358, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 2, 6360, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 3, 6361, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 4, 6362, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 5, 6363, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 7, 6365, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 8, 6366, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 10, 6368, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122401, 0, 0, 11, 6369, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 103, 6370, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101501, 0, 0, 103, 6371, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 1, 6372, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 2, 6373, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 3, 6374, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 4, 6375, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 5, 6376, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 101, 6377, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 102, 6378, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 1, 6379, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 2, 6380, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 3, 6381, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 4, 6382, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 101, 6383, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 102, 6384, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 1, 1, 6385, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 2, 2, 6386, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 3, 3, 6387, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 2, 6389, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 3, 6390, "かわいい～♪\n日光浴させて\nあげたいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 4, 6391, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 5, 6392, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 7, 6394, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 8, 6395, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 10, 6397, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122601, 0, 0, 11, 6398, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 1, 6399, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 2, 6400, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 3, 6401, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 4, 6402, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 5, 6403, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 101, 6404, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 102, 6405, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 1, 6406, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 2, 6407, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 3, 6408, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 4, 6409, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 101, 6410, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 102, 6411, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 1, 1, 6412, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 2, 2, 6413, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 3, 3, 6414, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 2, 6416, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 3, 6417, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 4, 6418, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 5, 6419, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 7, 6421, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 8, 6422, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 10, 6424, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122901, 0, 0, 11, 6425, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 1, 6426, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 2, 6427, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 3, 6428, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 4, 6429, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 5, 6430, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 101, 6431, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 102, 6432, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 1, 6433, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 2, 6434, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 3, 6435, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 4, 6436, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 101, 6437, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 102, 6438, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 1, 1, 6439, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 2, 2, 6440, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 3, 3, 6441, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 2, 6443, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 3, 6444, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 4, 6445, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 5, 6446, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 7, 6448, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 8, 6449, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 10, 6451, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122801, 0, 0, 11, 6452, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 103, 6453, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 103, 6454, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 1, 6455, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 2, 6456, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 3, 6457, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 4, 6458, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 5, 6459, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 101, 6460, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 102, 6461, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 1, 6462, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 2, 6463, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 3, 6464, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 4, 6465, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 101, 6466, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 102, 6467, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 1, 1, 6468, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 2, 2, 6469, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 3, 3, 6470, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 2, 6472, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 3, 6473, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 4, 6474, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 5, 6475, "濡れた体を\n乾かしましょう\nタオル、タオル…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 7, 6477, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 8, 6478, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 10, 6480, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (122701, 0, 0, 11, 6481, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 1, 6482, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 2, 6483, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 3, 6484, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 4, 6485, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 5, 6486, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 101, 6487, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 102, 6488, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 1, 6489, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 2, 6490, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 3, 6491, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 4, 6492, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 101, 6493, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 102, 6494, "どこまでだって\nあんたと一緒に\nいてやるんだから", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 1, 1, 6495, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 2, 2, 6496, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 3, 3, 6497, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 2, 6499, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 3, 6500, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 4, 6501, "いい香りね\n塔にもこういうの\n置いておこうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 5, 6502, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 7, 6504, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123001, 0, 0, 8, 6505, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 1, 6507, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 2, 6508, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 3, 6509, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 4, 6510, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 5, 6511, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 101, 6512, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 102, 6513, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 1, 6514, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 2, 6515, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 3, 6516, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 4, 6517, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 101, 6518, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 102, 6519, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 1, 1, 6520, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 2, 2, 6521, "支度が終わったら\n今日の冒険に\n出かけまひょか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 3, 3, 6522, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 2, 6524, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 3, 6525, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 4, 6526, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 5, 6527, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 7, 6529, "この料理…\n冒険するときに\n作ってみまひょか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123101, 0, 0, 8, 6530, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 103, 6532, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 103, 6533, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 103, 6534, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104401, 0, 0, 103, 6535, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 1, 6536, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 2, 6537, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 3, 6538, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 4, 6539, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 5, 6540, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 101, 6541, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 102, 6542, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 1, 6543, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 2, 6544, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 3, 6545, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 4, 6546, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 101, 6547, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 102, 6548, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 1, 1, 6549, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 2, 2, 6550, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 3, 3, 6551, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 2, 6553, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 3, 6554, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 4, 6555, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 5, 6556, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 7, 6558, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123201, 0, 0, 8, 6559, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 1, 6561, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 2, 6562, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 3, 6563, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 4, 6564, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 5, 6565, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 101, 6566, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 102, 6567, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 1, 6568, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 2, 6569, "んー？\n今はだるいからパース\nあとでねー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 3, 6570, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 4, 6571, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 101, 6572, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 102, 6573, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 1, 1, 6574, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 2, 2, 6575, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 3, 3, 6576, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 2, 6578, "あーラクー\nいっしょーここで\nキューケーできるわー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 3, 6579, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 4, 6580, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 5, 6581, "あったか～♪\n溶けてスライム\n戻りそ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 7, 6583, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123301, 0, 0, 8, 6584, "わ～なんかムズー\n読めなー\nウケるー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 1, 6586, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 2, 6587, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 3, 6588, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 4, 6589, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 5, 6590, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 101, 6591, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 102, 6592, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 1, 6593, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 2, 6594, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 3, 6595, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 4, 6596, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 101, 6597, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 102, 6598, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 1, 1, 6599, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 2, 2, 6600, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 3, 3, 6601, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 2, 6603, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 3, 6604, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 4, 6605, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 5, 6606, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 7, 6608, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 8, 6609, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123501, 0, 0, 12, 6611, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 1, 6613, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 2, 6614, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 3, 6615, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 4, 6616, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 5, 6617, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 101, 6618, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 102, 6619, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 1, 6620, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 2, 6621, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 3, 6622, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 4, 6623, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 101, 6624, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 102, 6625, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 1, 1, 6626, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 2, 2, 6627, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 3, 3, 6628, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 2, 6630, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 3, 6631, "…かわいい\nこの子もパレードに\n連れて行きたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 4, 6632, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 5, 6633, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 7, 6635, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 8, 6636, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123601, 0, 0, 12, 6638, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 103, 6640, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103701, 0, 0, 103, 6641, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 1, 6642, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 2, 6643, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 3, 6644, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 4, 6645, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 5, 6646, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 101, 6647, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 102, 6648, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 1, 6649, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 2, 6650, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 3, 6651, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 4, 6652, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 101, 6653, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 102, 6654, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 1, 1, 6655, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 2, 2, 6656, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 3, 3, 6657, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 2, 6659, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 3, 6660, "いや～\nかわいいものをみると\n癒されますな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 4, 6661, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 5, 6662, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 7, 6664, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 8, 6665, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123701, 0, 0, 12, 6667, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 1, 6669, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 2, 6670, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 3, 6671, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 4, 6672, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 5, 6673, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 101, 6674, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 102, 6675, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 1, 6676, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 2, 6677, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 3, 6678, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 4, 6679, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 101, 6680, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 102, 6681, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 1, 1, 6682, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 2, 2, 6683, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 3, 3, 6684, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 2, 6686, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 3, 6687, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 4, 6688, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 5, 6689, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 7, 6691, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123801, 0, 0, 8, 6692, "ウサギ族は地獄耳？\nワタシとの共通点だな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 103, 6694, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106401, 0, 0, 103, 6695, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 1, 6696, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 2, 6697, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 3, 6698, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 4, 6699, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 5, 6700, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 101, 6701, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 102, 6702, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 1, 6703, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 2, 6704, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 3, 6705, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 4, 6706, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 101, 6707, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 102, 6708, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 1, 1, 6709, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 2, 2, 6710, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 3, 3, 6711, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 2, 6713, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 3, 6714, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 4, 6715, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 5, 6716, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 7, 6718, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (123901, 0, 0, 8, 6719, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 1, 6721, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 2, 6722, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 3, 6723, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 4, 6724, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 5, 6725, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 101, 6726, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 102, 6727, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 1, 6728, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 2, 6729, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 3, 6730, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 4, 6731, "視力は権能に\n影響しないわよ\n当たり前じゃない", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 101, 6732, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 102, 6733, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 1, 1, 6734, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 2, 2, 6735, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 3, 3, 6736, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 2, 6738, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 3, 6739, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 4, 6740, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 5, 6741, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 7, 6743, "栄養価はともかく\n味のバランスは\n悪くないわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106901, 0, 0, 8, 6744, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 1, 6746, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 2, 6747, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 3, 6748, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 4, 6749, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 5, 6750, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 101, 6751, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 102, 6752, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 1, 6753, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 2, 6754, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 3, 6755, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 4, 6756, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 101, 6757, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 102, 6758, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 1, 1, 6759, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 2, 2, 6760, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 3, 3, 6761, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 2, 6763, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 3, 6764, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 4, 6765, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 5, 6766, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 7, 6768, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 8, 6769, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 14, 6771, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124001, 0, 0, 15, 6772, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 1, 6773, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 2, 6774, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 3, 6775, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 4, 6776, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 5, 6777, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 101, 6778, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 102, 6779, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 1, 6780, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 2, 6781, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 3, 6782, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 4, 6783, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 101, 6784, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 102, 6785, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 1, 1, 6786, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 2, 2, 6787, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 3, 3, 6788, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 2, 6790, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 3, 6791, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 4, 6792, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 5, 6793, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 7, 6795, "お腹いっぱい\n食べられるのも\n幸せなことよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 8, 6796, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 14, 6798, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124101, 0, 0, 15, 6799, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 103, 6800, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 103, 6801, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 1, 6802, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 2, 6803, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 3, 6804, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 4, 6805, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 5, 6806, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 101, 6807, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 102, 6808, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 1, 6809, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 2, 6810, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 3, 6811, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 4, 6812, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 101, 6813, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 102, 6814, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 1, 1, 6815, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 2, 2, 6816, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 3, 3, 6817, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 2, 6819, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 3, 6820, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 4, 6821, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 5, 6822, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 7, 6824, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 8, 6825, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 14, 6827, "わっ！\n…………いや\n驚いてなどいない", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124201, 0, 0, 15, 6828, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 1, 6829, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 2, 6830, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 3, 6831, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 4, 6832, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 5, 6833, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 101, 6834, "私たちも\nあの人を見習って\nバイトとかしよう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 102, 6835, "いつかあの人を\n私たちの学院に\n招待したいな！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 1, 6836, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 2, 6837, "これからなにするの？\n私たちもついて行って\nいいかな！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 3, 6838, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 4, 6839, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 101, 6840, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 102, 6841, "あなたは\nグレアと私の友達！\nこれからもずっと！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 1, 1, 6842, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 2, 2, 6843, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 3, 3, 6844, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 2, 6846, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 3, 6847, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 4, 6848, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 5, 6849, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 7, 6851, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124301, 0, 0, 8, 6852, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 1, 6855, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 2, 6856, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 3, 6857, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 4, 6858, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 5, 6859, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 101, 6860, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 102, 6861, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 1, 6862, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 2, 6863, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 3, 6864, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 4, 6865, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 101, 6866, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 102, 6867, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 1, 1, 6868, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 2, 2, 6869, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 3, 3, 6870, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 2, 6872, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 3, 6873, "これ…\nアンに見せたら\n喜ぶだろうな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 4, 6874, "いい香り…\nアンの感想も\n聞いてみたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 5, 6875, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 7, 6877, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124401, 0, 0, 8, 6878, "アンがおススメ\nしてくれた本は\nこれかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 1, 6881, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 2, 6882, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 3, 6883, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 4, 6884, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 5, 6885, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 101, 6886, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 102, 6887, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 1, 6888, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 2, 6889, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 3, 6890, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 4, 6891, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 101, 6892, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 102, 6893, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 1, 1, 6894, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 2, 2, 6895, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 3, 3, 6896, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 2, 6898, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 3, 6899, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 4, 6900, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 5, 6901, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 7, 6903, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124701, 0, 0, 8, 6904, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 1, 6906, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 2, 6907, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 3, 6908, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 4, 6909, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 5, 6910, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 101, 6911, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 102, 6912, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 1, 6913, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 2, 6914, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 3, 6915, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 4, 6916, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 101, 6917, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 102, 6918, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 1, 1, 6919, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 2, 2, 6920, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 3, 3, 6921, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 2, 6923, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 3, 6924, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 4, 6925, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 5, 6926, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 7, 6928, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124601, 0, 0, 8, 6929, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 103, 6931, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104701, 0, 0, 103, 6932, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 1, 6933, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 2, 6934, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 3, 6935, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 4, 6936, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 5, 6937, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 101, 6938, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 102, 6939, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 1, 6940, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 2, 6941, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 3, 6942, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 4, 6943, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 101, 6944, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 102, 6945, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 1, 1, 6946, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 2, 2, 6947, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 3, 3, 6948, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 2, 6950, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 3, 6951, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 4, 6952, "いい香りだけど\n着物に移らないように\n気をつけないとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 5, 6953, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 7, 6955, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124501, 0, 0, 8, 6956, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 1, 6958, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 2, 6959, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 3, 6960, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 4, 6961, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 5, 6962, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 101, 6963, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 102, 6964, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 1, 6965, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 2, 6966, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 3, 6967, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 4, 6968, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 101, 6969, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 102, 6970, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 1, 1, 6971, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 2, 2, 6972, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 3, 3, 6973, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 2, 6975, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 3, 6976, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 4, 6977, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 5, 6978, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 7, 6980, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124801, 0, 0, 8, 6981, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 1, 6983, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 2, 6984, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 3, 6985, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 4, 6986, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 5, 6987, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 101, 6988, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 102, 6989, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 1, 6990, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 2, 6991, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 3, 6992, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 4, 6993, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 101, 6994, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 102, 6995, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 1, 1, 6996, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 2, 2, 6997, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 3, 3, 6998, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 2, 7000, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 3, 7001, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 4, 7002, "嗅いだことない\n香りだな\nなかなか気に入った！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 5, 7003, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 7, 7005, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (124901, 0, 0, 8, 7006, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 103, 7008, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105501, 0, 0, 103, 7009, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 1, 7010, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 2, 7011, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 3, 7012, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 4, 7013, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 5, 7014, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 101, 7015, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 102, 7016, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 1, 7017, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 2, 7018, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 3, 7019, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 4, 7020, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 101, 7021, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 102, 7022, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 1, 1, 7023, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 2, 2, 7024, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 3, 3, 7025, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 2, 7027, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 3, 7028, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 4, 7029, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 5, 7030, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 7, 7032, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125001, 0, 0, 8, 7033, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 1, 7035, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 2, 7036, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 3, 7037, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 4, 7038, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 5, 7039, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 101, 7040, "退屈ですねー\n騎士さん、構って\nくれないかなー", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 102, 7041, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 1, 7042, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 2, 7043, "お出かけします？\nそれともおうちで\nまったりしますー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 3, 7044, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 4, 7045, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 101, 7046, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 102, 7047, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 1, 1, 7048, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 2, 2, 7049, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 3, 3, 7050, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 2, 7052, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 3, 7053, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 4, 7054, "くんくん★\nうーん…\nたぶん素敵な香り♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 5, 7055, "あったかーい★\n心も体も\nぽっかぽかですねー★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 7, 7057, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118201, 0, 0, 8, 7058, "へー★\nほほー★\nふんふーん★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 1, 7060, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 2, 7061, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 3, 7062, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 4, 7063, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 5, 7064, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 101, 7065, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 102, 7066, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 1, 7067, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 2, 7068, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 3, 7069, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 4, 7070, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 101, 7071, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 102, 7072, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 1, 1, 7073, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 2, 2, 7074, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 3, 3, 7075, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 2, 7077, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 3, 7078, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 4, 7079, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 5, 7080, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 7, 7082, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125101, 0, 0, 8, 7083, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 103, 7085, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (107101, 0, 0, 103, 7086, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 1, 7087, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 2, 7088, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 3, 7089, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 4, 7090, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 5, 7091, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 101, 7092, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 102, 7093, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 1, 7094, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 2, 7095, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 3, 7096, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 4, 7097, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 101, 7098, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 102, 7099, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 1, 1, 7100, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 2, 2, 7101, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 3, 3, 7102, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 2, 7104, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 3, 7105, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 4, 7106, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 5, 7107, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 7, 7109, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125201, 0, 0, 8, 7110, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 1, 7112, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 2, 7113, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 3, 7114, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 4, 7115, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 5, 7116, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 101, 7117, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 102, 7118, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 1, 7119, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 2, 7120, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 3, 7121, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 4, 7122, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 101, 7123, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 102, 7124, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 1, 1, 7125, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 2, 2, 7126, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 3, 3, 7127, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 2, 7129, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 3, 7130, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 4, 7131, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 5, 7132, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 7, 7134, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (120001, 0, 0, 8, 7135, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 1, 7137, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 2, 7138, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 3, 7139, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 4, 7140, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 5, 7141, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 101, 7142, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 102, 7143, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 1, 7144, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 2, 7145, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 3, 7146, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 4, 7147, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 101, 7148, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 102, 7149, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 1, 1, 7150, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 2, 2, 7151, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 3, 3, 7152, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 2, 7154, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 3, 7155, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 4, 7156, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 5, 7157, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 7, 7159, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125301, 0, 0, 8, 7160, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 103, 7162, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 103, 7163, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 1, 7164, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 2, 7165, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 3, 7166, "そろそろ\nパトロールの\n時間だ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 4, 7167, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 5, 7168, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 101, 7169, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 102, 7170, "こんな私にも\nできることはある…\nそう信じています", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 1, 7171, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 2, 7172, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 3, 7173, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 4, 7174, "ニャットちゃん\nあなたにも\n懐いてきましたね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 101, 7175, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 102, 7176, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 1, 1, 7177, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 2, 2, 7178, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 3, 3, 7179, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 2, 7181, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 3, 7182, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 4, 7183, "森の香りがします…\n今日はよく\n眠れそうです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 5, 7184, "ニャットちゃんと\n同じぐらい\n暖かいです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 7, 7186, "レンジャーの仕事を\nしていると\nお腹が空きます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125401, 0, 0, 8, 7187, "どんなに忙しくても\n読書だけは\nやめられませんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 1, 7189, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 2, 7190, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 3, 7191, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 4, 7192, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 5, 7193, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 101, 7194, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 102, 7195, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 1, 7196, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 2, 7197, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 3, 7198, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 4, 7199, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 101, 7200, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 102, 7201, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 1, 1, 7202, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 2, 2, 7203, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 3, 3, 7204, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 2, 7206, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 3, 7207, "と、飛びかかっては\nこない…ですよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 4, 7208, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 5, 7209, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 7, 7211, "こんなに食べていたら\nはしたない…\nでしょうか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125801, 0, 0, 8, 7212, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 1, 7214, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 2, 7215, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 3, 7216, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 4, 7217, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 5, 7218, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 101, 7219, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 102, 7220, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 1, 7221, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 2, 7222, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 3, 7223, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 4, 7224, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 101, 7225, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 102, 7226, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 1, 1, 7227, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 2, 2, 7228, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 3, 3, 7229, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 2, 7231, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 3, 7232, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 4, 7233, "いい匂い…\nそれになんだか\n集中力が増したかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 5, 7234, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 7, 7236, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125501, 0, 0, 8, 7237, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 1, 7239, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 2, 7240, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 3, 7241, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 4, 7242, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 5, 7243, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 101, 7244, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 102, 7245, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 1, 7246, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 2, 7247, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 3, 7248, "依頼で気になる\nところがあれば\n何でもご確認ください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 4, 7249, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 101, 7250, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 102, 7251, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 1, 1, 7252, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 2, 2, 7253, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 3, 3, 7254, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 2, 7256, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 3, 7257, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 4, 7258, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 5, 7259, "この火力…\n錬金術の調合に\n使えそうですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 7, 7261, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125701, 0, 0, 8, 7262, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 103, 7264, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106101, 0, 0, 103, 7265, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 1, 7266, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 2, 7267, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 3, 7268, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 4, 7269, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 5, 7270, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 101, 7271, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 102, 7272, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 1, 7273, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 2, 7274, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 3, 7275, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 4, 7276, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 101, 7277, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 102, 7278, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 1, 1, 7279, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 2, 2, 7280, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 3, 3, 7281, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 2, 7283, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 3, 7284, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 4, 7285, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 5, 7286, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 7, 7288, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (125601, 0, 0, 8, 7289, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 1, 7291, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 2, 7292, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 3, 7293, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 4, 7294, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 5, 7295, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 101, 7296, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 102, 7297, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 1, 7298, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 2, 7299, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 3, 7300, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 4, 7301, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 101, 7302, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 102, 7303, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 1, 1, 7304, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 2, 2, 7305, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 3, 3, 7306, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 2, 7308, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 3, 7309, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 4, 7310, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 5, 7311, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 7, 7313, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126201, 0, 0, 8, 7314, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 103, 7316, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 103, 7317, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 1, 7318, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 2, 7319, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 3, 7320, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 4, 7321, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 5, 7322, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 101, 7323, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 102, 7324, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 1, 7325, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 2, 7326, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 3, 7327, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 4, 7328, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 101, 7329, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 102, 7330, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 1, 1, 7331, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 2, 2, 7332, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 3, 3, 7333, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 2, 7335, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 3, 7336, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 4, 7337, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 5, 7338, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 7, 7340, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126301, 0, 0, 8, 7341, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 1, 7343, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 2, 7344, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 3, 7345, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 4, 7346, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 5, 7347, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 101, 7348, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 102, 7349, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 1, 7350, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 2, 7351, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 3, 7352, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 4, 7353, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 101, 7354, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 102, 7355, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 1, 1, 7356, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 2, 2, 7357, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 3, 3, 7358, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 2, 7360, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 3, 7361, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 4, 7362, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 5, 7363, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 7, 7365, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126401, 0, 0, 8, 7366, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 1, 7368, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 2, 7369, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 3, 7370, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 4, 7371, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 5, 7372, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 101, 7373, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 102, 7374, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 1, 7375, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 2, 7376, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 3, 7377, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 4, 7378, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 101, 7379, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 102, 7380, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 1, 1, 7381, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 2, 2, 7382, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 3, 3, 7383, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 2, 7385, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 3, 7386, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 4, 7387, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 5, 7388, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 7, 7390, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 8, 7391, "夏のトレンドを\n押さえておくのも\n大事ですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 10, 7393, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126701, 0, 0, 11, 7394, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 1, 7395, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 2, 7396, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 3, 7397, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 4, 7398, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 5, 7399, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 101, 7400, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 102, 7401, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 1, 7402, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 2, 7403, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 3, 7404, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 4, 7405, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 101, 7406, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 102, 7407, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 1, 1, 7408, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 2, 2, 7409, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 3, 3, 7410, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 2, 7412, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 3, 7413, "なかなか気に入った…\nというのは\n誰にも秘密です", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 4, 7414, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 5, 7415, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 7, 7417, "私がどのような味が\n好きなのかは…\n当然秘密です", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 8, 7418, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 10, 7420, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126601, 0, 0, 11, 7421, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 103, 7422, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105401, 0, 0, 103, 7423, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 1, 7424, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 2, 7425, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 3, 7426, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 4, 7427, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 5, 7428, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 101, 7429, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 102, 7430, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 1, 7431, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 2, 7432, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 3, 7433, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 4, 7434, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 101, 7435, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 102, 7436, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 1, 1, 7437, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 2, 2, 7438, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 3, 3, 7439, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 2, 7441, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 3, 7442, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 4, 7443, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 5, 7444, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 7, 7446, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 8, 7447, "試験に使った参考書、\nたまには読み返して\nおかないとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 10, 7449, "ふふ♪\nいい手触りね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126801, 0, 0, 11, 7450, "…はふう～…\nゴロゴロするのって\n最高ね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 1, 7451, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 2, 7452, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 3, 7453, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 4, 7454, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 5, 7455, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 101, 7456, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 102, 7457, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 1, 7458, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 2, 7459, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 3, 7460, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 4, 7461, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 101, 7462, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 102, 7463, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 1, 1, 7464, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 2, 2, 7465, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 3, 3, 7466, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 2, 7468, "素肌にふわふわが…\n気持ちいい…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 3, 7469, "うふふ…\n一緒に海…行く？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 4, 7470, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 5, 7471, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 7, 7473, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 8, 7474, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 10, 7476, "これで合ってる…？\n遊び方わからなくて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126901, 0, 0, 11, 7477, "ふわふわ…♪\n海の底にいるみたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 1, 7478, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 2, 7479, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 3, 7480, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 4, 7481, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 5, 7482, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 101, 7483, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 102, 7484, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 1, 7485, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 2, 7486, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 3, 7487, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 4, 7488, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 101, 7489, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 102, 7490, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 1, 1, 7491, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 2, 2, 7492, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 3, 3, 7493, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 2, 7495, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 3, 7496, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 4, 7497, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 5, 7498, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 7, 7500, "クウカの口の中に\nたくさん…♪\n止まりません…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 8, 7501, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 10, 7503, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127001, 0, 0, 11, 7504, "これは…\n妄想に役立ちそうな\n感触です…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 103, 7505, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100501, 0, 0, 103, 7506, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 1, 7507, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 2, 7508, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 3, 7509, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 4, 7510, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 5, 7511, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 101, 7512, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 102, 7513, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 1, 7514, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 2, 7515, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 3, 7516, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 4, 7517, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 101, 7518, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 102, 7519, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 1, 1, 7520, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 2, 2, 7521, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 3, 3, 7522, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 2, 7524, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 3, 7525, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 4, 7526, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 5, 7527, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 7, 7529, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 8, 7530, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 10, 7532, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127101, 0, 0, 11, 7533, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 1, 7534, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 2, 7535, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 3, 7536, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 4, 7537, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 5, 7538, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 101, 7539, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 102, 7540, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 1, 7541, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 2, 7542, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 3, 7543, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 4, 7544, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 101, 7545, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 102, 7546, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 1, 1, 7547, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 2, 2, 7548, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 3, 3, 7549, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 2, 7551, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 3, 7552, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 4, 7553, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 5, 7554, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 7, 7556, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126501, 0, 0, 8, 7557, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 1, 7559, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 2, 7560, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 3, 7561, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 4, 7562, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 5, 7563, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 101, 7564, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 102, 7565, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 1, 7566, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 2, 7567, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 3, 7568, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 4, 7569, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 101, 7570, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 102, 7571, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 1, 1, 7572, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 2, 2, 7573, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 3, 3, 7574, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 2, 7576, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 3, 7577, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 4, 7578, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 5, 7579, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 7, 7581, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127201, 0, 0, 8, 7582, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 103, 7584, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 103, 7585, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 1, 7586, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 2, 7587, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 3, 7588, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 4, 7589, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 5, 7590, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 101, 7591, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 102, 7592, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 1, 7593, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 2, 7594, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 3, 7595, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 4, 7596, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 101, 7597, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 102, 7598, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 1, 1, 7599, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 2, 2, 7600, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 3, 3, 7601, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 2, 7603, "充電ちう～\n…ガッコーのイスも\nこれにして～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 3, 7604, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 4, 7605, "リフレッシュの\n魔法発見！\nいい香り～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 5, 7606, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 7, 7608, "超おいし～♪\n学食にあったら\n毎日リピする～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127301, 0, 0, 8, 7609, "ふむふむ…\nねむみパない…\n続きはまた今度～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 1, 7611, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 2, 7612, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 3, 7613, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 4, 7614, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 5, 7615, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 101, 7616, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 102, 7617, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 1, 7618, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 2, 7619, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 3, 7620, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 4, 7621, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 101, 7622, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 102, 7623, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 1, 1, 7624, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 2, 2, 7625, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 3, 3, 7626, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 2, 7628, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 3, 7629, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 4, 7630, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 5, 7631, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 7, 7633, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127501, 0, 0, 8, 7634, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 103, 7636, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101401, 0, 0, 103, 7637, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 1, 7638, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 2, 7639, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 3, 7640, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 4, 7641, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 5, 7642, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 101, 7643, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 102, 7644, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 1, 7645, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 2, 7646, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 3, 7647, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 4, 7648, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 101, 7649, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 102, 7650, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 1, 1, 7651, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 2, 2, 7652, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 3, 3, 7653, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 2, 7655, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 3, 7656, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 4, 7657, "この香り…\nサラサリア土産の\n香木かい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 5, 7658, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 7, 7660, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127601, 0, 0, 8, 7661, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 1, 7663, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 2, 7664, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 3, 7665, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 4, 7666, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 5, 7667, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 101, 7668, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 102, 7669, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 1, 7670, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 2, 7671, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 3, 7672, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 4, 7673, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 101, 7674, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 102, 7675, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 1, 1, 7676, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 2, 2, 7677, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 3, 3, 7678, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 2, 7680, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 3, 7681, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 4, 7682, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 5, 7683, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 7, 7685, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126001, 0, 0, 8, 7686, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 103, 7688, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 103, 7689, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 1, 7690, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 2, 7691, "きょろきょろ\nここも\nぱとろる、するよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 3, 7692, "おうち\nにぎやか\nさみしく、ない", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 4, 7693, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 5, 7694, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 101, 7695, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 102, 7696, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 1, 7697, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 2, 7698, "くろぷれしあ\nかっこいい？\nん…あざます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 3, 7699, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 4, 7700, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 101, 7701, "かみ、りぼん\nおてつだい\nよろ\nです", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 102, 7702, "ここ\nすむ\nね？\nまいにち、いっしょ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 1, 1, 7703, "ぐっもにん\nあさごはん\nあさごはん", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 2, 2, 7704, "お兄\nおつかれ？\nれっつ、まいぺーす", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 3, 3, 7705, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 2, 7707, "ふかふか\nパパの、おなか\nみたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 3, 7708, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 4, 7709, "いい、におい\nでも、お兄\nもっと、いい、におい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 5, 7710, "とりま\nお肉、やこ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 7, 7712, "もぐもぐ…\nむげん肉まん、も\nきぼう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (126101, 0, 0, 8, 7713, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 1, 7715, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 2, 7716, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 3, 7717, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 4, 7718, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 5, 7719, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 101, 7720, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 102, 7721, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 1, 7722, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 2, 7723, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 3, 7724, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 4, 7725, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 101, 7726, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 102, 7727, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 1, 1, 7728, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 2, 2, 7729, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 3, 3, 7730, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 2, 7732, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 3, 7733, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 4, 7734, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 5, 7735, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 7, 7737, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 8, 7738, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 14, 7740, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127901, 0, 0, 15, 7741, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 103, 7742, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106601, 0, 0, 103, 7743, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 1, 7744, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 2, 7745, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 3, 7746, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 4, 7747, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 5, 7748, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 101, 7749, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 102, 7750, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 1, 7751, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 2, 7752, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 3, 7753, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 4, 7754, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 101, 7755, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 102, 7756, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 1, 1, 7757, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 2, 2, 7758, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 3, 3, 7759, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 2, 7761, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 3, 7762, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 4, 7763, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 5, 7764, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 7, 7766, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 8, 7767, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 14, 7769, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128001, 0, 0, 15, 7770, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 1, 7771, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 2, 7772, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 3, 7773, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 4, 7774, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 5, 7775, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 101, 7776, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 102, 7777, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 1, 7778, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 2, 7779, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 3, 7780, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 4, 7781, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 101, 7782, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 102, 7783, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 1, 1, 7784, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 2, 2, 7785, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 3, 3, 7786, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 2, 7788, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 3, 7789, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 4, 7790, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 5, 7791, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 7, 7793, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128101, 0, 0, 8, 7794, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 1, 7797, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 2, 7798, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 3, 7799, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 4, 7800, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 5, 7801, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 101, 7802, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 102, 7803, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 1, 7804, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 2, 7805, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 3, 7806, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 4, 7807, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 101, 7808, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 102, 7809, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 1, 1, 7810, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 2, 2, 7811, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 3, 3, 7812, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 2, 7814, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 3, 7815, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 4, 7816, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 5, 7817, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 7, 7819, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128201, 0, 0, 8, 7820, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 1, 7823, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 2, 7824, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 3, 7825, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 4, 7826, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 5, 7827, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 101, 7828, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 102, 7829, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 1, 7830, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 2, 7831, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 3, 7832, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 4, 7833, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 101, 7834, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 102, 7835, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 1, 1, 7836, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 2, 2, 7837, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 3, 3, 7838, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 2, 7840, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 3, 7841, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 4, 7842, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 5, 7843, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 7, 7845, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128301, 0, 0, 8, 7846, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 1, 7848, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 2, 7849, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 3, 7850, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 4, 7851, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 5, 7852, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 101, 7853, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 102, 7854, "来年も彼の隣で\n初日の出が\n見られるといいな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 1, 7855, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 2, 7856, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 3, 7857, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 4, 7858, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 101, 7859, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 102, 7860, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 1, 1, 7861, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 2, 2, 7862, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 3, 3, 7863, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 2, 7865, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 3, 7866, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 4, 7867, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 5, 7868, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 7, 7870, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128401, 0, 0, 8, 7871, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 103, 7873, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (106501, 0, 0, 103, 7874, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 1, 7875, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 2, 7876, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 3, 7877, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 4, 7878, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 5, 7879, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 101, 7880, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 102, 7881, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 1, 7882, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 2, 7883, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 3, 7884, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 4, 7885, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 101, 7886, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 102, 7887, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 1, 1, 7888, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 2, 2, 7889, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 3, 3, 7890, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 2, 7892, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 3, 7893, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 4, 7894, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 5, 7895, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 7, 7897, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128501, 0, 0, 8, 7898, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 1, 7900, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 2, 7901, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 3, 7902, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 4, 7903, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 5, 7904, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 101, 7905, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 102, 7906, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 1, 7907, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 2, 7908, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 3, 7909, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 4, 7910, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 101, 7911, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 102, 7912, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 1, 1, 7913, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 2, 2, 7914, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 3, 3, 7915, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 2, 7917, "休める時は休む…\n戦い続けるには\n必要なことだからね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 3, 7918, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 4, 7919, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 5, 7920, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 7, 7922, "この味付け…\n今度試してみようか\n味見は任せたからね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128801, 0, 0, 8, 7923, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 1, 7925, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 2, 7926, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 3, 7927, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 4, 7928, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 5, 7929, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 101, 7930, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 102, 7931, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 1, 7932, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 2, 7933, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 3, 7934, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 4, 7935, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 101, 7936, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 102, 7937, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 1, 1, 7938, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 2, 2, 7939, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 3, 3, 7940, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 2, 7942, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 3, 7943, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 4, 7944, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 5, 7945, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 7, 7947, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128901, 0, 0, 8, 7948, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 1, 7950, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 2, 7951, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 3, 7952, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 4, 7953, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 5, 7954, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 101, 7955, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 102, 7956, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 1, 7957, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 2, 7958, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 3, 7959, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 4, 7960, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 101, 7961, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 102, 7962, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 1, 1, 7963, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 2, 2, 7964, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 3, 3, 7965, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 2, 7967, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 3, 7968, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 4, 7969, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 5, 7970, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 7, 7972, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (128701, 0, 0, 8, 7973, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 1, 7975, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 2, 7976, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 3, 7977, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 4, 7978, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 5, 7979, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 101, 7980, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 102, 7981, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 1, 7982, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 2, 7983, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 3, 7984, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 4, 7985, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 101, 7986, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 102, 7987, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 1, 1, 7988, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 2, 2, 7989, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 3, 3, 7990, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 2, 7992, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 3, 7993, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 4, 7994, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 5, 7995, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 7, 7997, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129001, 0, 0, 8, 7998, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 1, 8000, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 2, 8001, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 3, 8002, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 4, 8003, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 5, 8004, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 101, 8005, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 102, 8006, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 1, 8007, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 2, 8008, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 3, 8009, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 4, 8010, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 101, 8011, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 102, 8012, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 1, 1, 8013, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 2, 2, 8014, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 3, 3, 8015, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 2, 8017, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 3, 8018, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 4, 8019, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 5, 8020, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 7, 8022, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 8, 8023, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 10, 8025, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129301, 0, 0, 11, 8026, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 103, 8027, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108901, 0, 0, 103, 8028, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 103, 8029, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108801, 0, 0, 103, 8030, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 103, 8031, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (108701, 0, 0, 103, 8032, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 1, 8033, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 2, 8034, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 3, 8035, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 4, 8036, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 5, 8037, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 101, 8038, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 102, 8039, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 1, 8040, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 2, 8041, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 3, 8042, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 4, 8043, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 101, 8044, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 102, 8045, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 1, 1, 8046, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 2, 2, 8047, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 3, 3, 8048, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 2, 8050, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 3, 8051, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 4, 8052, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 5, 8053, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 7, 8055, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 8, 8056, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 10, 8058, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129401, 0, 0, 11, 8059, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 1, 8060, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 2, 8061, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 3, 8062, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 4, 8063, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 5, 8064, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 101, 8065, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 102, 8066, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 1, 8067, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 2, 8068, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 3, 8069, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 4, 8070, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 101, 8071, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 102, 8072, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 1, 1, 8073, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 2, 2, 8074, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 3, 3, 8075, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 2, 8077, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 3, 8078, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 4, 8079, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 5, 8080, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 7, 8082, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129801, 0, 0, 8, 8083, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 1, 8085, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 2, 8086, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 3, 8087, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 4, 8088, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 5, 8089, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 101, 8090, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 102, 8091, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 1, 8092, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 2, 8093, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 3, 8094, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 4, 8095, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 101, 8096, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 102, 8097, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 1, 1, 8098, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 2, 2, 8099, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 3, 3, 8100, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 2, 8102, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 3, 8103, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 4, 8104, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 5, 8105, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 7, 8107, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129901, 0, 0, 8, 8108, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 1, 8110, "……（そわそわ）", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 2, 8111, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 3, 8112, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 4, 8113, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 5, 8114, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 101, 8115, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 102, 8116, "この家なら安全─\nかもしれません\n魔物が出ないので", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 1, 8117, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 2, 8118, "長話─苦手です\n疲れるので…\nふぅ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 3, 8119, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 4, 8120, "世話をするのは\n得意です\n…花の、ですよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 101, 8121, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 102, 8122, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 1, 1, 8123, "花に、お水─？\n…いりません", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 2, 2, 8124, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 3, 3, 8125, "夜番。私がします\nあまり眠らない\n性質なので…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 2, 8127, "ここなら根を張れる\nかもしれません\n…張りませんけど", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 3, 8128, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 4, 8129, "花みたいに─\nいい匂い、です", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 5, 8130, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 7, 8132, "食事も立派な戦支度\n媛が言ってました", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129601, 0, 0, 8, 8133, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 1, 8135, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 2, 8136, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 3, 8137, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 4, 8138, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 5, 8139, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 101, 8140, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 102, 8141, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 1, 8142, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 2, 8143, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 3, 8144, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 4, 8145, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 101, 8146, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 102, 8147, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 1, 1, 8148, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 2, 2, 8149, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 3, 3, 8150, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 2, 8152, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 3, 8153, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 4, 8154, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 5, 8155, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 7, 8157, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130701, 0, 0, 8, 8158, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 1, 8160, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 2, 8161, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 3, 8162, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 4, 8163, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 5, 8164, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 101, 8165, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 102, 8166, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 1, 8167, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 2, 8168, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 3, 8169, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 4, 8170, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 101, 8171, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 102, 8172, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 1, 1, 8173, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 2, 2, 8174, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 3, 3, 8175, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 2, 8177, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 3, 8178, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 4, 8179, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 5, 8180, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 7, 8182, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130801, 0, 0, 8, 8183, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 1, 8185, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 2, 8186, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 3, 8187, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 4, 8188, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 5, 8189, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 101, 8190, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 102, 8191, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 1, 8192, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 2, 8193, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 3, 8194, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 4, 8195, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 101, 8196, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 102, 8197, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 1, 1, 8198, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 2, 2, 8199, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 3, 3, 8200, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 2, 8202, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 3, 8203, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 4, 8204, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 5, 8205, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 7, 8207, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131001, 0, 0, 8, 8208, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 103, 8210, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105601, 0, 0, 103, 8211, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 1, 8212, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 2, 8213, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 3, 8214, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 4, 8215, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 5, 8216, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 101, 8217, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 102, 8218, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 1, 8219, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 2, 8220, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 3, 8221, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 4, 8222, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 101, 8223, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 102, 8224, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 1, 1, 8225, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 2, 2, 8226, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 3, 3, 8227, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 2, 8229, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 3, 8230, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 4, 8231, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 5, 8232, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 7, 8234, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (130901, 0, 0, 8, 8235, "パークの\nガイドブックとか\nあったらいいですね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 1, 8237, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 2, 8238, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 3, 8239, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 4, 8240, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 5, 8241, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 101, 8242, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 102, 8243, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 1, 8244, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 2, 8245, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 3, 8246, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 4, 8247, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 101, 8248, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 102, 8249, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 1, 1, 8250, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 2, 2, 8251, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 3, 3, 8252, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 2, 8254, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 3, 8255, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 4, 8256, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 5, 8257, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 7, 8259, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 8, 8260, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 10, 8262, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131101, 0, 0, 11, 8263, "あーやべ\n一瞬で寝そー\nヤバだわ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 1, 8264, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 2, 8265, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 3, 8266, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 4, 8267, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 5, 8268, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 101, 8269, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 102, 8270, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 1, 8271, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 2, 8272, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 3, 8273, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 4, 8274, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 101, 8275, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 102, 8276, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 1, 1, 8277, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 2, 2, 8278, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 3, 3, 8279, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 2, 8281, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 3, 8282, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 4, 8283, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 5, 8284, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 7, 8286, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (129701, 0, 0, 8, 8287, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 1, 8289, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 2, 8290, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 3, 8291, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 4, 8292, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 5, 8293, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 101, 8294, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 102, 8295, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 1, 8296, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 2, 8297, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 3, 8298, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 4, 8299, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 101, 8300, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 102, 8301, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 1, 1, 8302, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 2, 2, 8303, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 3, 3, 8304, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 2, 8306, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 3, 8307, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 4, 8308, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 5, 8309, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 7, 8311, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 8, 8312, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 10, 8314, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131201, 0, 0, 11, 8315, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 1, 8316, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 2, 8317, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 3, 8318, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 4, 8319, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 5, 8320, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 101, 8321, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 102, 8322, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 1, 8323, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 2, 8324, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 3, 8325, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 4, 8326, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 101, 8327, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 102, 8328, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 1, 1, 8329, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 2, 2, 8330, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 3, 3, 8331, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 2, 8333, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 3, 8334, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 4, 8335, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 5, 8336, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 7, 8338, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 8, 8339, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 10, 8341, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131601, 0, 0, 11, 8342, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 1, 8343, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 2, 8344, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 3, 8345, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 4, 8346, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 5, 8347, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 101, 8348, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 102, 8349, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 1, 8350, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 2, 8351, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 3, 8352, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 4, 8353, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 101, 8354, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 102, 8355, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 1, 1, 8356, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 2, 2, 8357, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 3, 3, 8358, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 2, 8360, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 3, 8361, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 4, 8362, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 5, 8363, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 7, 8365, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 8, 8366, "文字を読むのと\nデータを読むのは\nまったく違うわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 10, 8368, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131701, 0, 0, 11, 8369, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 1, 8370, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 2, 8371, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 3, 8372, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 4, 8373, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 5, 8374, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 101, 8375, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 102, 8376, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 1, 8377, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 2, 8378, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 3, 8379, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 4, 8380, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 101, 8381, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 102, 8382, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 1, 1, 8383, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 2, 2, 8384, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 3, 3, 8385, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 2, 8387, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 3, 8388, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 4, 8389, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 5, 8390, "なんていうか…\n心があたたかいわ\n…変な感じ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 7, 8392, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (118601, 0, 0, 8, 8393, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 1, 8395, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 2, 8396, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 3, 8397, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 4, 8398, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 5, 8399, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 101, 8400, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 102, 8401, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 1, 8402, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 2, 8403, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 3, 8404, "熱中症には\n気を付けないと\nだめだよ～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 4, 8405, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 101, 8406, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 102, 8407, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 1, 1, 8408, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 2, 2, 8409, "ふふ♪\n波音が心地良い\n時間だね～☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 3, 3, 8410, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 2, 8412, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 3, 8413, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 4, 8414, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 5, 8415, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 7, 8417, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 8, 8418, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 10, 8420, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131801, 0, 0, 11, 8421, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 1, 8422, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 2, 8423, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 3, 8424, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 4, 8425, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 5, 8426, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 101, 8427, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 102, 8428, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 1, 8429, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 2, 8430, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 3, 8431, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 4, 8432, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 101, 8433, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 102, 8434, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 1, 1, 8435, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 2, 2, 8436, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 3, 3, 8437, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 2, 8439, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 3, 8440, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 4, 8441, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 5, 8442, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 7, 8444, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (181201, 0, 0, 8, 8445, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 1, 8447, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 2, 8448, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 3, 8449, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 4, 8450, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 5, 8451, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 101, 8452, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 102, 8453, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 1, 8454, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 2, 8455, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 3, 8456, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 4, 8457, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 101, 8458, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 102, 8459, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 1, 1, 8460, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 2, 2, 8461, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 3, 3, 8462, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 2, 8464, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 3, 8465, "細かいところまで\nすごーく丁寧に\n作られてる…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 4, 8466, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 5, 8467, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 7, 8469, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 8, 8470, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 10, 8472, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (131901, 0, 0, 11, 8473, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 1, 8474, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 2, 8475, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 3, 8476, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 4, 8477, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 5, 8478, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 101, 8479, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 102, 8480, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 1, 8481, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 2, 8482, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 3, 8483, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 4, 8484, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 101, 8485, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 102, 8486, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 1, 1, 8487, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 2, 2, 8488, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 3, 3, 8489, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 2, 8491, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 3, 8492, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 4, 8493, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 5, 8494, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 7, 8496, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 8, 8497, "海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 10, 8499, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132101, 0, 0, 11, 8500, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 1, 8501, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 2, 8502, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 3, 8503, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 4, 8504, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 5, 8505, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 101, 8506, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 102, 8507, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 1, 8508, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 2, 8509, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 3, 8510, "この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 4, 8511, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 101, 8512, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 102, 8513, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 1, 1, 8514, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 2, 2, 8515, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 3, 3, 8516, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 2, 8518, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 3, 8519, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 4, 8520, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 5, 8521, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 7, 8523, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 8, 8524, "素晴らしいね\nここには叡智が\n詰まっている", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 10, 8526, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132001, 0, 0, 11, 8527, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 103, 8528, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109701, 0, 0, 103, 8529, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 103, 8530, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109801, 0, 0, 103, 8531, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 103, 8532, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 1, 1, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (109901, 0, 0, 103, 8533, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 1, 2, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 1, 8534, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 2, 8535, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 3, 8536, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 4, 8537, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 5, 8538, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 101, 8539, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 102, 8540, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 1, 8541, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 2, 8542, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 3, 8543, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 4, 8544, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 101, 8545, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 102, 8546, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 1, 1, 8547, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 2, 2, 8548, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 3, 3, 8549, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 2, 8551, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 3, 8552, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 4, 8553, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 5, 8554, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 7, 8556, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (132201, 0, 0, 8, 8557, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 1, 8559, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 2, 8560, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 3, 8561, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 4, 8562, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 5, 8563, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 101, 8564, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 102, 8565, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 1, 8566, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 2, 8567, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 3, 8568, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 4, 8569, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 101, 8570, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 102, 8571, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 1, 1, 8572, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 2, 2, 8573, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 3, 3, 8574, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 2, 8576, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 3, 8577, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 4, 8578, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 5, 8579, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 7, 8581, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (127701, 0, 0, 8, 8582, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 1, 3, 1);
