INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0, 3, 111601, 1, 0, 1, 3634, 15);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0, 1, 101801, 12, 0, 1, 3635, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0, 2, 101801, 12, 0, 1, 3636, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0, 1, 111701, 1, 0, 1, 3637, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0, 1, 111701, 1, 0, 1, 3638, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0, 1, 111701, 1, 0, 1, 3639, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0, 1, 111701, 1, 0, 1, 3640, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0, 1, 111701, 1, 0, 1, 3641, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0, 1, 111701, 4, 0, 1, 3642, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0, 1, 111701, 8, 0, 1, 3643, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0, 2, 111701, 1, 0, 1, 3644, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0, 2, 111701, 1, 0, 1, 3645, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0, 2, 111701, 1, 0, 1, 3646, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0, 2, 111701, 1, 0, 1, 3647, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0, 2, 111701, 4, 0, 1, 3648, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0, 2, 111701, 8, 0, 1, 3649, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 0, 2, 111701, 1, 1, 1, 3650, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 0, 2, 111701, 1, 2, 1, 3651, 2);
