INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0, 3, 116301, 1, 0, 1, 4974, 8);
