INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0, 2, 102901, 12, 0, 1, 4977, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0, 1, 116201, 1, 0, 1, 4978, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0, 1, 116201, 1, 0, 1, 4979, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0, 1, 116201, 1, 0, 1, 4980, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0, 1, 116201, 1, 0, 1, 4981, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0, 1, 116201, 1, 0, 1, 4982, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0, 1, 116201, 4, 0, 1, 4983, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0, 1, 116201, 8, 0, 1, 4984, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0, 2, 116201, 1, 0, 1, 4985, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0, 2, 116201, 1, 0, 1, 4986, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0, 2, 116201, 1, 0, 1, 4987, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0, 2, 116201, 1, 0, 1, 4988, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0, 2, 116201, 4, 0, 1, 4989, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0, 2, 116201, 8, 0, 1, 4990, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 0, 2, 116201, 1, 1, 1, 4991, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 0, 2, 116201, 1, 2, 1, 4992, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 0, 2, 116201, 1, 3, 1, 4993, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0, 3, 116201, 1, 0, 1, 4995, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0, 3, 116201, 1, 0, 1, 4996, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0, 3, 116201, 1, 0, 1, 4997, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0, 3, 116201, 1, 0, 1, 4998, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0, 3, 116201, 1, 0, 1, 5000, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0, 3, 116201, 1, 0, 1, 5001, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0, 1, 116501, 1, 0, 1, 5003, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0, 1, 116501, 1, 0, 1, 5004, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0, 1, 116501, 1, 0, 1, 5005, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0, 1, 116501, 1, 0, 1, 5006, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0, 1, 116501, 1, 0, 1, 5007, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0, 1, 116501, 4, 0, 1, 5008, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0, 1, 116501, 8, 0, 1, 5009, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0, 2, 116501, 1, 0, 1, 5010, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0, 2, 116501, 1, 0, 1, 5011, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0, 2, 116501, 1, 0, 1, 5012, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お兄さんも\nあたしがボスなら\nうれしいですよね！", 0, 2, 116501, 1, 0, 1, 5013, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0, 2, 116501, 4, 0, 1, 5014, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0, 2, 116501, 8, 0, 1, 5015, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 0, 2, 116501, 1, 1, 1, 5016, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お腹が空きました…\nご飯食べにいきますよ", 0, 2, 116501, 1, 2, 1, 5017, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 0, 2, 116501, 1, 3, 1, 5018, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0, 3, 116501, 1, 0, 1, 5020, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0, 3, 116501, 1, 0, 1, 5021, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0, 3, 116501, 1, 0, 1, 5022, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0, 3, 116501, 1, 0, 1, 5023, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0, 3, 116501, 1, 0, 1, 5025, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0, 3, 116501, 1, 0, 1, 5026, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0, 1, 102501, 12, 0, 1, 5028, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0, 2, 102501, 12, 0, 1, 5029, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0, 1, 116601, 1, 0, 1, 5030, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0, 1, 116601, 1, 0, 1, 5031, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0, 1, 116601, 1, 0, 1, 5032, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0, 1, 116601, 1, 0, 1, 5033, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0, 1, 116601, 1, 0, 1, 5034, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0, 1, 116601, 4, 0, 1, 5035, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0, 1, 116601, 8, 0, 1, 5036, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0, 2, 116601, 1, 0, 1, 5037, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0, 2, 116601, 1, 0, 1, 5038, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0, 2, 116601, 1, 0, 1, 5039, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0, 2, 116601, 1, 0, 1, 5040, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0, 2, 116601, 4, 0, 1, 5041, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0, 2, 116601, 8, 0, 1, 5042, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 0, 2, 116601, 1, 1, 1, 5043, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 0, 2, 116601, 1, 2, 1, 5044, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 0, 2, 116601, 1, 3, 1, 5045, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0, 3, 116601, 1, 0, 1, 5047, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0, 3, 116601, 1, 0, 1, 5048, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0, 3, 116601, 1, 0, 1, 5049, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0, 3, 116601, 1, 0, 1, 5050, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0, 3, 116601, 1, 0, 1, 5052, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0, 3, 116601, 1, 0, 1, 5053, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0, 1, 116701, 1, 0, 1, 5055, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0, 1, 116701, 1, 0, 1, 5056, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0, 1, 116701, 1, 0, 1, 5057, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0, 1, 116701, 1, 0, 1, 5058, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0, 1, 116701, 1, 0, 1, 5059, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0, 1, 116701, 4, 0, 1, 5060, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0, 1, 116701, 8, 0, 1, 5061, 102);
