INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0, 1, 118401, 4, 0, 1, 5327, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0, 1, 118401, 8, 0, 1, 5328, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0, 2, 118401, 1, 0, 1, 5329, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0, 2, 118401, 1, 0, 1, 5330, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この格好…\n似合ってますか…？\nえへへ、照れますね…", 0, 2, 118401, 1, 0, 1, 5331, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0, 2, 118401, 1, 0, 1, 5332, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0, 2, 118401, 4, 0, 1, 5333, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0, 2, 118401, 8, 0, 1, 5334, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 0, 2, 118401, 1, 1, 1, 5335, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 0, 2, 118401, 1, 2, 1, 5336, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 0, 2, 118401, 1, 3, 1, 5337, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0, 3, 118401, 1, 0, 1, 5339, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0, 3, 118401, 1, 0, 1, 5340, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0, 3, 118401, 1, 0, 1, 5341, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0, 3, 118401, 1, 0, 1, 5342, 5);
