INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新薬開発のために\n薬草を採取して\nおこうかしら", 0, 1, 117501, 1, 0, 1, 5351, 4);
