INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0, 1, 117501, 4, 0, 1, 5353, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0, 1, 117501, 8, 0, 1, 5354, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0, 2, 117501, 1, 0, 1, 5355, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0, 2, 117501, 1, 0, 1, 5356, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0, 2, 117501, 1, 0, 1, 5357, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0, 2, 117501, 1, 0, 1, 5358, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0, 2, 117501, 4, 0, 1, 5359, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0, 2, 117501, 8, 0, 1, 5360, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 0, 2, 117501, 1, 1, 1, 5361, 1);
