INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 0, 2, 117501, 1, 3, 1, 5363, 3);
