INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0, 2, 124401, 1, 0, 1, 6862, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0, 2, 124401, 1, 0, 1, 6863, 2);
