INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0, 2, 124401, 8, 0, 1, 6867, 102);
