INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 0, 2, 126501, 1, 1, 1, 7547, 1);
