INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0, 1, 128501, 1, 0, 1, 7875, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0, 1, 128501, 1, 0, 1, 7876, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0, 1, 128501, 1, 0, 1, 7877, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0, 1, 128501, 1, 0, 1, 7878, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0, 1, 128501, 1, 0, 1, 7879, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0, 1, 128501, 4, 0, 1, 7880, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0, 1, 128501, 8, 0, 1, 7881, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0, 2, 128501, 1, 0, 1, 7882, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0, 2, 128501, 1, 0, 1, 7883, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0, 2, 128501, 1, 0, 1, 7884, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0, 2, 128501, 1, 0, 1, 7885, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0, 2, 128501, 4, 0, 1, 7886, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0, 2, 128501, 8, 0, 1, 7887, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 0, 2, 128501, 1, 1, 1, 7888, 1);
