INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 0, 2, 128501, 1, 3, 1, 7890, 3);
