INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 0, 2, 131901, 8, 0, 1, 8459, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 0, 2, 131901, 1, 1, 1, 8460, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 0, 2, 131901, 1, 2, 1, 8461, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 0, 2, 131901, 1, 3, 1, 8462, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 0, 3, 131901, 1, 0, 1, 8464, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("細かいところまで\nすごーく丁寧に\n作られてる…", 0, 3, 131901, 1, 0, 1, 8465, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 0, 3, 131901, 1, 0, 1, 8466, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 0, 3, 131901, 1, 0, 1, 8467, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 0, 3, 131901, 1, 0, 1, 8469, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 0, 3, 131901, 1, 0, 1, 8470, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 0, 3, 131901, 1, 0, 1, 8472, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 0, 3, 131901, 1, 0, 1, 8473, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 0, 1, 132101, 1, 0, 1, 8474, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 0, 1, 132101, 1, 0, 1, 8475, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 0, 1, 132101, 1, 0, 1, 8476, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 0, 1, 132101, 1, 0, 1, 8477, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 0, 1, 132101, 1, 0, 1, 8478, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 0, 1, 132101, 4, 0, 1, 8479, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 0, 1, 132101, 8, 0, 1, 8480, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 0, 2, 132101, 1, 0, 1, 8481, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 0, 2, 132101, 1, 0, 1, 8482, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 0, 2, 132101, 1, 0, 1, 8483, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 0, 2, 132101, 1, 0, 1, 8484, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 0, 2, 132101, 4, 0, 1, 8485, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 0, 2, 132101, 8, 0, 1, 8486, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 0, 2, 132101, 1, 1, 1, 8487, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 0, 2, 132101, 1, 2, 1, 8488, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 0, 2, 132101, 1, 3, 1, 8489, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 0, 3, 132101, 1, 0, 1, 8491, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 0, 3, 132101, 1, 0, 1, 8492, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 0, 3, 132101, 1, 0, 1, 8493, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 0, 3, 132101, 1, 0, 1, 8494, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 0, 3, 132101, 1, 0, 1, 8496, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("海に関する詩文なども\nあるのでしょうか…\n気になります", 0, 3, 132101, 1, 0, 1, 8497, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 0, 3, 132101, 1, 0, 1, 8499, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 0, 3, 132101, 1, 0, 1, 8500, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これがこの世界の\n居住環境か…\n面白い構造だね", 0, 1, 132001, 1, 0, 1, 8501, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 0, 1, 132001, 1, 0, 1, 8502, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("目新しいものが\nたくさんあるね\n胸が躍るよ", 0, 1, 132001, 1, 0, 1, 8503, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 0, 1, 132001, 1, 0, 1, 8504, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 0, 1, 132001, 1, 0, 1, 8505, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 0, 1, 132001, 4, 0, 1, 8506, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("普段と違う格好で\n歩き回るのも\n悪くない経験だ", 0, 1, 132001, 8, 0, 1, 8507, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 0, 2, 132001, 1, 0, 1, 8508, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("気軽に魔女に\n話しかけるなんて\n君は物好きだね", 0, 2, 132001, 1, 0, 1, 8509, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この世界においては\nボクは異物だからね\n大人しくしているさ", 0, 2, 132001, 1, 0, 1, 8510, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 0, 2, 132001, 1, 0, 1, 8511, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 0, 2, 132001, 4, 0, 1, 8512, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 0, 2, 132001, 8, 0, 1, 8513, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 0, 2, 132001, 1, 1, 1, 8514, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 0, 2, 132001, 1, 2, 1, 8515, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 0, 2, 132001, 1, 3, 1, 8516, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 0, 3, 132001, 1, 0, 1, 8518, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 0, 3, 132001, 1, 0, 1, 8519, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい香りだ\nこれにはどんな\n効能があるんだろう？", 0, 3, 132001, 1, 0, 1, 8520, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("火の点け方に\nボクのいた世界との\n違いはないのかな？", 0, 3, 132001, 1, 0, 1, 8521, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 0, 3, 132001, 1, 0, 1, 8523, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("素晴らしいね\nここには叡智が\n詰まっている", 0, 3, 132001, 1, 0, 1, 8524, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 0, 3, 132001, 1, 0, 1, 8526, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 0, 3, 132001, 1, 0, 1, 8527, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 0, 1, 109701, 12, 0, 1, 8528, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 0, 2, 109701, 12, 0, 1, 8529, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 0, 1, 109801, 12, 0, 1, 8530, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 0, 2, 109801, 12, 0, 1, 8531, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 0, 1, 109901, 12, 0, 1, 8532, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 0, 2, 109901, 12, 0, 1, 8533, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 0, 1, 132201, 1, 0, 1, 8534, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 0, 1, 132201, 1, 0, 1, 8535, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 0, 1, 132201, 1, 0, 1, 8536, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 0, 1, 132201, 1, 0, 1, 8537, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 0, 1, 132201, 1, 0, 1, 8538, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 0, 1, 132201, 4, 0, 1, 8539, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 0, 1, 132201, 8, 0, 1, 8540, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 0, 2, 132201, 1, 0, 1, 8541, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 0, 2, 132201, 1, 0, 1, 8542, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 0, 2, 132201, 1, 0, 1, 8543, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 0, 2, 132201, 1, 0, 1, 8544, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 0, 2, 132201, 4, 0, 1, 8545, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 0, 2, 132201, 8, 0, 1, 8546, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 0, 2, 132201, 1, 1, 1, 8547, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 0, 2, 132201, 1, 2, 1, 8548, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 0, 2, 132201, 1, 3, 1, 8549, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 0, 3, 132201, 1, 0, 1, 8551, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 0, 3, 132201, 1, 0, 1, 8552, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 0, 3, 132201, 1, 0, 1, 8553, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 0, 3, 132201, 1, 0, 1, 8554, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 0, 3, 132201, 1, 0, 1, 8556, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 0, 3, 132201, 1, 0, 1, 8557, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 0, 1, 127701, 1, 0, 1, 8559, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 0, 1, 127701, 1, 0, 1, 8560, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 0, 1, 127701, 1, 0, 1, 8561, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 0, 1, 127701, 1, 0, 1, 8562, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 0, 1, 127701, 1, 0, 1, 8563, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 0, 1, 127701, 4, 0, 1, 8564, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 0, 1, 127701, 8, 0, 1, 8565, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 0, 2, 127701, 1, 0, 1, 8566, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 0, 2, 127701, 1, 0, 1, 8567, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 0, 2, 127701, 1, 0, 1, 8568, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 0, 2, 127701, 1, 0, 1, 8569, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 0, 2, 127701, 4, 0, 1, 8570, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 0, 2, 127701, 8, 0, 1, 8571, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 0, 2, 127701, 1, 1, 1, 8572, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 0, 2, 127701, 1, 2, 1, 8573, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 0, 2, 127701, 1, 3, 1, 8574, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 0, 3, 127701, 1, 0, 1, 8576, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 0, 3, 127701, 1, 0, 1, 8577, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 0, 3, 127701, 1, 0, 1, 8578, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 0, 3, 127701, 1, 0, 1, 8579, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 0, 3, 127701, 1, 0, 1, 8581, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 0, 3, 127701, 1, 0, 1, 8582, 8);
