INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 100101, 0, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 100101, 0, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 100101, 0, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 100101, 0, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 100101, 0, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 4, 100101, 0, 6);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 8, 100101, 0, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 1, 100101, 0, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 1, 100101, 0, 9);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "えっとね…しっぽは\n触っちゃダメだよ？", 1, 100101, 0, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 100101, 0, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 4, 100101, 0, 12);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 8, 100101, 0, 13);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよー！\n今日も元気に\nいっちゃうよー！", 1, 100101, 0, 14);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 100101, 0, 15);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 100101, 0, 16);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ちゃんとケアして\n傷が残らないように\nしないと…", 1, 100101, 0, 17);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一回休むと\nこのままずるずる\nいきそう…", 1, 100101, 0, 18);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 100101, 0, 19);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 1, 100101, 0, 20);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい所に\nいると体が丸く\nなっちゃう…", 1, 100101, 0, 21);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 1, 100101, 0, 22);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 1, 100101, 0, 23);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 1, 100101, 0, 24);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 100102, 0, 26);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1, 100102, 0, 27);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1, 100102, 0, 28);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1, 100102, 0, 29);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1, 100102, 0, 30);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 8, 100102, 0, 31);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 8, 100102, 0, 32);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 100102, 0, 33);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 1, 100102, 0, 34);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 1, 100102, 0, 35);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 1, 100102, 0, 36);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 8, 100102, 0, 37);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 8, 100102, 0, 38);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 1, 100102, 0, 39);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 100102, 0, 40);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 100102, 0, 41);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "これでよし♪\n騎士くん\n気づくかな…？", 1, 100102, 0, 42);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一回休むと\nこのままずるずる\nいきそう…", 1, 100102, 0, 43);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 100102, 0, 44);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "こういうの\nなんか気になって\nじーって見ちゃうんだ", 1, 100102, 0, 45);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい所に\nいると体が丸く\nなっちゃう…", 1, 100102, 0, 46);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "いつ何が起きるか\n分からないし\nもっと力をつけないと", 1, 100102, 0, 47);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹が減ると\nなんか力が\n出ないんだよね…", 1, 100102, 0, 48);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "色んなことを\n知っておくのは\n大事だよね！", 1, 100102, 0, 49);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1, 100201, 0, 51);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 100201, 0, 52);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 100201, 0, 53);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "戦いは\n得意じゃないけど\nお料理なら…！", 1, 100201, 0, 54);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "こういう平和な\n時間が、もっと\n続くといいな", 1, 100201, 0, 55);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 4, 100201, 0, 56);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 8, 100201, 0, 57);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 1, 100201, 0, 58);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 1, 100201, 0, 59);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "困ったことが\nあったら\nいつでも話してね", 1, 100201, 0, 60);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 100201, 0, 61);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 4, 100201, 0, 62);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 8, 100201, 0, 63);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 1, 100201, 0, 64);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 1, 100201, 0, 65);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 1, 100201, 0, 66);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 1, 100201, 0, 67);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "がんばることも\n必要だけど、\nお休みも大切だよね", 1, 100201, 0, 68);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 1, 100201, 0, 69);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 100201, 0, 70);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 1, 100201, 0, 71);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 100201, 0, 72);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 1, 100201, 0, 73);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 1, 100201, 0, 74);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "洗濯と掃除\nどっちから\nはじめよう？", 1, 100202, 0, 76);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 1, 100202, 0, 77);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 100202, 0, 78);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今日はどんな料理に\n挑戦しよう…♪", 1, 100202, 0, 79);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 100202, 0, 80);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 8, 100202, 0, 81);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "騎士クンのそばに\nいられるだけで\n幸せだよ", 8, 100202, 0, 82);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 1, 100202, 0, 83);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 100202, 0, 84);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 1, 100202, 0, 85);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 1, 100202, 0, 86);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 8, 100202, 0, 87);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 8, 100202, 0, 88);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 100202, 0, 89);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 100202, 0, 90);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 100202, 0, 91);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 1, 100202, 0, 92);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 100202, 0, 93);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 100202, 0, 94);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 100202, 0, 95);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あの…！\nよ、良かったら\n一緒に…！", 1, 100202, 0, 96);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 100202, 0, 97);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 100202, 0, 98);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 100202, 0, 99);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "装備の手入れでも\nしようかな…", 1, 100301, 0, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 100301, 0, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 100301, 0, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 100301, 0, 104);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 100301, 0, 105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "彼は\nどこまで強くなるのか\n私も負けられないな", 4, 100301, 0, 106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 100301, 0, 107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 100301, 0, 108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 100301, 0, 109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 100301, 0, 110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 100301, 0, 111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 4, 100301, 0, 112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 100301, 0, 113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう\n今日も充実した\n一日にしていこう", 1, 100301, 0, 114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 100301, 0, 115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 100301, 0, 116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 100301, 0, 117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 100301, 0, 118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 100301, 0, 119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 100301, 0, 120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 100301, 0, 121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 100301, 0, 122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 100301, 0, 123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 100301, 0, 124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 100302, 0, 126);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 100302, 0, 127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 100302, 0, 128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 100302, 0, 129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 100302, 0, 130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 8, 100302, 0, 131);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 100302, 0, 132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 100302, 0, 133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 100302, 0, 134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 100302, 0, 135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 100302, 0, 136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひょっとして\n今のも一本だなんて\n言わないよね？", 8, 100302, 0, 137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 100302, 0, 138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 100302, 0, 139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 100302, 0, 140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 100302, 0, 141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 100302, 0, 142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 100302, 0, 143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 100302, 0, 144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 100302, 0, 145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 100302, 0, 146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 100302, 0, 147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 100302, 0, 148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 100302, 0, 149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 100401, 0, 151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "落とし穴…どこに\n作ったんだっけ？", 1, 100401, 0, 152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 100401, 0, 153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 100401, 0, 154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "かくれんぼする人\nこの指、と～まれ！", 1, 100401, 0, 155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 4, 100401, 0, 156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ミソギと遊ぶ？\nミソギに遊ばれる？", 8, 100401, 0, 157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 100401, 0, 158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 100401, 0, 159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 100401, 0, 160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 100401, 0, 161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ここ、ミソギ探検隊の\n基地にしようよ！", 4, 100401, 0, 162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 100401, 0, 163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 100401, 0, 164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 100401, 0, 165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 100401, 0, 166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 100401, 0, 167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 100401, 0, 168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 100401, 0, 169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 100401, 0, 170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 100401, 0, 171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "探検家は\n体力勝負だよー！", 1, 100401, 0, 172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 100401, 0, 173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 100401, 0, 174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 100402, 0, 176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 100402, 0, 177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 100402, 0, 178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 100402, 0, 179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "かくれんぼする人\nこの指、と～まれ！", 1, 100402, 0, 180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 8, 100402, 0, 181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 8, 100402, 0, 182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 100402, 0, 183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 100402, 0, 184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "将来の夢は探検家！\n探したい物はないけど", 1, 100402, 0, 185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 100402, 0, 186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 100402, 0, 187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ここ、ミソギ探検隊の\n基地にしようよ！", 8, 100402, 0, 188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 100402, 0, 189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 100402, 0, 190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 100402, 0, 191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 100402, 0, 192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 100402, 0, 193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 100402, 0, 194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 100402, 0, 195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 100402, 0, 196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "探検家は\n体力勝負だよー！", 1, 100402, 0, 197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "探検のおやつに\nぴったりかも～！", 1, 100402, 0, 198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 100402, 0, 199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 100601, 0, 201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 100601, 0, 202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 100601, 0, 203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 100601, 0, 204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 100601, 0, 205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんと私は\n相性抜群です♪", 4, 100601, 0, 206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 100601, 0, 207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 100601, 0, 208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 1, 100601, 0, 209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そんなところ\n触っちゃダメ♪", 1, 100601, 0, 210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄ちゃん、イジワル\nしないでください…", 1, 100601, 0, 211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 4, 100601, 0, 212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "コーチ、\n今日も特訓\nお願いします！", 8, 100601, 0, 213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 100601, 0, 214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 100601, 0, 215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はこれからですよ\nお兄ちゃん♪", 1, 100601, 0, 216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "世界一、かわいく\nなるんだから♪", 1, 100601, 0, 217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちいい♪\nもう立てないよ～", 1, 100601, 0, 218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nアカリのライバル\nだね", 1, 100601, 0, 219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 100601, 0, 220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はぁ…アカリ、\nとろけちゃう…", 1, 100601, 0, 221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 1, 100601, 0, 222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 1, 100601, 0, 223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 1, 100601, 0, 224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 100602, 0, 226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 100602, 0, 227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 100602, 0, 228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 100602, 0, 229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 100602, 0, 230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 8, 100602, 0, 231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 100602, 0, 232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 100602, 0, 233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 1, 100602, 0, 234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 1, 100602, 0, 235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄ちゃん、イジワル\nしないでください…", 1, 100602, 0, 236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 8, 100602, 0, 237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "コーチ、\n今日も特訓\nお願いします！", 8, 100602, 0, 238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 100602, 0, 239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 1, 100602, 0, 240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 1, 100602, 0, 241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 100602, 0, 242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちいい♪\nもう立てないよ～", 1, 100602, 0, 243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nアカリのライバル\nだね", 1, 100602, 0, 244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 100602, 0, 245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 1, 100602, 0, 246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "汗をかくと\n気持ちいいですよね", 1, 100602, 0, 247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 100602, 0, 248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 100602, 0, 249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 100701, 0, 251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "う～ら～め～し～や～\nなの～♪", 1, 100701, 0, 252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 100701, 0, 253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "幽霊の正体見たり、\nミヤコなの～", 1, 100701, 0, 254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 100701, 0, 255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 4, 100701, 0, 256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 8, 100701, 0, 257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 100701, 0, 258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 100701, 0, 259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 100701, 0, 260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 100701, 0, 261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 4, 100701, 0, 262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 8, 100701, 0, 263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 1, 100701, 0, 264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 1, 100701, 0, 265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 1, 100701, 0, 266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～", 1, 100701, 0, 267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 1, 100701, 0, 268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "む～…かわいいけど\nプリンは分けて\nあげないの～", 1, 100701, 0, 269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "は～　いい気分なの…\n成仏しそうなの…", 1, 100701, 0, 270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 1, 100701, 0, 271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "幽霊には、体力も\n筋力も関係ないの～！", 1, 100701, 0, 272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 1, 100701, 0, 273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "プリンの写真集が\nあればいいのに\nなの～", 1, 100701, 0, 274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1, 100702, 0, 276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1, 100702, 0, 277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1, 100702, 0, 278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1, 100702, 0, 279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1, 100702, 0, 280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツがいて\nプリンもある\nきっとここが天国なの", 8, 100702, 0, 281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 8, 100702, 0, 282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 100702, 0, 283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 100702, 0, 284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 1, 100702, 0, 285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 100702, 0, 286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 8, 100702, 0, 287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 8, 100702, 0, 288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 1, 100702, 0, 289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 1, 100702, 0, 290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 1, 100702, 0, 291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "体が透けちゃうから\nうまく使えないの～ ", 1, 100702, 0, 292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 1, 100702, 0, 293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 1, 100702, 0, 294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "は～　いい気分なの…\n成仏しそうなの…", 1, 100702, 0, 295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 1, 100702, 0, 296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "幽霊には、体力も\n筋力も関係ないの～！　", 1, 100702, 0, 297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 1, 100702, 0, 298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "プリンの写真集が\nあればいいのに\nなの～ ", 1, 100702, 0, 299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 100801, 0, 301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 100801, 0, 302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 100801, 0, 303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 100801, 0, 304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 100801, 0, 305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "このボクがいるだけで\nあの人への\nおもてなしだよね", 4, 100801, 0, 306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 8, 100801, 0, 307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 100801, 0, 308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 100801, 0, 309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 1, 100801, 0, 310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 1, 100801, 0, 311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 4, 100801, 0, 312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 8, 100801, 0, 313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "美しいボクの\n美しい一日が\n始まるよ！", 1, 100801, 0, 314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 1, 100801, 0, 315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 1, 100801, 0, 316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 100801, 0, 317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 100801, 0, 318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 1, 100801, 0, 319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 100801, 0, 320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 1, 100801, 0, 321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 100801, 0, 322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 1, 100801, 0, 323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 1, 100801, 0, 324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 100802, 0, 326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 100802, 0, 327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1, 100802, 0, 328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 100802, 0, 329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1, 100802, 0, 330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "このボクがいるだけで\nあの人への\nおもてなしだよね", 8, 100802, 0, 331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人も今\n可愛いボクのこと\n考えてるかな…", 8, 100802, 0, 332);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 100802, 0, 333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 1, 100802, 0, 334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 1, 100802, 0, 335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 1, 100802, 0, 336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 8, 100802, 0, 337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 8, 100802, 0, 338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1, 100802, 0, 339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 1, 100802, 0, 340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 1, 100802, 0, 341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 100802, 0, 342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 100802, 0, 343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 1, 100802, 0, 344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 100802, 0, 345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 1, 100802, 0, 346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 100802, 0, 347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ボクが食べると\n食べ物まで\n可愛いなあ…", 1, 100802, 0, 348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 1, 100802, 0, 349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 100901, 0, 351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 100901, 0, 352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 100901, 0, 353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 100901, 0, 354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 100901, 0, 355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "シグルド…\n今頃何処で何を\nしているのだろう…", 4, 100901, 0, 356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 8, 100901, 0, 357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 100901, 0, 358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 100901, 0, 359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 100901, 0, 360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 100901, 0, 361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 4, 100901, 0, 362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 100901, 0, 363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 100901, 0, 364);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 100901, 0, 365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 100901, 0, 366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 100901, 0, 367);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 100901, 0, 368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 100901, 0, 369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 100901, 0, 370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これが我らの求めた\n安寧か…\n悪くない…", 1, 100901, 0, 371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "この程度\n前世での激闘に\n比べれば…！", 1, 100901, 0, 372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "最近金欠でな…\nいやぁありがたい…", 1, 100901, 0, 373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 100901, 0, 374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 100902, 0, 376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 100902, 0, 377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 100902, 0, 378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 100902, 0, 379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 100902, 0, 380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 100902, 0, 381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 8, 100902, 0, 382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 8, 100902, 0, 383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 100902, 0, 384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 100902, 0, 385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 100902, 0, 386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 100902, 0, 387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 100902, 0, 388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 8, 100902, 0, 389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 100902, 0, 390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 100902, 0, 391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 100902, 0, 392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 100902, 0, 393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 100902, 0, 394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 100902, 0, 395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これが我らの求めた\n安寧か…\n悪くない…", 1, 100902, 0, 396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 100902, 0, 397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 100902, 0, 398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 100902, 0, 399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 101001, 0, 401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 101001, 0, 402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 101001, 0, 403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 101001, 0, 404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 101001, 0, 405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 4, 101001, 0, 406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 8, 101001, 0, 407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 101001, 0, 408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 101001, 0, 409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 101001, 0, 410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うちの話し方\nのんびりしてます\nやろか？", 1, 101001, 0, 411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 4, 101001, 0, 412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 8, 101001, 0, 413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 101001, 0, 414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 101001, 0, 415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 101001, 0, 416);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 101001, 0, 417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 101001, 0, 418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 101001, 0, 419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "心地ええどす。まるで\nお花畑にいるようやね", 1, 101001, 0, 420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 101001, 0, 421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 101001, 0, 422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 101001, 0, 423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "絵本、絵本～\n幸せやわぁ♪", 1, 101001, 0, 424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "王子はんにとって\n自慢の姫になるえ～", 1, 101002, 0, 426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 101002, 0, 427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 101002, 0, 428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 101002, 0, 429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 101002, 0, 430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 8, 101002, 0, 431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 8, 101002, 0, 432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 1, 101002, 0, 433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 101002, 0, 434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 1, 101002, 0, 435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "絵本読むんも\nええなぁ…", 1, 101002, 0, 436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 8, 101002, 0, 437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 8, 101002, 0, 438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 101002, 0, 439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 1, 101002, 0, 440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 101002, 0, 441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 101002, 0, 442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 101002, 0, 443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 101002, 0, 444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 101002, 0, 445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 101002, 0, 446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 101002, 0, 447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 101002, 0, 448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 101002, 0, 449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 101101, 0, 451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 101101, 0, 452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "い、いけない！\n縫い針落としちゃった", 1, 101101, 0, 453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 101101, 0, 454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 101101, 0, 455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 4, 101101, 0, 456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 8, 101101, 0, 457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 101101, 0, 458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 101101, 0, 459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "勉強、見てくれるん\nですか？　やった～♪", 1, 101101, 0, 460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "服がほつれてますよ？\n直してあげます！", 1, 101101, 0, 461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 4, 101101, 0, 462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 8, 101101, 0, 463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 101101, 0, 464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 101101, 0, 465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ねむいです～\nでももうちょっと\n勉強しないと～…", 1, 101101, 0, 466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "この服、かわいい♪\n今度作って\nみよっと～！", 1, 101101, 0, 467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "勉強で疲れたから\n休憩しよ～っと", 1, 101101, 0, 468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\n私が服を作って\nあげよっか？", 1, 101101, 0, 469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 101101, 0, 470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "のんびりしたら、\nまた勉強しよっと！", 1, 101101, 0, 471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動すると勉強も\nはかどるよね！", 1, 101101, 0, 472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 101101, 0, 473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 101101, 0, 474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 101102, 0, 476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 101102, 0, 477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "い、いけない！\n縫い針落としちゃった", 1, 101102, 0, 478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "笑う時には服着ます！", 1, 101102, 0, 479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 101102, 0, 480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 8, 101102, 0, 481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 8, 101102, 0, 482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "勉強、見てくれるん\nですか？　やった～♪", 1, 101102, 0, 483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 101102, 0, 484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 1, 101102, 0, 485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "勉強は…\n明日にしようっと", 1, 101102, 0, 486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "妹だけど、大人な\nレディですからね！", 8, 101102, 0, 487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "な、ナデナデとか\n恥ずかしいな～", 8, 101102, 0, 488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 101102, 0, 489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 101102, 0, 490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 1, 101102, 0, 491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "服装や髪形が変わると\n気分も変わるよね", 1, 101102, 0, 492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 1, 101102, 0, 493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\n私が服を作って\nあげよっか？", 1, 101102, 0, 494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いまならどんな試験も\n合格できそうです！", 1, 101102, 0, 495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "のんびりしたら\nまた勉強しよっと！", 1, 101102, 0, 496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 101102, 0, 497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 101102, 0, 498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 101102, 0, 499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 101201, 0, 501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 101201, 0, 502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 101201, 0, 503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 101201, 0, 504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "もし、超能力のことが\nバレたら…ブルブル", 1, 101201, 0, 505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 4, 101201, 0, 506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミを抱き枕に…\nじょ、冗談冗談！", 8, 101201, 0, 507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ん～、なになに？\n一緒にお昼寝する？", 1, 101201, 0, 508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 101201, 0, 509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 101201, 0, 510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 101201, 0, 511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 4, 101201, 0, 512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 8, 101201, 0, 513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 1, 101201, 0, 514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 101201, 0, 515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 101201, 0, 516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 101201, 0, 517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 101201, 0, 518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 101201, 0, 519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 101201, 0, 520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 1, 101201, 0, 521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "眠くならないように、\n運動しよっと！", 1, 101201, 0, 522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹いっぱいになると\n眠くなるよね～", 1, 101201, 0, 523);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 101201, 0, 524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 101202, 0, 526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "もっといっぱい\n超能力使えたら\nいいのにな～", 1, 101202, 0, 527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 1, 101202, 0, 528);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 101202, 0, 529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 1, 101202, 0, 530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 8, 101202, 0, 531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミを抱き枕に…\nじょ、冗談冗談！", 8, 101202, 0, 532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 1, 101202, 0, 533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 1, 101202, 0, 534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "一緒に\nお出かけしたいなー\nデートしよっ！", 1, 101202, 0, 535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 1, 101202, 0, 536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 8, 101202, 0, 537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "う～だんだん眠く…\nねぇ一緒に寝よ？", 8, 101202, 0, 538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "キミって早起きだね！\n私は寝ようかな～", 1, 101202, 0, 539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 101202, 0, 540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 101202, 0, 541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 101202, 0, 542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 101202, 0, 543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 101202, 0, 544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 101202, 0, 545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 1, 101202, 0, 546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "よーし\n困ってる人のために\nパワーアップ☆", 1, 101202, 0, 547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 1, 101202, 0, 548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 101202, 0, 549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 101601, 0, 551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 101601, 0, 552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1, 101601, 0, 553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここで撮影会、\nしようかな～♪", 1, 101601, 0, 554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 101601, 0, 555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "やば…\nこのままじゃ、うち\nキュン死にかも…", 4, 101601, 0, 556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 8, 101601, 0, 557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "うちの写真撮るの？\nどんなポーズ？", 1, 101601, 0, 558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 1, 101601, 0, 559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "宿題見てくんない？\nひとりじゃムリ～", 1, 101601, 0, 560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 1, 101601, 0, 561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 4, 101601, 0, 562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 8, 101601, 0, 563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 1, 101601, 0, 564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 1, 101601, 0, 565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 1, 101601, 0, 566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 1, 101601, 0, 567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 101601, 0, 568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "きゃわたーん☆\n君もおしゃれしよっか", 1, 101601, 0, 569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 1, 101601, 0, 570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 101601, 0, 571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 1, 101601, 0, 572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 101601, 0, 573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 101601, 0, 574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 1, 101602, 0, 576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 101602, 0, 577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 1, 101602, 0, 578);
