INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 8, 101602, 0, 581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 8, 101602, 0, 582);
