INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 1, 101602, 0, 592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 101602, 0, 593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 1, 101602, 0, 594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 1, 101602, 0, 595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 101602, 0, 596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "食べた分は\n消費しないとね～", 1, 101602, 0, 597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 101602, 0, 598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 101602, 0, 599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 101701, 0, 601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 101701, 0, 602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 101701, 0, 603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 101701, 0, 604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "さぁ、みんなで歌って\n踊るさ～♪", 1, 101701, 0, 605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "毎日、最高に幸せさ～\nキミのおかげだねー", 4, 101701, 0, 606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 8, 101701, 0, 607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 101701, 0, 608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 101701, 0, 609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 101701, 0, 610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "空手の型の\n練習するさー\nキミもどう？", 1, 101701, 0, 611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 4, 101701, 0, 612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 8, 101701, 0, 613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ハイターイ！\n今日も一日、\nがんばるさー！", 1, 101701, 0, 614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 1, 101701, 0, 615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 1, 101701, 0, 616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 101701, 0, 617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 1, 101701, 0, 618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 1, 101701, 0, 619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 1, 101701, 0, 620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 1, 101701, 0, 621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 1, 101701, 0, 622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 1, 101701, 0, 623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 1, 101701, 0, 624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1, 101702, 0, 626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "さぁ、みんなで歌って\n踊るさ～♪", 1, 101702, 0, 627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "元気がないときは\n私のところに来るさー", 1, 101702, 0, 628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 101702, 0, 629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1, 101702, 0, 630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "毎日、最高に幸せさ～\nキミのおかげだねー", 8, 101702, 0, 631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "幸せすぎて踊らずには\nいられないさ～", 8, 101702, 0, 632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 101702, 0, 633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 1, 101702, 0, 634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 1, 101702, 0, 635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 101702, 0, 636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 8, 101702, 0, 637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 8, 101702, 0, 638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 1, 101702, 0, 639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 1, 101702, 0, 640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 1, 101702, 0, 641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 101702, 0, 642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 1, 101702, 0, 643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 1, 101702, 0, 644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 1, 101702, 0, 645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 1, 101702, 0, 646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 1, 101702, 0, 647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…もっと\nおかわりがほしいさー", 1, 101702, 0, 648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 1, 101702, 0, 649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 101801, 0, 651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1, 101801, 0, 652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 101801, 0, 653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 101801, 0, 654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はー…\nちょっと肩が\nこっちゃった…", 1, 101801, 0, 655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 4, 101801, 0, 656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 8, 101801, 0, 657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 101801, 0, 658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 101801, 0, 659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 1, 101801, 0, 660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 1, 101801, 0, 661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 4, 101801, 0, 662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 8, 101801, 0, 663);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "学校に\n遅刻しないように\n早く準備しないとね", 1, 101801, 0, 664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 1, 101801, 0, 665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 101801, 0, 666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 1, 101801, 0, 667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 1, 101801, 0, 668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪　あなたも\n私の生徒になる？", 1, 101801, 0, 669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 1, 101801, 0, 670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 1, 101801, 0, 671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動部の顧問も\nできるかしら？", 1, 101801, 0, 672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "学校の給食でも\n食べたいわぁ～", 1, 101801, 0, 673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "いい参考書を\n見つけちゃった♪", 1, 101801, 0, 674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1, 101802, 0, 676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1, 101802, 0, 677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1, 101802, 0, 678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 101802, 0, 679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1, 101802, 0, 680);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 8, 101802, 0, 681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 8, 101802, 0, 682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 101802, 0, 683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 101802, 0, 684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 1, 101802, 0, 685);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 1, 101802, 0, 686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 8, 101802, 0, 687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 8, 101802, 0, 688);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の特別授業\nしてあげましょうか？", 1, 101802, 0, 689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 1, 101802, 0, 690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 101802, 0, 691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "もしかして…\n少し露出多いかしら？", 1, 101802, 0, 692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "私の膝枕と、どっちが\nフカフカかしら？", 1, 101802, 0, 693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 1, 101802, 0, 694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 1, 101802, 0, 695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 1, 101802, 0, 696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動部の顧問も\nできるかしら？　", 1, 101802, 0, 697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あん！\nお胸にこぼしちゃった", 1, 101802, 0, 698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 1, 101802, 0, 699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "おにいちゃんは、\nにんじん食べられる？", 1, 102001, 0, 701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "えへへ、\nお絵かきしよ～♪", 1, 102001, 0, 702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あのね、ミミと\nあそんでほしいな～", 1, 102001, 0, 703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1, 102001, 0, 704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 1, 102001, 0, 705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "おにいちゃんの絵、\nかいてあげるね♪", 4, 102001, 0, 706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 8, 102001, 0, 707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 1, 102001, 0, 708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "やった～！\nおにいちゃんが\nほめてくれた～", 1, 102001, 0, 709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 1, 102001, 0, 710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 1, 102001, 0, 711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 4, 102001, 0, 712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 8, 102001, 0, 713);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 1, 102001, 0, 714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "うわぁ！\nお日さまがすごーく\n高いところにある～", 1, 102001, 0, 715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おにいちゃん\nおやすみのご本\nよんで～", 1, 102001, 0, 716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ミミも、\nうさぎさんみたいに、\nかわいくなりたいな♪", 1, 102001, 0, 717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "のんびり～♪\nゆったり～♪\nまったり～♪", 1, 102001, 0, 718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 1, 102001, 0, 719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ♪", 1, 102001, 0, 720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 1, 102001, 0, 721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ふ、ふぇ～　ミミ、\n運動苦手だよ～", 1, 102001, 0, 722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 1, 102001, 0, 723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ミミね！\nこのお話だーい好き！", 1, 102001, 0, 724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ぴょんぴょん\n跳ねるよ\nうっさぎっさ～ん♪", 1, 102002, 0, 726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "うさぎさんの\nマネだよ～\n似てるかな～？", 1, 102002, 0, 727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "モフモフすると\nきもちい～よ", 1, 102002, 0, 728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ミソギちゃん\nキョウカちゃん\nあ～そ～ぼっ！", 1, 102002, 0, 729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おにいちゃん\nおやすみのご本\nよんで～", 1, 102002, 0, 730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ねえ、おにいちゃん\nミミのこと、すき？", 8, 102002, 0, 731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 8, 102002, 0, 732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "やった～！\nおにいちゃんが\nほめてくれた～", 1, 102002, 0, 733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おにいちゃん\n遊んでくれるの？\nわぁい♪", 1, 102002, 0, 734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おにいちゃん\nあのね…\nいいこいいこして～", 1, 102002, 0, 735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あはは～\nくすぐったいよ～", 1, 102002, 0, 736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おにいちゃんの\nおひざの上、とっても\n安心するんだ～！", 8, 102002, 0, 737);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "わぁい♪\nおにいちゃんの\nなでなで\nうれし～な♪", 8, 102002, 0, 738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おひさまさん\nおはよ～！\n毎朝、早おきだね～", 1, 102002, 0, 739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼寝お昼寝～♪\nおにいちゃんと一緒に\nお昼寝～♪", 1, 102002, 0, 740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠くなっちゃった～\nおにいちゃん！\n今日は一緒に寝よ～", 1, 102002, 0, 741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ミミも\nうさぎさんみたいに\nかわいくなりたいな", 1, 102002, 0, 742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわふわ～モコモコ～\n気持ちいい～♪\nミミもゴロゴロ～ン♪", 1, 102002, 0, 743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 1, 102002, 0, 744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ～♪", 1, 102002, 0, 745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 1, 102002, 0, 746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁはぁ…\nふえ～ミミ\n疲れちゃうよ～！", 1, 102002, 0, 747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 1, 102002, 0, 748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ミミね！\nこのお話だーい好き！", 1, 102002, 0, 749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 1, 102101, 0, 751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お外より\nお部屋の中の方が\n落ち着くな…", 1, 102101, 0, 752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 1, 102101, 0, 753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あ、あめんぼあかいな\nあいうえお！", 1, 102101, 0, 754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 102101, 0, 755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 4, 102101, 0, 756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 8, 102101, 0, 757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "こ、この本、とっても\nおもしろいんですよ♪", 1, 102101, 0, 758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 1, 102101, 0, 759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 1, 102101, 0, 760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 1, 102101, 0, 761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 4, 102101, 0, 762);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 8, 102101, 0, 763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 1, 102101, 0, 764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 1, 102101, 0, 765);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 1, 102101, 0, 766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "可愛い女の子に変身！\n出来ますように…♪", 1, 102101, 0, 767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 1, 102101, 0, 768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子を相手に、\n演技の練習～", 1, 102101, 0, 769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 1, 102101, 0, 770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "えへへ。\nぽっかぽかです…", 1, 102101, 0, 771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 1, 102101, 0, 772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 1, 102101, 0, 773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 102101, 0, 774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 1, 102102, 0, 776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1, 102102, 0, 777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1, 102102, 0, 778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 102102, 0, 779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1, 102102, 0, 780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 8, 102102, 0, 781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 8, 102102, 0, 782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "こ、この本、とっても\nおもしろいんですよ♪", 1, 102102, 0, 783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 1, 102102, 0, 784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "何か私にできる事\n…そうだ！\nお掃除しますね！", 1, 102102, 0, 785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 1, 102102, 0, 786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 8, 102102, 0, 787);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 8, 102102, 0, 788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 1, 102102, 0, 789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 1, 102102, 0, 790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝る前に\n台本を読むと、\n覚えやすいんです", 1, 102102, 0, 791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "可愛い女の子に変身！\n出来ますように…♪", 1, 102102, 0, 792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 1, 102102, 0, 793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子を相手に\n演技の練習～", 1, 102102, 0, 794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 1, 102102, 0, 795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "えへへ\nぽっかぽかです…", 1, 102102, 0, 796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "え、演劇には\nた、体力も重要\nだから～", 1, 102102, 0, 797);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "残さず\n食べますね…！", 1, 102102, 0, 798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 102102, 0, 799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 102201, 0, 801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 102201, 0, 802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 102201, 0, 803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 102201, 0, 804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 102201, 0, 805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 4, 102201, 0, 806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 8, 102201, 0, 807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 102201, 0, 808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 102201, 0, 809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 102201, 0, 810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 102201, 0, 811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 4, 102201, 0, 812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 8, 102201, 0, 813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 102201, 0, 814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 102201, 0, 815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 102201, 0, 816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "アカリみたいに\n可愛くなるのは\n難しいかな", 1, 102201, 0, 817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 1, 102201, 0, 818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛い…けど、\n私には似合わないかな", 1, 102201, 0, 819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はあ、リラックスして\nゲームができるわ", 1, 102201, 0, 820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ん…暖かくて、\n寝落ちしちゃいそう", 1, 102201, 0, 821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 1, 102201, 0, 822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 102201, 0, 823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 102201, 0, 824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 102202, 0, 826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 102202, 0, 827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "レアカードをゲット！\nすごいでしょ！？", 1, 102202, 0, 828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 102202, 0, 829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 102202, 0, 830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 8, 102202, 0, 831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 8, 102202, 0, 832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 102202, 0, 833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 1, 102202, 0, 834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねえ、見て！\n新しいゲームを\n買って来たの！", 1, 102202, 0, 835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 1, 102202, 0, 836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 8, 102202, 0, 837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 8, 102202, 0, 838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 102202, 0, 839);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 102202, 0, 840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 102202, 0, 841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 102202, 0, 842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 1, 102202, 0, 843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛い…けど、\n私には似合わないかな", 1, 102202, 0, 844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 1, 102202, 0, 845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 1, 102202, 0, 846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "最近ゲームばかりだし\nたまにはいいかな？", 1, 102202, 0, 847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 102202, 0, 848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 102202, 0, 849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 102301, 0, 851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 102301, 0, 852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 102301, 0, 853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 102301, 0, 854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 102301, 0, 855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 4, 102301, 0, 856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 8, 102301, 0, 857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 102301, 0, 858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 102301, 0, 859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 102301, 0, 860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 102301, 0, 861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 4, 102301, 0, 862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 8, 102301, 0, 863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 102301, 0, 864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 102301, 0, 865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 102301, 0, 866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 102301, 0, 868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 102301, 0, 869);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 102301, 0, 870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 1, 102301, 0, 871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい～\nぷうきちも\n食べてみる？", 1, 102301, 0, 873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ぷうきち！\nこの本、読んで～！", 1, 102301, 0, 874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 102501, 0, 901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 102501, 0, 902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "どうしたら転ばなく\nなるんでしょうか？", 1, 102501, 0, 903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 102501, 0, 904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 102501, 0, 905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "おいしい料理で\nお出迎えしなくちゃ！", 4, 102501, 0, 906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "一生懸命\nご奉仕しますよ～♪", 8, 102501, 0, 907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "耳かきしましょうか？\nえ？　こわい？", 1, 102501, 0, 908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 1, 102501, 0, 909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おかえりなさいませ\nご主人さま♪", 1, 102501, 0, 910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "すみません\nすみません\nすみません～～", 1, 102501, 0, 911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 4, 102501, 0, 912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私のこと、\n専属メイドと思って\n頼ってくださいね", 8, 102501, 0, 913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1, 102501, 0, 914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 102501, 0, 915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 102501, 0, 916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 102501, 0, 917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 1, 102501, 0, 918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 102501, 0, 919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 102501, 0, 920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここでお洗濯ものを\n乾かしましょうか", 1, 102501, 0, 921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "あわわわっ…\nこ、転びそうです～！", 1, 102501, 0, 922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これくらい\nおいしいものを\n作ってみたいです～", 1, 102501, 0, 923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 102501, 0, 924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 102502, 0, 926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 102502, 0, 927);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "どうやったら、立派な\nメイドになれるのかな", 1, 102502, 0, 928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 102502, 0, 929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ご用があれば\nなんでも言って\nくださいね", 1, 102502, 0, 930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 8, 102502, 0, 931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 8, 102502, 0, 932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 1, 102502, 0, 933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 102502, 0, 934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私、またなにか\nやっちゃいましたか？", 1, 102502, 0, 935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 1, 102502, 0, 936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "転んだふりであの人に\n…って！\nなんでもないですよ！", 8, 102502, 0, 937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 8, 102502, 0, 938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1, 102502, 0, 939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 102502, 0, 940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 102502, 0, 941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 102502, 0, 942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 1, 102502, 0, 943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 102502, 0, 944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 102502, 0, 945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 1, 102502, 0, 946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動しようとすると\nいつも怪我するん\nですよね…", 1, 102502, 0, 947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 1, 102502, 0, 948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 102502, 0, 949);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 102701, 0, 951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 102701, 0, 952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 102701, 0, 953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 102701, 0, 954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 102701, 0, 955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 4, 102701, 0, 956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 8, 102701, 0, 957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 1, 102701, 0, 958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 1, 102701, 0, 959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お願いがあるのです。\n新しい研究の実験台に\n…", 1, 102701, 0, 960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 102701, 0, 961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 4, 102701, 0, 962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 8, 102701, 0, 963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 102701, 0, 964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 1, 102701, 0, 965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 1, 102701, 0, 966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 1, 102701, 0, 967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 102701, 0, 968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あら、キメラの素体に\nぴったりですわ", 1, 102701, 0, 969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 1, 102701, 0, 970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あたたかい…\n加熱調合にも\n使えそうですわね", 1, 102701, 0, 971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 102701, 0, 972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 102701, 0, 973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 102701, 0, 974);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 102702, 0, 976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 102702, 0, 977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 102702, 0, 978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 102702, 0, 979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 102702, 0, 980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 8, 102702, 0, 981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 8, 102702, 0, 982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 1, 102702, 0, 983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 1, 102702, 0, 984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お願いがあるのです\n新しい研究の\n実験台に…", 1, 102702, 0, 985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 1, 102702, 0, 986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 8, 102702, 0, 987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 8, 102702, 0, 988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 102702, 0, 989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 1, 102702, 0, 990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 1, 102702, 0, 991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "私ももう少し\n外見に気を配る\nべきでしょうか…", 1, 102702, 0, 992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 102702, 0, 993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 1, 102702, 0, 994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 1, 102702, 0, 995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 1, 102702, 0, 996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 102702, 0, 997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 102702, 0, 998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 102702, 0, 999);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 102801, 0, 1001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 102801, 0, 1002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 102801, 0, 1003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 102801, 0, 1004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 102801, 0, 1005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 4, 102801, 0, 1006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 102801, 0, 1007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねぇあんた、どこか\n安いお店知らない？", 1, 102801, 0, 1008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 1, 102801, 0, 1009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 1, 102801, 0, 1010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 1, 102801, 0, 1011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 4, 102801, 0, 1012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 8, 102801, 0, 1013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 102801, 0, 1014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 1, 102801, 0, 1015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 1, 102801, 0, 1016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 102801, 0, 1017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "これで仕事は一段落ね\nちょっと休憩…", 1, 102801, 0, 1018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うふふ、\nかわいいものを\n見るのは幸せね～", 1, 102801, 0, 1019);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 102801, 0, 1020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "風邪を引かないように\n身体を温めなくちゃ", 1, 102801, 0, 1021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 102801, 0, 1022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 1, 102801, 0, 1023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "知識から過去を学んで\n未来に活かすわよ", 1, 102801, 0, 1024);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "安売りのお店の情報\n整理しないと…", 1, 102802, 0, 1026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 102802, 0, 1027);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 102802, 0, 1028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 102802, 0, 1029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 102802, 0, 1030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 102802, 0, 1031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつと一緒に…\n色々なところに\nいきたいな", 8, 102802, 0, 1032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今度買い物に\n付き合いなさい\nセールがあるのよ", 1, 102802, 0, 1033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 1, 102802, 0, 1034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 1, 102802, 0, 1035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あんたとこうして\nのんびりするのも\nいいものね", 1, 102802, 0, 1036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 8, 102802, 0, 1037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 8, 102802, 0, 1038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1, 102802, 0, 1039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 1, 102802, 0, 1040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 1, 102802, 0, 1041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 1, 102802, 0, 1042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "これで仕事は一段落ね\nちょっと休憩…", 1, 102802, 0, 1043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 1, 102802, 0, 1044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 102802, 0, 1045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "風邪を引かないように\n身体を温めなくちゃ", 1, 102802, 0, 1046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 102802, 0, 1047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "しっかり食べて\n栄養補給、栄養補給！", 1, 102802, 0, 1048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "知識から過去を学んで\n未来に活かすわよ", 1, 102802, 0, 1049);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 102901, 0, 1051);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 102901, 0, 1052);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 102901, 0, 1053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "彼とデュエット\nしてみたいな～♪", 1, 102901, 0, 1054);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 102901, 0, 1055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 4, 102901, 0, 1056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "写真集見なくても、\n本物がここに\nいるのにな～", 8, 102901, 0, 1057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "握手会の練習、\n付き合ってくれない？", 1, 102901, 0, 1058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "写真集のポーズ\n練習中～！\nど、どうかな？", 1, 102901, 0, 1059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 1, 102901, 0, 1060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 1, 102901, 0, 1061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 4, 102901, 0, 1062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 8, 102901, 0, 1063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 102901, 0, 1064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 1, 102901, 0, 1065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 1, 102901, 0, 1066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 102901, 0, 1067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 102901, 0, 1068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 102901, 0, 1069);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 1, 102901, 0, 1070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 102901, 0, 1071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 102901, 0, 1072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 102901, 0, 1073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 1, 102901, 0, 1074);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 102902, 0, 1076);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 102902, 0, 1077);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 102902, 0, 1078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "彼とデュエット\nしてみたいな～♪", 1, 102902, 0, 1079);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 102902, 0, 1080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 8, 102902, 0, 1081);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 8, 102902, 0, 1082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 102902, 0, 1083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 1, 102902, 0, 1084);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 1, 102902, 0, 1085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 1, 102902, 0, 1086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 8, 102902, 0, 1087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 8, 102902, 0, 1088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 102902, 0, 1089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 1, 102902, 0, 1090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 1, 102902, 0, 1091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 102902, 0, 1092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 102902, 0, 1093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 102902, 0, 1094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 1, 102902, 0, 1095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 102902, 0, 1096);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 102902, 0, 1097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 102902, 0, 1098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 102902, 0, 1099);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 103001, 0, 1101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 103001, 0, 1102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 103001, 0, 1103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "むむ、アクダイカンの\n気配がするデース", 1, 103001, 0, 1104);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クニトリ合戦、\n楽しみデース！", 1, 103001, 0, 1105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ショーグンと\n出会えて、\nワタシは果報者デス！", 4, 103001, 0, 1106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 8, 103001, 0, 1107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 103001, 0, 1108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 103001, 0, 1109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 103001, 0, 1110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 103001, 0, 1111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 4, 103001, 0, 1112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 103001, 0, 1113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 103001, 0, 1114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 103001, 0, 1115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 103001, 0, 1116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 103001, 0, 1117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 103001, 0, 1118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 103001, 0, 1119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 103001, 0, 1120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "身体を暖めて\nイクサに向かうデス！", 1, 103001, 0, 1121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 103001, 0, 1122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うーん、\n美味しいデス…\n幸せデス～", 1, 103001, 0, 1123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 103001, 0, 1124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 103002, 0, 1126);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 103002, 0, 1127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1, 103002, 0, 1128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "むむ、アクダイカンの\n気配がするデース", 1, 103002, 0, 1129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クニトリ合戦\n楽しみデース！", 1, 103002, 0, 1130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 8, 103002, 0, 1131);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ショーグンの身は\n何があってもワタシが\n守るデス！", 8, 103002, 0, 1132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 103002, 0, 1133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 103002, 0, 1134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 103002, 0, 1135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 103002, 0, 1136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 8, 103002, 0, 1137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 103002, 0, 1138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 103002, 0, 1139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうお昼デス\nお昼寝するデスか？", 1, 103002, 0, 1140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 103002, 0, 1141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 103002, 0, 1142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 103002, 0, 1143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 103002, 0, 1144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 103002, 0, 1145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "身体を暖めてイクサに\n向かうデス！", 1, 103002, 0, 1146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 103002, 0, 1147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 103002, 0, 1148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 103002, 0, 1149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 103101, 0, 1151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "父は、いつも男性に\nきびしいですね", 1, 103101, 0, 1152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "占ってほしい人は、\nいつでも言って\nください", 1, 103101, 0, 1153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 103101, 0, 1154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この部屋、風水的にも\n運気が上がりそうです", 1, 103101, 0, 1155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 4, 103101, 0, 1156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 103101, 0, 1157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "女難の相が\n出ていますね…\n気をつけてください", 1, 103101, 0, 1158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 103101, 0, 1159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 103101, 0, 1160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 103101, 0, 1161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 4, 103101, 0, 1162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 8, 103101, 0, 1163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 103101, 0, 1164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 103101, 0, 1165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 103101, 0, 1166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 103101, 0, 1167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一休みしましょう。\n憑かれて疲れました", 1, 103101, 0, 1168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい…\n付喪神が宿らないかな", 1, 103101, 0, 1169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 103101, 0, 1170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "気持ちいいです…\n極楽浄土みたい…", 1, 103101, 0, 1171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 103101, 0, 1172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お父さんも\n食べたいの？\n死んでるのに？", 1, 103101, 0, 1173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本…\nもしかして予言書？", 1, 103101, 0, 1174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここにいる幽霊は\nみんな幸せそうです", 1, 103102, 0, 1176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 103102, 0, 1177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 103102, 0, 1178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 103102, 0, 1179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 103102, 0, 1180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 8, 103102, 0, 1181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 8, 103102, 0, 1182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 1, 103102, 0, 1183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 103102, 0, 1184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 103102, 0, 1185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ラップ音？\nいえ、父のいびきです", 1, 103102, 0, 1186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 103102, 0, 1187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "前世って信じますか？\n私とあなたは\nもしかすると…", 8, 103102, 0, 1188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 103102, 0, 1189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "明るい時間は\nあまり得意では\nないんです…", 1, 103102, 0, 1190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 103102, 0, 1191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 103102, 0, 1192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うとうと…\nあれ…\n走馬灯が見える…", 1, 103102, 0, 1193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい…\nお父さんみたい…", 1, 103102, 0, 1194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 103102, 0, 1195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 103102, 0, 1196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 103102, 0, 1197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お父さんの分も、\n私が食べるね", 1, 103102, 0, 1198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本の占い\n私の占いと違う…", 1, 103102, 0, 1199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 103201, 0, 1201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 103201, 0, 1202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 103201, 0, 1203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 103201, 0, 1204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 103201, 0, 1205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 4, 103201, 0, 1206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 103201, 0, 1207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 103201, 0, 1208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 103201, 0, 1209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 103201, 0, 1210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 103201, 0, 1211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "その、よろしければ\n私とお散歩など\nいかがです？", 4, 103201, 0, 1212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 8, 103201, 0, 1213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 103201, 0, 1214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 103201, 0, 1215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 103201, 0, 1216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 103201, 0, 1217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 103201, 0, 1218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 103201, 0, 1219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 103201, 0, 1220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 103201, 0, 1221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 103201, 0, 1222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 103201, 0, 1223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "経済の仕組みを\nしっかりと\n学ばなくては…", 1, 103201, 0, 1224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 103202, 0, 1226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 103202, 0, 1227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 103202, 0, 1228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 103202, 0, 1229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この狭さも\n慣れれば\n快適ですわ♪", 1, 103202, 0, 1230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 8, 103202, 0, 1231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 103202, 0, 1232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 103202, 0, 1233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 1, 103202, 0, 1234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 103202, 0, 1235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今度一緒に…\nパーティにでも\n行きませんこと？", 1, 103202, 0, 1236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "その、よろしければ\n私とお散歩など\nいかがです？", 8, 103202, 0, 1237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたさまの優しさは\nお金には\n代えられませんわね", 8, 103202, 0, 1238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 103202, 0, 1239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 103202, 0, 1240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 103202, 0, 1241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 103202, 0, 1242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 1, 103202, 0, 1243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 103202, 0, 1244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 1, 103202, 0, 1245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 103202, 0, 1246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "目標は遠くに\n置いた方が励みに\nなりますわ！", 1, 103202, 0, 1247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 103202, 0, 1248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "商人って、物語では\n悪役にされやすいの\nですね…", 1, 103202, 0, 1249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "家の中のものを使って\n物ボケ、考えるべ！", 1, 103301, 0, 1251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 103301, 0, 1252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "うちに塀ができたら、\nへー、って言いたい！", 1, 103301, 0, 1253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 103301, 0, 1254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "エリザベスも\n連れてきてぇ～", 1, 103301, 0, 1255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 4, 103301, 0, 1256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 8, 103301, 0, 1257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 103301, 0, 1258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 103301, 0, 1259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 103301, 0, 1260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 103301, 0, 1261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 4, 103301, 0, 1262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 8, 103301, 0, 1263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 103301, 0, 1264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 103301, 0, 1265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 103301, 0, 1266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 103301, 0, 1267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 103301, 0, 1268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 103301, 0, 1269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "必要以上に\n癒されちまうべや～", 1, 103301, 0, 1270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 103301, 0, 1271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "お笑い芸人は、\n体が資本だべさ～！", 1, 103301, 0, 1272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 103301, 0, 1273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 103301, 0, 1274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 103302, 0, 1276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この部屋でも、商品\n売っていいんだべか？", 1, 103302, 0, 1277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 103302, 0, 1278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 103302, 0, 1279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "エリザベスも\n連れてきてぇ～", 1, 103302, 0, 1280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 8, 103302, 0, 1281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 8, 103302, 0, 1282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 103302, 0, 1283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 103302, 0, 1284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 103302, 0, 1285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 103302, 0, 1286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 8, 103302, 0, 1287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 8, 103302, 0, 1288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 103302, 0, 1289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 103302, 0, 1290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 103302, 0, 1291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "うーん…衣装、\nずれてねぇべか？", 1, 103302, 0, 1292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 103302, 0, 1293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "はう～\nなまらめんこいべや～", 1, 103302, 0, 1294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "必要以上に\n癒されちまうべや～", 1, 103302, 0, 1295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 103302, 0, 1296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 103302, 0, 1297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 103302, 0, 1298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 103302, 0, 1299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 103401, 0, 1301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 103401, 0, 1302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 103401, 0, 1303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 103401, 0, 1304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 103401, 0, 1305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "デートとか…\n誘っても嫌がられ\nないかな…？", 4, 103401, 0, 1306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もしかして運命の\n相手だったり…\nするのかな", 8, 103401, 0, 1307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 103401, 0, 1308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 103401, 0, 1309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 103401, 0, 1310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 103401, 0, 1311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 4, 103401, 0, 1312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 8, 103401, 0, 1313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 103401, 0, 1314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おっひるー♪\nなに食べよっかな～♪", 1, 103401, 0, 1315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 103401, 0, 1316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 103401, 0, 1317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 103401, 0, 1318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 103401, 0, 1319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 103401, 0, 1320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 103401, 0, 1321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 103401, 0, 1322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 103401, 0, 1323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 103401, 0, 1324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 103402, 0, 1326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今日は\nのんびり\n過ごそうかな～", 1, 103402, 0, 1327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あ…\nお皿、洗って\nなかった…", 1, 103402, 0, 1328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 103402, 0, 1329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "素敵な彼氏…\n欲しいなぁ…", 1, 103402, 0, 1330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "彼と私\n釣り合うかな…\n大丈夫よね？", 8, 103402, 0, 1331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 8, 103402, 0, 1332);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 103402, 0, 1333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 103402, 0, 1334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 103402, 0, 1335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お姉さんが～\n可愛がって\nあげようか～？", 1, 103402, 0, 1336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 8, 103402, 0, 1337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 8, 103402, 0, 1338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さーて、今日も\nいっちょやるぞー！", 1, 103402, 0, 1339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "後でのんびり\nストレッチでも\nしようっと♪", 1, 103402, 0, 1340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 103402, 0, 1341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "仕事の前に、髪とか\n整えないと…", 1, 103402, 0, 1342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 103402, 0, 1343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 103402, 0, 1344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うわあ\nこれ、私の好きな\n香りだー！", 1, 103402, 0, 1345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 103402, 0, 1346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "地道な努力が\nリア充への\n第一歩！", 1, 103402, 0, 1347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 103402, 0, 1348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 103402, 0, 1349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 103801, 0, 1351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 103801, 0, 1352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 103801, 0, 1353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ちょっと、休憩…\n本でも読もうかな", 1, 103801, 0, 1354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "もっとたくさんの本に\n囲まれて生活したいな", 1, 103801, 0, 1355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "むかしむかしある所に\nシオリと\nあの人が…", 4, 103801, 0, 1356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 8, 103801, 0, 1357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 103801, 0, 1358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あ…お姉ちゃんと\n約束があったんだ…", 1, 103801, 0, 1359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 103801, 0, 1360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 103801, 0, 1361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 4, 103801, 0, 1362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 103801, 0, 1363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 103801, 0, 1364);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 1, 103801, 0, 1365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 1, 103801, 0, 1366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 103801, 0, 1367);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "少し休もう\n…やっぱり\nたくさん休もう", 1, 103801, 0, 1368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいものが\n枕元にあると\n安心できるんですよ", 1, 103801, 0, 1369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 1, 103801, 0, 1370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こうして温まって\nいると、病気も\n治る気がします", 1, 103801, 0, 1371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 1, 103801, 0, 1372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "少食ですけど\nこれならたくさん\n食べられそうです", 1, 103801, 0, 1373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "続きが気になって\n読むのを止める\nタイミングが…", 1, 103801, 0, 1374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "しっかり休まないと\n早く回復するように", 1, 103802, 0, 1376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 103802, 0, 1377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 103802, 0, 1378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 103802, 0, 1379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 103802, 0, 1380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "むかしむかしある所に\nシオリと\nあの人が…", 8, 103802, 0, 1381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人の\nためにも早く\n病気を治さないと…", 8, 103802, 0, 1382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 103802, 0, 1383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "新記録です！\n一週間連続、\n平熱を保ちました！", 1, 103802, 0, 1384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 1, 103802, 0, 1385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 103802, 0, 1386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 8, 103802, 0, 1387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 103802, 0, 1388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 103802, 0, 1389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 1, 103802, 0, 1390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 1, 103802, 0, 1391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 103802, 0, 1392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "少し休もう\n…やっぱり\nたくさん休もう", 1, 103802, 0, 1393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 103802, 0, 1394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 103802, 0, 1395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こうして温まって\nいると、病気も\n治る気がします", 1, 103802, 0, 1396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 103802, 0, 1397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい食べて\n健康な体にならないと", 1, 103802, 0, 1398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "続きが気になって\n読むのを止める\nタイミングが…", 1, 103802, 0, 1399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 104001, 0, 1401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 104001, 0, 1402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 104001, 0, 1403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 104001, 0, 1404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 104001, 0, 1405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 4, 104001, 0, 1406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 104001, 0, 1407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 104001, 0, 1408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 104001, 0, 1409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 104001, 0, 1410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 104001, 0, 1411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 4, 104001, 0, 1412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 104001, 0, 1413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 104001, 0, 1414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 104001, 0, 1415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 104001, 0, 1416);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 104001, 0, 1417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 104001, 0, 1418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 104001, 0, 1419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 104001, 0, 1420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "灯りを見てると、\n心がなごみます～", 1, 104001, 0, 1421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 104001, 0, 1422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 104001, 0, 1423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 104001, 0, 1424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 104002, 0, 1426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 104002, 0, 1427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 104002, 0, 1428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 104002, 0, 1429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 104002, 0, 1430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 8, 104002, 0, 1431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 104002, 0, 1432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 104002, 0, 1433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 104002, 0, 1434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 104002, 0, 1435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 104002, 0, 1436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 8, 104002, 0, 1437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 104002, 0, 1438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 104002, 0, 1439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 104002, 0, 1440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 104002, 0, 1441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 104002, 0, 1442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ほわあ～…\nふかふかって\nいいものですね～", 1, 104002, 0, 1443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 104002, 0, 1444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "灯りを見てると\n心がなごみます～", 1, 104002, 0, 1445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 104002, 0, 1446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "身体を鍛えて\nみなさんを\n守ります！", 1, 104002, 0, 1447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 104002, 0, 1448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 104002, 0, 1449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 104201, 0, 1451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 104201, 0, 1452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 104201, 0, 1453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 104201, 0, 1454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あとで曲を復習して\nおかないと…", 1, 104201, 0, 1455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 4, 104201, 0, 1456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 104201, 0, 1457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 104201, 0, 1458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 104201, 0, 1459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 104201, 0, 1460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 104201, 0, 1461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 4, 104201, 0, 1462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 104201, 0, 1463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 104201, 0, 1464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 104201, 0, 1465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 104201, 0, 1466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 104201, 0, 1467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 104201, 0, 1468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子たちを\n相手に練習して\nみようかな…♪", 1, 104201, 0, 1469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 104201, 0, 1470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 104201, 0, 1471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "歌うためには\n体力づくりは\n欠かせません", 1, 104201, 0, 1472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "歌った後は\nお腹が空いて\nしまうんです", 1, 104201, 0, 1473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "音楽について\n理論も勉強\nしないと…", 1, 104201, 0, 1474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 104202, 0, 1476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 104202, 0, 1477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 104202, 0, 1478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 104202, 0, 1479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 104202, 0, 1480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 104202, 0, 1481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私の中にある\n私だけの歌…\n作ってみようかな？", 8, 104202, 0, 1482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 104202, 0, 1483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 104202, 0, 1484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "魔物退治ですか！？\n…違います？\n良かった…", 1, 104202, 0, 1485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 104202, 0, 1486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 104202, 0, 1487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 8, 104202, 0, 1488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 104202, 0, 1489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "少し休んだら\n午後の練習に\n行ってきますね", 1, 104202, 0, 1490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 104202, 0, 1491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 104202, 0, 1492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 104202, 0, 1493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 104202, 0, 1494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 104202, 0, 1495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 104202, 0, 1496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 104202, 0, 1497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 104202, 0, 1498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 104202, 0, 1499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "店の新メニューでも\n考えるかな", 1, 104301, 0, 1501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あたしの店から、\n出前を届けようか？", 1, 104301, 0, 1502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 104301, 0, 1503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 104301, 0, 1504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 104301, 0, 1505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 4, 104301, 0, 1506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 104301, 0, 1507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 104301, 0, 1508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 104301, 0, 1509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "腹減ったか？　なんか\n作ってやろうか？", 1, 104301, 0, 1510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 104301, 0, 1511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 4, 104301, 0, 1512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 104301, 0, 1513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 104301, 0, 1514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 104301, 0, 1515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 104301, 0, 1516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 104301, 0, 1517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわぁ、\n一休みすっかなぁ～", 1, 104301, 0, 1518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うっ！　か、かわいい\nじゃねぇか", 1, 104301, 0, 1519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 104301, 0, 1520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 104301, 0, 1521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "オラオラオラーーー！\n燃えるぜーーー！", 1, 104301, 0, 1522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 104301, 0, 1523);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 104301, 0, 1524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "パトロール終了～\n本日も異常なしっと！", 1, 104302, 0, 1526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あたしの店から\n出前を届けようか？", 1, 104302, 0, 1527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あいつ、早く\n帰ってこないかな", 1, 104302, 0, 1528);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 104302, 0, 1529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 104302, 0, 1530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 8, 104302, 0, 1531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 104302, 0, 1532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 104302, 0, 1533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 104302, 0, 1534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "腹減ったか？　なんか\n作ってやろうか？", 1, 104302, 0, 1535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 104302, 0, 1536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 8, 104302, 0, 1537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 104302, 0, 1538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 104302, 0, 1539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 104302, 0, 1540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 104302, 0, 1541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 104302, 0, 1542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ～♪\n疲れがとれるな～", 1, 104302, 0, 1543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うっ！　か、かわいい\nじゃねぇか", 1, 104302, 0, 1544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 104302, 0, 1545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 104302, 0, 1546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "オラオラオラーーー！\n燃えるぜーーー！", 1, 104302, 0, 1547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 104302, 0, 1548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 104302, 0, 1549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 104501, 0, 1551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 104501, 0, 1552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 104501, 0, 1553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 104501, 0, 1554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 104501, 0, 1555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 4, 104501, 0, 1556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 8, 104501, 0, 1557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 104501, 0, 1558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 104501, 0, 1559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 104501, 0, 1560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 104501, 0, 1561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 4, 104501, 0, 1562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 8, 104501, 0, 1563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 104501, 0, 1564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 104501, 0, 1565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 104501, 0, 1566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 104501, 0, 1567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 104501, 0, 1568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 104501, 0, 1569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 104501, 0, 1570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 104501, 0, 1571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 104501, 0, 1572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 104501, 0, 1573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 104501, 0, 1574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 104502, 0, 1576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 104502, 0, 1577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 104502, 0, 1578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 104502, 0, 1579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 104502, 0, 1580);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 8, 104502, 0, 1581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 8, 104502, 0, 1582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 104502, 0, 1583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 104502, 0, 1584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 104502, 0, 1585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 104502, 0, 1586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 8, 104502, 0, 1587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 8, 104502, 0, 1588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 104502, 0, 1589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 104502, 0, 1590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 104502, 0, 1591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 104502, 0, 1592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 104502, 0, 1593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 104502, 0, 1594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 104502, 0, 1595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 104502, 0, 1596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 104502, 0, 1597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 104502, 0, 1598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "どんな本も\nよく見ると\nいやらしいです…！", 1, 104502, 0, 1599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 104601, 0, 1601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 104601, 0, 1602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 104601, 0, 1603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 104601, 0, 1604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 104601, 0, 1605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 4, 104601, 0, 1606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 8, 104601, 0, 1607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 104601, 0, 1608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 104601, 0, 1609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 104601, 0, 1610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 104601, 0, 1611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 4, 104601, 0, 1612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 8, 104601, 0, 1613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 104601, 0, 1614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 104601, 0, 1615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 104601, 0, 1616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 104601, 0, 1617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 104601, 0, 1618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 104601, 0, 1619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 104601, 0, 1620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 104601, 0, 1621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "本当は外で\n運動したいん\nだけどにゃ", 1, 104601, 0, 1622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 104601, 0, 1623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…猫の\nしつけ方は、と…", 1, 104601, 0, 1624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 104602, 0, 1626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 104602, 0, 1627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 104602, 0, 1628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 104602, 0, 1629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 104602, 0, 1630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 8, 104602, 0, 1631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 8, 104602, 0, 1632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 104602, 0, 1633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 104602, 0, 1634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 104602, 0, 1635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 104602, 0, 1636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 8, 104602, 0, 1637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 8, 104602, 0, 1638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 104602, 0, 1639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 104602, 0, 1640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 104602, 0, 1641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "にゃにゃ…\n髪が乱れてるにゃ", 1, 104602, 0, 1642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 104602, 0, 1643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 104602, 0, 1644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "猫でもこわくない\n優しい灯りにゃ！", 1, 104602, 0, 1645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽっかぽか\nしあわせな気分にゃ～", 1, 104602, 0, 1646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 104602, 0, 1647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 104602, 0, 1648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 104602, 0, 1649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "内職手伝える人\nいないかしら？", 1, 104801, 0, 1651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 104801, 0, 1652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 104801, 0, 1653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 104801, 0, 1654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 104801, 0, 1655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 4, 104801, 0, 1656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 104801, 0, 1657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 104801, 0, 1658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 104801, 0, 1659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 104801, 0, 1660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 104801, 0, 1661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 4, 104801, 0, 1662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 8, 104801, 0, 1663);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n今日も朝から\nバイトよ！", 1, 104801, 0, 1664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 104801, 0, 1665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 104801, 0, 1666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 104801, 0, 1667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ～バイトで\n疲れたから癒される～", 1, 104801, 0, 1668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これを内職で\n作ったら\n儲かるかしら？", 1, 104801, 0, 1669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 104801, 0, 1670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 104801, 0, 1671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 104801, 0, 1672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 104801, 0, 1673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "日々絶えず学んで、\n生活に活かすのよ！", 1, 104801, 0, 1674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 104802, 0, 1676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 104802, 0, 1677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 104802, 0, 1678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 104802, 0, 1679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 104802, 0, 1680);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 104802, 0, 1681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 8, 104802, 0, 1682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 104802, 0, 1683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 104802, 0, 1684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今から\n休憩するところなの\n一緒にどう？", 1, 104802, 0, 1685);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 104802, 0, 1686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 8, 104802, 0, 1687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 8, 104802, 0, 1688);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 104802, 0, 1689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 104802, 0, 1690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 104802, 0, 1691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 104802, 0, 1692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここで少し\n休んだら\nまたバイトよ！", 1, 104802, 0, 1693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これを内職で\n作ったら\n儲かるかしら？", 1, 104802, 0, 1694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 1, 104802, 0, 1695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 1, 104802, 0, 1696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 1, 104802, 0, 1697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 1, 104802, 0, 1698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "日々絶えず学んで\n生活に活かすのよ！", 1, 104802, 0, 1699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 104901, 0, 1701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 104901, 0, 1702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 104901, 0, 1703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 104901, 0, 1704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 104901, 0, 1705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "この部屋を、\n私と弟くんの\n愛の巣に…", 4, 104901, 0, 1706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "はぁ～…弟くん\n禁断症状だよ～", 8, 104901, 0, 1707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 104901, 0, 1708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 104901, 0, 1709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "姉と弟の外出は\nデートって\nいうのかな？", 1, 104901, 0, 1710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 1, 104901, 0, 1711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 4, 104901, 0, 1712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 8, 104901, 0, 1713);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 104901, 0, 1714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 1, 104901, 0, 1715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 1, 104901, 0, 1716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "きれいなお姉ちゃん\n好きだよね♪", 1, 104901, 0, 1717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 104901, 0, 1718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nお姉ちゃんチョップ！", 1, 104901, 0, 1719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り、\n弟くん好きかな？", 1, 104901, 0, 1720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これ、弟くんみたいに\nあったかいね～", 1, 104901, 0, 1721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 104901, 0, 1722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あ、これ\n弟くんが好きな\n味付けだね！", 1, 104901, 0, 1723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 1, 104901, 0, 1724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 104902, 0, 1726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 104902, 0, 1727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 104902, 0, 1728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 104902, 0, 1729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はっ！\n弟くんが困ってる\n気がする！", 1, 104902, 0, 1730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "はぁ～…弟くん、\n禁断症状だよ～", 8, 104902, 0, 1731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 8, 104902, 0, 1732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 1, 104902, 0, 1733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 104902, 0, 1734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 1, 104902, 0, 1735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 104902, 0, 1736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 8, 104902, 0, 1737);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 8, 104902, 0, 1738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 104902, 0, 1739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 1, 104902, 0, 1740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 1, 104902, 0, 1741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "弟くんを\n誘惑しちゃうぞ～♪", 1, 104902, 0, 1742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 104902, 0, 1743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nお姉ちゃんチョップ！", 1, 104902, 0, 1744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お肌に潤いが\nでてきたみたい～♪", 1, 104902, 0, 1745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これ、弟くんみたいに\nあったかいね～", 1, 104902, 0, 1746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 104902, 0, 1747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 1, 104902, 0, 1748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 1, 104902, 0, 1749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 105001, 0, 1751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 105001, 0, 1752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 105001, 0, 1753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 105001, 0, 1754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 105001, 0, 1755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 4, 105001, 0, 1756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 8, 105001, 0, 1757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "わーい、アメだー♪\n食べる食べるー♪", 1, 105001, 0, 1758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 1, 105001, 0, 1759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 1, 105001, 0, 1760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 1, 105001, 0, 1761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 4, 105001, 0, 1762);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 8, 105001, 0, 1763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝のスキンケアは\n大事なの！", 1, 105001, 0, 1764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 105001, 0, 1765);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "エイジングケアを\nしないとねー", 1, 105001, 0, 1766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 1, 105001, 0, 1767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 105001, 0, 1768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 1, 105001, 0, 1769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 105001, 0, 1770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかーい♪\nここで宿題\nしようかしら？", 1, 105001, 0, 1771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 105001, 0, 1772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 1, 105001, 0, 1773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 105001, 0, 1774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 105002, 0, 1776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "恋人にするなら\nイケメンか\nただイケよね～", 1, 105002, 0, 1777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 105002, 0, 1778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 105002, 0, 1779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 105002, 0, 1780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 8, 105002, 0, 1781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 8, 105002, 0, 1782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なーに？\nアタシに甘えたいの？", 1, 105002, 0, 1783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 105002, 0, 1784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 105002, 0, 1785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 105002, 0, 1786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 8, 105002, 0, 1787);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 8, 105002, 0, 1788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "レディの朝は\n忙しいのよ", 1, 105002, 0, 1789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 105002, 0, 1790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "エイジングケアを\nしないとねー", 1, 105002, 0, 1791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 105002, 0, 1792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 105002, 0, 1793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 105002, 0, 1794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 105002, 0, 1795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかーい♪\nここで宿題\nしようかしら？", 1, 105002, 0, 1796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 105002, 0, 1797);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レディは、食べ物にも\n気を使うのよ", 1, 105002, 0, 1798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 105002, 0, 1799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "魔法と科学\nどっちも極めて\nみせるわ", 1, 105101, 0, 1801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 105101, 0, 1802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "たまった治療代の\n請求書を\nまとめないと…", 1, 105101, 0, 1803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 105101, 0, 1804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 105101, 0, 1805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 4, 105101, 0, 1806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 105101, 0, 1807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 105101, 0, 1808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 105101, 0, 1809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 105101, 0, 1810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 105101, 0, 1811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 4, 105101, 0, 1812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 105101, 0, 1813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 105101, 0, 1814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 105101, 0, 1815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 105101, 0, 1816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "医者たるもの\nいつだって\n清潔に…♪", 1, 105101, 0, 1817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 105101, 0, 1818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぬいぐるみなら\n動物も…", 1, 105101, 0, 1819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 105101, 0, 1820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここにいると\nついウトウト\nしちゃうわ…", 1, 105101, 0, 1821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 105101, 0, 1822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 105101, 0, 1823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 105101, 0, 1824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 105102, 0, 1826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 105102, 0, 1827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 105102, 0, 1828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 105102, 0, 1829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 105102, 0, 1830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 8, 105102, 0, 1831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 105102, 0, 1832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 105102, 0, 1833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 105102, 0, 1834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 105102, 0, 1835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 105102, 0, 1836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 8, 105102, 0, 1837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 105102, 0, 1838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 105102, 0, 1839);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 105102, 0, 1840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 105102, 0, 1841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身なりについて\n研究するのも\n楽しそうね", 1, 105102, 0, 1842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 105102, 0, 1843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 105102, 0, 1844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 105102, 0, 1845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 105102, 0, 1846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "運動しないと\n体型の維持が…", 1, 105102, 0, 1847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "適度な食事も\n大切よね", 1, 105102, 0, 1848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 105102, 0, 1849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 1, 105201, 0, 1851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いっけな～い！\nひづめで床に傷が！", 1, 105201, 0, 1852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 105201, 0, 1853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 105201, 0, 1854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "みんな、体重\nどれくらいなのかな？", 1, 105201, 0, 1855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 4, 105201, 0, 1856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 8, 105201, 0, 1857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 105201, 0, 1858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 105201, 0, 1859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 105201, 0, 1860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "力仕事ならまかせて！", 1, 105201, 0, 1861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 4, 105201, 0, 1862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 105201, 0, 1863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 105201, 0, 1864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 105201, 0, 1865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 105201, 0, 1866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 105201, 0, 1867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ミシッっていった！\n私、重すぎるかも！？", 1, 105201, 0, 1868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あ！　あなたも\n美人さんね～♪", 1, 105201, 0, 1869);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 105201, 0, 1870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 105201, 0, 1871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "な、なんだか野生に\n帰っちゃいそう！", 1, 105201, 0, 1872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 105201, 0, 1873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 105201, 0, 1874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 105202, 0, 1876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 105202, 0, 1877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "動物苑…って\n嫌なギルド名ね…", 1, 105202, 0, 1878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 105202, 0, 1879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 105202, 0, 1880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 8, 105202, 0, 1881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 8, 105202, 0, 1882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 105202, 0, 1883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 105202, 0, 1884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ぺっぺっ！\n…い、いけないっ！", 1, 105202, 0, 1885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 105202, 0, 1886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 105202, 0, 1887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 8, 105202, 0, 1888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 105202, 0, 1889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ダイエット中だから\nお昼は抜いてるの～", 1, 105202, 0, 1890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 105202, 0, 1891);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 105202, 0, 1892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ミシッっていった！\n私、重すぎるかも！？", 1, 105202, 0, 1893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あ！　あなたも\n美人さんね～♪", 1, 105202, 0, 1894);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 105202, 0, 1895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 105202, 0, 1896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "な、なんだか野生に\n帰っちゃいそう！", 1, 105202, 0, 1897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 105202, 0, 1898);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 105202, 0, 1899);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 105301, 0, 1901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 105301, 0, 1902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 105301, 0, 1903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 105301, 0, 1904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 105301, 0, 1905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 4, 105301, 0, 1906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 8, 105301, 0, 1907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 105301, 0, 1908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 105301, 0, 1909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 105301, 0, 1910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "私ともっと高く、\n共に飛ぼう！", 1, 105301, 0, 1911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 4, 105301, 0, 1912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 8, 105301, 0, 1913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 105301, 0, 1914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 105301, 0, 1915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 105301, 0, 1916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 105301, 0, 1917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 105301, 0, 1918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 105301, 0, 1919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 105301, 0, 1920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 105301, 0, 1921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 105301, 0, 1922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 105301, 0, 1923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 105301, 0, 1924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 105302, 0, 1926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 105302, 0, 1927);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 105302, 0, 1928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 105302, 0, 1929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 105302, 0, 1930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 8, 105302, 0, 1931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 8, 105302, 0, 1932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 105302, 0, 1933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 105302, 0, 1934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 105302, 0, 1935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 105302, 0, 1936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 8, 105302, 0, 1937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 8, 105302, 0, 1938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 105302, 0, 1939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 105302, 0, 1940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 105302, 0, 1941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 105302, 0, 1942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 105302, 0, 1943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 105302, 0, 1944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 105302, 0, 1945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 105302, 0, 1946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 105302, 0, 1947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 105302, 0, 1948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 105302, 0, 1949);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 105701, 0, 1951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "団員募集の\nポスター\n作ろうっと♪", 1, 105701, 0, 1952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "父さん…\n今頃どこで\n何してるんだろ？", 1, 105701, 0, 1953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 105701, 0, 1954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 105701, 0, 1955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 4, 105701, 0, 1956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "父さん…\n私にもね、大切な\n仲間ができたよ", 8, 105701, 0, 1957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 105701, 0, 1958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 105701, 0, 1959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なーにー？\nどうしたの？\n今呼んだよね？", 1, 105701, 0, 1960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 1, 105701, 0, 1961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 4, 105701, 0, 1962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 8, 105701, 0, 1963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 105701, 0, 1964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 1, 105701, 0, 1965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 1, 105701, 0, 1966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 1, 105701, 0, 1967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 1, 105701, 0, 1968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 1, 105701, 0, 1969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～♪\n気持ちが\n落ち着く～…", 1, 105701, 0, 1970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これって\n騎空艇に\n置けるかな…？", 1, 105701, 0, 1971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "どんな仕事でも\nこなせるような\n体力がないとね…！", 1, 105701, 0, 1972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 1, 105701, 0, 1973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "空を冒険するには\n色んなことを\n知っておかないと…", 1, 105701, 0, 1974);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 105702, 0, 1976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "暇だよ…\nどこかに強い人は\nいないか？", 1, 105702, 0, 1977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 105702, 0, 1978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 105702, 0, 1979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 105702, 0, 1980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私の力の源は\n仲間との\n絆だよ！", 8, 105702, 0, 1981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 8, 105702, 0, 1982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 1, 105702, 0, 1983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私はいつも通りに\n喋ってるつもり\nだよ？", 1, 105702, 0, 1984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうした？\nもしかして手合せ\nしたいか？", 1, 105702, 0, 1985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 1, 105702, 0, 1986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "背中はあなたに\n預けたよ！\nよろしくね！", 8, 105702, 0, 1987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 8, 105702, 0, 1988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1, 105702, 0, 1989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "腹減ったね…\nなにか食べに\n行くよ！", 1, 105702, 0, 1990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "修行のしすぎは\n良くないよ？\n気をつけるね", 1, 105702, 0, 1991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "美しい技は\n美しい体が\nあってこそ！", 1, 105702, 0, 1992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 1, 105702, 0, 1993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…練習に\n使うのはさすがに\n可哀そうね", 1, 105702, 0, 1994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これは…\n瞑想の時にも\n使ってみたいね", 1, 105702, 0, 1995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "出来ればもっと\n熱いほうが\n嬉しいよ", 1, 105702, 0, 1996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "ここでも\n修行出来るなんて\n最高だよ！", 1, 105702, 0, 1997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "気力の源は体力！\n体力の源は\n健康とご飯！", 1, 105702, 0, 1998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "んー…\n難しいことは\nわかんないよ！", 1, 105702, 0, 1999);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 105801, 0, 2001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 105801, 0, 2002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ん？\n食べ物のにおい？", 1, 105801, 0, 2003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みんなと仲良く食卓を\n囲みたいですね～", 1, 105801, 0, 2004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この部屋が、わたしの\n帰る場所です♪", 1, 105801, 0, 2005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 4, 105801, 0, 2006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人、\n大食いの子って\n嫌いかな？", 8, 105801, 0, 2007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 1, 105801, 0, 2008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "つ、つまみ食いなんて\nしてませんよ？\nこれは味見です！", 1, 105801, 0, 2009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おやつは別腹、\nごはんも別腹、\n魔物も別腹～", 1, 105801, 0, 2010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "好きなものを、\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 105801, 0, 2011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたは、\nごはん王子ですね～", 4, 105801, 0, 2012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "大好きです～\n愛してます～\nえへへ～♪", 8, 105801, 0, 2013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 105801, 0, 2014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 105801, 0, 2015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 1, 105801, 0, 2016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 1, 105801, 0, 2017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふい～\n消化中です～", 1, 105801, 0, 2018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "おいしそ…\nか、かわいいです～", 1, 105801, 0, 2019);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りですね～\n空気までおいしそう～\nじゅるり♪", 1, 105801, 0, 2020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ほっかほか～♪\n魔物の丸焼きが\n作れちゃいますね！", 1, 105801, 0, 2021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "走って食べて！\n走って食べて！\n\この無限ループです！", 1, 105801, 0, 2022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いくつでも\n食べられますよ！\n幸せ～☆", 1, 105801, 0, 2023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "料理の絵を\n見ているだけで、\nよ、よだれが～", 1, 105801, 0, 2024);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 1, 105802, 0, 2026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ギルドハウス\nどんどん素敵に\nなっていくな～♪", 1, 105802, 0, 2027);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "「やばい」とか\n「おいっす」とか…\n民の言葉って難しい", 1, 105802, 0, 2028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お菓子の家に\n住みたいな～", 1, 105802, 0, 2029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あ、頼んでた出前が\n届いたかな？", 1, 105802, 0, 2030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "大切な人がいて\n大切な場所がある\nすごく幸せです♪", 8, 105802, 0, 2031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人\n大食いの子って\n嫌いかな？", 8, 105802, 0, 2032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 1, 105802, 0, 2033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "モグモグ…\nあげませんよ～", 1, 105802, 0, 2034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おやつは別腹\nごはんも別腹\n魔物も別腹～", 1, 105802, 0, 2035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "好きなものを\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 105802, 0, 2036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "大好きです～\n愛してます～\nえへへ～♪", 8, 105802, 0, 2037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "わたし食べても\n太らないんです♪\nほらほら～触って\nみてくださいよ～", 8, 105802, 0, 2038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 105802, 0, 2039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 105802, 0, 2040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お夕飯も、晩ごはんも\n食べたし～。さぁ！\nお夜食の時間ですよ☆", 1, 105802, 0, 2041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "お姫さまみたいに\nきれいに\nならないと…", 1, 105802, 0, 2042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "のんびりしていても\nお腹がすいちゃうん\nですよね～", 1, 105802, 0, 2043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "おいしそ…\nか、かわいいです～", 1, 105802, 0, 2044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "アップル系、ピーチ系\nオレンジ系…\n香りも色々ですね～", 1, 105802, 0, 2045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここでお魚を\n焼けたら\n最高ですね～♪", 1, 105802, 0, 2046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "身体も鍛えなきゃ～！\nお腹がすいちゃいます\nけど…", 1, 105802, 0, 2047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐもぐもぐもぐ\nもぐもぐもぐもぐもぐ\nもぐもぐもぐもぐ…", 1, 105802, 0, 2048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "流行語発表…\nこんな言葉遣いが！\nやばいですね", 1, 105802, 0, 2049);
