INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お出かけですか？\nわたくしもお供\nいたします", 1, 105902, 0, 2135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひゃん！\n主さま…\nいじわるです", 1, 105902, 0, 2136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 8, 105902, 0, 2137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "主さまの優しさに\nわたくしはいつも\n癒されています…", 8, 105902, 0, 2138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1, 105902, 0, 2139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 1, 105902, 0, 2140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 1, 105902, 0, 2141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 105902, 0, 2142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このふかふかの優しさ\n主さまのようです", 1, 105902, 0, 2143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\n主さまにそっくり♪", 1, 105902, 0, 2144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この心地よさ…\n主さまのようです♪", 1, 105902, 0, 2145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここで洗濯物を\n乾かせるでしょうか　", 1, 105902, 0, 2146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 6, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 1, 105902, 0, 2147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 1, 105902, 0, 2148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "都会のことを\n学ばないと…\nふむふむ", 1, 105902, 0, 2149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 190801, 0, 2151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ギルドハウスの\n使い心地は\nどうですか？", 1, 190801, 0, 2152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ご用があれば\nなんでも言って\nくださいね～♪", 1, 190801, 0, 2153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 1, 190801, 0, 2154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 190801, 0, 2155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 1, 190801, 0, 2156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ここは夜でも\nにぎやかですね", 1, 190801, 0, 2157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…\n少しうとうと\nしてしまった", 1, 104701, 0, 2158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 104701, 0, 2159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 104701, 0, 2160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 104701, 0, 2161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 104701, 0, 2162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "少年のことを考えると\n顔がにやけてしまう", 4, 104701, 0, 2163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "少年は今頃\n平和に過ごせて\nいるかな…", 8, 104701, 0, 2164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ん？　悪いが\n兜は脱がないぞ", 1, 104701, 0, 2165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 1, 104701, 0, 2166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 1, 104701, 0, 2167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 1, 104701, 0, 2168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君といると、兜の中が\nいつもより暑いんだ", 4, 104701, 0, 2169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 8, 104701, 0, 2170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 104701, 0, 2171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 1, 104701, 0, 2172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 1, 104701, 0, 2173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 1, 104701, 0, 2175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 1, 104701, 0, 2176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 1, 104701, 0, 2177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 1, 104701, 0, 2178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 1, 104701, 0, 2180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 1, 104701, 0, 2181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 102601, 0, 2183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ドングリの木\n近くに生えてない？", 1, 102601, 0, 2184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 102601, 0, 2185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 102601, 0, 2186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 102601, 0, 2187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あんパン買ったし\nあいつにも\n分けてあげよう", 4, 102601, 0, 2188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 8, 102601, 0, 2189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 102601, 0, 2190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 1, 102601, 0, 2191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ドングリあげるから\nあんパン買ってきて～", 1, 102601, 0, 2192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "う～\n疲れちゃったよ～\nおんぶして運んで～", 1, 102601, 0, 2193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたといると\nリア充って\n感じがするよ", 4, 102601, 0, 2194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 8, 102601, 0, 2195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 102601, 0, 2196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろお昼寝でも\nしたい気分だねぇ", 1, 102601, 0, 2197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 1, 102601, 0, 2198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 1, 102601, 0, 2200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 1, 102601, 0, 2201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 1, 102601, 0, 2202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 1, 102601, 0, 2203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 1, 102601, 0, 2205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 1, 102601, 0, 2206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ランドソル…\nおっきな街だなぁ…", 1, 106301, 0, 2208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ロザリア…\nきっと、いるよね？", 1, 106301, 0, 2209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "最近、弓の練習\nサボっちゃってるな…", 1, 106301, 0, 2210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 106301, 0, 2211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ちょっとだけ\n慣れてきたかも？", 1, 106301, 0, 2212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いつか、\nあの人と一緒に…", 4, 106301, 0, 2213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…なんか最近、\nあの人のこと\nばっかり…", 8, 106301, 0, 2214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "もしよかったら\n街に出かけない？", 1, 106301, 0, 2215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この部屋、\n妖精さんも\n気に入るかも！", 1, 106301, 0, 2216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日は\nなににしよっか？", 1, 106301, 0, 2217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今日はどこ行く？", 1, 106301, 0, 2218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "たまには二人で…\nな、なんちゃって！", 4, 106301, 0, 2219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 8, 106301, 0, 2220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 106301, 0, 2221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 1, 106301, 0, 2222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ランドソルは\n夜でも賑やかだね！", 1, 106301, 0, 2223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "わぁっ！\nすっごく\nふわふわだよ！", 1, 106301, 0, 2225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 1, 106301, 0, 2226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん\nなんか…いい匂い？", 1, 106301, 0, 2227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "すっごいなー\nあったかいなー！", 1, 106301, 0, 2228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 1, 106301, 0, 2230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 1, 106301, 0, 2231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 103601, 0, 2233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "カップケーキが\n食べたいな…", 1, 103601, 0, 2234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 103601, 0, 2235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 103601, 0, 2236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 103601, 0, 2237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 4, 103601, 0, 2238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "早く大人になりたいな\n…あの人と同じくらい", 8, 103601, 0, 2239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 103601, 0, 2240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 1, 103601, 0, 2241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "これ宿題です\nやっておいて\nくださいね", 1, 103601, 0, 2242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あ、遅刻ですよ\n待っていたんですから", 1, 103601, 0, 2243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 4, 103601, 0, 2244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あの…何歳まで\n子供だと思いますか？", 8, 103601, 0, 2245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 103601, 0, 2246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 1, 103601, 0, 2247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 1, 103601, 0, 2248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 1, 103601, 0, 2250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "つ、つぶらな瞳には\n弱いんです", 1, 103601, 0, 2251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リラックスできますね\n集中力も\n上がりそうです", 1, 103601, 0, 2252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 1, 103601, 0, 2253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ピーマンもこれくらい\nおいしければ\nいいのに…", 1, 103601, 0, 2255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 1, 103601, 0, 2256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 101501, 0, 2258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 101501, 0, 2259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 101501, 0, 2260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 101501, 0, 2261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 101501, 0, 2262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "彼は優しいから、\nきっと良い\nお父さんになるわね", 4, 101501, 0, 2263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 8, 101501, 0, 2264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 101501, 0, 2265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 1, 101501, 0, 2266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "よかったら一緒に\n絵本を作らない？", 1, 101501, 0, 2267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたはいつも元気ね\n先生も見習わないと♪", 1, 101501, 0, 2268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 4, 101501, 0, 2269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 8, 101501, 0, 2270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 101501, 0, 2271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 1, 101501, 0, 2272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 1, 101501, 0, 2273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 1, 101501, 0, 2275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 1, 101501, 0, 2276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね～\n保育園にも\n置こうかしら", 1, 101501, 0, 2277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 1, 101501, 0, 2278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 1, 101501, 0, 2280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 1, 101501, 0, 2281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 104401, 0, 2283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "拠点とはやはり\n斯くあるべきじゃの", 1, 104401, 0, 2284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 104401, 0, 2285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 104401, 0, 2286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 104401, 0, 2287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 4, 104401, 0, 2288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 8, 104401, 0, 2289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 104401, 0, 2290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 1, 104401, 0, 2291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 1, 104401, 0, 2292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 1, 104401, 0, 2293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 4, 104401, 0, 2294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 8, 104401, 0, 2295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 104401, 0, 2296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 1, 104401, 0, 2297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 1, 104401, 0, 2298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "よい座り心地じゃ\nわらわの玉座としよう", 1, 104401, 0, 2300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 1, 104401, 0, 2301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 1, 104401, 0, 2302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あたたかい…\n身も心も\n浄化されるようじゃ", 1, 104401, 0, 2303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 1, 104401, 0, 2305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 1, 104401, 0, 2306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 105401, 0, 2308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 105401, 0, 2309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 105401, 0, 2310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 105401, 0, 2311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 105401, 0, 2312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 4, 105401, 0, 2313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "まさか私が\nアイドルになるなんて\n思ってなかったです", 8, 105401, 0, 2314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 105401, 0, 2315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 1, 105401, 0, 2316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 1, 105401, 0, 2317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 1, 105401, 0, 2318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "どうしました？\n新しい服が\n欲しくなりましたか？", 4, 105401, 0, 2319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 8, 105401, 0, 2320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 105401, 0, 2321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 1, 105401, 0, 2322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の方が\n作業が捗るのは\n私だけですかね？", 1, 105401, 0, 2323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このふかふか具合\nいい素材を\n使ってますね", 1, 105401, 0, 2325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 1, 105401, 0, 2326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 1, 105401, 0, 2327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 1, 105401, 0, 2328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "何をするにも\n食事は大切！\n体力が資本です！", 1, 105401, 0, 2330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "私もいつか\nデザインを\nまとめて本に…", 1, 105401, 0, 2331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "またお魚を\n捕りにいこうかな♪", 1, 107501, 0, 2333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 107501, 0, 2334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ここなら、\n日焼けの心配は\nありませんね！", 1, 107501, 0, 2335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 107501, 0, 2336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "泳ぐのも食べるのも\n大得意です！", 1, 107501, 0, 2337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 4, 107501, 0, 2338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 8, 107501, 0, 2339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 107501, 0, 2340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 1, 107501, 0, 2341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 1, 107501, 0, 2342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたの水着、\n私が選んであげます！", 1, 107501, 0, 2343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 4, 107501, 0, 2344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 8, 107501, 0, 2345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 107501, 0, 2346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 1, 107501, 0, 2347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 1, 107501, 0, 2348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "泳ぎ疲れたので\nちょっと休憩です～", 1, 107501, 0, 2350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あなたにも、\n可愛い水着を\n着せてあげたいです", 1, 107501, 0, 2351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんだか、\nリゾート気分です…", 1, 107501, 0, 2352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 1, 107501, 0, 2353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 1, 107501, 0, 2355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "南国のフルーツ図鑑\n…じゅるり…", 1, 107501, 0, 2356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "わ～♪　おいしそ…\nか、かわいいですね～", 1, 107501, 0, 2358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 1, 107501, 0, 2359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 107601, 0, 2360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 107601, 0, 2361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 107601, 0, 2362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 107601, 0, 2363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クロールの練習なら、\n部屋でもできそうです", 1, 107601, 0, 2364);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 4, 107601, 0, 2365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 8, 107601, 0, 2366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 107601, 0, 2367);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 1, 107601, 0, 2368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "海の美しさは、\n森とはちがう\n魅力がありました", 1, 107601, 0, 2369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この髪飾りですか？\n造花ですから\n水の中も平気です", 1, 107601, 0, 2370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 4, 107601, 0, 2371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "はじめての海が、\n主さまと一緒で\n幸せです♪", 8, 107601, 0, 2372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 107601, 0, 2373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 1, 107601, 0, 2374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 1, 107601, 0, 2375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 1, 107601, 0, 2377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 1, 107601, 0, 2378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…\nきっと主さまも\n気に入ります", 1, 107601, 0, 2379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 1, 107601, 0, 2380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "泳いだ後の食事は\n一段と美味しく\n感じます", 1, 107601, 0, 2382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 1, 107601, 0, 2383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 1, 107601, 0, 2385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふかふかですね\n主さまのようです♪", 1, 107601, 0, 2386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 107701, 0, 2387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 107701, 0, 2388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今日はドジを\nしませんように！", 1, 107701, 0, 2389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 107701, 0, 2390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 107701, 0, 2391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 4, 107701, 0, 2392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "水着でご奉仕したら、\nあの人は喜ぶかな？", 8, 107701, 0, 2393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 107701, 0, 2394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 1, 107701, 0, 2395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 1, 107701, 0, 2396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 1, 107701, 0, 2397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 4, 107701, 0, 2398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 8, 107701, 0, 2399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 107701, 0, 2400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 1, 107701, 0, 2401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 1, 107701, 0, 2402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 1, 107701, 0, 2404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "きれいにすれば\nかわいさも倍増\nですよ～♪", 1, 107701, 0, 2405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 1, 107701, 0, 2406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 1, 107701, 0, 2407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 1, 107701, 0, 2409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 1, 107701, 0, 2410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "えへへ♪\n南の島まで\n乗せてください！", 1, 107701, 0, 2412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "なんだか、\n人魚姫に\nなった気分です♪", 1, 107701, 0, 2413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 107801, 0, 2414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 107801, 0, 2415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ビーチにいると\n日焼けが心配だわ", 1, 107801, 0, 2416);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 107801, 0, 2417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お、泳ぎの練習なんて\nしないからね！", 1, 107801, 0, 2418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 4, 107801, 0, 2419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 8, 107801, 0, 2420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 107801, 0, 2421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 1, 107801, 0, 2422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 1, 107801, 0, 2423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 1, 107801, 0, 2424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 4, 107801, 0, 2425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 8, 107801, 0, 2426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 107801, 0, 2427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 1, 107801, 0, 2428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 1, 107801, 0, 2429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 1, 107801, 0, 2431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こんな魔物がいたら\n操るのも楽しいかも", 1, 107801, 0, 2432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "にゃう～♪\nまるで南国にいる\n気分ね♪", 1, 107801, 0, 2433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 1, 107801, 0, 2434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 1, 107801, 0, 2436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 1, 107801, 0, 2437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 1, 107801, 0, 2439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "リゾートリゾートっ\nのんびりしましょ♪", 1, 107801, 0, 2440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 108001, 0, 2441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 108001, 0, 2442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 108001, 0, 2443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 108001, 0, 2444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 108001, 0, 2445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 4, 108001, 0, 2446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミだけね…\n私のことをここまで\n理解してくれるのは", 8, 108001, 0, 2447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 108001, 0, 2448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 1, 108001, 0, 2449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 1, 108001, 0, 2450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 1, 108001, 0, 2451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 4, 108001, 0, 2452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 8, 108001, 0, 2453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 108001, 0, 2454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 1, 108001, 0, 2455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 1, 108001, 0, 2456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 1, 108001, 0, 2458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 1, 108001, 0, 2459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね…\n働く意欲が\n湧いてくるわ", 1, 108001, 0, 2460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 1, 108001, 0, 2461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 1, 108001, 0, 2463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "座学も大切ね\nもっと知識を\n身につけないと…", 1, 108001, 0, 2464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 1, 108001, 0, 2466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "落ち着くわ…\n海とひとつになった\n気分ね！", 1, 108001, 0, 2467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 107901, 0, 2468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "猫グッズで\n海の家を飾りたいにゃ", 1, 107901, 0, 2469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 107901, 0, 2470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 107901, 0, 2471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 107901, 0, 2472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 4, 107901, 0, 2473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あたしの宝は\nここにあるにゃ～", 8, 107901, 0, 2474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 107901, 0, 2475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今からキミを\nあたしの相棒に\n任命するにゃ！", 1, 107901, 0, 2476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 1, 107901, 0, 2477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 1, 107901, 0, 2478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 4, 107901, 0, 2479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 8, 107901, 0, 2480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 107901, 0, 2481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 1, 107901, 0, 2482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 1, 107901, 0, 2483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 1, 107901, 0, 2485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "にゃにゃ～\nなんてかわいさにゃ～", 1, 107901, 0, 2486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 1, 107901, 0, 2487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 1, 107901, 0, 2488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 1, 107901, 0, 2490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 1, 107901, 0, 2491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 1, 107901, 0, 2493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 1, 107901, 0, 2494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "人の家って、道場とは\n違った香りがするね", 1, 103701, 0, 2495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 103701, 0, 2496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 103701, 0, 2497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 103701, 0, 2498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "もっと\n上手く立ち回って\n出世したいな", 1, 103701, 0, 2499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 4, 103701, 0, 2500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 8, 103701, 0, 2501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 103701, 0, 2502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 1, 103701, 0, 2503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 1, 103701, 0, 2504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 1, 103701, 0, 2505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 4, 103701, 0, 2506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 8, 103701, 0, 2507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 103701, 0, 2508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 1, 103701, 0, 2509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 1, 103701, 0, 2510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いざという時のため\nにも、休憩は大事だね", 1, 103701, 0, 2512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 1, 103701, 0, 2513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 1, 103701, 0, 2514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいなぁ。\nたまにはのんびり\nしようか", 1, 103701, 0, 2515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 1, 103701, 0, 2517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 1, 103701, 0, 2518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 100501, 0, 2520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 100501, 0, 2521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 100501, 0, 2522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 100501, 0, 2523);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 100501, 0, 2524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 4, 100501, 0, 2525);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 8, 100501, 0, 2526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 100501, 0, 2527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 1, 100501, 0, 2528);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 1, 100501, 0, 2529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 1, 100501, 0, 2530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 4, 100501, 0, 2531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 8, 100501, 0, 2532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 100501, 0, 2533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 1, 100501, 0, 2534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜のパトロールで\n一緒に平和を\n守るッス！", 1, 100501, 0, 2535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 1, 100501, 0, 2537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 1, 100501, 0, 2538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 1, 100501, 0, 2539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 1, 100501, 0, 2540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 1, 100501, 0, 2542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 1, 100501, 0, 2543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 108101, 0, 2545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 108101, 0, 2546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 108101, 0, 2547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 108101, 0, 2548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 108101, 0, 2549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 4, 108101, 0, 2550);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 8, 108101, 0, 2551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 108101, 0, 2552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 1, 108101, 0, 2553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 1, 108101, 0, 2554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 1, 108101, 0, 2555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 4, 108101, 0, 2556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 8, 108101, 0, 2557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 108101, 0, 2558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 1, 108101, 0, 2559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 1, 108101, 0, 2560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 1, 108101, 0, 2562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "つん、つん。ふふっ♪\n癒やされます…", 1, 108101, 0, 2563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 1, 108101, 0, 2564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいね\nお父さん…\nえ？　分からない？", 1, 108101, 0, 2565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "霊のみなさんは\n食べられないので…\n私がいただきます", 1, 108101, 0, 2567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 1, 108101, 0, 2568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "わぁ、お菓子が沢山…", 1, 108101, 0, 2570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 108201, 0, 2572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 108201, 0, 2573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 108201, 0, 2574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 108201, 0, 2575);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 108201, 0, 2576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 4, 108201, 0, 2577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 8, 108201, 0, 2578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 108201, 0, 2579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 1, 108201, 0, 2580);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 1, 108201, 0, 2581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "プリンの可能性は\n無限大なの～", 1, 108201, 0, 2582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 4, 108201, 0, 2583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 8, 108201, 0, 2584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 108201, 0, 2585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ランチは豪華に\nプリンアラモードなの", 1, 108201, 0, 2586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 1, 108201, 0, 2587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 1, 108201, 0, 2589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 1, 108201, 0, 2590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "癒されるの～\nまるで天国なの～", 1, 108201, 0, 2591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここでなら、\n焼きプリンが\n作れるの！", 1, 108201, 0, 2592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "腹が減っては\nハロウィンは\nできぬなの！", 1, 108201, 0, 2594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 1, 108201, 0, 2595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 1, 108201, 0, 2597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 108301, 0, 2599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 108301, 0, 2600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 108301, 0, 2601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 108301, 0, 2602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 108301, 0, 2603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 4, 108301, 0, 2604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アイツを魅了する\n魔法とかないかしら？", 8, 108301, 0, 2605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 108301, 0, 2606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 1, 108301, 0, 2607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 1, 108301, 0, 2608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 1, 108301, 0, 2609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 4, 108301, 0, 2610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 8, 108301, 0, 2611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 108301, 0, 2612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 1, 108301, 0, 2613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 1, 108301, 0, 2614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あう～、お菓子の\n食べすぎで\n動けな～い", 1, 108301, 0, 2616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 1, 108301, 0, 2617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "大人な香りに\n包まれてる～♪", 1, 108301, 0, 2618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 1, 108301, 0, 2619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 1, 108301, 0, 2621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 1, 108301, 0, 2622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 1, 108301, 0, 2624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "次はどんな策で団長を\nからかってみようか…", 1, 107101, 0, 2626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 107101, 0, 2627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 107101, 0, 2628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 107101, 0, 2629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 107101, 0, 2630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 4, 107101, 0, 2631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 8, 107101, 0, 2632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 107101, 0, 2633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 1, 107101, 0, 2634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 1, 107101, 0, 2635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 1, 107101, 0, 2636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 4, 107101, 0, 2637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 8, 107101, 0, 2638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 107101, 0, 2639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 1, 107101, 0, 2640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 1, 107101, 0, 2641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 1, 107101, 0, 2643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 1, 107101, 0, 2644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 1, 107101, 0, 2645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 1, 107101, 0, 2646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 1, 107101, 0, 2648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 1, 107101, 0, 2649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 105601, 0, 2651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 105601, 0, 2652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 105601, 0, 2653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 105601, 0, 2654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ふあぁ～…\n昼寝でもするか", 1, 105601, 0, 2655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ふふっ…\nあの子、見た目より\n根性あるんだね", 4, 105601, 0, 2656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 8, 105601, 0, 2657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 105601, 0, 2658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 1, 105601, 0, 2659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 1, 105601, 0, 2660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 1, 105601, 0, 2661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 4, 105601, 0, 2662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 8, 105601, 0, 2663);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 105601, 0, 2664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 1, 105601, 0, 2665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 1, 105601, 0, 2666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 1, 105601, 0, 2668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 105601, 0, 2669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 1, 105601, 0, 2670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 1, 105601, 0, 2671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 1, 105601, 0, 2673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 1, 105601, 0, 2674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 101301, 0, 2676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 101301, 0, 2677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 101301, 0, 2678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 101301, 0, 2679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 101301, 0, 2680);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "次はどの漫画を\n貸そうかなー♪", 4, 101301, 0, 2681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 8, 101301, 0, 2682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 101301, 0, 2683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 1, 101301, 0, 2684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 1, 101301, 0, 2685);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "新魔法思いついた！\n私って天才！？", 1, 101301, 0, 2686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 4, 101301, 0, 2687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 8, 101301, 0, 2688);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 101301, 0, 2689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 1, 101301, 0, 2690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜からが、\nオタ活本番でしょ！", 1, 101301, 0, 2691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふいー！\nくつろいで本を読む！\n最高だねー！", 1, 101301, 0, 2693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 1, 101301, 0, 2694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 1, 101301, 0, 2695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 1, 101301, 0, 2696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ウマ―！\nこれの詳細きぼんぬ！", 1, 101301, 0, 2698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "こ、古代語の本！？\n現代語でおk…", 1, 101301, 0, 2699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 190901, 0, 2701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 190901, 0, 2702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 190901, 0, 2703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 190901, 0, 2704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 190901, 0, 2705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 190901, 0, 2706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 1, 190901, 0, 2707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 1, 190901, 0, 2708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 190901, 0, 2709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 190901, 0, 2710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 190901, 0, 2711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 1, 190901, 0, 2712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 190901, 0, 2714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 190901, 0, 2715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 190901, 0, 2716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 190901, 0, 2717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 190901, 0, 2719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 190901, 0, 2720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 108401, 0, 2722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 108401, 0, 2723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 108401, 0, 2724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 108401, 0, 2725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 108401, 0, 2726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 4, 108401, 0, 2727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 8, 108401, 0, 2728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 108401, 0, 2729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "歌を届ける\nサンタさんに\nなりたいな…", 1, 108401, 0, 2730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 108401, 0, 2731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 1, 108401, 0, 2732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 4, 108401, 0, 2733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 8, 108401, 0, 2734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 108401, 0, 2735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 1, 108401, 0, 2736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 1, 108401, 0, 2737);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 1, 108401, 0, 2739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 1, 108401, 0, 2740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 1, 108401, 0, 2741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この光みたいに\n灯火になるような\n歌を歌いたい…", 1, 108401, 0, 2742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 1, 108401, 0, 2744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "物語には\n曲作りのヒントが\n詰まっているんです", 1, 108401, 0, 2745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 1, 108401, 0, 2747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 1, 108401, 0, 2748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 108501, 0, 2749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 108501, 0, 2750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 108501, 0, 2751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 108501, 0, 2752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 108501, 0, 2753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 4, 108501, 0, 2754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 8, 108501, 0, 2755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 108501, 0, 2756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 1, 108501, 0, 2757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 1, 108501, 0, 2758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 1, 108501, 0, 2759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 4, 108501, 0, 2760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 8, 108501, 0, 2761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 108501, 0, 2762);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 1, 108501, 0, 2763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 1, 108501, 0, 2764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 1, 108501, 0, 2766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子と一緒に\nおままごとしたいな♪", 1, 108501, 0, 2767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 1, 108501, 0, 2768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 1, 108501, 0, 2769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 1, 108501, 0, 2771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 1, 108501, 0, 2772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 1, 108501, 0, 2774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 1, 108501, 0, 2775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 108601, 0, 2776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 108601, 0, 2777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 108601, 0, 2778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 108601, 0, 2779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 108601, 0, 2780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 4, 108601, 0, 2781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 8, 108601, 0, 2782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 108601, 0, 2783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 1, 108601, 0, 2784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 1, 108601, 0, 2785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 1, 108601, 0, 2786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 4, 108601, 0, 2787);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 8, 108601, 0, 2788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 108601, 0, 2789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 1, 108601, 0, 2790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 1, 108601, 0, 2791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 1, 108601, 0, 2793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 1, 108601, 0, 2794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 1, 108601, 0, 2795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 1, 108601, 0, 2796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 1, 108601, 0, 2798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 1, 108601, 0, 2799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 1, 108601, 0, 2801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 1, 108601, 0, 2802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "着物も慣れると\n動きやすいものさ", 1, 108901, 0, 2803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 108901, 0, 2804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 108901, 0, 2805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 108901, 0, 2806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 108901, 0, 2807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "これからもずっと\n仲間と、彼と\n一緒に…", 4, 108901, 0, 2808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 8, 108901, 0, 2809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 108901, 0, 2810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 1, 108901, 0, 2811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 1, 108901, 0, 2812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 1, 108901, 0, 2813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミといると\n気持ちが段々と\n安らいでいくよ", 4, 108901, 0, 2814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 8, 108901, 0, 2815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 108901, 0, 2816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 1, 108901, 0, 2817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 1, 108901, 0, 2818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "…疲れていたのかな\nもう少しで眠る\nところだった…", 1, 108901, 0, 2820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 1, 108901, 0, 2821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 1, 108901, 0, 2822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 1, 108901, 0, 2823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 1, 108901, 0, 2825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "外を出歩くのも\nいいけど、中で\n読書もありだな", 1, 108901, 0, 2826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 108801, 0, 2828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 108801, 0, 2829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今年は\nどんな福袋を\n買おうかな…♪", 1, 108801, 0, 2830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 108801, 0, 2831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 108801, 0, 2832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 4, 108801, 0, 2833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 8, 108801, 0, 2834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 108801, 0, 2835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 1, 108801, 0, 2836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 1, 108801, 0, 2837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 1, 108801, 0, 2838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 4, 108801, 0, 2839);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 8, 108801, 0, 2840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 108801, 0, 2841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 1, 108801, 0, 2842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 1, 108801, 0, 2843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ…\n帯も少し\nゆるめちゃおう…", 1, 108801, 0, 2845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "お正月用に\n着物を縫って\nあげたいな", 1, 108801, 0, 2846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これでお肌が\n保湿出来たら\nいいのになぁ", 1, 108801, 0, 2847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ん～…\nぬくぬくしてて\n動きたくないよ～", 1, 108801, 0, 2848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 1, 108801, 0, 2850);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "年末年始は\n告白のチャンス…\nむ、無理無理！", 1, 108801, 0, 2851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 108701, 0, 2853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 108701, 0, 2854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 108701, 0, 2855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 108701, 0, 2856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 108701, 0, 2857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "うーん…\n騎士くんはお正月\n何してるんだろ…", 4, 108701, 0, 2858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 8, 108701, 0, 2859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 108701, 0, 2860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 1, 108701, 0, 2861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 1, 108701, 0, 2862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 1, 108701, 0, 2863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 4, 108701, 0, 2864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 8, 108701, 0, 2865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 108701, 0, 2866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 1, 108701, 0, 2867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 1, 108701, 0, 2868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 1, 108701, 0, 2870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 1, 108701, 0, 2871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁー♪\nいい香りー…\n気分スッキリ♪", 1, 108701, 0, 2872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 1, 108701, 0, 2873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ど、どうしよう…！\n手が、止まんないよ！", 1, 108701, 0, 2875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 1, 108701, 0, 2876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 109001, 0, 2878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここを私のチョコで\n埋め尽くしますわ", 1, 109001, 0, 2879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 109001, 0, 2880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 109001, 0, 2881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 109001, 0, 2882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 4, 109001, 0, 2883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 8, 109001, 0, 2884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 109001, 0, 2885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 1, 109001, 0, 2886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 1, 109001, 0, 2887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "解毒薬はどこに\nいれたかしら…？", 1, 109001, 0, 2888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 4, 109001, 0, 2889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 8, 109001, 0, 2890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 109001, 0, 2891);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 1, 109001, 0, 2892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 1, 109001, 0, 2893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 1, 109001, 0, 2895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この中、何か\n入りそうですわね\n…チョコレート、とか", 1, 109001, 0, 2896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 1, 109001, 0, 2897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 1, 109001, 0, 2898);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 1, 109001, 0, 2900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 1, 109001, 0, 2901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 109101, 0, 2903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 109101, 0, 2904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 109101, 0, 2905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 109101, 0, 2906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 109101, 0, 2907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お姉ちゃんのチョコ\n食べさせてあげたい…", 4, 109101, 0, 2908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 8, 109101, 0, 2909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 109101, 0, 2910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 1, 109101, 0, 2911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ナデナデするのは\nお姉ちゃんの仕事！", 1, 109101, 0, 2912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 1, 109101, 0, 2913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 4, 109101, 0, 2914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 8, 109101, 0, 2915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 109101, 0, 2916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 1, 109101, 0, 2917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 1, 109101, 0, 2918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 1, 109101, 0, 2920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このかわいさは\nお姉ちゃんも大満足♪", 1, 109101, 0, 2921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り、チョコに\n使えるかな…？", 1, 109101, 0, 2922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "弟くんと一緒に\nぬくぬくしたいな…♪", 1, 109101, 0, 2923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 1, 109101, 0, 2925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 1, 109101, 0, 2926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "アタシって…\nここにいても\nいいのか…？", 1, 106101, 0, 2928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 106101, 0, 2929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 106101, 0, 2930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 106101, 0, 2931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 106101, 0, 2932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "みんなが早く\nアタシのこと\n思い出さないかな", 4, 106101, 0, 2933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 8, 106101, 0, 2934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 106101, 0, 2935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうした？\n戦うのか！？\n…なんだ、違うのか", 1, 106101, 0, 2936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "身長のことは\nあんまり言うな！\n気にしてるからな！", 1, 106101, 0, 2937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 1, 106101, 0, 2938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 4, 106101, 0, 2939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 8, 106101, 0, 2940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 106101, 0, 2941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 1, 106101, 0, 2942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 1, 106101, 0, 2943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぃ～…\n極楽極楽…", 1, 106101, 0, 2945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 1, 106101, 0, 2946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん…\nたまにはこういうのも\n悪くないかもな", 1, 106101, 0, 2947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あー…\nもっと前にコイツと\n出会いたかった…", 1, 106101, 0, 2948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 1, 106101, 0, 2950);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 1, 106101, 0, 2951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 101401, 0, 2953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 101401, 0, 2954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 101401, 0, 2955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この前の\n捜査資料…\nどこにあったかな？", 1, 101401, 0, 2956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 101401, 0, 2957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 4, 101401, 0, 2958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 8, 101401, 0, 2959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 101401, 0, 2960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 1, 101401, 0, 2961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 1, 101401, 0, 2962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 1, 101401, 0, 2963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 4, 101401, 0, 2964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "助手くんは\n私にとって\n最高の相棒だよ☆", 8, 101401, 0, 2965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 101401, 0, 2966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "一休みしたら\n推理の答え合わせを\nしてみようか？", 1, 101401, 0, 2967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 1, 101401, 0, 2968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 1, 101401, 0, 2970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この愛らしさ…\n要捜査対象だね！", 1, 101401, 0, 2971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "思考が活性化される\n素敵な香りだね…", 1, 101401, 0, 2972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 1, 101401, 0, 2973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 1, 101401, 0, 2975);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "探偵だからね\nミステリには\n一家言あるんだ", 1, 101401, 0, 2976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 105501, 0, 2978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 105501, 0, 2979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "先輩を観察しやすい\n場所はあるかな…", 1, 105501, 0, 2980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 105501, 0, 2981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 105501, 0, 2982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 4, 105501, 0, 2983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "せ、先輩になら\n観察されてもいいなぁ", 8, 105501, 0, 2984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 105501, 0, 2985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 1, 105501, 0, 2986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 1, 105501, 0, 2987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ま、また…\n先輩と一緒に\nバイトしたい…です", 1, 105501, 0, 2988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 4, 105501, 0, 2989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 8, 105501, 0, 2990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 105501, 0, 2991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 1, 105501, 0, 2992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 1, 105501, 0, 2993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 1, 105501, 0, 2995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 1, 105501, 0, 2996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ここから先輩の\n香りがすればなあ", 1, 105501, 0, 2997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 1, 105501, 0, 2998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいし～\nこれは\n先輩も好きな味かな", 1, 105501, 0, 3000);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "す、好きな人に\n近づく方法…", 1, 105501, 0, 3001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 109201, 0, 3003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 109201, 0, 3004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 109201, 0, 3005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "わー！\nあれもこれも\n気になっちゃうな！", 1, 109201, 0, 3006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 109201, 0, 3007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 4, 109201, 0, 3008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 8, 109201, 0, 3009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "歩き方がきれい？\nえへへ、そうかな…？", 1, 109201, 0, 3010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 1, 109201, 0, 3011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 1, 109201, 0, 3012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 1, 109201, 0, 3013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 4, 109201, 0, 3014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お茶の時間に\nしたいな～…\n一緒にどう？", 8, 109201, 0, 3015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 109201, 0, 3016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 1, 109201, 0, 3017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 1, 109201, 0, 3018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 1, 109201, 0, 3020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 1, 109201, 0, 3021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～！\nなんだか気持ちが\n落ち着くね", 1, 109201, 0, 3022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 1, 109201, 0, 3023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こ、これ美味し～！\n止められないよー！", 1, 109201, 0, 3025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 1, 109201, 0, 3026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 109301, 0, 3028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 109301, 0, 3029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 109301, 0, 3030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 109301, 0, 3031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "なにか面白そうなもの\nないですかね～？", 1, 109301, 0, 3032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ここのみんなとも\nもうお友だちですぅ！", 4, 109301, 0, 3033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おなかが減ったです…", 8, 109301, 0, 3034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 109301, 0, 3035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 1, 109301, 0, 3036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あなたもお勉強\n苦手ですか？", 1, 109301, 0, 3037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 1, 109301, 0, 3038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お友だちいっぱいで、\nルゥはうれしいです♪", 4, 109301, 0, 3039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 8, 109301, 0, 3040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 109301, 0, 3041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午後もいっぱい\n遊ぶですよ～！", 1, 109301, 0, 3042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 1, 109301, 0, 3043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 1, 109301, 0, 3045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 1, 109301, 0, 3046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 1, 109301, 0, 3047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 1, 109301, 0, 3048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 1, 109301, 0, 3050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 1, 109301, 0, 3051);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ピアノ…\nどこかにないかな？", 1, 109401, 0, 3053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふぅ…\n落ち着くな…", 1, 109401, 0, 3054);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "マナリアはいつも\n賑やかなんだ…", 1, 109401, 0, 3055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みんな…\n今頃何してるかな？", 1, 109401, 0, 3056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 109401, 0, 3057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "人と話すのは\n得意じゃないな…", 4, 109401, 0, 3058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 8, 109401, 0, 3059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "やっぱり、私の翼や\n尻尾…気になる？", 1, 109401, 0, 3060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 1, 109401, 0, 3061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 1, 109401, 0, 3062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 1, 109401, 0, 3063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 4, 109401, 0, 3064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 8, 109401, 0, 3065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 109401, 0, 3066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 1, 109401, 0, 3067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 1, 109401, 0, 3068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "羽を伸ばしたく\nなっちゃうね…ふふ", 1, 109401, 0, 3070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 1, 109401, 0, 3071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\nこれ、\nアンも好きそう…", 1, 109401, 0, 3072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 1, 109401, 0, 3073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 1, 109401, 0, 3075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 1, 109401, 0, 3076);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 109501, 0, 3078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 109501, 0, 3079);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 109501, 0, 3080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 109501, 0, 3081);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 109501, 0, 3082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 4, 109501, 0, 3083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 8, 109501, 0, 3084);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 109501, 0, 3085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 1, 109501, 0, 3086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "こんな場所で\nなんというプレイを！\n…妄想でした", 1, 109501, 0, 3087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 1, 109501, 0, 3088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 4, 109501, 0, 3089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 8, 109501, 0, 3090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 109501, 0, 3091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 1, 109501, 0, 3092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 1, 109501, 0, 3093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 1, 109501, 0, 3095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 1, 109501, 0, 3096);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 1, 109501, 0, 3097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 1, 109501, 0, 3098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 1, 109501, 0, 3100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クウカの探している\nジャンルはどこに\nありますかね？", 1, 109501, 0, 3101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 109601, 0, 3103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 109601, 0, 3104);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 109601, 0, 3105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 109601, 0, 3106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 109601, 0, 3107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 4, 109601, 0, 3108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 8, 109601, 0, 3109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 109601, 0, 3110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 1, 109601, 0, 3111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 1, 109601, 0, 3112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 1, 109601, 0, 3113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 4, 109601, 0, 3114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 8, 109601, 0, 3115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 109601, 0, 3116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 1, 109601, 0, 3117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 1, 109601, 0, 3118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "天下分け目の\n戦いに備えて…\nマッタリデース", 1, 109601, 0, 3120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "口寄せの術で\n召喚したいくらい\nかわいいデース", 1, 109601, 0, 3121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 1, 109601, 0, 3122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 1, 109601, 0, 3123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 1, 109601, 0, 3125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 1, 109601, 0, 3126);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 109701, 0, 3128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 109701, 0, 3129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 109701, 0, 3130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 109701, 0, 3131);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 109701, 0, 3132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 4, 109701, 0, 3133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 8, 109701, 0, 3134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 109701, 0, 3135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "レムにできること\nでしたら、なんでも\nお申し付けください", 1, 109701, 0, 3136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 1, 109701, 0, 3137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 1, 109701, 0, 3138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 4, 109701, 0, 3139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 8, 109701, 0, 3140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 109701, 0, 3141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食の時間ですね\n何か食べたいものは\nありますか？", 1, 109701, 0, 3142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 1, 109701, 0, 3143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 1, 109701, 0, 3145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 1, 109701, 0, 3146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 109701, 0, 3147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 109701, 0, 3148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 109701, 0, 3150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 109701, 0, 3151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 109801, 0, 3153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 109801, 0, 3154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 109801, 0, 3155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 109801, 0, 3156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 109801, 0, 3157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 4, 109801, 0, 3158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 8, 109801, 0, 3159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 109801, 0, 3160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 109801, 0, 3161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 109801, 0, 3162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 109801, 0, 3163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 4, 109801, 0, 3164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 8, 109801, 0, 3165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 109801, 0, 3166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 109801, 0, 3167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 109801, 0, 3168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 109801, 0, 3170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 109801, 0, 3171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 109801, 0, 3172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 109801, 0, 3173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 109801, 0, 3175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 1, 109801, 0, 3176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 109901, 0, 3178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 109901, 0, 3179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 109901, 0, 3180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 109901, 0, 3181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 109901, 0, 3182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "また、みんなで\n一緒に買い物に\n行きたいな", 4, 109901, 0, 3183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 8, 109901, 0, 3184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 109901, 0, 3185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 1, 109901, 0, 3186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 1, 109901, 0, 3187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 1, 109901, 0, 3188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 4, 109901, 0, 3189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 8, 109901, 0, 3190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 109901, 0, 3191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 1, 109901, 0, 3192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 1, 109901, 0, 3193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 1, 109901, 0, 3195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 1, 109901, 0, 3196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 1, 109901, 0, 3197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 1, 109901, 0, 3198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 109901, 0, 3200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 1, 109901, 0, 3201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 110001, 0, 3203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 110001, 0, 3204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 110001, 0, 3205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 110001, 0, 3206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 110001, 0, 3207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 4, 110001, 0, 3208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 8, 110001, 0, 3209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 110001, 0, 3210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 1, 110001, 0, 3211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 1, 110001, 0, 3212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 1, 110001, 0, 3213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 4, 110001, 0, 3214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 8, 110001, 0, 3215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 110001, 0, 3216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 1, 110001, 0, 3217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 1, 110001, 0, 3218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 1, 110001, 0, 3220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 1, 110001, 0, 3221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 1, 110001, 0, 3222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い♪\n夏の太陽みたい！", 1, 110001, 0, 3223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 1, 110001, 0, 3225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏のために\n最新ファッションを\n勉強ちう！", 1, 110001, 0, 3226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 1, 110001, 0, 3228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 1, 110001, 0, 3229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 110101, 0, 3230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 110101, 0, 3231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 110101, 0, 3232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 110101, 0, 3233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 110101, 0, 3234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海が楽しいって\n思えたのも\nあの子のおかげね", 4, 110101, 0, 3235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 8, 110101, 0, 3236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 110101, 0, 3237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 1, 110101, 0, 3238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 1, 110101, 0, 3239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 1, 110101, 0, 3240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 4, 110101, 0, 3241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 8, 110101, 0, 3242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 110101, 0, 3243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 1, 110101, 0, 3244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 1, 110101, 0, 3245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 1, 110101, 0, 3247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 1, 110101, 0, 3248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この匂い…\nなんだか夏の海を\n思い出すわ～", 1, 110101, 0, 3249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 1, 110101, 0, 3250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 1, 110101, 0, 3252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏休み中でも\nしっかりお勉強\nしないと！", 1, 110101, 0, 3253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 1, 110101, 0, 3255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 1, 110101, 0, 3256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 110301, 0, 3257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 110301, 0, 3258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 110301, 0, 3259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 110301, 0, 3260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 110301, 0, 3261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 4, 110301, 0, 3262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 8, 110301, 0, 3263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 110301, 0, 3264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 1, 110301, 0, 3265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 1, 110301, 0, 3266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 1, 110301, 0, 3267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 4, 110301, 0, 3268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 8, 110301, 0, 3269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 110301, 0, 3270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 1, 110301, 0, 3271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 1, 110301, 0, 3272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 1, 110301, 0, 3274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 1, 110301, 0, 3275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "夏らしい\nいい香りね…\nなんの香りかしら？", 1, 110301, 0, 3276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 1, 110301, 0, 3277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 1, 110301, 0, 3279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 1, 110301, 0, 3280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 1, 110301, 0, 3282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 1, 110301, 0, 3283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 110401, 0, 3284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 110401, 0, 3285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 110401, 0, 3286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏ならではの\n料理…か\n何がいいかな…", 1, 110401, 0, 3287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "やっぱりここは\n涼しくて快適だな", 1, 110401, 0, 3288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 4, 110401, 0, 3289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "水着で部屋の中に\nいるのって\n変な感じだな…", 8, 110401, 0, 3290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 110401, 0, 3291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 1, 110401, 0, 3292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "夏バテしてないか？\nここで休んでけよ", 1, 110401, 0, 3293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "…そう見られると\n照れるっていうか…", 1, 110401, 0, 3294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 4, 110401, 0, 3295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "おまえと\n夏の思い出ができて\nよかったよ", 8, 110401, 0, 3296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 110401, 0, 3297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 1, 110401, 0, 3298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もお疲れ\n明日のパトロールも\nよろしくな", 1, 110401, 0, 3299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "泳ぎ疲れたからな\n少し休憩だ…", 1, 110401, 0, 3301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…\nいや、なんでもねぇ！", 1, 110401, 0, 3302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 1, 110401, 0, 3303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 1, 110401, 0, 3304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "海で食べると\nまた一味違うよな", 1, 110401, 0, 3306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏のサバイバル特集…\nちょっと読んでみるか", 1, 110401, 0, 3307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 1, 110401, 0, 3309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 1, 110401, 0, 3310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 110501, 0, 3311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 110501, 0, 3312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 110501, 0, 3313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 110501, 0, 3314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クロールも\n犬かきも\n大得意さ～！", 1, 110501, 0, 3315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "なんだか歌いたい\n気分さ～\nキミもどう～？", 4, 110501, 0, 3316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 8, 110501, 0, 3317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 110501, 0, 3318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 1, 110501, 0, 3319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 1, 110501, 0, 3320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 1, 110501, 0, 3321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 4, 110501, 0, 3322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 8, 110501, 0, 3323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 110501, 0, 3324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 1, 110501, 0, 3325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 1, 110501, 0, 3326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 1, 110501, 0, 3328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 1, 110501, 0, 3329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 1, 110501, 0, 3330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 1, 110501, 0, 3331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 1, 110501, 0, 3333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 1, 110501, 0, 3334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 1, 110501, 0, 3336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 1, 110501, 0, 3337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あの人が\nわたしの騎士だったら\nいいのにな…", 12, 105801, 0, 3338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 12, 105801, 0, 3339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 12, 105901, 0, 3340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 12, 105901, 0, 3341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 12, 106001, 0, 3342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 12, 106001, 0, 3343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 110601, 0, 3344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 110601, 0, 3345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 110601, 0, 3346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 110601, 0, 3347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 110601, 0, 3348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 4, 110601, 0, 3349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 8, 110601, 0, 3350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 110601, 0, 3351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 1, 110601, 0, 3352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "日が落ちたら\n線香花火\nやりたいわぁ♪", 1, 110601, 0, 3353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "木陰でのんびり\nお昼寝…\n最高やね～", 1, 110601, 0, 3354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 4, 110601, 0, 3355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 8, 110601, 0, 3356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 110601, 0, 3357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 1, 110601, 0, 3358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 1, 110601, 0, 3359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 1, 110601, 0, 3361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 1, 110601, 0, 3362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 1, 110601, 0, 3363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これで\n冷えた身体も\nポカポカやねぇ", 1, 110601, 0, 3364);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 1, 110601, 0, 3366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 1, 110601, 0, 3367);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 1, 110601, 0, 3369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 1, 110601, 0, 3370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "真似したくなる\n人がここには\n大勢いますね", 1, 107001, 0, 3371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "偽物を一人\n紛れこませました\n…冗談です", 1, 107001, 0, 3372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "動物に変身して\nその辺りを\n散歩してきます", 1, 107001, 0, 3373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 107001, 0, 3374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 107001, 0, 3375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "次は彼に\nどんなことを\n教えましょうか…", 4, 107001, 0, 3376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "知識はともかく\n私の力はまだ\n必要なようですね", 8, 107001, 0, 3377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしました？\n…何でもない？\n…そうですか", 1, 107001, 0, 3378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "退屈しています\n芸でもして\n楽しませなさい", 1, 107001, 0, 3379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "研究のためです\n少し話し相手に\nなりなさい", 1, 107001, 0, 3380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 1, 107001, 0, 3381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴方にはまだ色々と\n教えるべきことが\nありますね", 4, 107001, 0, 3382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "随分多くの女性と\n知り合いなのですね\n驚きました", 8, 107001, 0, 3383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "…\nおはようございます\n…まだ朝ですよ", 1, 107001, 0, 3384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "食事にします\n…どうしました？\n早く持ってきなさい", 1, 107001, 0, 3385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 1, 107001, 0, 3386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "マサキより\n座り心地は\nいいですね", 1, 107001, 0, 3388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "意外に複雑な\n構造ですね", 1, 107001, 0, 3389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "真似るなら\n香りまで\n再現しなくては", 1, 107001, 0, 3390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "発火現象の\n模倣…なかなか\n難しいものです", 1, 107001, 0, 3391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 1, 107001, 0, 3393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "暇つぶしには\nちょうどよさそうです\n…", 1, 107001, 0, 3394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 110701, 0, 3396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 110701, 0, 3397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 110701, 0, 3398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 110701, 0, 3399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 110701, 0, 3400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 4, 110701, 0, 3401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 8, 110701, 0, 3402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 110701, 0, 3403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 1, 110701, 0, 3404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 1, 110701, 0, 3405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 1, 110701, 0, 3406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "学院でも、\nBB団の活動を\nしていきましょうね♪", 4, 110701, 0, 3407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 8, 110701, 0, 3408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 110701, 0, 3409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 1, 110701, 0, 3410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 1, 110701, 0, 3411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 1, 110701, 0, 3413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 1, 110701, 0, 3414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 1, 110701, 0, 3415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 1, 110701, 0, 3416);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 1, 110701, 0, 3418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 1, 110701, 0, 3419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あんた、何してんの。\nいいけど、なんでも", 1, 110801, 0, 3421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 110801, 0, 3422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 110801, 0, 3423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 110801, 0, 3424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 110801, 0, 3425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 4, 110801, 0, 3426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 8, 110801, 0, 3427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 110801, 0, 3428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 1, 110801, 0, 3429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 1, 110801, 0, 3430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 1, 110801, 0, 3431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 4, 110801, 0, 3432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 8, 110801, 0, 3433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 110801, 0, 3434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 1, 110801, 0, 3435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 1, 110801, 0, 3436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 1, 110801, 0, 3438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なんだこれ\nかわいいかよ", 1, 110801, 0, 3439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふーん。\nこの匂い…\nなんかいいじゃん", 1, 110801, 0, 3440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 1, 110801, 0, 3441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 1, 110801, 0, 3443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 1, 110801, 0, 3444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "イタズラってなにを\nしたらいいのかな？", 1, 111101, 0, 3446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 111101, 0, 3447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 111101, 0, 3448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "オバケ…いないよね？\n怖くなんてないけど！", 1, 111101, 0, 3449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 111101, 0, 3450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 4, 111101, 0, 3451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 8, 111101, 0, 3452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 111101, 0, 3453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "触りましたね？\nイタズラしましたね？\nへんた～い！", 1, 111101, 0, 3454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 1, 111101, 0, 3455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 1, 111101, 0, 3456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 4, 111101, 0, 3457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 8, 111101, 0, 3458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 111101, 0, 3459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 1, 111101, 0, 3460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 1, 111101, 0, 3461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 1, 111101, 0, 3463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 1, 111101, 0, 3464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "甘い匂いがいっぱい…\nお菓子の香りかな？", 1, 111101, 0, 3465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 1, 111101, 0, 3466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ニンジンもこれくらい\nおいしければ\nいいのに…", 1, 111101, 0, 3468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 1, 111101, 0, 3469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 1, 111101, 0, 3471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 111201, 0, 3473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1, 111201, 0, 3474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1, 111201, 0, 3475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1, 111201, 0, 3476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "どうすればみんなを\nびっくりさせられる\nかな～？", 1, 111201, 0, 3477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 4, 111201, 0, 3478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 8, 111201, 0, 3479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 1, 111201, 0, 3480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 1, 111201, 0, 3481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 1, 111201, 0, 3482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 1, 111201, 0, 3483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 4, 111201, 0, 3484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 8, 111201, 0, 3485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1, 111201, 0, 3486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 1, 111201, 0, 3487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 1, 111201, 0, 3488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 1, 111201, 0, 3490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 1, 111201, 0, 3491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 1, 111201, 0, 3492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 1, 111201, 0, 3493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 1, 111201, 0, 3495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本のなかに\nいたずらのヒント\nないかな～？", 1, 111201, 0, 3496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 1, 111201, 0, 3498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 12, 101101, 0, 3500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 12, 101101, 0, 3501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1, 111301, 0, 3502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1, 111301, 0, 3503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1, 111301, 0, 3504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1, 111301, 0, 3505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1, 111301, 0, 3506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 4, 111301, 0, 3507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 8, 111301, 0, 3508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 1, 111301, 0, 3509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 1, 111301, 0, 3510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 1, 111301, 0, 3511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 1, 111301, 0, 3512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 4, 111301, 0, 3513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 8, 111301, 0, 3514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1, 111301, 0, 3515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 1, 111301, 0, 3516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 1, 111301, 0, 3517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 1, 111301, 0, 3519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 1, 111301, 0, 3520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 1, 111301, 0, 3521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 1, 111301, 0, 3522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい～♪\nおにいちゃんも\n食べようよー！", 1, 111301, 0, 3524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 1, 111301, 0, 3525);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 1, 111301, 0, 3527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここ、好きだよ。\nみんな優しいから…", 1, 111401, 0, 3529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "なんかさみしいな", 1, 111401, 0, 3530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "知らない人怖いな", 1, 111401, 0, 3531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お部屋おっきいねー", 1, 111401, 0, 3532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ルナね…\nお昼寝したいかも", 1, 111401, 0, 3533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "おにいさん！\n今日は何して遊ぶの？", 4, 111401, 0, 3534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あのね、\nお料理教えてほしい！", 8, 111401, 0, 3535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あっ、おにいさんだ！", 1, 111401, 0, 3536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ルナは元気だよ！\nいいこだもん！", 1, 111401, 0, 3537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日は何しようかな", 1, 111401, 0, 3538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ルナと遊んでよ！", 1, 111401, 0, 3539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねーねー、\nルナとお出かけして！", 4, 111401, 0, 3540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "おにいさんと、\n遊びたいなー！", 8, 111401, 0, 3541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふふっ！\nなんかまぶしいね！", 1, 111401, 0, 3542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ルナ、お腹空いたな", 1, 111401, 0, 3543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "どこに行こうかな？", 1, 111401, 0, 3544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ルナ、これ好きだな", 1, 111401, 0, 3546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい！　好き！", 1, 111401, 0, 3547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんかいい匂い！", 1, 111401, 0, 3548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいねー", 1, 111401, 0, 3549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ふふっ\nおいしいね", 1, 111401, 0, 3551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ルナ、\n眠くなっちゃう…", 1, 111401, 0, 3552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 12, 105201, 0, 3554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あなたとなら\nどんなことだって\nできる気がするわ♪", 12, 105201, 0, 3555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 106501, 0, 3556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 106501, 0, 3557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 106501, 0, 3558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 106501, 0, 3559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 106501, 0, 3560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 4, 106501, 0, 3561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 8, 106501, 0, 3562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 106501, 0, 3563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 1, 106501, 0, 3564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 1, 106501, 0, 3565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 1, 106501, 0, 3566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 4, 106501, 0, 3567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 8, 106501, 0, 3568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 106501, 0, 3569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 1, 106501, 0, 3570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 1, 106501, 0, 3571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 1, 106501, 0, 3573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 1, 106501, 0, 3574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 1, 106501, 0, 3575);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 1, 106501, 0, 3576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いつでも全力で\nケンカするために\n力を蓄えないとな", 1, 106501, 0, 3578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 1, 106501, 0, 3579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 111501, 0, 3581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 111501, 0, 3582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 111501, 0, 3583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 111501, 0, 3584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 111501, 0, 3585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 4, 111501, 0, 3586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 8, 111501, 0, 3587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 111501, 0, 3588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 1, 111501, 0, 3589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 1, 111501, 0, 3590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 1, 111501, 0, 3591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 4, 111501, 0, 3592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 8, 111501, 0, 3593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 111501, 0, 3594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 1, 111501, 0, 3595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 1, 111501, 0, 3596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 1, 111501, 0, 3598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 1, 111501, 0, 3599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 1, 111501, 0, 3600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体が温まってくると\nひと暴れしたくなるな", 1, 111501, 0, 3601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 1, 111501, 0, 3603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 1, 111501, 0, 3604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 1, 111501, 0, 3606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 1, 111501, 0, 3607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 111601, 0, 3608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 111601, 0, 3609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 111601, 0, 3610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 111601, 0, 3611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 111601, 0, 3612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 4, 111601, 0, 3613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 8, 111601, 0, 3614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 111601, 0, 3615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 1, 111601, 0, 3616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 1, 111601, 0, 3617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 1, 111601, 0, 3618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 4, 111601, 0, 3619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 8, 111601, 0, 3620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 111601, 0, 3621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 1, 111601, 0, 3622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 1, 111601, 0, 3623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 1, 111601, 0, 3625);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 1, 111601, 0, 3626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 1, 111601, 0, 3627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 1, 111601, 0, 3628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 1, 111601, 0, 3630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 1, 111601, 0, 3631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "面白ーい！\nこの演出、ライブでも\nできないかな？", 1, 111601, 0, 3633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 1, 111601, 0, 3634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 12, 101801, 0, 3635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 12, 101801, 0, 3636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 111701, 0, 3637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 111701, 0, 3638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 111701, 0, 3639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 111701, 0, 3640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 111701, 0, 3641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 4, 111701, 0, 3642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 8, 111701, 0, 3643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 111701, 0, 3644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 1, 111701, 0, 3645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 1, 111701, 0, 3646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 1, 111701, 0, 3647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 4, 111701, 0, 3648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 8, 111701, 0, 3649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 111701, 0, 3650);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 1, 111701, 0, 3651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 1, 111701, 0, 3652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 1, 111701, 0, 3654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 1, 111701, 0, 3655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 1, 111701, 0, 3656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 1, 111701, 0, 3657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 1, 111701, 0, 3659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 1, 111701, 0, 3660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 1, 111701, 0, 3662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 1, 111701, 0, 3663);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 112001, 0, 3664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 112001, 0, 3665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "初日の出って\n特別な感じがして\nいいわよね…", 1, 112001, 0, 3666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 112001, 0, 3667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 112001, 0, 3668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 4, 112001, 0, 3669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今年もあいつと\n居られること…\n結構うれしいわ", 8, 112001, 0, 3670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 112001, 0, 3671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 1, 112001, 0, 3672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 1, 112001, 0, 3673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "門松飾るから\nちょっと手伝って\nくれる？", 1, 112001, 0, 3674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 4, 112001, 0, 3675);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 8, 112001, 0, 3676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 112001, 0, 3677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おせちに飽きたら\n次は何を食べよう\nかしら…", 1, 112001, 0, 3678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 1, 112001, 0, 3679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 1, 112001, 0, 3681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 1, 112001, 0, 3682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂いね\n落ち着いて\n眠れそう…", 1, 112001, 0, 3683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 1, 112001, 0, 3684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 1, 112001, 0, 3686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "魔物を使わない\nおせちを\n調べておかなきゃ…", 1, 112001, 0, 3687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 112101, 0, 3689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 112101, 0, 3690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 112101, 0, 3691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 112101, 0, 3692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 112101, 0, 3693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 4, 112101, 0, 3694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 8, 112101, 0, 3695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 112101, 0, 3696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 1, 112101, 0, 3697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 1, 112101, 0, 3698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 1, 112101, 0, 3699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 4, 112101, 0, 3700);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 8, 112101, 0, 3701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 112101, 0, 3702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 1, 112101, 0, 3703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 1, 112101, 0, 3704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 1, 112101, 0, 3706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 1, 112101, 0, 3707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 1, 112101, 0, 3708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 1, 112101, 0, 3709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 1, 112101, 0, 3711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 1, 112101, 0, 3712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 12, 103401, 0, 3714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 12, 103401, 0, 3715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 111901, 0, 3716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 111901, 0, 3717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 111901, 0, 3718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 111901, 0, 3719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 111901, 0, 3720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "主さまの温もりは\n心まで温めてくれる\nようです", 4, 111901, 0, 3721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 8, 111901, 0, 3722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 111901, 0, 3723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お餅はゆっくりと\n召し上がって\nくださいまし", 1, 111901, 0, 3724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 1, 111901, 0, 3725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 1, 111901, 0, 3726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 4, 111901, 0, 3727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 8, 111901, 0, 3728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 111901, 0, 3729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 1, 111901, 0, 3730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 1, 111901, 0, 3731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか…\nこのまま\n寝てしまいそうです", 1, 111901, 0, 3733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 1, 111901, 0, 3734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 1, 111901, 0, 3735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "風邪をひきませんよう\n暖かくいたしましょう", 1, 111901, 0, 3736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べ過ぎは\n正月太りの\nもとだというのに…", 1, 111901, 0, 3738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "主さまのために\n参拝の作法を\n予習しておきましょう", 1, 111901, 0, 3739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 112201, 0, 3741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 112201, 0, 3742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 112201, 0, 3743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 112201, 0, 3744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 112201, 0, 3745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 4, 112201, 0, 3746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 8, 112201, 0, 3747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 112201, 0, 3748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 1, 112201, 0, 3749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 1, 112201, 0, 3750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 1, 112201, 0, 3751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 4, 112201, 0, 3752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 8, 112201, 0, 3753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 112201, 0, 3754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 1, 112201, 0, 3755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 1, 112201, 0, 3756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 1, 112201, 0, 3758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 1, 112201, 0, 3759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 1, 112201, 0, 3760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 1, 112201, 0, 3761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 1, 112201, 0, 3763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 1, 112201, 0, 3764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 112301, 0, 3766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 112301, 0, 3767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 112301, 0, 3768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 112301, 0, 3769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 112301, 0, 3770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人がいると\nなんだかホッとする\n不思議…", 4, 112301, 0, 3771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 8, 112301, 0, 3772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 112301, 0, 3773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "よかったら魔法の\n練習に付き合って\nくれませんか？", 1, 112301, 0, 3774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あの…\nこの格好どうですか？\n似合ってると…いいな", 1, 112301, 0, 3775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 1, 112301, 0, 3776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私、あなたのそばに\nいられたら、それで…", 4, 112301, 0, 3777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 8, 112301, 0, 3778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 112301, 0, 3779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 1, 112301, 0, 3780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 1, 112301, 0, 3781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 1, 112301, 0, 3783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 1, 112301, 0, 3784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 1, 112301, 0, 3785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 1, 112301, 0, 3786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 1, 112301, 0, 3788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふふ…\n魔法少女になっても\n読書はやめられないな", 1, 112301, 0, 3789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 12, 101001, 0, 3791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 12, 101001, 0, 3792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 180401, 0, 3793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 180401, 0, 3794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 180401, 0, 3795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 180401, 0, 3796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 180401, 0, 3797);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 4, 180401, 0, 3798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 8, 180401, 0, 3799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 180401, 0, 3800);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 1, 180401, 0, 3801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 1, 180401, 0, 3802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 1, 180401, 0, 3803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 4, 180401, 0, 3804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 8, 180401, 0, 3805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 180401, 0, 3806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 1, 180401, 0, 3807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 1, 180401, 0, 3808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 1, 180401, 0, 3810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 1, 180401, 0, 3811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 1, 180401, 0, 3812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 1, 180401, 0, 3813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 1, 180401, 0, 3815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 1, 180401, 0, 3816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 112401, 0, 3818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 112401, 0, 3819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 112401, 0, 3820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 112401, 0, 3821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 112401, 0, 3822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 4, 112401, 0, 3823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 8, 112401, 0, 3824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 112401, 0, 3825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 1, 112401, 0, 3826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 1, 112401, 0, 3827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 1, 112401, 0, 3828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 4, 112401, 0, 3829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 8, 112401, 0, 3830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 112401, 0, 3831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 1, 112401, 0, 3832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 1, 112401, 0, 3833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 1, 112401, 0, 3835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 1, 112401, 0, 3836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リラックスできる\nいい香りですね～♪", 1, 112401, 0, 3837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 1, 112401, 0, 3838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 1, 112401, 0, 3840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 1, 112401, 0, 3841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 112601, 0, 3843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 112601, 0, 3844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 112601, 0, 3845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 112601, 0, 3846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 112601, 0, 3847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 4, 112601, 0, 3848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 8, 112601, 0, 3849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 112601, 0, 3850);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 1, 112601, 0, 3851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 1, 112601, 0, 3852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 1, 112601, 0, 3853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 4, 112601, 0, 3854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 8, 112601, 0, 3855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 112601, 0, 3856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 1, 112601, 0, 3857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 1, 112601, 0, 3858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 1, 112601, 0, 3860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 1, 112601, 0, 3861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 1, 112601, 0, 3862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 1, 112601, 0, 3863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 1, 112601, 0, 3865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 1, 112601, 0, 3866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 12, 100101, 0, 3868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 12, 100101, 0, 3869);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "騎士クンの前で\n好きって言えるように\n練習しないと…", 12, 100201, 0, 3870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 12, 100201, 0, 3871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 12, 100301, 0, 3872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 12, 100301, 0, 3873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 112501, 0, 3874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 112501, 0, 3875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 112501, 0, 3876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 112501, 0, 3877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 112501, 0, 3878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 4, 112501, 0, 3879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 8, 112501, 0, 3880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 112501, 0, 3881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 1, 112501, 0, 3882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 1, 112501, 0, 3883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 1, 112501, 0, 3884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 4, 112501, 0, 3885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 8, 112501, 0, 3886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 112501, 0, 3887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 1, 112501, 0, 3888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 1, 112501, 0, 3889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 1, 112501, 0, 3891);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 1, 112501, 0, 3892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 1, 112501, 0, 3893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 1, 112501, 0, 3894);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 1, 112501, 0, 3896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 1, 112501, 0, 3897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 111001, 0, 3899);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 111001, 0, 3900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 111001, 0, 3901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 111001, 0, 3902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 111001, 0, 3903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 4, 111001, 0, 3904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 8, 111001, 0, 3905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 111001, 0, 3906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 1, 111001, 0, 3907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 1, 111001, 0, 3908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 1, 111001, 0, 3909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 4, 111001, 0, 3910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 8, 111001, 0, 3911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 111001, 0, 3912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 1, 111001, 0, 3913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 1, 111001, 0, 3914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 1, 111001, 0, 3916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 1, 111001, 0, 3917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 1, 111001, 0, 3918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "炎はいいぞ\nそこには人類発展の\nドラマがある", 1, 111001, 0, 3919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "フオオー！\n次から次へと固形食物\n原理気になるー！", 1, 111001, 0, 3921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 1, 111001, 0, 3922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 12, 101201, 0, 3924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 12, 101201, 0, 3925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちぇるーん☆", 1, 192101, 0, 3933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 110901, 0, 3951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "どこかに楽しいもの\n落ちてないかにゃー", 1, 110901, 0, 3952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 110901, 0, 3953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 110901, 0, 3954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 110901, 0, 3955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 4, 110901, 0, 3956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 8, 110901, 0, 3957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 110901, 0, 3958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 1, 110901, 0, 3959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 1, 110901, 0, 3960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 1, 110901, 0, 3961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 4, 110901, 0, 3962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 8, 110901, 0, 3963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 110901, 0, 3964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のランチは\nどこで\n映えよっかにゃー", 1, 110901, 0, 3965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 1, 110901, 0, 3966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかチエルン\n略して、ふかルン♪", 1, 110901, 0, 3968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 1, 110901, 0, 3969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 1, 110901, 0, 3970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "マシュマロは\n焼かない方のが\nかわいくないです？", 1, 110901, 0, 3971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "チエル食べても\n太らない体質\nなんでぇ～！", 1, 110901, 0, 3973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 1, 110901, 0, 3974);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 112701, 0, 3976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 112701, 0, 3977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あたしの代わりに誰か\n働いてくれないかね", 1, 112701, 0, 3978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "労働の後に食べる\nあんパン最高～", 1, 112701, 0, 3979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 112701, 0, 3980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 4, 112701, 0, 3981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "次の休みはあいつと\n一日中だらだら\nしたいな～", 8, 112701, 0, 3982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 112701, 0, 3983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お、差し入れ？\n気が利くねぇ\nあんがと", 1, 112701, 0, 3984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "新しい依頼？\n今日はもう無理～\n閉店で～す", 1, 112701, 0, 3985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 1, 112701, 0, 3986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 4, 112701, 0, 3987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 8, 112701, 0, 3988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 112701, 0, 3989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 1, 112701, 0, 3990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 1, 112701, 0, 3991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 1, 112701, 0, 3993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 1, 112701, 0, 3994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "あんパンの香りの\nアロマとか\nあればいいのにな～", 1, 112701, 0, 3995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 1, 112701, 0, 3996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 1, 112701, 0, 3998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 1, 112701, 0, 3999);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 12, 104601, 0, 4001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 12, 104601, 0, 4002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "フロンティア\nスピリッツを\nお見せするべ！", 1, 112801, 0, 4003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 112801, 0, 4004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 112801, 0, 4005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 112801, 0, 4006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 112801, 0, 4007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "みんなオラより\n若ぇのに働いてて\n立派だべな", 4, 112801, 0, 4008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 8, 112801, 0, 4009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 112801, 0, 4010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 1, 112801, 0, 4011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 1, 112801, 0, 4012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 1, 112801, 0, 4013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 4, 112801, 0, 4014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 8, 112801, 0, 4015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 112801, 0, 4016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 1, 112801, 0, 4017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 1, 112801, 0, 4018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "牧草みたいに\nふっかふかだべ～", 1, 112801, 0, 4020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子、目の感じが\nエリザベスに似てるべ", 1, 112801, 0, 4021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂いだべ～\n疲れた体に\nしみるべな～", 1, 112801, 0, 4022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったけぇな～\n牧場にも\n置いてみっかな～", 1, 112801, 0, 4023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うまいべ！\n生産者のこだわりを\n感じるべさ", 1, 112801, 0, 4025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "酪農も戦いも\nまずは知識だべ！", 1, 112801, 0, 4026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 113001, 0, 4028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 113001, 0, 4029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 113001, 0, 4030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 113001, 0, 4031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 113001, 0, 4032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 4, 113001, 0, 4033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 8, 113001, 0, 4034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 113001, 0, 4035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 1, 113001, 0, 4036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 1, 113001, 0, 4037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 1, 113001, 0, 4038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 4, 113001, 0, 4039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 8, 113001, 0, 4040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 113001, 0, 4041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 1, 113001, 0, 4042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 1, 113001, 0, 4043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 1, 113001, 0, 4045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 1, 113001, 0, 4046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 1, 113001, 0, 4047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 1, 113001, 0, 4048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 1, 113001, 0, 4050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 1, 113001, 0, 4051);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 180501, 0, 4053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 180501, 0, 4054);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 180501, 0, 4055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 180501, 0, 4056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 180501, 0, 4057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 4, 180501, 0, 4058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 8, 180501, 0, 4059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おでかけですか？\nではわたくしも\nお供いたします", 1, 180501, 0, 4060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 1, 180501, 0, 4061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 1, 180501, 0, 4062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 1, 180501, 0, 4063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 4, 180501, 0, 4064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 8, 180501, 0, 4065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 180501, 0, 4066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 1, 180501, 0, 4067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 1, 180501, 0, 4068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 1, 180501, 0, 4070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 1, 180501, 0, 4071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 1, 180501, 0, 4072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "主さまの心と\n繋がったときのような\n温もりを感じます", 1, 180501, 0, 4073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 1, 180501, 0, 4075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 1, 180501, 0, 4076);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 12, 104801, 0, 4078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 12, 104801, 0, 4079);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 112901, 0, 4080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 112901, 0, 4081);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 112901, 0, 4082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 112901, 0, 4083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 112901, 0, 4084);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 4, 112901, 0, 4085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 8, 112901, 0, 4086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 112901, 0, 4087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 1, 112901, 0, 4088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 1, 112901, 0, 4089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 1, 112901, 0, 4090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 4, 112901, 0, 4091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 8, 112901, 0, 4092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 112901, 0, 4093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 1, 112901, 0, 4094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 1, 112901, 0, 4095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 1, 112901, 0, 4097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 1, 112901, 0, 4098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 1, 112901, 0, 4099);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 1, 112901, 0, 4100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 1, 112901, 0, 4102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 1, 112901, 0, 4103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "退屈です…\nなにか楽しいことは\nないですか？", 1, 106601, 0, 4105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 106601, 0, 4106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 106601, 0, 4107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 106601, 0, 4108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 106601, 0, 4109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 4, 106601, 0, 4110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 8, 106601, 0, 4111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 106601, 0, 4112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 1, 106601, 0, 4113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 1, 106601, 0, 4114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄さんは\nヒマそうですね…\nうらやましいです", 1, 106601, 0, 4115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 4, 106601, 0, 4116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 8, 106601, 0, 4117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 106601, 0, 4118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 1, 106601, 0, 4119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 1, 106601, 0, 4120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ～…\nもうここから一歩も\n動きたくないです", 1, 106601, 0, 4122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 1, 106601, 0, 4123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんかおしゃれな\n匂いがするですね…", 1, 106601, 0, 4124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 1, 106601, 0, 4125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 1, 106601, 0, 4127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふーむ…\nもっと楽しい本は\nないですかね？", 1, 106601, 0, 4128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 113201, 0, 4130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 113201, 0, 4131);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 113201, 0, 4132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 113201, 0, 4133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 113201, 0, 4134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 4, 113201, 0, 4135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 8, 113201, 0, 4136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 113201, 0, 4137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 1, 113201, 0, 4138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 1, 113201, 0, 4139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 1, 113201, 0, 4140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 4, 113201, 0, 4141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 8, 113201, 0, 4142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 113201, 0, 4143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 1, 113201, 0, 4144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 1, 113201, 0, 4145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 1, 113201, 0, 4147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 1, 113201, 0, 4148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "心地よい香りで\n我が精神も安らぐ…", 1, 113201, 0, 4149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 1, 113201, 0, 4150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 1, 113201, 0, 4152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 1, 113201, 0, 4153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 1, 113201, 0, 4155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 1, 113201, 0, 4156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 180201, 0, 4157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 180201, 0, 4158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 180201, 0, 4159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 180201, 0, 4160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 180201, 0, 4161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "騎士クン…\n今どこにいるの…？\n…会いたいな", 4, 180201, 0, 4162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 8, 180201, 0, 4163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 180201, 0, 4164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 1, 180201, 0, 4165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 1, 180201, 0, 4166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 1, 180201, 0, 4167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 4, 180201, 0, 4168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 8, 180201, 0, 4169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 180201, 0, 4170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 1, 180201, 0, 4171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 1, 180201, 0, 4172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 1, 180201, 0, 4174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 1, 180201, 0, 4175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 1, 180201, 0, 4176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "温かい…\n騎士クンがくれる\n力みたい…", 1, 180201, 0, 4177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 1, 180201, 0, 4179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 1, 180201, 0, 4180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 12, 104901, 0, 4182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 12, 104901, 0, 4183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 113101, 0, 4184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 113101, 0, 4185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 113101, 0, 4186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 113101, 0, 4187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 113101, 0, 4188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 4, 113101, 0, 4189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 8, 113101, 0, 4190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 113101, 0, 4191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 1, 113101, 0, 4192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 1, 113101, 0, 4193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 1, 113101, 0, 4194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 4, 113101, 0, 4195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 8, 113101, 0, 4196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 113101, 0, 4197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 1, 113101, 0, 4198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 1, 113101, 0, 4199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 1, 113101, 0, 4201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あんたたちも\n海を楽しみたいって\n思っているのかね", 1, 113101, 0, 4202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 1, 113101, 0, 4203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こういう格好だと\n少し冷えちまうんだよ", 1, 113101, 0, 4204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 1, 113101, 0, 4206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 1, 113101, 0, 4207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "へえ…\nなかなかいい\n乗り心地じゃないか", 1, 113101, 0, 4209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 1, 113101, 0, 4210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 113301, 0, 4211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 113301, 0, 4212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今のナナカちゃんは\nたぶん水属性！", 1, 113301, 0, 4213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 113301, 0, 4214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 113301, 0, 4215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 4, 113301, 0, 4216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 8, 113301, 0, 4217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 113301, 0, 4218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 1, 113301, 0, 4219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 1, 113301, 0, 4220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 1, 113301, 0, 4221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 4, 113301, 0, 4222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 8, 113301, 0, 4223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 113301, 0, 4224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 1, 113301, 0, 4225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 1, 113301, 0, 4226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 1, 113301, 0, 4228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 1, 113301, 0, 4229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 1, 113301, 0, 4230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "夜になると急に冷えて\n肌寒いんだよね～…", 1, 113301, 0, 4231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 1, 113301, 0, 4233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 1, 113301, 0, 4234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 1, 113301, 0, 4236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ナナカちゃんの\n誕生～\nなんちゃって", 1, 113301, 0, 4237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 113401, 0, 4238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 113401, 0, 4239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 113401, 0, 4240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 113401, 0, 4241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 113401, 0, 4242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 4, 113401, 0, 4243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "キミへの応援\nちゃんと\n届いてるかな…", 8, 113401, 0, 4244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 113401, 0, 4245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうしたの？\n元気がないなら\n私が応援するよ☆", 1, 113401, 0, 4246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 1, 113401, 0, 4247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 1, 113401, 0, 4248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 4, 113401, 0, 4249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミを応援\nすることができて…\n私、すごく幸せ…", 8, 113401, 0, 4250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 113401, 0, 4251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 1, 113401, 0, 4252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 1, 113401, 0, 4253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 1, 113401, 0, 4255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 1, 113401, 0, 4256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 1, 113401, 0, 4257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 1, 113401, 0, 4258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 1, 113401, 0, 4260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 1, 113401, 0, 4261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 1, 113401, 0, 4263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 1, 113401, 0, 4264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 113501, 0, 4265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 113501, 0, 4266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 113501, 0, 4267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 113501, 0, 4268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 113501, 0, 4269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 4, 113501, 0, 4270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 8, 113501, 0, 4271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 113501, 0, 4272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 1, 113501, 0, 4273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 1, 113501, 0, 4274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 1, 113501, 0, 4275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 4, 113501, 0, 4276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 8, 113501, 0, 4277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 113501, 0, 4278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 1, 113501, 0, 4279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 1, 113501, 0, 4280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 1, 113501, 0, 4282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 1, 113501, 0, 4283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 1, 113501, 0, 4284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 1, 113501, 0, 4285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 1, 113501, 0, 4287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 1, 113501, 0, 4288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 1, 113501, 0, 4290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 1, 113501, 0, 4291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 12, 102301, 0, 4292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 12, 102301, 0, 4293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 113601, 0, 4294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 113601, 0, 4295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 113601, 0, 4296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 113601, 0, 4297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 113601, 0, 4298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 4, 113601, 0, 4299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "少年との\n海のバカンス…\nまた行きたい\nものだな", 8, 113601, 0, 4300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 113601, 0, 4301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 1, 113601, 0, 4302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 1, 113601, 0, 4303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 1, 113601, 0, 4304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 4, 113601, 0, 4305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 8, 113601, 0, 4306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 113601, 0, 4307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食のお誘いかい？\nいいよ、付き合おう", 1, 113601, 0, 4308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう夜か…\n一日が経つのは\nはやいものだね", 1, 113601, 0, 4309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 1, 113601, 0, 4311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うん…ふわふわの\n感触が\nよくわかるな…", 1, 113601, 0, 4312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 1, 113601, 0, 4313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 1, 113601, 0, 4314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "兜を気にせず\n食べられるのは\n楽でいいな…", 1, 113601, 0, 4316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむ…鎧がないと\nページがめくりやすい", 1, 113601, 0, 4317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 1, 113601, 0, 4319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 1, 113601, 0, 4320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 106801, 0, 4321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 106801, 0, 4322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "色んな人を観察できて\n眼福眼福…♪", 1, 106801, 0, 4323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 106801, 0, 4324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 106801, 0, 4325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 4, 106801, 0, 4326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 8, 106801, 0, 4327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 106801, 0, 4328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 1, 106801, 0, 4329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 1, 106801, 0, 4330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 1, 106801, 0, 4331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 4, 106801, 0, 4332);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 8, 106801, 0, 4333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 106801, 0, 4334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 1, 106801, 0, 4335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 1, 106801, 0, 4336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 1, 106801, 0, 4338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…これ\nネネカが変身\nしてないよね？", 1, 106801, 0, 4339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだ…\n解析完了\n今度生成しようっと♪", 1, 106801, 0, 4340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "安心するな～…\nアジトに置けないから\n羨ましいよ", 1, 106801, 0, 4341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 1, 106801, 0, 4343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 1, 106801, 0, 4344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 113701, 0, 4346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 113701, 0, 4347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 113701, 0, 4348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 113701, 0, 4349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 113701, 0, 4350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 4, 113701, 0, 4351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 8, 113701, 0, 4352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 113701, 0, 4353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 1, 113701, 0, 4354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 1, 113701, 0, 4355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 1, 113701, 0, 4356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 4, 113701, 0, 4357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 8, 113701, 0, 4358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 113701, 0, 4359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 1, 113701, 0, 4360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 1, 113701, 0, 4361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 1, 113701, 0, 4363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 1, 113701, 0, 4364);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 1, 113701, 0, 4365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 1, 113701, 0, 4366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 1, 113701, 0, 4368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 1, 113701, 0, 4369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あの方の前では\n常に気高く\nありたいですわね", 12, 103201, 0, 4371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 12, 103201, 0, 4372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 12, 102801, 0, 4373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 12, 102801, 0, 4374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 113801, 0, 4375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 113801, 0, 4376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 113801, 0, 4377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 113801, 0, 4378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 113801, 0, 4379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 4, 113801, 0, 4380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 8, 113801, 0, 4381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 113801, 0, 4382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 1, 113801, 0, 4383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 1, 113801, 0, 4384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 1, 113801, 0, 4385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 4, 113801, 0, 4386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 8, 113801, 0, 4387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 113801, 0, 4388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふぅ、いいことした\n後のランチは\nおいしいわね", 1, 113801, 0, 4389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 1, 113801, 0, 4390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 1, 113801, 0, 4392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 1, 113801, 0, 4393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 1, 113801, 0, 4394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 1, 113801, 0, 4395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 1, 113801, 0, 4397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 1, 113801, 0, 4398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 113901, 0, 4400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 113901, 0, 4401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 113901, 0, 4402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 113901, 0, 4403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "マントは動きに\nメリハリがついて\nいいですね", 1, 113901, 0, 4404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 4, 113901, 0, 4405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 8, 113901, 0, 4406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 113901, 0, 4407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 1, 113901, 0, 4408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 1, 113901, 0, 4409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 1, 113901, 0, 4410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 4, 113901, 0, 4411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 8, 113901, 0, 4412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 113901, 0, 4413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 1, 113901, 0, 4414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 1, 113901, 0, 4415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このフィット感\nどうにか服作りにも\n活かせれば…！", 1, 113901, 0, 4417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 1, 113901, 0, 4418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 1, 113901, 0, 4419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 1, 113901, 0, 4420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 1, 113901, 0, 4422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 1, 113901, 0, 4423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 1, 113901, 0, 4425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 114101, 0, 4427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 114101, 0, 4428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 114101, 0, 4429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 114101, 0, 4430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 114101, 0, 4431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 4, 114101, 0, 4432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 8, 114101, 0, 4433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 114101, 0, 4434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 1, 114101, 0, 4435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 1, 114101, 0, 4436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 1, 114101, 0, 4437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたも一緒に\n新しい技の練習\nどうッスか？", 4, 114101, 0, 4438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 8, 114101, 0, 4439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 114101, 0, 4440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 1, 114101, 0, 4441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 1, 114101, 0, 4442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 1, 114101, 0, 4444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 1, 114101, 0, 4445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 1, 114101, 0, 4446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 1, 114101, 0, 4447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 1, 114101, 0, 4449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 1, 114101, 0, 4450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 1, 114101, 0, 4452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 12, 103001, 0, 4454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 12, 103001, 0, 4455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 114001, 0, 4456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 114001, 0, 4457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 114001, 0, 4458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 114001, 0, 4459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 114001, 0, 4460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 4, 114001, 0, 4461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 8, 114001, 0, 4462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 114001, 0, 4463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 1, 114001, 0, 4464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうしたの？\n出かけるなら\n私も同行しようか", 1, 114001, 0, 4465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 1, 114001, 0, 4466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 4, 114001, 0, 4467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 8, 114001, 0, 4468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 114001, 0, 4469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 1, 114001, 0, 4470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 1, 114001, 0, 4471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ハロウィンはまだ\nこれからだ\n一休みしようか", 1, 114001, 0, 4473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 1, 114001, 0, 4474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 1, 114001, 0, 4475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 1, 114001, 0, 4476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 1, 114001, 0, 4478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 1, 114001, 0, 4479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 1, 114001, 0, 4481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 114201, 0, 4483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 114201, 0, 4484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 114201, 0, 4485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 114201, 0, 4486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 114201, 0, 4487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 4, 114201, 0, 4488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 8, 114201, 0, 4489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 114201, 0, 4490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 1, 114201, 0, 4491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 1, 114201, 0, 4492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 1, 114201, 0, 4493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 4, 114201, 0, 4494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 8, 114201, 0, 4495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 114201, 0, 4496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 1, 114201, 0, 4497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 1, 114201, 0, 4498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 1, 114201, 0, 4500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 1, 114201, 0, 4501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 1, 114201, 0, 4502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この温かさ…\n愛と希望に\n通じるものがある…", 1, 114201, 0, 4503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 1, 114201, 0, 4505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 1, 114201, 0, 4506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 12, 103301, 0, 4508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 12, 103301, 0, 4509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ぴっぷる！", 1, 192501, 0, 4510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぴっぷるぴっぷる♪", 1, 192501, 0, 4511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ぴっ！", 1, 192501, 0, 4512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ぷっぷー", 1, 192501, 0, 4513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ぴぃ？", 1, 192501, 0, 4514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ぴっぷるんるん♪", 1, 192501, 0, 4515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぷぅ！", 1, 192501, 0, 4516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ぴぴっ♪", 1, 192501, 0, 4517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ぷぅー…", 1, 192501, 0, 4518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ちゅ！", 1, 192501, 0, 4519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ぷはー", 1, 192501, 0, 4520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ぴぇー", 1, 192501, 0, 4521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 114301, 0, 4531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 114301, 0, 4532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 114301, 0, 4533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 114301, 0, 4534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 114301, 0, 4535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 4, 114301, 0, 4536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 8, 114301, 0, 4537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 114301, 0, 4538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 114301, 0, 4539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 114301, 0, 4540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 114301, 0, 4541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 4, 114301, 0, 4542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 8, 114301, 0, 4543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 114301, 0, 4544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 114301, 0, 4545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 1, 114301, 0, 4546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 1, 114301, 0, 4548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 1, 114301, 0, 4549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 1, 114301, 0, 4550);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 1, 114301, 0, 4551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こ、これは…\nおいしすぎて手が\n止まらないかも", 1, 114301, 0, 4553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 1, 114301, 0, 4554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 114401, 0, 4556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 114401, 0, 4557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 114401, 0, 4558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 114401, 0, 4559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 114401, 0, 4560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 4, 114401, 0, 4561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 8, 114401, 0, 4562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 114401, 0, 4563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 1, 114401, 0, 4564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 1, 114401, 0, 4565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 1, 114401, 0, 4566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 4, 114401, 0, 4567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 8, 114401, 0, 4568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 114401, 0, 4569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 1, 114401, 0, 4570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 1, 114401, 0, 4571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "適度な休息は\n成功の秘訣ですわ！", 1, 114401, 0, 4573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 1, 114401, 0, 4574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 1, 114401, 0, 4575);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 1, 114401, 0, 4576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 1, 114401, 0, 4578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 1, 114401, 0, 4579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 1, 114401, 0, 4581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 1, 114401, 0, 4582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 114601, 0, 4583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 114601, 0, 4584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 114601, 0, 4585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 114601, 0, 4586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 114601, 0, 4587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 4, 114601, 0, 4588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 8, 114601, 0, 4589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 114601, 0, 4590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 1, 114601, 0, 4591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 1, 114601, 0, 4592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お姉さんの\n頼れるところ\n見せてあげるわね", 1, 114601, 0, 4593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "何かあったら\n頼ってくれても\nいいんだからね？", 4, 114601, 0, 4594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 8, 114601, 0, 4595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 114601, 0, 4596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 1, 114601, 0, 4597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 1, 114601, 0, 4598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 1, 114601, 0, 4600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "私だって…\nかわいいものは\n好きでいいわよね？", 1, 114601, 0, 4601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 1, 114601, 0, 4602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 1, 114601, 0, 4603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 1, 114601, 0, 4605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 1, 114601, 0, 4606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 1, 114601, 0, 4608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 1, 114601, 0, 4609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 12, 101601, 0, 4610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 12, 101601, 0, 4611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 114501, 0, 4612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 114501, 0, 4613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 114501, 0, 4614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 114501, 0, 4615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 114501, 0, 4616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 4, 114501, 0, 4617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 8, 114501, 0, 4618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 114501, 0, 4619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 1, 114501, 0, 4620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 1, 114501, 0, 4621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 1, 114501, 0, 4622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 4, 114501, 0, 4623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 8, 114501, 0, 4624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 114501, 0, 4625);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 1, 114501, 0, 4626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 1, 114501, 0, 4627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 1, 114501, 0, 4629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 1, 114501, 0, 4630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "華やかな香りね\nクリスマスに\nピッタリだわ", 1, 114501, 0, 4631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 1, 114501, 0, 4632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 1, 114501, 0, 4634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 1, 114501, 0, 4635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 1, 114501, 0, 4637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 1, 114501, 0, 4638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 114701, 0, 4639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 114701, 0, 4640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 114701, 0, 4641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 114701, 0, 4642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 114701, 0, 4643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 4, 114701, 0, 4644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 8, 114701, 0, 4645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 114701, 0, 4646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 1, 114701, 0, 4647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 1, 114701, 0, 4648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 1, 114701, 0, 4649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 4, 114701, 0, 4650);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 8, 114701, 0, 4651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 114701, 0, 4652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 1, 114701, 0, 4653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 1, 114701, 0, 4654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 1, 114701, 0, 4656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なんだ？\nオマエも初詣に\n行きたいのか？", 1, 114701, 0, 4657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 1, 114701, 0, 4658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "コイツがあれば\nうまい魚が\n焼けそうだな…", 1, 114701, 0, 4659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 1, 114701, 0, 4661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おみくじって\n書いてあることが\nむずかしいんだよな…", 1, 114701, 0, 4662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 180101, 0, 4664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 180101, 0, 4665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 180101, 0, 4666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 180101, 0, 4667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 180101, 0, 4668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 4, 180101, 0, 4669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 8, 180101, 0, 4670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 180101, 0, 4671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 1, 180101, 0, 4672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 1, 180101, 0, 4673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 1, 180101, 0, 4674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 4, 180101, 0, 4675);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 8, 180101, 0, 4676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 180101, 0, 4677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 1, 180101, 0, 4678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 1, 180101, 0, 4679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 1, 180101, 0, 4681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 1, 180101, 0, 4682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 1, 180101, 0, 4683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 1, 180101, 0, 4684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 1, 180101, 0, 4686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 1, 180101, 0, 4687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 111801, 0, 4689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 111801, 0, 4690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 111801, 0, 4691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 111801, 0, 4692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 111801, 0, 4693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ずっとこうして\n年の始まりを\n過ごしたいですね", 4, 111801, 0, 4694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつか二人きりで\n初日の出を\n見たいです", 8, 111801, 0, 4695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 111801, 0, 4696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 1, 111801, 0, 4697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 1, 111801, 0, 4698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 1, 111801, 0, 4699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 4, 111801, 0, 4700);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 8, 111801, 0, 4701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 111801, 0, 4702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 1, 111801, 0, 4703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あたたかくして\n幸せな初夢を\n見ましょうね", 1, 111801, 0, 4704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 1, 111801, 0, 4706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 1, 111801, 0, 4707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 1, 111801, 0, 4708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 1, 111801, 0, 4709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしすぎて\n食べる手が\n止まりません～", 1, 111801, 0, 4711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 1, 111801, 0, 4712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 12, 100601, 0, 4714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 12, 100601, 0, 4715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 115001, 0, 4716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 115001, 0, 4717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 115001, 0, 4718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 115001, 0, 4719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 115001, 0, 4720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 4, 115001, 0, 4721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 8, 115001, 0, 4722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 115001, 0, 4723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 1, 115001, 0, 4724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 1, 115001, 0, 4725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 1, 115001, 0, 4726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 4, 115001, 0, 4727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 8, 115001, 0, 4728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 115001, 0, 4729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 1, 115001, 0, 4730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 1, 115001, 0, 4731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "少し休みます…\n時間が来たら\n起こすように", 1, 115001, 0, 4733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 1, 115001, 0, 4734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 1, 115001, 0, 4735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 1, 115001, 0, 4736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いかなる強者でも\n兵站、補給は\n必要なものです", 1, 115001, 0, 4738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 1, 115001, 0, 4739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 115501, 0, 4741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 115501, 0, 4742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "主さまも精霊の召喚を\n試してみますか？", 1, 115501, 0, 4743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 115501, 0, 4744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 115501, 0, 4745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "進化した精霊に\n負けぬよう\n修行に励みます", 4, 115501, 0, 4746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 8, 115501, 0, 4747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 115501, 0, 4748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 1, 115501, 0, 4749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "里で採れた果物を\n持って参りました\n召し上がってください", 1, 115501, 0, 4750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 1, 115501, 0, 4751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 4, 115501, 0, 4752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 8, 115501, 0, 4753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 115501, 0, 4754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 1, 115501, 0, 4755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 1, 115501, 0, 4756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この癒し…\n主さまにも味わって\nいただきたい…", 1, 115501, 0, 4758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 1, 115501, 0, 4759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 1, 115501, 0, 4760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "瞑想するのに\nちょうどよい暖かさで\nございます", 1, 115501, 0, 4761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 1, 115501, 0, 4763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "風の精霊について\nもっと詳しく知って\nおかなければ…", 1, 115501, 0, 4764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 115601, 0, 4766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 115601, 0, 4767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 115601, 0, 4768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 115601, 0, 4769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 115601, 0, 4770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 4, 115601, 0, 4771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 8, 115601, 0, 4772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 115601, 0, 4773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 1, 115601, 0, 4774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 115601, 0, 4775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 115601, 0, 4776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 4, 115601, 0, 4777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 8, 115601, 0, 4778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 115601, 0, 4779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 1, 115601, 0, 4780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 1, 115601, 0, 4781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 1, 115601, 0, 4783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 1, 115601, 0, 4784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 1, 115601, 0, 4785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 1, 115601, 0, 4786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "自然の恵みに\n感謝して…\nいただきます♪", 1, 115601, 0, 4788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 1, 115601, 0, 4789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 12, 102201, 0, 4791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 12, 102201, 0, 4792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 106401, 0, 4793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 106401, 0, 4794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 106401, 0, 4795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 106401, 0, 4796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 106401, 0, 4797);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 4, 106401, 0, 4798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 8, 106401, 0, 4799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 106401, 0, 4800);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 1, 106401, 0, 4801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 1, 106401, 0, 4802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 1, 106401, 0, 4803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 4, 106401, 0, 4804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 8, 106401, 0, 4805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 106401, 0, 4806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 1, 106401, 0, 4807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 1, 106401, 0, 4808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 1, 106401, 0, 4810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 1, 106401, 0, 4811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 1, 106401, 0, 4812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 1, 106401, 0, 4813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 1, 106401, 0, 4815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ん…なるほどね。\n勉強になるわ…", 1, 106401, 0, 4816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 115701, 0, 4818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "依頼があれば\n海でも山でも\n赴くさ！", 1, 115701, 0, 4819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 115701, 0, 4820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 115701, 0, 4821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 115701, 0, 4822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 4, 115701, 0, 4823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 8, 115701, 0, 4824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 115701, 0, 4825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 1, 115701, 0, 4826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 1, 115701, 0, 4827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 1, 115701, 0, 4828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 4, 115701, 0, 4829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 8, 115701, 0, 4830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 115701, 0, 4831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 1, 115701, 0, 4832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 1, 115701, 0, 4833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 1, 115701, 0, 4835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 1, 115701, 0, 4836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 1, 115701, 0, 4837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカするね…\n助手くん\n君もおいでよ", 1, 115701, 0, 4838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 1, 115701, 0, 4840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 1, 115701, 0, 4841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 1, 115701, 0, 4843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 1, 115701, 0, 4844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 180601, 0, 4845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 180601, 0, 4846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 180601, 0, 4847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 180601, 0, 4848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 180601, 0, 4849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 4, 180601, 0, 4850);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 8, 180601, 0, 4851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 180601, 0, 4852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 1, 180601, 0, 4853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 1, 180601, 0, 4854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 1, 180601, 0, 4855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 4, 180601, 0, 4856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 8, 180601, 0, 4857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 180601, 0, 4858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 1, 180601, 0, 4859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 1, 180601, 0, 4860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 1, 180601, 0, 4862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 1, 180601, 0, 4863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 1, 180601, 0, 4864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 1, 180601, 0, 4865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 1, 180601, 0, 4867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 1, 180601, 0, 4868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 115801, 0, 4870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 115801, 0, 4871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 115801, 0, 4872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 115801, 0, 4873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 115801, 0, 4874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 4, 115801, 0, 4875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 8, 115801, 0, 4876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 115801, 0, 4877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 1, 115801, 0, 4878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 1, 115801, 0, 4879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 1, 115801, 0, 4880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 4, 115801, 0, 4881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 8, 115801, 0, 4882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 115801, 0, 4883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 1, 115801, 0, 4884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 1, 115801, 0, 4885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 1, 115801, 0, 4887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 1, 115801, 0, 4888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すてきな香り…\nお花かしら？\n森で探してみようかな", 1, 115801, 0, 4889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 1, 115801, 0, 4890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レッスンのために\n栄養補給して\nおかないと！", 1, 115801, 0, 4892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "教養を身に着ける\nことも大切よね！\nなになに…", 1, 115801, 0, 4893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 115901, 0, 4895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 115901, 0, 4896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 115901, 0, 4897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 115901, 0, 4898);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 115901, 0, 4899);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 4, 115901, 0, 4900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 8, 115901, 0, 4901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 115901, 0, 4902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 1, 115901, 0, 4903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 1, 115901, 0, 4904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 1, 115901, 0, 4905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 4, 115901, 0, 4906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 8, 115901, 0, 4907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 115901, 0, 4908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 1, 115901, 0, 4909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 1, 115901, 0, 4910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 1, 115901, 0, 4912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 1, 115901, 0, 4913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 1, 115901, 0, 4914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 1, 115901, 0, 4915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 1, 115901, 0, 4917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 1, 115901, 0, 4918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 12, 102001, 0, 4920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 12, 102001, 0, 4921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 12, 100401, 0, 4922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 12, 100401, 0, 4923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 12, 103601, 0, 4924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 12, 103601, 0, 4925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 116001, 0, 4926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 116001, 0, 4927);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 116001, 0, 4928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 116001, 0, 4929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 116001, 0, 4930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 4, 116001, 0, 4931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 8, 116001, 0, 4932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 116001, 0, 4933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 1, 116001, 0, 4934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 1, 116001, 0, 4935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 1, 116001, 0, 4936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 4, 116001, 0, 4937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 8, 116001, 0, 4938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 116001, 0, 4939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 1, 116001, 0, 4940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 1, 116001, 0, 4941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 1, 116001, 0, 4943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 1, 116001, 0, 4944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 1, 116001, 0, 4945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 1, 116001, 0, 4946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 1, 116001, 0, 4948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 1, 116001, 0, 4949);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 116301, 0, 4951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 116301, 0, 4952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 116301, 0, 4953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 116301, 0, 4954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 116301, 0, 4955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 4, 116301, 0, 4956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 8, 116301, 0, 4957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 116301, 0, 4958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 1, 116301, 0, 4959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 1, 116301, 0, 4960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 1, 116301, 0, 4961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 4, 116301, 0, 4962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 8, 116301, 0, 4963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 116301, 0, 4964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 1, 116301, 0, 4965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 1, 116301, 0, 4966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 1, 116301, 0, 4968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 1, 116301, 0, 4969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 1, 116301, 0, 4970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 1, 116301, 0, 4971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 1, 116301, 0, 4973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 1, 116301, 0, 4974);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 12, 102901, 0, 4976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 12, 102901, 0, 4977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 116201, 0, 4978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 116201, 0, 4979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 116201, 0, 4980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 116201, 0, 4981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 116201, 0, 4982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 4, 116201, 0, 4983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 8, 116201, 0, 4984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 116201, 0, 4985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 1, 116201, 0, 4986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 1, 116201, 0, 4987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 116201, 0, 4988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 4, 116201, 0, 4989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 8, 116201, 0, 4990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 116201, 0, 4991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 1, 116201, 0, 4992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 1, 116201, 0, 4993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 1, 116201, 0, 4995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 1, 116201, 0, 4996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 1, 116201, 0, 4997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 1, 116201, 0, 4998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 1, 116201, 0, 5000);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 1, 116201, 0, 5001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 116501, 0, 5003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 116501, 0, 5004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 116501, 0, 5005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 116501, 0, 5006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 116501, 0, 5007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 4, 116501, 0, 5008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 8, 116501, 0, 5009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 116501, 0, 5010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 1, 116501, 0, 5011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 1, 116501, 0, 5012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 1, 116501, 0, 5013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 4, 116501, 0, 5014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 8, 116501, 0, 5015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 116501, 0, 5016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹が空きました…\nご飯食べにいきますよ", 1, 116501, 0, 5017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 1, 116501, 0, 5018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 1, 116501, 0, 5020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 1, 116501, 0, 5021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 1, 116501, 0, 5022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 1, 116501, 0, 5023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 1, 116501, 0, 5025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 1, 116501, 0, 5026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 12, 102501, 0, 5028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 12, 102501, 0, 5029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 116601, 0, 5030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 116601, 0, 5031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 116601, 0, 5032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 116601, 0, 5033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 116601, 0, 5034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 4, 116601, 0, 5035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 8, 116601, 0, 5036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 116601, 0, 5037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 1, 116601, 0, 5038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 1, 116601, 0, 5039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 1, 116601, 0, 5040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 4, 116601, 0, 5041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 8, 116601, 0, 5042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 116601, 0, 5043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 1, 116601, 0, 5044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 1, 116601, 0, 5045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 1, 116601, 0, 5047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 1, 116601, 0, 5048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 1, 116601, 0, 5049);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 1, 116601, 0, 5050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 1, 116601, 0, 5052);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 1, 116601, 0, 5053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 116701, 0, 5055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 116701, 0, 5056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 116701, 0, 5057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 116701, 0, 5058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 116701, 0, 5059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 4, 116701, 0, 5060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 8, 116701, 0, 5061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 116701, 0, 5062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 1, 116701, 0, 5063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 1, 116701, 0, 5064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 1, 116701, 0, 5065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 4, 116701, 0, 5066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 8, 116701, 0, 5067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 116701, 0, 5068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 1, 116701, 0, 5069);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 1, 116701, 0, 5070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 1, 116701, 0, 5072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 1, 116701, 0, 5073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 1, 116701, 0, 5074);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽか…\n幸せなひとときですね", 1, 116701, 0, 5075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 1, 116701, 0, 5077);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 1, 116701, 0, 5078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 116801, 0, 5080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 116801, 0, 5081);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 116801, 0, 5082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 116801, 0, 5083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 116801, 0, 5084);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 4, 116801, 0, 5085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 8, 116801, 0, 5086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 116801, 0, 5087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 1, 116801, 0, 5088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 1, 116801, 0, 5089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 1, 116801, 0, 5090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 4, 116801, 0, 5091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 8, 116801, 0, 5092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 116801, 0, 5093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 1, 116801, 0, 5094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 1, 116801, 0, 5095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 1, 116801, 0, 5097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 1, 116801, 0, 5098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 1, 116801, 0, 5099);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 1, 116801, 0, 5100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 1, 116801, 0, 5102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 1, 116801, 0, 5103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 12, 102101, 0, 5105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 12, 102101, 0, 5106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 116901, 0, 5107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 116901, 0, 5108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 116901, 0, 5109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 116901, 0, 5110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 116901, 0, 5111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 4, 116901, 0, 5112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 8, 116901, 0, 5113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 116901, 0, 5114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 1, 116901, 0, 5115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 1, 116901, 0, 5116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 1, 116901, 0, 5117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 4, 116901, 0, 5118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 8, 116901, 0, 5119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 116901, 0, 5120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 1, 116901, 0, 5121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 1, 116901, 0, 5122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 1, 116901, 0, 5124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 1, 116901, 0, 5125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 1, 116901, 0, 5126);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 1, 116901, 0, 5127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 1, 116901, 0, 5129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 1, 116901, 0, 5130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "変身している間に\n色々と試しておこう\nまずは…", 1, 180301, 0, 5132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 180301, 0, 5133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 180301, 0, 5134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 180301, 0, 5135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 180301, 0, 5136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 4, 180301, 0, 5137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 8, 180301, 0, 5138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 180301, 0, 5139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 1, 180301, 0, 5140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 1, 180301, 0, 5141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 1, 180301, 0, 5142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 4, 180301, 0, 5143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 8, 180301, 0, 5144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 180301, 0, 5145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 1, 180301, 0, 5146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 1, 180301, 0, 5147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 1, 180301, 0, 5149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 1, 180301, 0, 5150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 1, 180301, 0, 5151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ありがたいな\n少し足元が\n冷えていたところだ", 1, 180301, 0, 5152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これは…！\n持ち運べるように\n改良できないかな？", 1, 180301, 0, 5154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 1, 180301, 0, 5155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 117001, 0, 5157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 117001, 0, 5158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 117001, 0, 5159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 117001, 0, 5160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 117001, 0, 5161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あのおかたの全てが\n早く私のものに\nなりますように…", 4, 117001, 0, 5162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 8, 117001, 0, 5163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 117001, 0, 5164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 1, 117001, 0, 5165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 1, 117001, 0, 5166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 1, 117001, 0, 5167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 4, 117001, 0, 5168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 8, 117001, 0, 5169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 117001, 0, 5170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 1, 117001, 0, 5171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 1, 117001, 0, 5172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 1, 117001, 0, 5174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 1, 117001, 0, 5175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 1, 117001, 0, 5176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あのおかたの\nぬくもりを\n思い出しますわ…", 1, 117001, 0, 5177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 1, 117001, 0, 5179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 1, 117001, 0, 5180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "二人乗りができると\nいいですわね…", 1, 117001, 0, 5182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 1, 117001, 0, 5183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 12, 101701, 0, 5184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 12, 101701, 0, 5185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 117101, 0, 5186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 117101, 0, 5187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 117101, 0, 5188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 117101, 0, 5189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 117101, 0, 5190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 4, 117101, 0, 5191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 8, 117101, 0, 5192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 117101, 0, 5193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 1, 117101, 0, 5194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "海で迷子に\nならないように\n手を繋ごうね♪", 1, 117101, 0, 5195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 1, 117101, 0, 5196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 4, 117101, 0, 5197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 8, 117101, 0, 5198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 117101, 0, 5199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 1, 117101, 0, 5200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 1, 117101, 0, 5201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 1, 117101, 0, 5203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 1, 117101, 0, 5204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 1, 117101, 0, 5205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 1, 117101, 0, 5206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 1, 117101, 0, 5208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 1, 117101, 0, 5209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 1, 117101, 0, 5211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 1, 117101, 0, 5212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 117201, 0, 5213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 117201, 0, 5214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 117201, 0, 5215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 117201, 0, 5216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 117201, 0, 5217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 4, 117201, 0, 5218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 8, 117201, 0, 5219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 117201, 0, 5220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 1, 117201, 0, 5221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 1, 117201, 0, 5222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 1, 117201, 0, 5223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 4, 117201, 0, 5224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 8, 117201, 0, 5225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 117201, 0, 5226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 1, 117201, 0, 5227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 1, 117201, 0, 5228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 1, 117201, 0, 5230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 1, 117201, 0, 5231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 1, 117201, 0, 5232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 1, 117201, 0, 5233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 1, 117201, 0, 5235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 1, 117201, 0, 5236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 1, 117201, 0, 5238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 1, 117201, 0, 5239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 117401, 0, 5240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 117401, 0, 5241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 117401, 0, 5242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 117401, 0, 5243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 117401, 0, 5244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 4, 117401, 0, 5245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 8, 117401, 0, 5246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 117401, 0, 5247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 1, 117401, 0, 5248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 1, 117401, 0, 5249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "仕立て屋も\nアイドルも\n諦めません！", 1, 117401, 0, 5250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 4, 117401, 0, 5251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 8, 117401, 0, 5252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 117401, 0, 5253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 1, 117401, 0, 5254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 1, 117401, 0, 5255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 1, 117401, 0, 5257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 1, 117401, 0, 5258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りですね～\n波の音が聞こえて\nきそうです…", 1, 117401, 0, 5259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "泳いだ後は\n少し寒いので\nちょうどいいですね", 1, 117401, 0, 5260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "夏バテしないように\n栄養はしっかり\n取らないと！", 1, 117401, 0, 5262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 1, 117401, 0, 5263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "デザインはもちろん\n触り心地も\n最高ですね♪", 1, 117401, 0, 5265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 1, 117401, 0, 5266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 12, 104001, 0, 5267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 12, 104001, 0, 5268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 117301, 0, 5269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 117301, 0, 5270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 117301, 0, 5271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 117301, 0, 5272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 117301, 0, 5273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 4, 117301, 0, 5274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "水着、似合ってるって\n言ってもらえるかな…", 8, 117301, 0, 5275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 117301, 0, 5276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 1, 117301, 0, 5277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なんでしょう？\nもしかして…\nボイトレに興味が？", 1, 117301, 0, 5278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 1, 117301, 0, 5279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 4, 117301, 0, 5280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 8, 117301, 0, 5281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 117301, 0, 5282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 1, 117301, 0, 5283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 1, 117301, 0, 5284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 1, 117301, 0, 5286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 1, 117301, 0, 5287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 1, 117301, 0, 5288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 1, 117301, 0, 5289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "アイドルとして、\n食べすぎには\n気をつけないと…", 1, 117301, 0, 5291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 1, 117301, 0, 5292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子に乗りながら\nライブをする…\nというのはどうですか", 1, 117301, 0, 5294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "まるで人魚になった\n気分…なんて、ふふ", 1, 117301, 0, 5295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 118301, 0, 5296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 118301, 0, 5297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 118301, 0, 5298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 118301, 0, 5299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 118301, 0, 5300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 4, 118301, 0, 5301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 8, 118301, 0, 5302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 118301, 0, 5303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 1, 118301, 0, 5304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 1, 118301, 0, 5305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 1, 118301, 0, 5306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 4, 118301, 0, 5307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 8, 118301, 0, 5308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 118301, 0, 5309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 1, 118301, 0, 5310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 1, 118301, 0, 5311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 1, 118301, 0, 5313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 1, 118301, 0, 5314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 1, 118301, 0, 5315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 1, 118301, 0, 5316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 1, 118301, 0, 5318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 1, 118301, 0, 5319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 118401, 0, 5322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 118401, 0, 5323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 118401, 0, 5324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 118401, 0, 5325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 118401, 0, 5326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 4, 118401, 0, 5327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 8, 118401, 0, 5328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 118401, 0, 5329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 1, 118401, 0, 5330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 1, 118401, 0, 5331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 1, 118401, 0, 5332);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 4, 118401, 0, 5333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 8, 118401, 0, 5334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 118401, 0, 5335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 1, 118401, 0, 5336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 1, 118401, 0, 5337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 1, 118401, 0, 5339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 1, 118401, 0, 5340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 1, 118401, 0, 5341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 1, 118401, 0, 5342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 1, 118401, 0, 5344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 1, 118401, 0, 5345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 117501, 0, 5348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 117501, 0, 5349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 117501, 0, 5350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 117501, 0, 5351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 117501, 0, 5352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 4, 117501, 0, 5353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 8, 117501, 0, 5354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 117501, 0, 5355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 1, 117501, 0, 5356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 1, 117501, 0, 5357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 1, 117501, 0, 5358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 4, 117501, 0, 5359);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 8, 117501, 0, 5360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 117501, 0, 5361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 1, 117501, 0, 5362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 1, 117501, 0, 5363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 1, 117501, 0, 5365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 1, 117501, 0, 5366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 1, 117501, 0, 5367);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 1, 117501, 0, 5368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 1, 117501, 0, 5370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 1, 117501, 0, 5371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 117601, 0, 5400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 117601, 0, 5401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 117601, 0, 5402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 117601, 0, 5403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 117601, 0, 5404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 4, 117601, 0, 5405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 8, 117601, 0, 5406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 117601, 0, 5407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 1, 117601, 0, 5408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 1, 117601, 0, 5409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 1, 117601, 0, 5410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 4, 117601, 0, 5411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 8, 117601, 0, 5412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 117601, 0, 5413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "美は食で作られる\n美容にいいものを\n食べようよ！", 1, 117601, 0, 5414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 1, 117601, 0, 5415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふう～…\nリラックスする\nボクも美しい", 1, 117601, 0, 5417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 1, 117601, 0, 5418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "美しいボクは\n常にいい香りを\nしていなくちゃね", 1, 117601, 0, 5419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 1, 117601, 0, 5420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "バランスのいい食事が\n美しいボクを\n作るのさ", 1, 117601, 0, 5422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 1, 117601, 0, 5423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 12, 105701, 0, 5425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 12, 105701, 0, 5426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 118001, 0, 5427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 118001, 0, 5428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 118001, 0, 5429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 118001, 0, 5430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 118001, 0, 5431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 4, 118001, 0, 5432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 8, 118001, 0, 5433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 118001, 0, 5434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 1, 118001, 0, 5435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 1, 118001, 0, 5436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 1, 118001, 0, 5437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 4, 118001, 0, 5438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 8, 118001, 0, 5439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 118001, 0, 5440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 1, 118001, 0, 5441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 1, 118001, 0, 5442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 1, 118001, 0, 5444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 1, 118001, 0, 5445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 1, 118001, 0, 5446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 1, 118001, 0, 5447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 1, 118001, 0, 5449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 1, 118001, 0, 5450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 117701, 0, 5452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 117701, 0, 5453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 117701, 0, 5454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 117701, 0, 5455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 117701, 0, 5456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 4, 117701, 0, 5457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 8, 117701, 0, 5458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 117701, 0, 5459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この制服、\nハロウィンらしくて\n大好きさ～♪", 1, 117701, 0, 5460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 1, 117701, 0, 5461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 1, 117701, 0, 5462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 4, 117701, 0, 5463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 8, 117701, 0, 5464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 117701, 0, 5465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 1, 117701, 0, 5466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 1, 117701, 0, 5467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 1, 117701, 0, 5469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 1, 117701, 0, 5470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 1, 117701, 0, 5471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 1, 117701, 0, 5472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 1, 117701, 0, 5474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 1, 117701, 0, 5475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 1, 117701, 0, 5477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 117801, 0, 5479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 117801, 0, 5480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 117801, 0, 5481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 117801, 0, 5482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 117801, 0, 5483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "幽霊になる前に\n天下統一を\n果たしたいデスね", 4, 117801, 0, 5484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 8, 117801, 0, 5485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 117801, 0, 5486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "妖魔を家来に\nしてみるのは\nどうデス？", 1, 117801, 0, 5487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "マントを使って\nウツセミの術の精度を\n高めるデス！", 1, 117801, 0, 5488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 1, 117801, 0, 5489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あの世でも\nショーグンに\nお仕えしマスよー！", 4, 117801, 0, 5490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 8, 117801, 0, 5491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 117801, 0, 5492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 1, 117801, 0, 5493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 1, 117801, 0, 5494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 1, 117801, 0, 5496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 1, 117801, 0, 5497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 1, 117801, 0, 5498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "パレードの熱気に\n負けないくらい\n熱いデス！", 1, 117801, 0, 5499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 1, 117801, 0, 5501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 1, 117801, 0, 5502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 1, 117801, 0, 5504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 12, 101301, 0, 5531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 12, 101301, 0, 5532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 117901, 0, 5533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 117901, 0, 5534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 117901, 0, 5535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 117901, 0, 5536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 117901, 0, 5537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 4, 117901, 0, 5538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 8, 117901, 0, 5539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 117901, 0, 5540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 1, 117901, 0, 5541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 1, 117901, 0, 5542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 1, 117901, 0, 5543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 4, 117901, 0, 5544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 8, 117901, 0, 5545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 117901, 0, 5546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 1, 117901, 0, 5547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 1, 117901, 0, 5548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 1, 117901, 0, 5550);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 1, 117901, 0, 5551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 1, 117901, 0, 5552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "やばばば…\nあったかくて\n眠くなっちゃう～", 1, 117901, 0, 5553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これ食べて\nもうひとがんばり\nしよーっと！", 1, 117901, 0, 5555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 1, 117901, 0, 5556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 1, 117901, 0, 5558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 118501, 0, 5560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 118501, 0, 5561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 118501, 0, 5562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 118501, 0, 5563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 118501, 0, 5564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 4, 118501, 0, 5565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 8, 118501, 0, 5566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 118501, 0, 5567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 1, 118501, 0, 5568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 1, 118501, 0, 5569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 1, 118501, 0, 5570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 4, 118501, 0, 5571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 8, 118501, 0, 5572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も一日\n管理人として\nがんばります♪", 1, 118501, 0, 5573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹がすきました…\nそろそろお昼に\nしませんか？", 1, 118501, 0, 5574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 1, 118501, 0, 5575);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "良質なお仕事は\n良質な休憩から\nです♪", 1, 118501, 0, 5577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 1, 118501, 0, 5578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 1, 118501, 0, 5579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 1, 118501, 0, 5580);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 1, 118501, 0, 5582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "新条例はこのように\n活用されている、と…\nふむふむ…", 1, 118501, 0, 5583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 106701, 0, 5585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 106701, 0, 5586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 106701, 0, 5587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 106701, 0, 5588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 106701, 0, 5589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 4, 106701, 0, 5590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 8, 106701, 0, 5591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 106701, 0, 5592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 1, 106701, 0, 5593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 1, 106701, 0, 5594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 1, 106701, 0, 5595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 4, 106701, 0, 5596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 8, 106701, 0, 5597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 106701, 0, 5598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 1, 106701, 0, 5599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 1, 106701, 0, 5600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 1, 106701, 0, 5602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 1, 106701, 0, 5603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 1, 106701, 0, 5604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 1, 106701, 0, 5605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この味付け…\nあっちでも再現\nできるかな？", 1, 106701, 0, 5607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 1, 106701, 0, 5608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 119001, 0, 5610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 119001, 0, 5611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 119001, 0, 5612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 119001, 0, 5613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 119001, 0, 5614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 4, 119001, 0, 5615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 8, 119001, 0, 5616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 119001, 0, 5617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 1, 119001, 0, 5618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 1, 119001, 0, 5619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 1, 119001, 0, 5620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 4, 119001, 0, 5621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "君との秘密が\nまた一つ\n増えちゃったね♪", 8, 119001, 0, 5622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 119001, 0, 5623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 1, 119001, 0, 5624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 1, 119001, 0, 5625);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 1, 119001, 0, 5627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 1, 119001, 0, 5628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 1, 119001, 0, 5629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 1, 119001, 0, 5630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "生徒の見本に\nなれるように\n好き嫌いはしないの", 1, 119001, 0, 5632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 1, 119001, 0, 5633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 12, 106301, 0, 5635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 12, 106301, 0, 5636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 12, 105301, 0, 5637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 12, 105301, 0, 5638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 119101, 0, 5639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 119101, 0, 5640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 119101, 0, 5641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 119101, 0, 5642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 119101, 0, 5643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 4, 119101, 0, 5644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 8, 119101, 0, 5645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 119101, 0, 5646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 1, 119101, 0, 5647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 1, 119101, 0, 5648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 1, 119101, 0, 5649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 4, 119101, 0, 5650);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 8, 119101, 0, 5651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 119101, 0, 5652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 1, 119101, 0, 5653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 1, 119101, 0, 5654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 1, 119101, 0, 5656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 1, 119101, 0, 5657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 1, 119101, 0, 5658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 1, 119101, 0, 5659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 1, 119101, 0, 5661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 1, 119101, 0, 5662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 119901, 0, 5664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 119901, 0, 5665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 119901, 0, 5666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 119901, 0, 5667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 119901, 0, 5668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 4, 119901, 0, 5669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 8, 119901, 0, 5670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 119901, 0, 5671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 1, 119901, 0, 5672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 1, 119901, 0, 5673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 1, 119901, 0, 5674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 4, 119901, 0, 5675);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 8, 119901, 0, 5676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 119901, 0, 5677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 1, 119901, 0, 5678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 1, 119901, 0, 5679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 1, 119901, 0, 5681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 1, 119901, 0, 5682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 1, 119901, 0, 5683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 1, 119901, 0, 5684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これはオマエがくれる\nプリンとは\n別腹なの～", 1, 119901, 0, 5686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 1, 119901, 0, 5687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 1, 119901, 0, 5689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 1, 119901, 0, 5690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 119201, 0, 5691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 119201, 0, 5692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 119201, 0, 5693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 119201, 0, 5694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 119201, 0, 5695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 4, 119201, 0, 5696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 8, 119201, 0, 5697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 119201, 0, 5698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 1, 119201, 0, 5699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 1, 119201, 0, 5700);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 1, 119201, 0, 5701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 4, 119201, 0, 5702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 8, 119201, 0, 5703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 119201, 0, 5704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 1, 119201, 0, 5705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 1, 119201, 0, 5706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 1, 119201, 0, 5708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 1, 119201, 0, 5709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 1, 119201, 0, 5710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 1, 119201, 0, 5711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 1, 119201, 0, 5713);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 1, 119201, 0, 5714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 1, 119201, 0, 5716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 1, 119201, 0, 5717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 12, 105001, 0, 5718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 12, 105001, 0, 5719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 119301, 0, 5720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 119301, 0, 5721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 119301, 0, 5722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 119301, 0, 5723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 119301, 0, 5724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 4, 119301, 0, 5725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 8, 119301, 0, 5726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 119301, 0, 5727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 1, 119301, 0, 5728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 1, 119301, 0, 5729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 1, 119301, 0, 5730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 4, 119301, 0, 5731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 8, 119301, 0, 5732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 119301, 0, 5733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 1, 119301, 0, 5734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 1, 119301, 0, 5735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 1, 119301, 0, 5737);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 1, 119301, 0, 5738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 1, 119301, 0, 5739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 1, 119301, 0, 5740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 1, 119301, 0, 5742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 1, 119301, 0, 5743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 1, 119301, 0, 5745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 1, 119301, 0, 5746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 120601, 0, 5747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 120601, 0, 5748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 120601, 0, 5749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 120601, 0, 5750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 120601, 0, 5751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 4, 120601, 0, 5752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 8, 120601, 0, 5753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 120601, 0, 5754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "きゃあっ！？\nへ、変なところを\n触らないでください！", 1, 120601, 0, 5755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 1, 120601, 0, 5756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 1, 120601, 0, 5757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 4, 120601, 0, 5758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 8, 120601, 0, 5759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 120601, 0, 5760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 1, 120601, 0, 5761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 1, 120601, 0, 5762);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 1, 120601, 0, 5764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 1, 120601, 0, 5765);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 1, 120601, 0, 5766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 1, 120601, 0, 5767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 1, 120601, 0, 5769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 1, 120601, 0, 5770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 120401, 0, 5773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 120401, 0, 5774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 120401, 0, 5775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 120401, 0, 5776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 120401, 0, 5777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 4, 120401, 0, 5778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 8, 120401, 0, 5779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 120401, 0, 5780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 1, 120401, 0, 5781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 1, 120401, 0, 5782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 1, 120401, 0, 5783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 4, 120401, 0, 5784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 8, 120401, 0, 5785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 120401, 0, 5786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 1, 120401, 0, 5787);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 1, 120401, 0, 5788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 1, 120401, 0, 5790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 1, 120401, 0, 5791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 1, 120401, 0, 5792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 1, 120401, 0, 5793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 1, 120401, 0, 5795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 1, 120401, 0, 5796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 120501, 0, 5799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 120501, 0, 5800);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 120501, 0, 5801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 120501, 0, 5802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 120501, 0, 5803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 4, 120501, 0, 5804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 8, 120501, 0, 5805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 120501, 0, 5806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 1, 120501, 0, 5807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 1, 120501, 0, 5808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 1, 120501, 0, 5809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 4, 120501, 0, 5810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 8, 120501, 0, 5811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 120501, 0, 5812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 1, 120501, 0, 5813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 1, 120501, 0, 5814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 1, 120501, 0, 5816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 1, 120501, 0, 5817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "においをうまく使った\nイタズラって…\nなにかないかな？", 1, 120501, 0, 5818);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 1, 120501, 0, 5819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 1, 120501, 0, 5821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 1, 120501, 0, 5822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 120801, 0, 5825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 120801, 0, 5826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 120801, 0, 5827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 120801, 0, 5828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 120801, 0, 5829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 4, 120801, 0, 5830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 8, 120801, 0, 5831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 120801, 0, 5832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 1, 120801, 0, 5833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 1, 120801, 0, 5834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 1, 120801, 0, 5835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 4, 120801, 0, 5836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 8, 120801, 0, 5837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 120801, 0, 5838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 1, 120801, 0, 5839);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 1, 120801, 0, 5840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 1, 120801, 0, 5842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 1, 120801, 0, 5843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これは冬の花の香り…\n風情があって\nいいじゃないか", 1, 120801, 0, 5844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 1, 120801, 0, 5845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 1, 120801, 0, 5847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 1, 120801, 0, 5848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 120701, 0, 5850);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 120701, 0, 5851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この格好も\n慣れてくれば\n動きやすいわ", 1, 120701, 0, 5852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 120701, 0, 5853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 120701, 0, 5854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 4, 120701, 0, 5855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 8, 120701, 0, 5856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 120701, 0, 5857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 1, 120701, 0, 5858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 1, 120701, 0, 5859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 1, 120701, 0, 5860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 4, 120701, 0, 5861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 8, 120701, 0, 5862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 120701, 0, 5863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 1, 120701, 0, 5864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 1, 120701, 0, 5865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 1, 120701, 0, 5867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ\nかわいいものって\n癒されるわね…", 1, 120701, 0, 5868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "…ん\nこれが梅の香り？\n…いい匂いね", 1, 120701, 0, 5869);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 1, 120701, 0, 5870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 1, 120701, 0, 5872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 1, 120701, 0, 5873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 12, 104201, 0, 5875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 12, 104201, 0, 5876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 120901, 0, 5877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 120901, 0, 5878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 120901, 0, 5879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 120901, 0, 5880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 120901, 0, 5881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 4, 120901, 0, 5882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 8, 120901, 0, 5883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 120901, 0, 5884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 1, 120901, 0, 5885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 1, 120901, 0, 5886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 1, 120901, 0, 5887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 4, 120901, 0, 5888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 8, 120901, 0, 5889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 120901, 0, 5890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 1, 120901, 0, 5891);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 1, 120901, 0, 5892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 1, 120901, 0, 5894);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 1, 120901, 0, 5895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 1, 120901, 0, 5896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 1, 120901, 0, 5897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 1, 120901, 0, 5899);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 1, 120901, 0, 5900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 121001, 0, 5902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 121001, 0, 5903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 121001, 0, 5904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 121001, 0, 5905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 121001, 0, 5906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 4, 121001, 0, 5907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 8, 121001, 0, 5908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 121001, 0, 5909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 1, 121001, 0, 5910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 1, 121001, 0, 5911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたの想いが\n全力全開を超える\n力をくれました！", 1, 121001, 0, 5912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 4, 121001, 0, 5913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 8, 121001, 0, 5914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 121001, 0, 5915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 1, 121001, 0, 5916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 1, 121001, 0, 5917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 1, 121001, 0, 5919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 1, 121001, 0, 5920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 1, 121001, 0, 5921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 1, 121001, 0, 5922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 1, 121001, 0, 5924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 1, 121001, 0, 5925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 121201, 0, 5927);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 121201, 0, 5928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 121201, 0, 5929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 121201, 0, 5930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 121201, 0, 5931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "少年が\n目を覚ましてくれて\n本当によかった…", 4, 121201, 0, 5932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 8, 121201, 0, 5933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 121201, 0, 5934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 1, 121201, 0, 5935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 1, 121201, 0, 5936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 1, 121201, 0, 5937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 4, 121201, 0, 5938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 8, 121201, 0, 5939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 121201, 0, 5940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 1, 121201, 0, 5941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 1, 121201, 0, 5942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 1, 121201, 0, 5944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 1, 121201, 0, 5945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 1, 121201, 0, 5946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 1, 121201, 0, 5947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食の大切さは\nいつの食事からも\n感じられるね♪", 1, 121201, 0, 5949);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむ…面白い！\n今度全巻\n借りにこようっと", 1, 121201, 0, 5950);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 116401, 0, 5952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 116401, 0, 5953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 116401, 0, 5954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 116401, 0, 5955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 116401, 0, 5956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 4, 116401, 0, 5957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 8, 116401, 0, 5958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 116401, 0, 5959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 1, 116401, 0, 5960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 1, 116401, 0, 5961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 1, 116401, 0, 5962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 4, 116401, 0, 5963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 8, 116401, 0, 5964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 116401, 0, 5965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 1, 116401, 0, 5966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 1, 116401, 0, 5967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 1, 116401, 0, 5969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 1, 116401, 0, 5970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 1, 116401, 0, 5971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 1, 116401, 0, 5972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 1, 116401, 0, 5974);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 1, 116401, 0, 5975);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 121101, 0, 5977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 121101, 0, 5978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 121101, 0, 5979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 121101, 0, 5980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 121101, 0, 5981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 4, 121101, 0, 5982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 8, 121101, 0, 5983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 121101, 0, 5984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 1, 121101, 0, 5985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 1, 121101, 0, 5986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 1, 121101, 0, 5987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 4, 121101, 0, 5988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 8, 121101, 0, 5989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 121101, 0, 5990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 1, 121101, 0, 5991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 1, 121101, 0, 5992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 1, 121101, 0, 5994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 1, 121101, 0, 5995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 1, 121101, 0, 5996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 1, 121101, 0, 5997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 1, 121101, 0, 5999);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 1, 121101, 0, 6000);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 118101, 0, 6002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 118101, 0, 6003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 118101, 0, 6004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "～♪", 1, 118101, 0, 6005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 118101, 0, 6006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 4, 118101, 0, 6007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 8, 118101, 0, 6008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 118101, 0, 6009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 1, 118101, 0, 6010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お喋り…？\nわたしでいいの…？\nうれしい…", 1, 118101, 0, 6011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたの好きな歌…\n教えて…？\n練習、するから…", 1, 118101, 0, 6012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 4, 118101, 0, 6013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "安心、して…\nわたしの歌で、\nあなたを守るから…", 8, 118101, 0, 6014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 118101, 0, 6015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 1, 118101, 0, 6016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 1, 118101, 0, 6017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こんな特等席…\n恐縮、です…", 1, 118101, 0, 6019);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "い、今のうちに、\nこの子でお世話の\n練習を…", 1, 118101, 0, 6020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\n…つらい気持ちも\n癒してくれる", 1, 118101, 0, 6021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\nいつまでも、\nこうしていられそう…", 1, 118101, 0, 6022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 1, 118101, 0, 6024);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 1, 118101, 0, 6025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 121301, 0, 6027);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 121301, 0, 6028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 121301, 0, 6029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 121301, 0, 6030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 121301, 0, 6031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 4, 121301, 0, 6032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 8, 121301, 0, 6033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 121301, 0, 6034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 1, 121301, 0, 6035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 1, 121301, 0, 6036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 1, 121301, 0, 6037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 4, 121301, 0, 6038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 8, 121301, 0, 6039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 121301, 0, 6040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 1, 121301, 0, 6041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 1, 121301, 0, 6042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 1, 121301, 0, 6044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "今日も演技の\n練習に付き合ってね♪", 1, 121301, 0, 6045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 1, 121301, 0, 6046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 1, 121301, 0, 6047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 1, 121301, 0, 6049);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 1, 121301, 0, 6050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 12, 107501, 0, 6052);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 12, 107501, 0, 6053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 12, 107601, 0, 6054);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 12, 107601, 0, 6055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 12, 107801, 0, 6056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 12, 107801, 0, 6057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 121401, 0, 6058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 121401, 0, 6059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 121401, 0, 6060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 121401, 0, 6061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 121401, 0, 6062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 4, 121401, 0, 6063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 8, 121401, 0, 6064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 121401, 0, 6065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 1, 121401, 0, 6066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 1, 121401, 0, 6067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 1, 121401, 0, 6068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 4, 121401, 0, 6069);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 8, 121401, 0, 6070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 121401, 0, 6071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 1, 121401, 0, 6072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 1, 121401, 0, 6073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 1, 121401, 0, 6075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 1, 121401, 0, 6076);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 1, 121401, 0, 6077);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 1, 121401, 0, 6078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 1, 121401, 0, 6080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 1, 121401, 0, 6081);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 121501, 0, 6083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 121501, 0, 6084);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 121501, 0, 6085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 121501, 0, 6086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 121501, 0, 6087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 4, 121501, 0, 6088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 8, 121501, 0, 6089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 121501, 0, 6090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 1, 121501, 0, 6091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 1, 121501, 0, 6092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 1, 121501, 0, 6093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 4, 121501, 0, 6094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 8, 121501, 0, 6095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 121501, 0, 6096);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 1, 121501, 0, 6097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 1, 121501, 0, 6098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 1, 121501, 0, 6100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 1, 121501, 0, 6101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 1, 121501, 0, 6102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 1, 121501, 0, 6103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 1, 121501, 0, 6105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 1, 121501, 0, 6106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 12, 104501, 0, 6108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 12, 104501, 0, 6109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 121601, 0, 6110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 121601, 0, 6111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 121601, 0, 6112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 121601, 0, 6113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 121601, 0, 6114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 4, 121601, 0, 6115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 8, 121601, 0, 6116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 121601, 0, 6117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 1, 121601, 0, 6118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 1, 121601, 0, 6119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "しーっ！\n今、尾行中なんです\n話はあとで…", 1, 121601, 0, 6120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 4, 121601, 0, 6121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 8, 121601, 0, 6122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 121601, 0, 6123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 1, 121601, 0, 6124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 1, 121601, 0, 6125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 1, 121601, 0, 6127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "くう…！\nこのかわいさは\n盗めないです…♪", 1, 121601, 0, 6128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 1, 121601, 0, 6129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 1, 121601, 0, 6130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 1, 121601, 0, 6132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 1, 121601, 0, 6133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 121701, 0, 6135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 121701, 0, 6136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 121701, 0, 6137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 121701, 0, 6138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 121701, 0, 6139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 4, 121701, 0, 6140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 8, 121701, 0, 6141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 121701, 0, 6142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 1, 121701, 0, 6143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 1, 121701, 0, 6144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 1, 121701, 0, 6145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 4, 121701, 0, 6146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 8, 121701, 0, 6147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さあ！\n今日も一日\n全力で参りますわ！", 1, 121701, 0, 6148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 1, 121701, 0, 6149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もう少しだけ…\n明日の準備をして\nおかないと…", 1, 121701, 0, 6150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 1, 121701, 0, 6152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 1, 121701, 0, 6153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 1, 121701, 0, 6154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 1, 121701, 0, 6155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 1, 121701, 0, 6157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 1, 121701, 0, 6158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 121801, 0, 6161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 121801, 0, 6162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 121801, 0, 6163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 121801, 0, 6164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 121801, 0, 6165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 4, 121801, 0, 6166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 8, 121801, 0, 6167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 121801, 0, 6168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 1, 121801, 0, 6169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 1, 121801, 0, 6170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 1, 121801, 0, 6171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 4, 121801, 0, 6172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 8, 121801, 0, 6173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 121801, 0, 6174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 1, 121801, 0, 6175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 1, 121801, 0, 6176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 1, 121801, 0, 6178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 1, 121801, 0, 6179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 1, 121801, 0, 6180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 1, 121801, 0, 6181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 1, 121801, 0, 6183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 1, 121801, 0, 6184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 122001, 0, 6187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 122001, 0, 6188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 122001, 0, 6189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 122001, 0, 6190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 122001, 0, 6191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 4, 122001, 0, 6192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 8, 122001, 0, 6193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 122001, 0, 6194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 1, 122001, 0, 6195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 1, 122001, 0, 6196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 1, 122001, 0, 6197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "船の上では食材が\n限られるので料理の\nいい練習になります", 4, 122001, 0, 6198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 8, 122001, 0, 6199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 122001, 0, 6200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "船の上では\n規則正しい生活が\n重要ですから", 1, 122001, 0, 6201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 1, 122001, 0, 6202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "海に揺られるような\n心地よさですね\n気が休まります…", 1, 122001, 0, 6204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 1, 122001, 0, 6205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 1, 122001, 0, 6206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "温かい…\n潮風で冷えた体に\n沁みますね", 1, 122001, 0, 6207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 1, 122001, 0, 6209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 1, 122001, 0, 6210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 12, 102601, 0, 6212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 12, 102601, 0, 6213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 121901, 0, 6214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 121901, 0, 6215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 121901, 0, 6216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 121901, 0, 6217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 121901, 0, 6218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 4, 121901, 0, 6219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 8, 121901, 0, 6220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 121901, 0, 6221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 1, 121901, 0, 6222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 1, 121901, 0, 6223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 1, 121901, 0, 6224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 4, 121901, 0, 6225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 8, 121901, 0, 6226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 121901, 0, 6227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 1, 121901, 0, 6228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 1, 121901, 0, 6229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 1, 121901, 0, 6231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 1, 121901, 0, 6232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 1, 121901, 0, 6233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 1, 121901, 0, 6234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 1, 121901, 0, 6236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 1, 121901, 0, 6237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 122101, 0, 6239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 122101, 0, 6240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 122101, 0, 6241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 122101, 0, 6242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 122101, 0, 6243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 4, 122101, 0, 6244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 8, 122101, 0, 6245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 122101, 0, 6246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 1, 122101, 0, 6247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 1, 122101, 0, 6248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 1, 122101, 0, 6249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 4, 122101, 0, 6250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 8, 122101, 0, 6251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 122101, 0, 6252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 1, 122101, 0, 6253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 1, 122101, 0, 6254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 1, 122101, 0, 6256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 1, 122101, 0, 6257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 1, 122101, 0, 6258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 1, 122101, 0, 6259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 1, 122101, 0, 6261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 1, 122101, 0, 6262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 122201, 0, 6264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 122201, 0, 6265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 122201, 0, 6266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 122201, 0, 6267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 122201, 0, 6268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 4, 122201, 0, 6269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 8, 122201, 0, 6270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 122201, 0, 6271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 1, 122201, 0, 6272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 1, 122201, 0, 6273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 1, 122201, 0, 6274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 4, 122201, 0, 6275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 8, 122201, 0, 6276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 122201, 0, 6277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 1, 122201, 0, 6278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 1, 122201, 0, 6279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 1, 122201, 0, 6281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 1, 122201, 0, 6282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 1, 122201, 0, 6283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 1, 122201, 0, 6284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 1, 122201, 0, 6286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 1, 122201, 0, 6287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 12, 100701, 0, 6289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 12, 100701, 0, 6290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 122301, 0, 6291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 122301, 0, 6292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 122301, 0, 6293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 122301, 0, 6294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 122301, 0, 6295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 4, 122301, 0, 6296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 8, 122301, 0, 6297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 122301, 0, 6298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "よしよし！\n今日のけんぞくも\nいい子だね♪", 1, 122301, 0, 6299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 1, 122301, 0, 6300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 1, 122301, 0, 6301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 4, 122301, 0, 6302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 8, 122301, 0, 6303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 122301, 0, 6304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 1, 122301, 0, 6305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 1, 122301, 0, 6306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "わー！\nふかふかだ！\nきもちぃー♪", 1, 122301, 0, 6308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 1, 122301, 0, 6309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 1, 122301, 0, 6310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 1, 122301, 0, 6311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "はむはむ…\nんー！\nおいしー♪", 1, 122301, 0, 6313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 1, 122301, 0, 6314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 122501, 0, 6316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 122501, 0, 6317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 122501, 0, 6318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 122501, 0, 6319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 122501, 0, 6320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "終わらない夏は\nないけれど…\nつい願ってしまうな", 4, 122501, 0, 6321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 8, 122501, 0, 6322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 122501, 0, 6323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 1, 122501, 0, 6324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 1, 122501, 0, 6325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 1, 122501, 0, 6326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 4, 122501, 0, 6327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 8, 122501, 0, 6328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 122501, 0, 6329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 1, 122501, 0, 6330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 1, 122501, 0, 6331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 1, 122501, 0, 6333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "肌触りのよさが\nこの格好だと\nよく分かるよ", 1, 122501, 0, 6334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 1, 122501, 0, 6335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 1, 122501, 0, 6336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "夏を楽しみきるには\nエネルギーが\n必要だからね", 1, 122501, 0, 6338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 1, 122501, 0, 6339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 1, 122501, 0, 6341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 1, 122501, 0, 6342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 122401, 0, 6343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 122401, 0, 6344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 122401, 0, 6345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 122401, 0, 6346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 122401, 0, 6347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 4, 122401, 0, 6348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 8, 122401, 0, 6349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 122401, 0, 6350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 1, 122401, 0, 6351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 1, 122401, 0, 6352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 1, 122401, 0, 6353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 4, 122401, 0, 6354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 8, 122401, 0, 6355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 122401, 0, 6356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 1, 122401, 0, 6357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 1, 122401, 0, 6358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 1, 122401, 0, 6360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 1, 122401, 0, 6361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 1, 122401, 0, 6362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 1, 122401, 0, 6363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 1, 122401, 0, 6365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 1, 122401, 0, 6366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 1, 122401, 0, 6368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 1, 122401, 0, 6369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 12, 101501, 0, 6370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 12, 101501, 0, 6371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 122601, 0, 6372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 122601, 0, 6373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 122601, 0, 6374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 122601, 0, 6375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 122601, 0, 6376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 4, 122601, 0, 6377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 8, 122601, 0, 6378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 122601, 0, 6379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 1, 122601, 0, 6380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 1, 122601, 0, 6381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 1, 122601, 0, 6382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 4, 122601, 0, 6383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 8, 122601, 0, 6384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 122601, 0, 6385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 1, 122601, 0, 6386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 1, 122601, 0, 6387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 1, 122601, 0, 6389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいい～♪\n日光浴させて\nあげたいな♪", 1, 122601, 0, 6390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 1, 122601, 0, 6391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 1, 122601, 0, 6392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 1, 122601, 0, 6394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 1, 122601, 0, 6395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 1, 122601, 0, 6397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 1, 122601, 0, 6398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 122901, 0, 6399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 122901, 0, 6400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 122901, 0, 6401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 122901, 0, 6402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 122901, 0, 6403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 4, 122901, 0, 6404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 8, 122901, 0, 6405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 122901, 0, 6406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 1, 122901, 0, 6407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 1, 122901, 0, 6408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 1, 122901, 0, 6409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 4, 122901, 0, 6410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 8, 122901, 0, 6411);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 122901, 0, 6412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 1, 122901, 0, 6413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 1, 122901, 0, 6414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 1, 122901, 0, 6416);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 1, 122901, 0, 6417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 1, 122901, 0, 6418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 1, 122901, 0, 6419);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 1, 122901, 0, 6421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 1, 122901, 0, 6422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 1, 122901, 0, 6424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 1, 122901, 0, 6425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 122801, 0, 6426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 122801, 0, 6427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 122801, 0, 6428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 122801, 0, 6429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 122801, 0, 6430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 4, 122801, 0, 6431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 8, 122801, 0, 6432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 122801, 0, 6433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 1, 122801, 0, 6434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 1, 122801, 0, 6435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 1, 122801, 0, 6436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 4, 122801, 0, 6437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 8, 122801, 0, 6438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 122801, 0, 6439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 1, 122801, 0, 6440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 1, 122801, 0, 6441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 1, 122801, 0, 6443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 1, 122801, 0, 6444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 1, 122801, 0, 6445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 1, 122801, 0, 6446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 1, 122801, 0, 6448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 1, 122801, 0, 6449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 1, 122801, 0, 6451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 1, 122801, 0, 6452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 12, 102701, 0, 6453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 12, 102701, 0, 6454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 122701, 0, 6455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 122701, 0, 6456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 122701, 0, 6457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 122701, 0, 6458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 122701, 0, 6459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 4, 122701, 0, 6460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 8, 122701, 0, 6461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 122701, 0, 6462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 1, 122701, 0, 6463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 1, 122701, 0, 6464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 1, 122701, 0, 6465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 4, 122701, 0, 6466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 8, 122701, 0, 6467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 122701, 0, 6468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 1, 122701, 0, 6469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 1, 122701, 0, 6470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 1, 122701, 0, 6472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 1, 122701, 0, 6473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 1, 122701, 0, 6474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "濡れた体を\n乾かしましょう\nタオル、タオル…", 1, 122701, 0, 6475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 1, 122701, 0, 6477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 1, 122701, 0, 6478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 1, 122701, 0, 6480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 1, 122701, 0, 6481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 123001, 0, 6482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 123001, 0, 6483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 123001, 0, 6484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 123001, 0, 6485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 123001, 0, 6486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 4, 123001, 0, 6487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 8, 123001, 0, 6488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 123001, 0, 6489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 1, 123001, 0, 6490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 1, 123001, 0, 6491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 1, 123001, 0, 6492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 4, 123001, 0, 6493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "どこまでだって\nあんたと一緒に\nいてやるんだから", 8, 123001, 0, 6494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 123001, 0, 6495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 1, 123001, 0, 6496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 1, 123001, 0, 6497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 1, 123001, 0, 6499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 1, 123001, 0, 6500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね\n塔にもこういうの\n置いておこうかしら", 1, 123001, 0, 6501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 1, 123001, 0, 6502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 1, 123001, 0, 6504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 1, 123001, 0, 6505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 123101, 0, 6507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 123101, 0, 6508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 123101, 0, 6509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 123101, 0, 6510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 123101, 0, 6511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 4, 123101, 0, 6512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 8, 123101, 0, 6513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 123101, 0, 6514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 1, 123101, 0, 6515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 1, 123101, 0, 6516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 1, 123101, 0, 6517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 4, 123101, 0, 6518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 8, 123101, 0, 6519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 123101, 0, 6520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "支度が終わったら\n今日の冒険に\n出かけまひょか", 1, 123101, 0, 6521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 1, 123101, 0, 6522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 1, 123101, 0, 6524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 1, 123101, 0, 6525);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 1, 123101, 0, 6526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 1, 123101, 0, 6527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この料理…\n冒険するときに\n作ってみまひょか", 1, 123101, 0, 6529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 1, 123101, 0, 6530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 12, 103101, 0, 6532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 12, 103101, 0, 6533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 12, 104401, 0, 6534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 12, 104401, 0, 6535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 123201, 0, 6536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 123201, 0, 6537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 123201, 0, 6538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 123201, 0, 6539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 123201, 0, 6540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 4, 123201, 0, 6541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 8, 123201, 0, 6542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 123201, 0, 6543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 1, 123201, 0, 6544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 1, 123201, 0, 6545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 1, 123201, 0, 6546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 4, 123201, 0, 6547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 8, 123201, 0, 6548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 123201, 0, 6549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 1, 123201, 0, 6550);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 1, 123201, 0, 6551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 1, 123201, 0, 6553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 1, 123201, 0, 6554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 1, 123201, 0, 6555);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 1, 123201, 0, 6556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 1, 123201, 0, 6558);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 1, 123201, 0, 6559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 123301, 0, 6561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 123301, 0, 6562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 123301, 0, 6563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 123301, 0, 6564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 123301, 0, 6565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 4, 123301, 0, 6566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 8, 123301, 0, 6567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 123301, 0, 6568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "んー？\n今はだるいからパース\nあとでねー", 1, 123301, 0, 6569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 1, 123301, 0, 6570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 1, 123301, 0, 6571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 4, 123301, 0, 6572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 8, 123301, 0, 6573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 123301, 0, 6574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 1, 123301, 0, 6575);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 1, 123301, 0, 6576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あーラクー\nいっしょーここで\nキューケーできるわー", 1, 123301, 0, 6578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 1, 123301, 0, 6579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 1, 123301, 0, 6580);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったか～♪\n溶けてスライム\n戻りそ～", 1, 123301, 0, 6581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 1, 123301, 0, 6583);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "わ～なんかムズー\n読めなー\nウケるー", 1, 123301, 0, 6584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 123501, 0, 6586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 123501, 0, 6587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 123501, 0, 6588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 123501, 0, 6589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 123501, 0, 6590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 4, 123501, 0, 6591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 8, 123501, 0, 6592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 123501, 0, 6593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 1, 123501, 0, 6594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 1, 123501, 0, 6595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 1, 123501, 0, 6596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 4, 123501, 0, 6597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 8, 123501, 0, 6598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 123501, 0, 6599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 1, 123501, 0, 6600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 1, 123501, 0, 6601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 1, 123501, 0, 6603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 1, 123501, 0, 6604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 1, 123501, 0, 6605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 1, 123501, 0, 6606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 1, 123501, 0, 6608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 1, 123501, 0, 6609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 1, 123501, 0, 6611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 123601, 0, 6613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 123601, 0, 6614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 123601, 0, 6615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 123601, 0, 6616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 123601, 0, 6617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 4, 123601, 0, 6618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 8, 123601, 0, 6619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 123601, 0, 6620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 1, 123601, 0, 6621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 1, 123601, 0, 6622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 1, 123601, 0, 6623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 4, 123601, 0, 6624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 8, 123601, 0, 6625);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 123601, 0, 6626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 1, 123601, 0, 6627);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 1, 123601, 0, 6628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 1, 123601, 0, 6630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…かわいい\nこの子もパレードに\n連れて行きたいな", 1, 123601, 0, 6631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 1, 123601, 0, 6632);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 1, 123601, 0, 6633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 1, 123601, 0, 6635);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 1, 123601, 0, 6636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 1, 123601, 0, 6638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 12, 103701, 0, 6640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 12, 103701, 0, 6641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 123701, 0, 6642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 123701, 0, 6643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 123701, 0, 6644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 123701, 0, 6645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 123701, 0, 6646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 4, 123701, 0, 6647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 8, 123701, 0, 6648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 123701, 0, 6649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 1, 123701, 0, 6650);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 1, 123701, 0, 6651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 1, 123701, 0, 6652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 4, 123701, 0, 6653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 8, 123701, 0, 6654);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 123701, 0, 6655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 1, 123701, 0, 6656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 1, 123701, 0, 6657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 1, 123701, 0, 6659);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "いや～\nかわいいものをみると\n癒されますな～", 1, 123701, 0, 6660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 1, 123701, 0, 6661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 1, 123701, 0, 6662);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 1, 123701, 0, 6664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 1, 123701, 0, 6665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 12, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 1, 123701, 0, 6667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 123801, 0, 6669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 123801, 0, 6670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 123801, 0, 6671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 123801, 0, 6672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 123801, 0, 6673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 4, 123801, 0, 6674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 8, 123801, 0, 6675);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 123801, 0, 6676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 1, 123801, 0, 6677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 1, 123801, 0, 6678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 1, 123801, 0, 6679);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 4, 123801, 0, 6680);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 8, 123801, 0, 6681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 123801, 0, 6682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 1, 123801, 0, 6683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 1, 123801, 0, 6684);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 1, 123801, 0, 6686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 1, 123801, 0, 6687);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 1, 123801, 0, 6688);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 1, 123801, 0, 6689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 1, 123801, 0, 6691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ウサギ族は地獄耳？\nワタシとの共通点だな", 1, 123801, 0, 6692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 12, 106401, 0, 6694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 12, 106401, 0, 6695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 123901, 0, 6696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 123901, 0, 6697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 123901, 0, 6698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 123901, 0, 6699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 123901, 0, 6700);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 4, 123901, 0, 6701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 8, 123901, 0, 6702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 123901, 0, 6703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 1, 123901, 0, 6704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 1, 123901, 0, 6705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 1, 123901, 0, 6706);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 4, 123901, 0, 6707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 8, 123901, 0, 6708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 123901, 0, 6709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 1, 123901, 0, 6710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 1, 123901, 0, 6711);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 1, 123901, 0, 6713);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 1, 123901, 0, 6714);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 1, 123901, 0, 6715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 1, 123901, 0, 6716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 1, 123901, 0, 6718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 1, 123901, 0, 6719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 106901, 0, 6721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 106901, 0, 6722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 106901, 0, 6723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 106901, 0, 6724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 106901, 0, 6725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 4, 106901, 0, 6726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 8, 106901, 0, 6727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 106901, 0, 6728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 1, 106901, 0, 6729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 1, 106901, 0, 6730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "視力は権能に\n影響しないわよ\n当たり前じゃない", 1, 106901, 0, 6731);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 4, 106901, 0, 6732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 8, 106901, 0, 6733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 106901, 0, 6734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 1, 106901, 0, 6735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 1, 106901, 0, 6736);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 1, 106901, 0, 6738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 1, 106901, 0, 6739);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 1, 106901, 0, 6740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 1, 106901, 0, 6741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "栄養価はともかく\n味のバランスは\n悪くないわね", 1, 106901, 0, 6743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 1, 106901, 0, 6744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 124001, 0, 6746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 124001, 0, 6747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 124001, 0, 6748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 124001, 0, 6749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 124001, 0, 6750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 4, 124001, 0, 6751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 8, 124001, 0, 6752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 124001, 0, 6753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 1, 124001, 0, 6754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 1, 124001, 0, 6755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 1, 124001, 0, 6756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 4, 124001, 0, 6757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 8, 124001, 0, 6758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 124001, 0, 6759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 1, 124001, 0, 6760);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 1, 124001, 0, 6761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 1, 124001, 0, 6763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 1, 124001, 0, 6764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 1, 124001, 0, 6765);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 1, 124001, 0, 6766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 1, 124001, 0, 6768);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 1, 124001, 0, 6769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 1, 124001, 0, 6771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 1, 124001, 0, 6772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 124101, 0, 6773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 124101, 0, 6774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 124101, 0, 6775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 124101, 0, 6776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 124101, 0, 6777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 4, 124101, 0, 6778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 8, 124101, 0, 6779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 124101, 0, 6780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 1, 124101, 0, 6781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 1, 124101, 0, 6782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 1, 124101, 0, 6783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 4, 124101, 0, 6784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 8, 124101, 0, 6785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 124101, 0, 6786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 1, 124101, 0, 6787);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 1, 124101, 0, 6788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 1, 124101, 0, 6790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 1, 124101, 0, 6791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 1, 124101, 0, 6792);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 1, 124101, 0, 6793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹いっぱい\n食べられるのも\n幸せなことよね", 1, 124101, 0, 6795);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 1, 124101, 0, 6796);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 1, 124101, 0, 6798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 1, 124101, 0, 6799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 12, 105101, 0, 6800);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 12, 105101, 0, 6801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 124201, 0, 6802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 124201, 0, 6803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 124201, 0, 6804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 124201, 0, 6805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 124201, 0, 6806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 4, 124201, 0, 6807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 8, 124201, 0, 6808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 124201, 0, 6809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 1, 124201, 0, 6810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 1, 124201, 0, 6811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 1, 124201, 0, 6812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 4, 124201, 0, 6813);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 8, 124201, 0, 6814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 124201, 0, 6815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 1, 124201, 0, 6816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 1, 124201, 0, 6817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 1, 124201, 0, 6819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 1, 124201, 0, 6820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 1, 124201, 0, 6821);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 1, 124201, 0, 6822);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 1, 124201, 0, 6824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 1, 124201, 0, 6825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "わっ！\n…………いや\n驚いてなどいない", 1, 124201, 0, 6827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 1, 124201, 0, 6828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 124301, 0, 6829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 124301, 0, 6830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 124301, 0, 6831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 124301, 0, 6832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 124301, 0, 6833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私たちも\nあの人を見習って\nバイトとかしよう！", 4, 124301, 0, 6834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつかあの人を\n私たちの学院に\n招待したいな！", 8, 124301, 0, 6835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 124301, 0, 6836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "これからなにするの？\n私たちもついて行って\nいいかな！？", 1, 124301, 0, 6837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 1, 124301, 0, 6838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 1, 124301, 0, 6839);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 4, 124301, 0, 6840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたは\nグレアと私の友達！\nこれからもずっと！", 8, 124301, 0, 6841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 124301, 0, 6842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 1, 124301, 0, 6843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 1, 124301, 0, 6844);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 1, 124301, 0, 6846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 1, 124301, 0, 6847);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 1, 124301, 0, 6848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 1, 124301, 0, 6849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 1, 124301, 0, 6851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 1, 124301, 0, 6852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 124401, 0, 6855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 124401, 0, 6856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 124401, 0, 6857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 124401, 0, 6858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 124401, 0, 6859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 4, 124401, 0, 6860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 8, 124401, 0, 6861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 124401, 0, 6862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 1, 124401, 0, 6863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 1, 124401, 0, 6864);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 1, 124401, 0, 6865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 4, 124401, 0, 6866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 8, 124401, 0, 6867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 124401, 0, 6868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 1, 124401, 0, 6869);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 1, 124401, 0, 6870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 1, 124401, 0, 6872);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "これ…\nアンに見せたら\n喜ぶだろうな…", 1, 124401, 0, 6873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り…\nアンの感想も\n聞いてみたいな", 1, 124401, 0, 6874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 1, 124401, 0, 6875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 1, 124401, 0, 6877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "アンがおススメ\nしてくれた本は\nこれかな…？", 1, 124401, 0, 6878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 124701, 0, 6881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 124701, 0, 6882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 124701, 0, 6883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 124701, 0, 6884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 124701, 0, 6885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 4, 124701, 0, 6886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 8, 124701, 0, 6887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 124701, 0, 6888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 1, 124701, 0, 6889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 1, 124701, 0, 6890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 1, 124701, 0, 6891);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 4, 124701, 0, 6892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 8, 124701, 0, 6893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 124701, 0, 6894);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 1, 124701, 0, 6895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 1, 124701, 0, 6896);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 1, 124701, 0, 6898);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 1, 124701, 0, 6899);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 1, 124701, 0, 6900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 1, 124701, 0, 6901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 1, 124701, 0, 6903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 1, 124701, 0, 6904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 124601, 0, 6906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 124601, 0, 6907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 124601, 0, 6908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 124601, 0, 6909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 124601, 0, 6910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 4, 124601, 0, 6911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 8, 124601, 0, 6912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 124601, 0, 6913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 1, 124601, 0, 6914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 1, 124601, 0, 6915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 1, 124601, 0, 6916);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 4, 124601, 0, 6917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 8, 124601, 0, 6918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 124601, 0, 6919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 1, 124601, 0, 6920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 1, 124601, 0, 6921);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 1, 124601, 0, 6923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 1, 124601, 0, 6924);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 1, 124601, 0, 6925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 1, 124601, 0, 6926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 1, 124601, 0, 6928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 1, 124601, 0, 6929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 12, 104701, 0, 6931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 12, 104701, 0, 6932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 124501, 0, 6933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 124501, 0, 6934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 124501, 0, 6935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 124501, 0, 6936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 124501, 0, 6937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 4, 124501, 0, 6938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 8, 124501, 0, 6939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 124501, 0, 6940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 1, 124501, 0, 6941);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 1, 124501, 0, 6942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 1, 124501, 0, 6943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 4, 124501, 0, 6944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 8, 124501, 0, 6945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 124501, 0, 6946);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 1, 124501, 0, 6947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 1, 124501, 0, 6948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 1, 124501, 0, 6950);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 1, 124501, 0, 6951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだけど\n着物に移らないように\n気をつけないとね", 1, 124501, 0, 6952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 1, 124501, 0, 6953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 1, 124501, 0, 6955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 1, 124501, 0, 6956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 124801, 0, 6958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 124801, 0, 6959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 124801, 0, 6960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 124801, 0, 6961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 124801, 0, 6962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 4, 124801, 0, 6963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 8, 124801, 0, 6964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 124801, 0, 6965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 1, 124801, 0, 6966);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 1, 124801, 0, 6967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 1, 124801, 0, 6968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 4, 124801, 0, 6969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 8, 124801, 0, 6970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 124801, 0, 6971);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 1, 124801, 0, 6972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 1, 124801, 0, 6973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 1, 124801, 0, 6975);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 1, 124801, 0, 6976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 1, 124801, 0, 6977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 1, 124801, 0, 6978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 1, 124801, 0, 6980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 1, 124801, 0, 6981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 124901, 0, 6983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 124901, 0, 6984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 124901, 0, 6985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 124901, 0, 6986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 124901, 0, 6987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 4, 124901, 0, 6988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 8, 124901, 0, 6989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 124901, 0, 6990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 1, 124901, 0, 6991);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 1, 124901, 0, 6992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 1, 124901, 0, 6993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 4, 124901, 0, 6994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 8, 124901, 0, 6995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 124901, 0, 6996);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 1, 124901, 0, 6997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 1, 124901, 0, 6998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 1, 124901, 0, 7000);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 1, 124901, 0, 7001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "嗅いだことない\n香りだな\nなかなか気に入った！", 1, 124901, 0, 7002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 1, 124901, 0, 7003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 1, 124901, 0, 7005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 1, 124901, 0, 7006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 12, 105501, 0, 7008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 12, 105501, 0, 7009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 125001, 0, 7010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 125001, 0, 7011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 125001, 0, 7012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 125001, 0, 7013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 125001, 0, 7014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 4, 125001, 0, 7015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 8, 125001, 0, 7016);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 125001, 0, 7017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 1, 125001, 0, 7018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 1, 125001, 0, 7019);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 1, 125001, 0, 7020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 4, 125001, 0, 7021);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 8, 125001, 0, 7022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 125001, 0, 7023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 1, 125001, 0, 7024);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 1, 125001, 0, 7025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 1, 125001, 0, 7027);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 1, 125001, 0, 7028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 1, 125001, 0, 7029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 1, 125001, 0, 7030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 1, 125001, 0, 7032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 1, 125001, 0, 7033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 118201, 0, 7035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 118201, 0, 7036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 118201, 0, 7037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 118201, 0, 7038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 118201, 0, 7039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "退屈ですねー\n騎士さん、構って\nくれないかなー", 4, 118201, 0, 7040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 8, 118201, 0, 7041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 118201, 0, 7042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お出かけします？\nそれともおうちで\nまったりしますー？", 1, 118201, 0, 7043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 1, 118201, 0, 7044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 1, 118201, 0, 7045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 4, 118201, 0, 7046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 8, 118201, 0, 7047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 118201, 0, 7048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 1, 118201, 0, 7049);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 1, 118201, 0, 7050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 1, 118201, 0, 7052);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 1, 118201, 0, 7053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん★\nうーん…\nたぶん素敵な香り♪", 1, 118201, 0, 7054);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかーい★\n心も体も\nぽっかぽかですねー★", 1, 118201, 0, 7055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 1, 118201, 0, 7057);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "へー★\nほほー★\nふんふーん★", 1, 118201, 0, 7058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 125101, 0, 7060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 125101, 0, 7061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 125101, 0, 7062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 125101, 0, 7063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 125101, 0, 7064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 4, 125101, 0, 7065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 8, 125101, 0, 7066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 125101, 0, 7067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 1, 125101, 0, 7068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 1, 125101, 0, 7069);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 1, 125101, 0, 7070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 4, 125101, 0, 7071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 8, 125101, 0, 7072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 125101, 0, 7073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 1, 125101, 0, 7074);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 1, 125101, 0, 7075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 1, 125101, 0, 7077);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 1, 125101, 0, 7078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 1, 125101, 0, 7079);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 1, 125101, 0, 7080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 1, 125101, 0, 7082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 1, 125101, 0, 7083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 12, 107101, 0, 7085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 12, 107101, 0, 7086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 125201, 0, 7087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 125201, 0, 7088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 125201, 0, 7089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 125201, 0, 7090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 125201, 0, 7091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 4, 125201, 0, 7092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 8, 125201, 0, 7093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 125201, 0, 7094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 1, 125201, 0, 7095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 1, 125201, 0, 7096);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 1, 125201, 0, 7097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 4, 125201, 0, 7098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 8, 125201, 0, 7099);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 125201, 0, 7100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 1, 125201, 0, 7101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 1, 125201, 0, 7102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 1, 125201, 0, 7104);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 1, 125201, 0, 7105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 1, 125201, 0, 7106);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 1, 125201, 0, 7107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 1, 125201, 0, 7109);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 1, 125201, 0, 7110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 120001, 0, 7112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 120001, 0, 7113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 120001, 0, 7114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 120001, 0, 7115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 120001, 0, 7116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 4, 120001, 0, 7117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 8, 120001, 0, 7118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 120001, 0, 7119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 1, 120001, 0, 7120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 1, 120001, 0, 7121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 1, 120001, 0, 7122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 4, 120001, 0, 7123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 8, 120001, 0, 7124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 120001, 0, 7125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 1, 120001, 0, 7126);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 1, 120001, 0, 7127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 1, 120001, 0, 7129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 1, 120001, 0, 7130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 1, 120001, 0, 7131);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 1, 120001, 0, 7132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 1, 120001, 0, 7134);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 1, 120001, 0, 7135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 125301, 0, 7137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 125301, 0, 7138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 125301, 0, 7139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 125301, 0, 7140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 125301, 0, 7141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 4, 125301, 0, 7142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 8, 125301, 0, 7143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 125301, 0, 7144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 1, 125301, 0, 7145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 1, 125301, 0, 7146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 1, 125301, 0, 7147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 4, 125301, 0, 7148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 8, 125301, 0, 7149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 125301, 0, 7150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 1, 125301, 0, 7151);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 1, 125301, 0, 7152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 1, 125301, 0, 7154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 1, 125301, 0, 7155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 1, 125301, 0, 7156);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 1, 125301, 0, 7157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 1, 125301, 0, 7159);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 1, 125301, 0, 7160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 12, 100901, 0, 7162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 12, 100901, 0, 7163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 125401, 0, 7164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 125401, 0, 7165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "そろそろ\nパトロールの\n時間だ…", 1, 125401, 0, 7166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 125401, 0, 7167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 125401, 0, 7168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 4, 125401, 0, 7169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "こんな私にも\nできることはある…\nそう信じています", 8, 125401, 0, 7170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 125401, 0, 7171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 1, 125401, 0, 7172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 1, 125401, 0, 7173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ニャットちゃん\nあなたにも\n懐いてきましたね", 1, 125401, 0, 7174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 4, 125401, 0, 7175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 8, 125401, 0, 7176);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 125401, 0, 7177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 1, 125401, 0, 7178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 1, 125401, 0, 7179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 1, 125401, 0, 7181);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 1, 125401, 0, 7182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "森の香りがします…\n今日はよく\n眠れそうです…", 1, 125401, 0, 7183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ニャットちゃんと\n同じぐらい\n暖かいです…", 1, 125401, 0, 7184);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "レンジャーの仕事を\nしていると\nお腹が空きます", 1, 125401, 0, 7186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "どんなに忙しくても\n読書だけは\nやめられませんね", 1, 125401, 0, 7187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 125801, 0, 7189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 125801, 0, 7190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 125801, 0, 7191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 125801, 0, 7192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 125801, 0, 7193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 4, 125801, 0, 7194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 8, 125801, 0, 7195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 125801, 0, 7196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 1, 125801, 0, 7197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 1, 125801, 0, 7198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 1, 125801, 0, 7199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 4, 125801, 0, 7200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 8, 125801, 0, 7201);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 125801, 0, 7202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 1, 125801, 0, 7203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 1, 125801, 0, 7204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 1, 125801, 0, 7206);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "と、飛びかかっては\nこない…ですよね…？", 1, 125801, 0, 7207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 1, 125801, 0, 7208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 1, 125801, 0, 7209);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "こんなに食べていたら\nはしたない…\nでしょうか？", 1, 125801, 0, 7211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 1, 125801, 0, 7212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 125501, 0, 7214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 125501, 0, 7215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 125501, 0, 7216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 125501, 0, 7217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 125501, 0, 7218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 4, 125501, 0, 7219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 8, 125501, 0, 7220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 125501, 0, 7221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 1, 125501, 0, 7222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 1, 125501, 0, 7223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 1, 125501, 0, 7224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 4, 125501, 0, 7225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 8, 125501, 0, 7226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 125501, 0, 7227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 1, 125501, 0, 7228);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 1, 125501, 0, 7229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 1, 125501, 0, 7231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 1, 125501, 0, 7232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂い…\nそれになんだか\n集中力が増したかも", 1, 125501, 0, 7233);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 1, 125501, 0, 7234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 1, 125501, 0, 7236);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 1, 125501, 0, 7237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 125701, 0, 7239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 125701, 0, 7240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 125701, 0, 7241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 125701, 0, 7242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 125701, 0, 7243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 4, 125701, 0, 7244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 8, 125701, 0, 7245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 125701, 0, 7246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 1, 125701, 0, 7247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "依頼で気になる\nところがあれば\n何でもご確認ください", 1, 125701, 0, 7248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 1, 125701, 0, 7249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 4, 125701, 0, 7250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 8, 125701, 0, 7251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 125701, 0, 7252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 1, 125701, 0, 7253);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 1, 125701, 0, 7254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 1, 125701, 0, 7256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 1, 125701, 0, 7257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 1, 125701, 0, 7258);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "この火力…\n錬金術の調合に\n使えそうですね", 1, 125701, 0, 7259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 1, 125701, 0, 7261);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 1, 125701, 0, 7262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 12, 106101, 0, 7264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 12, 106101, 0, 7265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 125601, 0, 7266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 125601, 0, 7267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 125601, 0, 7268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 125601, 0, 7269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 125601, 0, 7270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 4, 125601, 0, 7271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 8, 125601, 0, 7272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 125601, 0, 7273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 1, 125601, 0, 7274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 1, 125601, 0, 7275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 1, 125601, 0, 7276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 4, 125601, 0, 7277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 8, 125601, 0, 7278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 125601, 0, 7279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 1, 125601, 0, 7280);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 1, 125601, 0, 7281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 1, 125601, 0, 7283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 1, 125601, 0, 7284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 1, 125601, 0, 7285);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 1, 125601, 0, 7286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 1, 125601, 0, 7288);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 1, 125601, 0, 7289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 126201, 0, 7291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 126201, 0, 7292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 126201, 0, 7293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 126201, 0, 7294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 126201, 0, 7295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 4, 126201, 0, 7296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 8, 126201, 0, 7297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 126201, 0, 7298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 1, 126201, 0, 7299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 1, 126201, 0, 7300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 1, 126201, 0, 7301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 4, 126201, 0, 7302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 8, 126201, 0, 7303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 126201, 0, 7304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 1, 126201, 0, 7305);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 1, 126201, 0, 7306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 1, 126201, 0, 7308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 1, 126201, 0, 7309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 1, 126201, 0, 7310);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 1, 126201, 0, 7311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 1, 126201, 0, 7313);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 1, 126201, 0, 7314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 12, 100801, 0, 7316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 12, 100801, 0, 7317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 126301, 0, 7318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 126301, 0, 7319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 126301, 0, 7320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 126301, 0, 7321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 126301, 0, 7322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 4, 126301, 0, 7323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 8, 126301, 0, 7324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 126301, 0, 7325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 1, 126301, 0, 7326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 1, 126301, 0, 7327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 1, 126301, 0, 7328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 4, 126301, 0, 7329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 8, 126301, 0, 7330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 126301, 0, 7331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 1, 126301, 0, 7332);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 1, 126301, 0, 7333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 1, 126301, 0, 7335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 1, 126301, 0, 7336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 1, 126301, 0, 7337);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 1, 126301, 0, 7338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 1, 126301, 0, 7340);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 1, 126301, 0, 7341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 126401, 0, 7343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 126401, 0, 7344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 126401, 0, 7345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 126401, 0, 7346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 126401, 0, 7347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 4, 126401, 0, 7348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 8, 126401, 0, 7349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 126401, 0, 7350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 1, 126401, 0, 7351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 1, 126401, 0, 7352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 1, 126401, 0, 7353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 4, 126401, 0, 7354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 8, 126401, 0, 7355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 126401, 0, 7356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 1, 126401, 0, 7357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 1, 126401, 0, 7358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 1, 126401, 0, 7360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 1, 126401, 0, 7361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 1, 126401, 0, 7362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 1, 126401, 0, 7363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 1, 126401, 0, 7365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 1, 126401, 0, 7366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 126701, 0, 7368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 126701, 0, 7369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 126701, 0, 7370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 126701, 0, 7371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 126701, 0, 7372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 4, 126701, 0, 7373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 8, 126701, 0, 7374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 126701, 0, 7375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 1, 126701, 0, 7376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 1, 126701, 0, 7377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 1, 126701, 0, 7378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 4, 126701, 0, 7379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 8, 126701, 0, 7380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 126701, 0, 7381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 1, 126701, 0, 7382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 1, 126701, 0, 7383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 1, 126701, 0, 7385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 1, 126701, 0, 7386);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 1, 126701, 0, 7387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 1, 126701, 0, 7388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 1, 126701, 0, 7390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏のトレンドを\n押さえておくのも\n大事ですわね", 1, 126701, 0, 7391);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 1, 126701, 0, 7393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 1, 126701, 0, 7394);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 126601, 0, 7395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 126601, 0, 7396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 126601, 0, 7397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 126601, 0, 7398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 126601, 0, 7399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 4, 126601, 0, 7400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 8, 126601, 0, 7401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 126601, 0, 7402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 1, 126601, 0, 7403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 1, 126601, 0, 7404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 1, 126601, 0, 7405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 4, 126601, 0, 7406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 8, 126601, 0, 7407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 126601, 0, 7408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 1, 126601, 0, 7409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 1, 126601, 0, 7410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 1, 126601, 0, 7412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なかなか気に入った…\nというのは\n誰にも秘密です", 1, 126601, 0, 7413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 1, 126601, 0, 7414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 1, 126601, 0, 7415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "私がどのような味が\n好きなのかは…\n当然秘密です", 1, 126601, 0, 7417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 1, 126601, 0, 7418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 1, 126601, 0, 7420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 1, 126601, 0, 7421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 12, 105401, 0, 7422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 12, 105401, 0, 7423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 126801, 0, 7424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 126801, 0, 7425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 126801, 0, 7426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 126801, 0, 7427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 126801, 0, 7428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 4, 126801, 0, 7429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 8, 126801, 0, 7430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 126801, 0, 7431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 1, 126801, 0, 7432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 1, 126801, 0, 7433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 1, 126801, 0, 7434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 4, 126801, 0, 7435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 8, 126801, 0, 7436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 126801, 0, 7437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 1, 126801, 0, 7438);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 1, 126801, 0, 7439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 1, 126801, 0, 7441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 1, 126801, 0, 7442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 1, 126801, 0, 7443);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 1, 126801, 0, 7444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 1, 126801, 0, 7446);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "試験に使った参考書、\nたまには読み返して\nおかないとね", 1, 126801, 0, 7447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ふふ♪\nいい手触りね～", 1, 126801, 0, 7449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "…はふう～…\nゴロゴロするのって\n最高ね～", 1, 126801, 0, 7450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 126901, 0, 7451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 126901, 0, 7452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 126901, 0, 7453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 126901, 0, 7454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 126901, 0, 7455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 4, 126901, 0, 7456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 8, 126901, 0, 7457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 126901, 0, 7458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 1, 126901, 0, 7459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 1, 126901, 0, 7460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 1, 126901, 0, 7461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 4, 126901, 0, 7462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 8, 126901, 0, 7463);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 126901, 0, 7464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 1, 126901, 0, 7465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 1, 126901, 0, 7466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "素肌にふわふわが…\n気持ちいい…♪", 1, 126901, 0, 7468);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うふふ…\n一緒に海…行く？", 1, 126901, 0, 7469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 1, 126901, 0, 7470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 1, 126901, 0, 7471);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 1, 126901, 0, 7473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 1, 126901, 0, 7474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "これで合ってる…？\n遊び方わからなくて…", 1, 126901, 0, 7476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふわふわ…♪\n海の底にいるみたい…", 1, 126901, 0, 7477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 127001, 0, 7478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 127001, 0, 7479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 127001, 0, 7480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 127001, 0, 7481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 127001, 0, 7482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 4, 127001, 0, 7483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 8, 127001, 0, 7484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 127001, 0, 7485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 1, 127001, 0, 7486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 1, 127001, 0, 7487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 1, 127001, 0, 7488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 4, 127001, 0, 7489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 8, 127001, 0, 7490);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 127001, 0, 7491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 1, 127001, 0, 7492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 1, 127001, 0, 7493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 1, 127001, 0, 7495);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 1, 127001, 0, 7496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 1, 127001, 0, 7497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 1, 127001, 0, 7498);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "クウカの口の中に\nたくさん…♪\n止まりません…！", 1, 127001, 0, 7500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 1, 127001, 0, 7501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 1, 127001, 0, 7503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "これは…\n妄想に役立ちそうな\n感触です…！", 1, 127001, 0, 7504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 12, 100501, 0, 7505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 12, 100501, 0, 7506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 127101, 0, 7507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 127101, 0, 7508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 127101, 0, 7509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 127101, 0, 7510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 127101, 0, 7511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 4, 127101, 0, 7512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 8, 127101, 0, 7513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 127101, 0, 7514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 1, 127101, 0, 7515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 1, 127101, 0, 7516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 1, 127101, 0, 7517);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 4, 127101, 0, 7518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 8, 127101, 0, 7519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 127101, 0, 7520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 1, 127101, 0, 7521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 1, 127101, 0, 7522);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 1, 127101, 0, 7524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 1, 127101, 0, 7525);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 1, 127101, 0, 7526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 1, 127101, 0, 7527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 1, 127101, 0, 7529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 1, 127101, 0, 7530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 1, 127101, 0, 7532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 1, 127101, 0, 7533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 126501, 0, 7534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 126501, 0, 7535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 126501, 0, 7536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 126501, 0, 7537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 126501, 0, 7538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 4, 126501, 0, 7539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 8, 126501, 0, 7540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 126501, 0, 7541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 1, 126501, 0, 7542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 1, 126501, 0, 7543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 1, 126501, 0, 7544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 4, 126501, 0, 7545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 8, 126501, 0, 7546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 126501, 0, 7547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 1, 126501, 0, 7548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 1, 126501, 0, 7549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 1, 126501, 0, 7551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 1, 126501, 0, 7552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 1, 126501, 0, 7553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 1, 126501, 0, 7554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 1, 126501, 0, 7556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 1, 126501, 0, 7557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 127201, 0, 7559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 127201, 0, 7560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 127201, 0, 7561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 127201, 0, 7562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 127201, 0, 7563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 4, 127201, 0, 7564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 8, 127201, 0, 7565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 127201, 0, 7566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 1, 127201, 0, 7567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 1, 127201, 0, 7568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 1, 127201, 0, 7569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 4, 127201, 0, 7570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 8, 127201, 0, 7571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 127201, 0, 7572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 1, 127201, 0, 7573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 1, 127201, 0, 7574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 1, 127201, 0, 7576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 1, 127201, 0, 7577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 1, 127201, 0, 7578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 1, 127201, 0, 7579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 1, 127201, 0, 7581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 1, 127201, 0, 7582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 12, 104301, 0, 7584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 12, 104301, 0, 7585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 127301, 0, 7586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 127301, 0, 7587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 127301, 0, 7588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 127301, 0, 7589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 127301, 0, 7590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 4, 127301, 0, 7591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 8, 127301, 0, 7592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 127301, 0, 7593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 1, 127301, 0, 7594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 1, 127301, 0, 7595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 1, 127301, 0, 7596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 4, 127301, 0, 7597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 8, 127301, 0, 7598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 127301, 0, 7599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 1, 127301, 0, 7600);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 1, 127301, 0, 7601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "充電ちう～\n…ガッコーのイスも\nこれにして～", 1, 127301, 0, 7603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 1, 127301, 0, 7604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "リフレッシュの\n魔法発見！\nいい香り～♪", 1, 127301, 0, 7605);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 1, 127301, 0, 7606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "超おいし～♪\n学食にあったら\n毎日リピする～", 1, 127301, 0, 7608);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nねむみパない…\n続きはまた今度～", 1, 127301, 0, 7609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 127501, 0, 7611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 127501, 0, 7612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 127501, 0, 7613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 127501, 0, 7614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 127501, 0, 7615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 4, 127501, 0, 7616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 8, 127501, 0, 7617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 127501, 0, 7618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 1, 127501, 0, 7619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 1, 127501, 0, 7620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 1, 127501, 0, 7621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 4, 127501, 0, 7622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 8, 127501, 0, 7623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 127501, 0, 7624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 1, 127501, 0, 7625);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 1, 127501, 0, 7626);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 1, 127501, 0, 7628);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 1, 127501, 0, 7629);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 1, 127501, 0, 7630);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 1, 127501, 0, 7631);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 1, 127501, 0, 7633);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 1, 127501, 0, 7634);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 12, 101401, 0, 7636);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 12, 101401, 0, 7637);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 127601, 0, 7638);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 127601, 0, 7639);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 127601, 0, 7640);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 127601, 0, 7641);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 127601, 0, 7642);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 4, 127601, 0, 7643);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 8, 127601, 0, 7644);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 127601, 0, 7645);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 1, 127601, 0, 7646);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 1, 127601, 0, 7647);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 1, 127601, 0, 7648);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 4, 127601, 0, 7649);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 8, 127601, 0, 7650);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 127601, 0, 7651);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 1, 127601, 0, 7652);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 1, 127601, 0, 7653);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 1, 127601, 0, 7655);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 1, 127601, 0, 7656);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "この香り…\nサラサリア土産の\n香木かい？", 1, 127601, 0, 7657);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 1, 127601, 0, 7658);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 1, 127601, 0, 7660);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 1, 127601, 0, 7661);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 126001, 0, 7663);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 126001, 0, 7664);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 126001, 0, 7665);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 126001, 0, 7666);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 126001, 0, 7667);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 4, 126001, 0, 7668);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 8, 126001, 0, 7669);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 126001, 0, 7670);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 1, 126001, 0, 7671);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 1, 126001, 0, 7672);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 1, 126001, 0, 7673);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 4, 126001, 0, 7674);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 8, 126001, 0, 7675);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 126001, 0, 7676);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 1, 126001, 0, 7677);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 1, 126001, 0, 7678);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 1, 126001, 0, 7680);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 1, 126001, 0, 7681);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 1, 126001, 0, 7682);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 1, 126001, 0, 7683);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 1, 126001, 0, 7685);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 1, 126001, 0, 7686);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 12, 103801, 0, 7688);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 12, 103801, 0, 7689);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 126101, 0, 7690);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "きょろきょろ\nここも\nぱとろる、するよ", 1, 126101, 0, 7691);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "おうち\nにぎやか\nさみしく、ない", 1, 126101, 0, 7692);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 126101, 0, 7693);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 126101, 0, 7694);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 4, 126101, 0, 7695);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 8, 126101, 0, 7696);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 126101, 0, 7697);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "くろぷれしあ\nかっこいい？\nん…あざます", 1, 126101, 0, 7698);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 1, 126101, 0, 7699);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 1, 126101, 0, 7700);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "かみ、りぼん\nおてつだい\nよろ\nです", 4, 126101, 0, 7701);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ここ\nすむ\nね？\nまいにち、いっしょ", 8, 126101, 0, 7702);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ぐっもにん\nあさごはん\nあさごはん", 1, 126101, 0, 7703);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お兄\nおつかれ？\nれっつ、まいぺーす", 1, 126101, 0, 7704);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 1, 126101, 0, 7705);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふかふか\nパパの、おなか\nみたい", 1, 126101, 0, 7707);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 1, 126101, 0, 7708);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい、におい\nでも、お兄\nもっと、いい、におい", 1, 126101, 0, 7709);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "とりま\nお肉、やこ", 1, 126101, 0, 7710);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nむげん肉まん、も\nきぼう", 1, 126101, 0, 7712);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 1, 126101, 0, 7713);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 127901, 0, 7715);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 127901, 0, 7716);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 127901, 0, 7717);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 127901, 0, 7718);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 127901, 0, 7719);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 4, 127901, 0, 7720);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 8, 127901, 0, 7721);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 127901, 0, 7722);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 1, 127901, 0, 7723);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 1, 127901, 0, 7724);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 1, 127901, 0, 7725);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 4, 127901, 0, 7726);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 8, 127901, 0, 7727);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 127901, 0, 7728);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 1, 127901, 0, 7729);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 1, 127901, 0, 7730);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 1, 127901, 0, 7732);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 1, 127901, 0, 7733);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 1, 127901, 0, 7734);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 1, 127901, 0, 7735);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 1, 127901, 0, 7737);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 1, 127901, 0, 7738);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 1, 127901, 0, 7740);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 1, 127901, 0, 7741);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 12, 106601, 0, 7742);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 12, 106601, 0, 7743);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 128001, 0, 7744);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 128001, 0, 7745);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 128001, 0, 7746);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 128001, 0, 7747);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 128001, 0, 7748);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 4, 128001, 0, 7749);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 8, 128001, 0, 7750);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 128001, 0, 7751);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 1, 128001, 0, 7752);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 1, 128001, 0, 7753);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 1, 128001, 0, 7754);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 4, 128001, 0, 7755);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 8, 128001, 0, 7756);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 128001, 0, 7757);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 1, 128001, 0, 7758);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 1, 128001, 0, 7759);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 1, 128001, 0, 7761);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 1, 128001, 0, 7762);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 1, 128001, 0, 7763);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 1, 128001, 0, 7764);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 1, 128001, 0, 7766);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 1, 128001, 0, 7767);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 14, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 1, 128001, 0, 7769);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 15, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 1, 128001, 0, 7770);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 128101, 0, 7771);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 128101, 0, 7772);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 128101, 0, 7773);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 128101, 0, 7774);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 128101, 0, 7775);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 4, 128101, 0, 7776);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 8, 128101, 0, 7777);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 128101, 0, 7778);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 1, 128101, 0, 7779);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 1, 128101, 0, 7780);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 1, 128101, 0, 7781);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 4, 128101, 0, 7782);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 8, 128101, 0, 7783);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 128101, 0, 7784);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 1, 128101, 0, 7785);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 1, 128101, 0, 7786);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 1, 128101, 0, 7788);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 1, 128101, 0, 7789);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 1, 128101, 0, 7790);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 1, 128101, 0, 7791);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 1, 128101, 0, 7793);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 1, 128101, 0, 7794);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 128201, 0, 7797);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 128201, 0, 7798);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 128201, 0, 7799);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 128201, 0, 7800);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 128201, 0, 7801);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 4, 128201, 0, 7802);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 8, 128201, 0, 7803);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 128201, 0, 7804);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 1, 128201, 0, 7805);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 1, 128201, 0, 7806);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 1, 128201, 0, 7807);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 4, 128201, 0, 7808);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 8, 128201, 0, 7809);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 128201, 0, 7810);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 1, 128201, 0, 7811);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 1, 128201, 0, 7812);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 1, 128201, 0, 7814);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 1, 128201, 0, 7815);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 1, 128201, 0, 7816);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 1, 128201, 0, 7817);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 1, 128201, 0, 7819);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 1, 128201, 0, 7820);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 128301, 0, 7823);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 128301, 0, 7824);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 128301, 0, 7825);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 128301, 0, 7826);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 128301, 0, 7827);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 4, 128301, 0, 7828);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 8, 128301, 0, 7829);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 128301, 0, 7830);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 1, 128301, 0, 7831);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 1, 128301, 0, 7832);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 1, 128301, 0, 7833);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 4, 128301, 0, 7834);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 8, 128301, 0, 7835);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 128301, 0, 7836);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 1, 128301, 0, 7837);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 1, 128301, 0, 7838);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 1, 128301, 0, 7840);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 1, 128301, 0, 7841);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 1, 128301, 0, 7842);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 1, 128301, 0, 7843);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 1, 128301, 0, 7845);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 1, 128301, 0, 7846);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 128401, 0, 7848);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 128401, 0, 7849);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 128401, 0, 7850);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 128401, 0, 7851);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 128401, 0, 7852);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 4, 128401, 0, 7853);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "来年も彼の隣で\n初日の出が\n見られるといいな～", 8, 128401, 0, 7854);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 128401, 0, 7855);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 1, 128401, 0, 7856);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 1, 128401, 0, 7857);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 1, 128401, 0, 7858);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 4, 128401, 0, 7859);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 8, 128401, 0, 7860);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 128401, 0, 7861);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 1, 128401, 0, 7862);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 1, 128401, 0, 7863);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 1, 128401, 0, 7865);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 1, 128401, 0, 7866);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 1, 128401, 0, 7867);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 1, 128401, 0, 7868);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 1, 128401, 0, 7870);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 1, 128401, 0, 7871);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 12, 106501, 0, 7873);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 12, 106501, 0, 7874);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 128501, 0, 7875);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 128501, 0, 7876);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 128501, 0, 7877);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 128501, 0, 7878);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 128501, 0, 7879);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 4, 128501, 0, 7880);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 8, 128501, 0, 7881);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 128501, 0, 7882);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 1, 128501, 0, 7883);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 1, 128501, 0, 7884);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 1, 128501, 0, 7885);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 4, 128501, 0, 7886);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 8, 128501, 0, 7887);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 128501, 0, 7888);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 1, 128501, 0, 7889);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 1, 128501, 0, 7890);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 1, 128501, 0, 7892);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 1, 128501, 0, 7893);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 1, 128501, 0, 7894);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 1, 128501, 0, 7895);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 1, 128501, 0, 7897);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 1, 128501, 0, 7898);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 128801, 0, 7900);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 128801, 0, 7901);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 128801, 0, 7902);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 128801, 0, 7903);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 128801, 0, 7904);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 4, 128801, 0, 7905);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 8, 128801, 0, 7906);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 128801, 0, 7907);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 1, 128801, 0, 7908);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 1, 128801, 0, 7909);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 1, 128801, 0, 7910);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 4, 128801, 0, 7911);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 8, 128801, 0, 7912);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 128801, 0, 7913);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 1, 128801, 0, 7914);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 1, 128801, 0, 7915);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休める時は休む…\n戦い続けるには\n必要なことだからね", 1, 128801, 0, 7917);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 1, 128801, 0, 7918);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 1, 128801, 0, 7919);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 1, 128801, 0, 7920);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "この味付け…\n今度試してみようか\n味見は任せたからね", 1, 128801, 0, 7922);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 1, 128801, 0, 7923);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 128901, 0, 7925);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 128901, 0, 7926);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 128901, 0, 7927);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 128901, 0, 7928);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 128901, 0, 7929);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 4, 128901, 0, 7930);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 8, 128901, 0, 7931);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 128901, 0, 7932);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 1, 128901, 0, 7933);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 1, 128901, 0, 7934);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 1, 128901, 0, 7935);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 4, 128901, 0, 7936);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 8, 128901, 0, 7937);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 128901, 0, 7938);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 1, 128901, 0, 7939);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 1, 128901, 0, 7940);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 1, 128901, 0, 7942);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 1, 128901, 0, 7943);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 1, 128901, 0, 7944);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 1, 128901, 0, 7945);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 1, 128901, 0, 7947);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 1, 128901, 0, 7948);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 128701, 0, 7950);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 128701, 0, 7951);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 128701, 0, 7952);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 128701, 0, 7953);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 128701, 0, 7954);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 4, 128701, 0, 7955);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 8, 128701, 0, 7956);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 128701, 0, 7957);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 1, 128701, 0, 7958);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 1, 128701, 0, 7959);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 1, 128701, 0, 7960);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 4, 128701, 0, 7961);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 8, 128701, 0, 7962);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 128701, 0, 7963);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 1, 128701, 0, 7964);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 1, 128701, 0, 7965);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 1, 128701, 0, 7967);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 1, 128701, 0, 7968);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 1, 128701, 0, 7969);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 1, 128701, 0, 7970);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 1, 128701, 0, 7972);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 1, 128701, 0, 7973);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 129001, 0, 7975);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 129001, 0, 7976);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 129001, 0, 7977);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 129001, 0, 7978);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 129001, 0, 7979);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 4, 129001, 0, 7980);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 8, 129001, 0, 7981);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 129001, 0, 7982);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 129001, 0, 7983);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 1, 129001, 0, 7984);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 1, 129001, 0, 7985);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 4, 129001, 0, 7986);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 8, 129001, 0, 7987);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 129001, 0, 7988);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 129001, 0, 7989);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 129001, 0, 7990);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 1, 129001, 0, 7992);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 1, 129001, 0, 7993);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 1, 129001, 0, 7994);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 1, 129001, 0, 7995);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 1, 129001, 0, 7997);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 1, 129001, 0, 7998);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 129301, 0, 8000);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 129301, 0, 8001);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 129301, 0, 8002);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 129301, 0, 8003);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 129301, 0, 8004);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 4, 129301, 0, 8005);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 8, 129301, 0, 8006);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 129301, 0, 8007);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 129301, 0, 8008);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 1, 129301, 0, 8009);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 1, 129301, 0, 8010);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 4, 129301, 0, 8011);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 8, 129301, 0, 8012);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 129301, 0, 8013);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 1, 129301, 0, 8014);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 129301, 0, 8015);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 129301, 0, 8017);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 1, 129301, 0, 8018);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 1, 129301, 0, 8019);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 129301, 0, 8020);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 129301, 0, 8022);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 1, 129301, 0, 8023);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 129301, 0, 8025);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 1, 129301, 0, 8026);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 12, 108901, 0, 8027);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 12, 108901, 0, 8028);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 12, 108801, 0, 8029);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 12, 108801, 0, 8030);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 12, 108701, 0, 8031);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 12, 108701, 0, 8032);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 129401, 0, 8033);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 129401, 0, 8034);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 129401, 0, 8035);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 129401, 0, 8036);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 129401, 0, 8037);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 4, 129401, 0, 8038);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 8, 129401, 0, 8039);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 129401, 0, 8040);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 129401, 0, 8041);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 129401, 0, 8042);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 129401, 0, 8043);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 4, 129401, 0, 8044);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 8, 129401, 0, 8045);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 129401, 0, 8046);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 129401, 0, 8047);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 129401, 0, 8048);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 129401, 0, 8050);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 129401, 0, 8051);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 129401, 0, 8052);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 129401, 0, 8053);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 129401, 0, 8055);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 129401, 0, 8056);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 129401, 0, 8058);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 129401, 0, 8059);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 129801, 0, 8060);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 129801, 0, 8061);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 129801, 0, 8062);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 129801, 0, 8063);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 129801, 0, 8064);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 4, 129801, 0, 8065);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 8, 129801, 0, 8066);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 129801, 0, 8067);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 129801, 0, 8068);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 129801, 0, 8069);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 129801, 0, 8070);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 4, 129801, 0, 8071);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 8, 129801, 0, 8072);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 129801, 0, 8073);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 129801, 0, 8074);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 129801, 0, 8075);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 129801, 0, 8077);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 129801, 0, 8078);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 129801, 0, 8079);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 129801, 0, 8080);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 129801, 0, 8082);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 129801, 0, 8083);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 129901, 0, 8085);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 129901, 0, 8086);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 129901, 0, 8087);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 129901, 0, 8088);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 129901, 0, 8089);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 4, 129901, 0, 8090);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 8, 129901, 0, 8091);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 129901, 0, 8092);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 129901, 0, 8093);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 129901, 0, 8094);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 129901, 0, 8095);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 4, 129901, 0, 8096);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 8, 129901, 0, 8097);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 129901, 0, 8098);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 129901, 0, 8099);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 129901, 0, 8100);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 129901, 0, 8102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 129901, 0, 8103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 129901, 0, 8104);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 129901, 0, 8105);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 129901, 0, 8107);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 129901, 0, 8108);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "……（そわそわ）", 1, 129601, 0, 8110);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 129601, 0, 8111);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 129601, 0, 8112);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 129601, 0, 8113);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 129601, 0, 8114);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 4, 129601, 0, 8115);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この家なら安全─\nかもしれません\n魔物が出ないので", 8, 129601, 0, 8116);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 129601, 0, 8117);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "長話─苦手です\n疲れるので…\nふぅ…", 1, 129601, 0, 8118);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 1, 129601, 0, 8119);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "世話をするのは\n得意です\n…花の、ですよ？", 1, 129601, 0, 8120);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 4, 129601, 0, 8121);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 8, 129601, 0, 8122);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "花に、お水─？\n…いりません", 1, 129601, 0, 8123);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 1, 129601, 0, 8124);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜番。私がします\nあまり眠らない\n性質なので…", 1, 129601, 0, 8125);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここなら根を張れる\nかもしれません\n…張りませんけど", 1, 129601, 0, 8127);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 1, 129601, 0, 8128);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "花みたいに─\nいい匂い、です", 1, 129601, 0, 8129);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 1, 129601, 0, 8130);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "食事も立派な戦支度\n媛が言ってました", 1, 129601, 0, 8132);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 1, 129601, 0, 8133);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 130701, 0, 8135);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 130701, 0, 8136);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 130701, 0, 8137);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 130701, 0, 8138);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 130701, 0, 8139);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 4, 130701, 0, 8140);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 8, 130701, 0, 8141);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 130701, 0, 8142);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 1, 130701, 0, 8143);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 1, 130701, 0, 8144);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 1, 130701, 0, 8145);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 4, 130701, 0, 8146);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 8, 130701, 0, 8147);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 130701, 0, 8148);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 1, 130701, 0, 8149);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 1, 130701, 0, 8150);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 1, 130701, 0, 8152);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 1, 130701, 0, 8153);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 1, 130701, 0, 8154);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 1, 130701, 0, 8155);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 1, 130701, 0, 8157);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 1, 130701, 0, 8158);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 130801, 0, 8160);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 130801, 0, 8161);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 130801, 0, 8162);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 130801, 0, 8163);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 130801, 0, 8164);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 4, 130801, 0, 8165);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 8, 130801, 0, 8166);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 130801, 0, 8167);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 1, 130801, 0, 8168);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 1, 130801, 0, 8169);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 1, 130801, 0, 8170);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 4, 130801, 0, 8171);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 8, 130801, 0, 8172);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 130801, 0, 8173);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 1, 130801, 0, 8174);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 1, 130801, 0, 8175);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 1, 130801, 0, 8177);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 1, 130801, 0, 8178);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 1, 130801, 0, 8179);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 1, 130801, 0, 8180);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 1, 130801, 0, 8182);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 1, 130801, 0, 8183);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 131001, 0, 8185);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 131001, 0, 8186);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 131001, 0, 8187);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 131001, 0, 8188);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 131001, 0, 8189);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 4, 131001, 0, 8190);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 8, 131001, 0, 8191);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 131001, 0, 8192);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 1, 131001, 0, 8193);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 1, 131001, 0, 8194);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 1, 131001, 0, 8195);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 4, 131001, 0, 8196);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 8, 131001, 0, 8197);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 131001, 0, 8198);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 1, 131001, 0, 8199);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 1, 131001, 0, 8200);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 1, 131001, 0, 8202);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 1, 131001, 0, 8203);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 1, 131001, 0, 8204);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 1, 131001, 0, 8205);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 1, 131001, 0, 8207);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 1, 131001, 0, 8208);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 12, 105601, 0, 8210);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 12, 105601, 0, 8211);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 130901, 0, 8212);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 130901, 0, 8213);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 130901, 0, 8214);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 130901, 0, 8215);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 130901, 0, 8216);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 4, 130901, 0, 8217);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 8, 130901, 0, 8218);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 130901, 0, 8219);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 1, 130901, 0, 8220);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 1, 130901, 0, 8221);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 1, 130901, 0, 8222);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 4, 130901, 0, 8223);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 8, 130901, 0, 8224);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 130901, 0, 8225);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 1, 130901, 0, 8226);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 1, 130901, 0, 8227);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 1, 130901, 0, 8229);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 1, 130901, 0, 8230);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 1, 130901, 0, 8231);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 1, 130901, 0, 8232);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 1, 130901, 0, 8234);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "パークの\nガイドブックとか\nあったらいいですね♪", 1, 130901, 0, 8235);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 131101, 0, 8237);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 131101, 0, 8238);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 131101, 0, 8239);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 131101, 0, 8240);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 131101, 0, 8241);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 4, 131101, 0, 8242);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 8, 131101, 0, 8243);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 131101, 0, 8244);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 1, 131101, 0, 8245);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 1, 131101, 0, 8246);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 1, 131101, 0, 8247);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 4, 131101, 0, 8248);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 8, 131101, 0, 8249);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 131101, 0, 8250);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 1, 131101, 0, 8251);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 1, 131101, 0, 8252);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 1, 131101, 0, 8254);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 1, 131101, 0, 8255);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 1, 131101, 0, 8256);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 1, 131101, 0, 8257);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 1, 131101, 0, 8259);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 1, 131101, 0, 8260);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 1, 131101, 0, 8262);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "あーやべ\n一瞬で寝そー\nヤバだわ～", 1, 131101, 0, 8263);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 129701, 0, 8264);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 129701, 0, 8265);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 129701, 0, 8266);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 129701, 0, 8267);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 129701, 0, 8268);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 4, 129701, 0, 8269);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 8, 129701, 0, 8270);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 129701, 0, 8271);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 1, 129701, 0, 8272);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 1, 129701, 0, 8273);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 1, 129701, 0, 8274);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 4, 129701, 0, 8275);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 8, 129701, 0, 8276);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 129701, 0, 8277);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 1, 129701, 0, 8278);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 1, 129701, 0, 8279);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 1, 129701, 0, 8281);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 1, 129701, 0, 8282);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 1, 129701, 0, 8283);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 1, 129701, 0, 8284);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 1, 129701, 0, 8286);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 1, 129701, 0, 8287);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 131201, 0, 8289);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 131201, 0, 8290);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 131201, 0, 8291);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 131201, 0, 8292);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 131201, 0, 8293);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 4, 131201, 0, 8294);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 8, 131201, 0, 8295);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 131201, 0, 8296);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 1, 131201, 0, 8297);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 1, 131201, 0, 8298);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 1, 131201, 0, 8299);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 4, 131201, 0, 8300);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 8, 131201, 0, 8301);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 131201, 0, 8302);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 1, 131201, 0, 8303);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 1, 131201, 0, 8304);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 1, 131201, 0, 8306);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 1, 131201, 0, 8307);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 1, 131201, 0, 8308);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 1, 131201, 0, 8309);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 1, 131201, 0, 8311);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 1, 131201, 0, 8312);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 1, 131201, 0, 8314);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 1, 131201, 0, 8315);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 131601, 0, 8316);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 131601, 0, 8317);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 131601, 0, 8318);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 131601, 0, 8319);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 131601, 0, 8320);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 4, 131601, 0, 8321);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 8, 131601, 0, 8322);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 131601, 0, 8323);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 1, 131601, 0, 8324);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 1, 131601, 0, 8325);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 1, 131601, 0, 8326);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 4, 131601, 0, 8327);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 8, 131601, 0, 8328);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 131601, 0, 8329);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 1, 131601, 0, 8330);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 1, 131601, 0, 8331);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 1, 131601, 0, 8333);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 1, 131601, 0, 8334);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 1, 131601, 0, 8335);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 1, 131601, 0, 8336);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 1, 131601, 0, 8338);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 1, 131601, 0, 8339);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 1, 131601, 0, 8341);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 1, 131601, 0, 8342);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 131701, 0, 8343);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 131701, 0, 8344);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 131701, 0, 8345);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 131701, 0, 8346);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 131701, 0, 8347);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 4, 131701, 0, 8348);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 8, 131701, 0, 8349);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 131701, 0, 8350);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 1, 131701, 0, 8351);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 1, 131701, 0, 8352);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 1, 131701, 0, 8353);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 4, 131701, 0, 8354);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 8, 131701, 0, 8355);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 131701, 0, 8356);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 1, 131701, 0, 8357);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 1, 131701, 0, 8358);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 1, 131701, 0, 8360);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 1, 131701, 0, 8361);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 1, 131701, 0, 8362);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 1, 131701, 0, 8363);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 1, 131701, 0, 8365);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "文字を読むのと\nデータを読むのは\nまったく違うわ", 1, 131701, 0, 8366);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 1, 131701, 0, 8368);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 1, 131701, 0, 8369);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 118601, 0, 8370);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 118601, 0, 8371);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 118601, 0, 8372);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 118601, 0, 8373);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 118601, 0, 8374);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 4, 118601, 0, 8375);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 8, 118601, 0, 8376);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 118601, 0, 8377);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 1, 118601, 0, 8378);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 1, 118601, 0, 8379);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 1, 118601, 0, 8380);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 4, 118601, 0, 8381);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 8, 118601, 0, 8382);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 118601, 0, 8383);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 1, 118601, 0, 8384);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 1, 118601, 0, 8385);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 1, 118601, 0, 8387);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 1, 118601, 0, 8388);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 1, 118601, 0, 8389);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "なんていうか…\n心があたたかいわ\n…変な感じ！", 1, 118601, 0, 8390);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 1, 118601, 0, 8392);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 1, 118601, 0, 8393);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 131801, 0, 8395);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 131801, 0, 8396);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 131801, 0, 8397);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 131801, 0, 8398);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 131801, 0, 8399);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 4, 131801, 0, 8400);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 8, 131801, 0, 8401);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 131801, 0, 8402);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 1, 131801, 0, 8403);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "熱中症には\n気を付けないと\nだめだよ～？", 1, 131801, 0, 8404);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 1, 131801, 0, 8405);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 4, 131801, 0, 8406);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 8, 131801, 0, 8407);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 131801, 0, 8408);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ふふ♪\n波音が心地良い\n時間だね～☆", 1, 131801, 0, 8409);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 1, 131801, 0, 8410);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 1, 131801, 0, 8412);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 1, 131801, 0, 8413);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 1, 131801, 0, 8414);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 1, 131801, 0, 8415);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 1, 131801, 0, 8417);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 1, 131801, 0, 8418);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 1, 131801, 0, 8420);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 1, 131801, 0, 8421);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 181201, 0, 8422);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 181201, 0, 8423);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 181201, 0, 8424);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 181201, 0, 8425);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 181201, 0, 8426);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 4, 181201, 0, 8427);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 8, 181201, 0, 8428);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 181201, 0, 8429);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 1, 181201, 0, 8430);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 1, 181201, 0, 8431);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 1, 181201, 0, 8432);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 4, 181201, 0, 8433);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 8, 181201, 0, 8434);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 181201, 0, 8435);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 1, 181201, 0, 8436);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 1, 181201, 0, 8437);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 1, 181201, 0, 8439);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 1, 181201, 0, 8440);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 1, 181201, 0, 8441);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 1, 181201, 0, 8442);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 1, 181201, 0, 8444);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 1, 181201, 0, 8445);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 131901, 0, 8447);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 131901, 0, 8448);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 131901, 0, 8449);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 131901, 0, 8450);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 131901, 0, 8451);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 4, 131901, 0, 8452);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 8, 131901, 0, 8453);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 131901, 0, 8454);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 1, 131901, 0, 8455);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 1, 131901, 0, 8456);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 1, 131901, 0, 8457);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 4, 131901, 0, 8458);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 8, 131901, 0, 8459);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 131901, 0, 8460);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 131901, 0, 8461);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 131901, 0, 8462);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 131901, 0, 8464);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "細かいところまで\nすごーく丁寧に\n作られてる…", 1, 131901, 0, 8465);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 131901, 0, 8466);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 131901, 0, 8467);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 131901, 0, 8469);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 131901, 0, 8470);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 131901, 0, 8472);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 131901, 0, 8473);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 132101, 0, 8474);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 132101, 0, 8475);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 132101, 0, 8476);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 132101, 0, 8477);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 132101, 0, 8478);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 4, 132101, 0, 8479);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 8, 132101, 0, 8480);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 132101, 0, 8481);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 132101, 0, 8482);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 132101, 0, 8483);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 132101, 0, 8484);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 4, 132101, 0, 8485);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 8, 132101, 0, 8486);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 132101, 0, 8487);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 132101, 0, 8488);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 132101, 0, 8489);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 132101, 0, 8491);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 132101, 0, 8492);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 132101, 0, 8493);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 132101, 0, 8494);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 132101, 0, 8496);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 132101, 0, 8497);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 132101, 0, 8499);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 132101, 0, 8500);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 132001, 0, 8501);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 132001, 0, 8502);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 132001, 0, 8503);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 132001, 0, 8504);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 132001, 0, 8505);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 4, 132001, 0, 8506);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 8, 132001, 0, 8507);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 132001, 0, 8508);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 132001, 0, 8509);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 132001, 0, 8510);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 132001, 0, 8511);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 4, 132001, 0, 8512);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 8, 132001, 0, 8513);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 132001, 0, 8514);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 132001, 0, 8515);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 132001, 0, 8516);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 132001, 0, 8518);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 132001, 0, 8519);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 132001, 0, 8520);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 1, 132001, 0, 8521);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 132001, 0, 8523);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "素晴らしいね\nここには叡智が\n詰まっている", 1, 132001, 0, 8524);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 10, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 132001, 0, 8526);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 11, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 132001, 0, 8527);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 12, 109701, 0, 8528);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 12, 109701, 0, 8529);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 12, 109801, 0, 8530);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 12, 109801, 0, 8531);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 103, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 12, 109901, 0, 8532);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 103, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 12, 109901, 0, 8533);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 132201, 0, 8534);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 132201, 0, 8535);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 132201, 0, 8536);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 132201, 0, 8537);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 132201, 0, 8538);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 4, 132201, 0, 8539);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 8, 132201, 0, 8540);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 132201, 0, 8541);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 1, 132201, 0, 8542);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 1, 132201, 0, 8543);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 1, 132201, 0, 8544);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 4, 132201, 0, 8545);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 8, 132201, 0, 8546);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 132201, 0, 8547);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 1, 132201, 0, 8548);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 1, 132201, 0, 8549);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 1, 132201, 0, 8551);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 1, 132201, 0, 8552);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 1, 132201, 0, 8553);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 1, 132201, 0, 8554);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 1, 132201, 0, 8556);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 1, 132201, 0, 8557);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 127701, 0, 8559);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1, 127701, 0, 8560);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1, 127701, 0, 8561);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1, 127701, 0, 8562);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1, 127701, 0, 8563);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 4, 127701, 0, 8564);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 8, 127701, 0, 8565);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 1, 127701, 0, 8566);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 1, 127701, 0, 8567);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 1, 127701, 0, 8568);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 1, 127701, 0, 8569);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 4, 127701, 0, 8570);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 8, 127701, 0, 8571);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 127701, 0, 8572);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 1, 127701, 0, 8573);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 1, 127701, 0, 8574);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 1, 127701, 0, 8576);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 1, 127701, 0, 8577);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 1, 127701, 0, 8578);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 1, 127701, 0, 8579);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 1, 127701, 0, 8581);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 1, 127701, 0, 8582);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1, 127801, 0, 8584);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 1, 127801, 0, 8585);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 1, 127801, 0, 8586);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 1, 127801, 0, 8587);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 1, 127801, 0, 8588);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 4, 127801, 0, 8589);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 8, 127801, 0, 8590);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 1, 127801, 0, 8591);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 1, 127801, 0, 8592);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 1, 127801, 0, 8593);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 1, 127801, 0, 8594);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 4, 127801, 0, 8595);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 8, 127801, 0, 8596);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1, 127801, 0, 8597);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 1, 127801, 0, 8598);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "よく休んでください\n布団は干しといたんで\n…姉さんが", 1, 127801, 0, 8599);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "ふー…\nこのままずーっと\nここに居たい…", 1, 127801, 0, 8601);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 3, "あんたも一緒に\nサボっちゃうか～\n…なんて", 1, 127801, 0, 8602);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 4, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 1, 127801, 0, 8603);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 5, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 1, 127801, 0, 8604);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 7, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 1, 127801, 0, 8606);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 8, "食に関する本が\nやけに多い…\nお国柄ですかね", 1, 127801, 0, 8607);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1, 130201, 0, 8609);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 2, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 1, 130201, 0, 8610);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 3, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 1, 130201, 0, 8611);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 4, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 1, 130201, 0, 8612);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 5, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 1, 130201, 0, 8613);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 101, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 4, 130201, 0, 8614);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 0, 102, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 8, 130201, 0, 8615);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 1, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 1, 130201, 0, 8616);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 2, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 1, 130201, 0, 8617);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 3, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 1, 130201, 0, 8618);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 4, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 1, 130201, 0, 8619);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 101, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 4, 130201, 0, 8620);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 0, 102, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 8, 130201, 0, 8621);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 1, 1, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1, 130201, 0, 8622);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 2, 2, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 1, 130201, 0, 8623);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (2, 1, 3, 3, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 1, 130201, 0, 8624);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (3, 1, 0, 2, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 1, 130201, 0, 8626);
