INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 124501, 1, 0, 0, 6941, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 124501, 1, 0, 0, 6942, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 124501, 1, 0, 0, 6943, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 124501, 4, 0, 0, 6944, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 124501, 8, 0, 0, 6945, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 124501, 1, 0, 1, 6946, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お昼だね～\nおせちを用意したから\nみんなで食べよっか", 124501, 1, 0, 2, 6947, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 124501, 1, 0, 3, 6948, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このまま寝たら\nしわになるかな？\n着替えた方がいい？", 124501, 1, 0, 0, 6950, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 124501, 1, 0, 0, 6951, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい香りだけど\n着物に移らないように\n気をつけないとね", 124501, 1, 0, 0, 6952, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 124501, 1, 0, 0, 6953, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 124501, 1, 0, 0, 6955, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 124501, 1, 0, 0, 6956, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 124801, 1, 0, 0, 6958, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 124801, 1, 0, 0, 6959, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 124801, 1, 0, 0, 6960, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 124801, 1, 0, 0, 6961, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 124801, 1, 0, 0, 6962, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 124801, 4, 0, 0, 6963, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 124801, 8, 0, 0, 6964, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 124801, 1, 0, 0, 6965, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 124801, 1, 0, 0, 6966, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 124801, 1, 0, 0, 6967, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 124801, 1, 0, 0, 6968, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 124801, 4, 0, 0, 6969, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 124801, 8, 0, 0, 6970, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 124801, 1, 0, 1, 6971, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 124801, 1, 0, 2, 6972, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 124801, 1, 0, 3, 6973, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 124801, 1, 0, 0, 6975, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 124801, 1, 0, 0, 6976, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 124801, 1, 0, 0, 6977, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 124801, 1, 0, 0, 6978, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 124801, 1, 0, 0, 6980, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 124801, 1, 0, 0, 6981, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 124901, 1, 0, 0, 6983, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 124901, 1, 0, 0, 6984, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 124901, 1, 0, 0, 6985, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 124901, 1, 0, 0, 6986, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 124901, 1, 0, 0, 6987, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 124901, 4, 0, 0, 6988, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 124901, 8, 0, 0, 6989, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 124901, 1, 0, 0, 6990, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 124901, 1, 0, 0, 6991, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 124901, 1, 0, 0, 6992, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 124901, 1, 0, 0, 6993, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 124901, 4, 0, 0, 6994, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 124901, 8, 0, 0, 6995, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 124901, 1, 0, 1, 6996, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 124901, 1, 0, 2, 6997, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 124901, 1, 0, 3, 6998, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 124901, 1, 0, 0, 7000, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 124901, 1, 0, 0, 7001, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("嗅いだことない\n香りだな\nなかなか気に入った！", 124901, 1, 0, 0, 7002, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 124901, 1, 0, 0, 7003, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 124901, 1, 0, 0, 7005, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 124901, 1, 0, 0, 7006, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 105501, 12, 0, 0, 7008, 1, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 105501, 12, 0, 0, 7009, 2, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 125001, 1, 0, 0, 7010, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 125001, 1, 0, 0, 7011, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 125001, 1, 0, 0, 7012, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 125001, 1, 0, 0, 7013, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 125001, 1, 0, 0, 7014, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここにいれば\nいい思い出がたくさん\n作れそうだな！", 125001, 4, 0, 0, 7015, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 125001, 8, 0, 0, 7016, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 125001, 1, 0, 0, 7017, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 125001, 1, 0, 0, 7018, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 125001, 1, 0, 0, 7019, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 125001, 1, 0, 0, 7020, 2, 1, 4);
