INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 125801, 1, 0, 0, 7196, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 125801, 1, 0, 0, 7197, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 125801, 1, 0, 0, 7198, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 125801, 1, 0, 0, 7199, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 125801, 4, 0, 0, 7200, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("わたくしはここに\nいて…よいのですね？\n『仲間』として…", 125801, 8, 0, 0, 7201, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 125801, 1, 0, 1, 7202, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 125801, 1, 0, 2, 7203, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 125801, 1, 0, 3, 7204, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 125801, 1, 0, 0, 7206, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("と、飛びかかっては\nこない…ですよね…？", 125801, 1, 0, 0, 7207, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 125801, 1, 0, 0, 7208, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 125801, 1, 0, 0, 7209, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こんなに食べていたら\nはしたない…\nでしょうか？", 125801, 1, 0, 0, 7211, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 125801, 1, 0, 0, 7212, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 125501, 1, 0, 0, 7214, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 125501, 1, 0, 0, 7215, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 125501, 1, 0, 0, 7216, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 125501, 1, 0, 0, 7217, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 125501, 1, 0, 0, 7218, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 125501, 4, 0, 0, 7219, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("仲間がいるから\n騎空士は限界だって\n超えられるんだね", 125501, 8, 0, 0, 7220, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 125501, 1, 0, 0, 7221, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あなたとなら\nどんな相手だって\n怖くなんてないよ！", 125501, 1, 0, 0, 7222, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 125501, 1, 0, 0, 7223, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 125501, 1, 0, 0, 7224, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 125501, 4, 0, 0, 7225, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 125501, 8, 0, 0, 7226, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 125501, 1, 0, 1, 7227, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 125501, 1, 0, 2, 7228, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 125501, 1, 0, 3, 7229, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 125501, 1, 0, 0, 7231, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 125501, 1, 0, 0, 7232, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい匂い…\nそれになんだか\n集中力が増したかも", 125501, 1, 0, 0, 7233, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 125501, 1, 0, 0, 7234, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 125501, 1, 0, 0, 7236, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 125501, 1, 0, 0, 7237, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 125701, 1, 0, 0, 7239, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 125701, 1, 0, 0, 7240, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 125701, 1, 0, 0, 7241, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("せっかくですから\nより良い素材を\n自分で集めたいですね", 125701, 1, 0, 0, 7242, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このポーションが\nみなさんの役に立つと\nうれしいですね", 125701, 1, 0, 0, 7243, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 125701, 4, 0, 0, 7244, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 125701, 8, 0, 0, 7245, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 125701, 1, 0, 0, 7246, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 125701, 1, 0, 0, 7247, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("依頼で気になる\nところがあれば\n何でもご確認ください", 125701, 1, 0, 0, 7248, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 125701, 1, 0, 0, 7249, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 125701, 4, 0, 0, 7250, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 125701, 8, 0, 0, 7251, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 125701, 1, 0, 1, 7252, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 125701, 1, 0, 2, 7253, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 125701, 1, 0, 3, 7254, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 125701, 1, 0, 0, 7256, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 125701, 1, 0, 0, 7257, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 125701, 1, 0, 0, 7258, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この火力…\n錬金術の調合に\n使えそうですね", 125701, 1, 0, 0, 7259, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 125701, 1, 0, 0, 7261, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 125701, 1, 0, 0, 7262, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 106101, 12, 0, 0, 7264, 1, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 106101, 12, 0, 0, 7265, 2, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("みんなー！\nギルドハウスで\nぼくと握手！", 125601, 1, 0, 0, 7266, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 125601, 1, 0, 0, 7267, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 125601, 1, 0, 0, 7268, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 125601, 1, 0, 0, 7269, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 125601, 1, 0, 0, 7270, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 125601, 4, 0, 0, 7271, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 125601, 8, 0, 0, 7272, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 125601, 1, 0, 0, 7273, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 125601, 1, 0, 0, 7274, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 125601, 1, 0, 0, 7275, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 125601, 1, 0, 0, 7276, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 125601, 4, 0, 0, 7277, 2, 1, 101);
