INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 128701, 1, 0, 0, 7958, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 128701, 1, 0, 0, 7959, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 128701, 1, 0, 0, 7960, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 128701, 4, 0, 0, 7961, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 128701, 8, 0, 0, 7962, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 128701, 1, 0, 1, 7963, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 128701, 1, 0, 2, 7964, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 128701, 1, 0, 3, 7965, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 128701, 1, 0, 0, 7967, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 128701, 1, 0, 0, 7968, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 128701, 1, 0, 0, 7969, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 128701, 1, 0, 0, 7970, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 128701, 1, 0, 0, 7972, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 128701, 1, 0, 0, 7973, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 129001, 1, 0, 0, 7975, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 129001, 1, 0, 0, 7976, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 129001, 1, 0, 0, 7977, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 129001, 1, 0, 0, 7978, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 129001, 1, 0, 0, 7979, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 129001, 4, 0, 0, 7980, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 129001, 8, 0, 0, 7981, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お招きありがとう\nそれで…何をして\nいればいい…かな？", 129001, 1, 0, 0, 7982, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 129001, 1, 0, 0, 7983, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 129001, 1, 0, 0, 7984, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 129001, 1, 0, 0, 7985, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 129001, 4, 0, 0, 7986, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 129001, 8, 0, 0, 7987, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 129001, 1, 0, 1, 7988, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 129001, 1, 0, 2, 7989, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 129001, 1, 0, 3, 7990, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 129001, 1, 0, 0, 7992, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 129001, 1, 0, 0, 7993, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 129001, 1, 0, 0, 7994, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 129001, 1, 0, 0, 7995, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 129001, 1, 0, 0, 7997, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 129001, 1, 0, 0, 7998, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 129301, 1, 0, 0, 8000, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 129301, 1, 0, 0, 8001, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 129301, 1, 0, 0, 8002, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 129301, 1, 0, 0, 8003, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 129301, 1, 0, 0, 8004, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 129301, 4, 0, 0, 8005, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 129301, 8, 0, 0, 8006, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 129301, 1, 0, 0, 8007, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 129301, 1, 0, 0, 8008, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 129301, 1, 0, 0, 8009, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 129301, 1, 0, 0, 8010, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 129301, 4, 0, 0, 8011, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 129301, 8, 0, 0, 8012, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 129301, 1, 0, 1, 8013, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 129301, 1, 0, 2, 8014, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 129301, 1, 0, 3, 8015, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 129301, 1, 0, 0, 8017, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 129301, 1, 0, 0, 8018, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 129301, 1, 0, 0, 8019, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 129301, 1, 0, 0, 8020, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ええい、\n固形食物はいらん！　\nポーションを出せ！", 129301, 1, 0, 0, 8022, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 129301, 1, 0, 0, 8023, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 129301, 1, 0, 0, 8025, 3, 1, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これは…なんだ？\n何が面白いんだ？\n難しいな…", 129301, 1, 0, 0, 8026, 3, 1, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 108901, 12, 0, 0, 8027, 1, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 108901, 12, 0, 0, 8028, 2, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 108801, 12, 0, 0, 8029, 1, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 108801, 12, 0, 0, 8030, 2, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 108701, 12, 0, 0, 8031, 1, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 108701, 12, 0, 0, 8032, 2, 1, 103);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 129401, 1, 0, 0, 8033, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 129401, 1, 0, 0, 8034, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 129401, 1, 0, 0, 8035, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 129401, 1, 0, 0, 8036, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 129401, 1, 0, 0, 8037, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 129401, 4, 0, 0, 8038, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 129401, 8, 0, 0, 8039, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 129401, 1, 0, 0, 8040, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 129401, 1, 0, 0, 8041, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 129401, 1, 0, 0, 8042, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 129401, 1, 0, 0, 8043, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 129401, 4, 0, 0, 8044, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 129401, 8, 0, 0, 8045, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 129401, 1, 0, 1, 8046, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 129401, 1, 0, 2, 8047, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 129401, 1, 0, 3, 8048, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 129401, 1, 0, 0, 8050, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 129401, 1, 0, 0, 8051, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 129401, 1, 0, 0, 8052, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 129401, 1, 0, 0, 8053, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 129401, 1, 0, 0, 8055, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 129401, 1, 0, 0, 8056, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 129401, 1, 0, 0, 8058, 3, 1, 10);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 129401, 1, 0, 0, 8059, 3, 1, 11);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("さてと…\n明日の訓練メニュー\n考えねえとな", 129801, 1, 0, 0, 8060, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 129801, 1, 0, 0, 8061, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 129801, 1, 0, 0, 8062, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 129801, 1, 0, 0, 8063, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 129801, 1, 0, 0, 8064, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 129801, 4, 0, 0, 8065, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 129801, 8, 0, 0, 8066, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 129801, 1, 0, 0, 8067, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 129801, 1, 0, 0, 8068, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 129801, 1, 0, 0, 8069, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 129801, 1, 0, 0, 8070, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 129801, 4, 0, 0, 8071, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 129801, 8, 0, 0, 8072, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 129801, 1, 0, 1, 8073, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 129801, 1, 0, 2, 8074, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 129801, 1, 0, 3, 8075, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 129801, 1, 0, 0, 8077, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 129801, 1, 0, 0, 8078, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 129801, 1, 0, 0, 8079, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 129801, 1, 0, 0, 8080, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 129801, 1, 0, 0, 8082, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 129801, 1, 0, 0, 8083, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 129901, 1, 0, 0, 8085, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 129901, 1, 0, 0, 8086, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 129901, 1, 0, 0, 8087, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 129901, 1, 0, 0, 8088, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 129901, 1, 0, 0, 8089, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 129901, 4, 0, 0, 8090, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 129901, 8, 0, 0, 8091, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 129901, 1, 0, 0, 8092, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 129901, 1, 0, 0, 8093, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 129901, 1, 0, 0, 8094, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 129901, 1, 0, 0, 8095, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 129901, 4, 0, 0, 8096, 2, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 129901, 8, 0, 0, 8097, 2, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 129901, 1, 0, 1, 8098, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 129901, 1, 0, 2, 8099, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 129901, 1, 0, 3, 8100, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 129901, 1, 0, 0, 8102, 3, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 129901, 1, 0, 0, 8103, 3, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 129901, 1, 0, 0, 8104, 3, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 129901, 1, 0, 0, 8105, 3, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 129901, 1, 0, 0, 8107, 3, 1, 7);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 129901, 1, 0, 0, 8108, 3, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("……（そわそわ）", 129601, 1, 0, 0, 8110, 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここに寄らずの花陣…\n作ったらダメですよね", 129601, 1, 0, 0, 8111, 1, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 129601, 1, 0, 0, 8112, 1, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("身を隠せる場所が\nあると…\n助かるんですけど", 129601, 1, 0, 0, 8113, 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 129601, 1, 0, 0, 8114, 1, 1, 5);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("出入りするひと\nみんな、笑顔…\n…変わった家ですね", 129601, 4, 0, 0, 8115, 1, 1, 101);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("この家なら安全─\nかもしれません\n魔物が出ないので", 129601, 8, 0, 0, 8116, 1, 1, 102);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("これも、視察の一環\n遊びに来たわけじゃ\nないです", 129601, 1, 0, 0, 8117, 2, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("長話─苦手です\n疲れるので…\nふぅ…", 129601, 1, 0, 0, 8118, 2, 1, 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("ここで友達を作れと\nネアさんが…\nそう、言われても…", 129601, 1, 0, 0, 8119, 2, 1, 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("世話をするのは\n得意です\n…花の、ですよ？", 129601, 1, 0, 0, 8120, 2, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES ("媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 129601, 4, 0, 0, 8121, 2, 1, 101);
