INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (401, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "アヤネ", 24001, 102301, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (402, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 2.21, "アヤネ", 24002, 102301, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (403, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "アヤネ", 24003, 102301, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (701, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 1.99, "アサヒ", 24004, 192201, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (702, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 2.21, "アサヒ", 24005, 192201, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (703, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 3.33, "アサヒ", 24006, 192201, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (801, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "部員３", 24007, 192301, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (802, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 2.68, "部員３", 24008, 192301, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (803, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "部員３", 24009, 192301, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (501, 24, 1, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 0.0, "", 24010, 192501, "やった！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (502, 24, 1, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 0.0, "", 24011, 192501, "勝ったよ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (503, 24, 1, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 0.0, "", 24012, 192501, "いい調子！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (111, 24, 6, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 5.33, "ミサト", 24013, 113501, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (112, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 6.85, "ミサト", 24014, 113501, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (113, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 3.0, "ミサト", 24015, 113501, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (611, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 6.11, "ダイチ", 24016, 192401, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (612, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 6.75, "ダイチ", 24017, 192401, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (613, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "ダイチ", 24018, 192401, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (211, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 4.52, "ハツネ", 24019, 113401, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (212, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 3.89, "ハツネ", 24020, 113401, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (213, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 13.49, "ハツネ", 24021, 113401, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (311, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 3.17, "カヤ", 24022, 106501, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (312, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 4.42, "カヤ", 24023, 106501, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (313, 24, 6, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 2.89, "カヤ", 24024, 106501, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (421, 24, 4, "2030/12/17 14:59:59", 10046, 3, "2020/07/31 12:00:00", 3.71, "アヤネ", 24025, 102301, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (721, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 5.1, "アサヒ", 24026, 192201, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (821, 24, 1, "2030/12/17 14:59:59", 10046, 3, "2020/07/31 12:00:00", 4.34, "部員３", 24027, 192301, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (521, 24, 1, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 0.0, "", 24028, 192501, "いい勝負だったよ\nまたやろうね", 0.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (101, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 5.62, "ミサト", 24029, 113501, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", -360.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (102, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 9.58, "ミサト", 24030, 113501, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", -360.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (103, 24, 2, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 0.0, "ミサト", 24031, 113501, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", -360.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (104, 24, 2, "2030/12/17 14:59:59", 10046, 1, "2020/07/31 12:00:00", 9.13, "ミサト", 24032, 113501, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", -360.0, 0.0);
INSERT INTO `v1_46b87f4d2473dceeb492d04bf317fe9a208af53de197c753de08bc78ca7edba5` VALUES (105, 24, 1, "2030/12/17 14:59:59", 10046, 2, "2020/07/31 12:00:00", 8.27, "ミサト", 24033, 113501, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", -360.0, 0.0);
