INSERT INTO `v1_4739b09491c534ae8292368e96cb2c225f10040cea7bf7c72160ae443112d67f` VALUES (0, 2800, "蒼珠の腕輪", "バトル開始時ＴＰ200回復", 2, "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 21003, 2, 10, 21003, 1);
INSERT INTO `v1_4739b09491c534ae8292368e96cb2c225f10040cea7bf7c72160ae443112d67f` VALUES (0, 2800, "星明りの儀杖", "ＴＰ消費軽減＋4", 2, "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 21004, 2, 10, 21004, 1);
