INSERT INTO `v1_4739b09491c534ae8292368e96cb2c225f10040cea7bf7c72160ae443112d67f` VALUES (0, 2800, "紅反鋼のアーマー", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 2, "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 24002, 2, 10, 24002, 4);
