INSERT INTO `v1_4739b09491c534ae8292368e96cb2c225f10040cea7bf7c72160ae443112d67f` VALUES (0, 2800, "魔護霊装", "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 2, "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 25004, 2, 10, 25004, 5);
