INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 1, 100101, 2.295, 0, 1, 100101, 450, "2015/04/01 15:00:00", 1, 200, 0, 100101, 100101, "2030/12/31 15:00:00", "ひより", "ヒヨリ", "【物理】前衛で、敵前線を押し返す笑顔の元気娘。\n前衛に対して大ダメージを与えるユニオンバーストと、\n自身の攻撃力を強化するスキルを持つ攻撃役。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 2, 0, 7, 100201, 2.27, 0, 1, 100201, 450, "2015/04/01 15:00:00", 7, 800, 0, 100201, 100201, "2030/12/31 15:00:00", "ゆい", "ユイ", "【魔法】最後衛から、味方の回復と補助を行う魔法士。\n打たれ弱いが高い魔法攻撃力を持ち、ランクを上げると\n攻撃も補助もバランスよくこなせるようになる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 4, 100301, 1.965, 0, 1, 100301, 450, "2015/04/01 15:00:00", 4, 250, 0, 100301, 100301, "2030/12/31 15:00:00", "れい", "レイ", "【物理】前衛で、一点突破で敵を討つ、孤高の剣士。\nユニオンバースト、スキル共に眼前の敵を倒す事に優れ、\n攻撃スキルやカウンターを駆使して前線を押し上げる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 1, 100401, 2.17, 0, 1, 100401, 450, "2015/04/01 15:00:00", 1, 205, 0, 100401, 100401, "2030/12/31 15:00:00", "みそぎ", "ミソギ", "【物理】最前衛で、敵の物理攻撃を邪魔するいたずら娘。\n目の前の敵を暗闇状態にし、命中率を大幅に下げるほか、\n敵一体の物理攻撃力を下げるスキルを持つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 2, 100501, 2.125, 0, 2, 100501, 450, "2018/09/18 15:00:00", 2, 185, 0, 100501, 100501, "2030/12/31 15:00:00", "まつり", "マツリ", "【物理】前衛の、機敏な動きで敵をかき回す騎士見習い。\nユニオンバーストで相手の陣地の真ん中に飛び込み、\n自分の周囲を攻撃するスキルで相手を攻め立てる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 14, 2, 0, 6, 100601, 2.19, 0, 2, 100601, 450, "2015/04/01 15:00:00", 6, 570, 0, 100601, 100601, "2030/12/31 15:00:00", "あかり", "アカリ", "【魔法】中衛で、魔法パーティの強化を担う、双子の妹。\n敵の魔法防御力を大幅に下げるスキルや、魔法攻撃力を\n上昇させ、回復効果を付与するユニオンバーストを使う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 14, 1, 0, 3, 100701, 1.7, 0, 2, 100701, 450, "2015/04/01 15:00:00", 3, 125, 0, 100701, 100701, "2030/12/31 15:00:00", "みやこ", "ミヤコ", "【物理】最前衛で、ひたすら敵の攻撃を避ける幽霊少女。\nスキルで幽霊に変身することで敵の攻撃を躱し、\nユニオンバーストでは敵をプリンにして食べてしまう。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 100801, 2.07, 0, 2, 100801, 450, "2015/04/01 15:00:00", 21, 805, 0, 100801, 100801, "2030/12/31 15:00:00", "ゆき", "ユキ", "【魔法】後衛で物理パーティを補助する、歩く芸術作品。\n味方のユニオンバースト発動を早めるスキルのほか、\n暗闇状態にするスキルで敵の物理攻撃を封じる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 2, 0, 5, 100901, 2.25, 0, 3, 100901, 450, "2015/04/01 15:00:00", 5, 440, 0, 100901, 100901, "2030/12/31 15:00:00", "あんな", "アンナ", "【魔法】中衛から圧倒的な魔力で攻撃する、疾風の冥姫。\nユニオンバーストで敵全体に大ダメージを与えるが、\n高すぎる魔力の為、自分自身にも相応のリスクが生じる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 2, 0, 7, 101001, 2.27, 0, 3, 101001, 450, "2015/04/01 15:00:00", 7, 795, 0, 101001, 101001, "2030/12/31 15:00:00", "まほ", "マホ", "【魔法】後衛で味方のサポートを担当するメルヘン少女。\n味方全体を強化してＴＰを回復するユニオンバーストや、\nＨＰ回復、敵を暗闇にするスキルで戦況を好転させる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 8, 101101, 1.97, 0, 3, 101101, 450, "2015/04/01 15:00:00", 8, 700, 0, 101101, 101101, "2030/12/31 15:00:00", "りの", "リノ", "【物理】後衛から、矢の雨を降らせる妹系アーチャー。\nクリティカル率を高める重ね掛け可能なスキルを持ち、\nダメージ回数の多いユニオンバーストをより強力にする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 101201, 2.07, 0, 3, 101201, 450, "2015/04/01 15:00:00", 7, 755, 0, 101201, 101201, "2030/12/31 15:00:00", "はつね", "ハツネ", "【魔法】後衛の、対物理パーティが得意な超能力ガール。\nスキルが物理攻撃の敵に対して真価を発揮するため、\n物理攻撃型の敵パーティを一網打尽にする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 101301, 2.07, 0, 2, 101301, 450, "2018/11/19 15:00:00", 7, 740, 0, 101301, 101301, "2030/12/31 15:00:00", "ななか", "ナナカ", "【魔法】後衛から多彩な魔法を放つ、オタク魔法少女。\n体力が一番多い相手を狙うユニオンバーストやスキルは、\n敵のＨＰ状況により攻撃対象が変わる高威力の魔法攻撃。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 101401, 2.27, 0, 3, 101401, 450, "2019/02/28 12:00:00", 21, 730, 0, 101401, 101401, "2030/12/31 15:00:00", "かすみ", "カスミ", "【魔法】後衛で、相手の行動妨害に特化した名探偵。\n束縛や混乱を付与するスキルで相手の行動を妨害し、\n魔法防御力ダウンのフィールドを展開して王手をかける。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 101501, 2.27, 0, 2, 101501, 450, "2015/04/01 15:00:00", 9, 735, 0, 101501, 101501, "2030/12/31 15:00:00", "みさと", "ミサト", "【魔法】後衛で魔法キャラを支援する、みんなの先生。\nユニオンバーストのＨＰ回復＆魔防アップ、先頭キャラの\n継続ＨＰ回復スキルは、味方の耐久力を大幅に高める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 1, 0, 8, 101601, 1.97, 0, 2, 101601, 450, "2015/04/01 15:00:00", 8, 705, 0, 101601, 101601, "2030/12/31 15:00:00", "すずな", "スズナ", "【物理】後衛からクリティカル攻撃するカリスマモデル。\nダメージ2倍のクリティカルダメージは\nスキルによる攻撃力アップでさらに破壊力を増す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 1, 101701, 2.17, 0, 2, 101701, 450, "2015/04/01 15:00:00", 1, 145, 0, 101701, 101701, "2030/12/31 15:00:00", "かおり", "カオリ", "【物理】最前衛で、累積スキルでダメージを稼ぐ南国娘。\nスキル「精神統一」を発動すると、カオリが攻撃する度に\n敵に効果が累積し、次のダメージがどんどん増えていく。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 5, 101801, 2.6, 0, 3, 101801, 450, "2015/04/01 15:00:00", 5, 715, 0, 101801, 101801, "2030/12/31 15:00:00", "いお", "イオ", "【魔法】後衛で、敵全体を誘惑するセクシー先生。\n敵のＴＰを奪うスキルでユニオンバースト発動を遅らせ、\n誘惑のユニオンバーストを放ち、戦場を大混乱に陥れる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 5, 102001, 2.125, 0, 2, 102001, 450, "2015/04/01 15:00:00", 5, 360, 0, 102001, 102001, "2030/12/31 15:00:00", "みみ", "ミミ", "【物理】中衛で、敵前衛を崩す、うさぎっこ。\nユニオンバーストは、最も近い敵に大ダメージを与えた後\n隣の敵にも攻撃するので、前衛を早期に攻め落とせる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 3, 102101, 1.675, 0, 1, 102101, 450, "2015/04/01 15:00:00", 3, 240, 0, 102101, 102101, "2030/12/31 15:00:00", "くるみ", "クルミ", "【物理】前衛で、味方の守りに特化した、極度の照れ屋。\nスキルで自分の周囲の物理、魔法防御力を上げつつ、\n近くの敵をスタンさせて足止めする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 14, 2, 0, 6, 102201, 2.19, 0, 1, 102201, 450, "2015/04/01 15:00:00", 6, 575, 0, 102201, 102201, "2030/12/31 15:00:00", "より", "ヨリ", "【魔法】中衛から、強力な魔法で攻め立てる、双子の姉。\n自身のＨＰと引き換えに魔法攻撃力を増幅し、\nユニオンバーストやスキルの威力を高めて攻撃する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 3, 102301, 1.425, 0, 2, 102301, 450, "2015/04/01 15:00:00", 3, 210, 0, 102301, 102301, "2030/12/31 15:00:00", "あやね", "アヤネ", "【物理】前衛で、戦場をかき乱す、くまっ子。\nユニオンバーストで、相手を思い切り吹き飛ばすと同時に\nスタンと大ダメージを与えることで敵陣形を大きく乱す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 2, 0, 7, 102501, 2.27, 0, 1, 102501, 450, "2015/04/01 15:00:00", 7, 720, 0, 102501, 102501, "2030/12/31 15:00:00", "すずめ", "スズメ", "【魔法】後衛で、回復と攻撃をこなすドジっ娘メイド。\n周りの味方のＨＰを回復しつつ物理攻撃力を高める\nスキルは、中衛・後衛の攻撃役と抜群の相性。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 6, 102601, 2.34, 0, 2, 102601, 450, "2015/04/01 15:00:00", 6, 550, 0, 102601, 102601, "2030/12/31 15:00:00", "りん", "リン", "【物理】中衛で物理パーティを強化するものぐさ少女。\n味方の物理攻撃力をアップしつつ、味方の魔法防御を\n高めることで攻守ともにパーティをサポートする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 1, 0, 3, 102701, 1.425, 0, 2, 102701, 450, "2015/04/01 15:00:00", 3, 230, 0, 102701, 102701, "2030/12/31 15:00:00", "えりこ", "エリコ", "【物理】前衛で、毒スキルで敵を粉砕する、通称壊し屋。\n最高クラスの攻撃力を持ち、ユニオンバーストで敵に\nとどめの一撃を放つ毎に、その攻撃力はさらに上昇する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 4, 102801, 2.09, 0, 3, 102801, 450, "2015/04/01 15:00:00", 4, 430, 0, 102801, 102801, "2030/12/31 15:00:00", "されん", "サレン", "【物理】中衛で一発逆転を狙う、みんなのサレンママ。\nＨＰが下がるほどダメージが上昇するユニオンバーストと\n味方のＴＰを回復するスキルで、一気に形勢を逆転する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 1, 0, 4, 102901, 1.965, 0, 3, 102901, 450, "2015/04/01 15:00:00", 4, 160, 0, 102901, 102901, "2030/12/31 15:00:00", "のぞみ", "ノゾミ", "【物理】前衛で、皆の壁になって戦うアイドルのぞみん。\n彼女がステージに立てば、味方の物理攻撃力が\n大きくアップし、さらに敵の注意を一手に惹き付ける。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 5, 103001, 2.25, 0, 3, 103001, 450, "2015/04/01 15:00:00", 5, 415, 0, 103001, 103001, "2030/12/31 15:00:00", "にのん", "ニノン", "【物理】中衛で、強力な範囲攻撃を操る、忍術の使い手。\n範囲攻撃のスキルで敵を倒す事で、自分のＴＰを回復し、\n強力なユニオンバーストの発動頻度を上げる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 14, 1, 0, 5, 103101, 2.25, 0, 2, 103101, 450, "2015/04/01 15:00:00", 5, 365, 0, 103101, 103101, "2030/12/31 15:00:00", "しのぶ", "シノブ", "【物理】中衛で、父親のドクロを召喚して戦う霊能少女。\n召喚した父親は、自分とは別に攻撃を行ってくれるほか、\nダメージを代わりに受けてくれることもある。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 5, 103201, 2.125, 0, 3, 103201, 450, "2015/04/01 15:00:00", 5, 180, 0, 103201, 103201, "2030/12/31 15:00:00", "あきの", "アキノ", "【物理】前衛で大暴れして攻める、令嬢剣士。\n自分の周りに、味方を回復するフィールドを作り出し、\n自ら前線を支え導く姿も、名家を背負う者のあるべき姿。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 6, 103301, 2.34, 0, 2, 103301, 450, "2015/04/01 15:00:00", 6, 395, 0, 103301, 103301, "2030/12/31 15:00:00", "まひる", "マヒル", "【物理】中衛で、相手の動きを乱す牧場主。\n敵前衛をノックバックさせることで、隊列を崩し、\n他のキャラが使う範囲攻撃の対象に敵を押し込む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 5, 103401, 2.4, 0, 1, 103401, 450, "2015/04/01 15:00:00", 5, 405, 0, 103401, 103401, "2030/12/31 15:00:00", "ゆかり", "ユカリ", "【物理】中衛から、バリアや回復で味方を守る元聖騎士。\n傷ついた味方のＨＰやＴＰを回復するスキルを操り、\n打ち合いで消耗した所を、ピンポイントでサポートする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 103601, 2.07, 0, 3, 103601, 450, "2015/04/01 15:00:00", 7, 810, 0, 103601, 103601, "2030/12/31 15:00:00", "きょうか", "キョウカ", "【魔法】後衛から強力な魔法で攻撃するちびっこ優等生。\nスキルで魔法攻撃力を瞬間的に高めた後に放つ\nユニオンバーストは敵単体への威力抜群。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 4, 103701, 1.965, 0, 3, 103701, 450, "2015/04/01 15:00:00", 4, 220, 0, 103701, 103701, "2030/12/31 15:00:00", "とも", "トモ", "【物理】前衛で、素早い斬撃を仕掛け敵を屠る剣術少女。\nユニオンバーストで自分の攻撃力と行動速度を上昇させ、\nほか攻撃スキルの殲滅能力を極限まで高め敵を圧倒する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 8, 103801, 1.97, 0, 2, 103801, 450, "2015/04/01 15:00:00", 8, 710, 0, 103801, 103801, "2030/12/31 15:00:00", "しおり", "シオリ", "【物理】最後衛から高火力スキルを連発するスナイパー。\nダメージを与えつつ、ＴＰを回復するスキルを持ち、\n強力な一撃のユニオンバーストを、素早く発動する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 1, 0, 8, 104001, 1.97, 0, 1, 104001, 450, "2015/04/01 15:00:00", 8, 785, 0, 104001, 104001, "2030/12/31 15:00:00", "あおい", "アオイ", "【物理】最後衛から状態異常の矢を放つ、ぼっちの射手。\nダメージと共に付与する毒や麻痺の追加効果により、\n防御力の高い敵であっても、確実にＨＰを削りとる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 2, 0, 7, 104201, 2.27, 0, 2, 104201, 450, "2015/04/01 15:00:00", 7, 790, 0, 104201, 104201, "2030/12/31 15:00:00", "ちか", "チカ", "【魔法】後衛から、前衛の戦いをサポートする唱喚士。\nユニオンバーストで、味方の回復と敵への攻撃を行う\n精霊を最前線に唱喚する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 5, 104301, 2.125, 0, 3, 104301, 450, "2015/04/01 15:00:00", 5, 165, 0, 104301, 104301, "2030/12/31 15:00:00", "まこと", "マコト", "【物理】前衛で、敵の防御力を削り取る、正義の獣人。\n敵の盾役の防御力を下げ、着実にダメージを与えることで\n早期に敵の守りを突破する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 3, 104401, 1.425, 0, 3, 104401, 450, "2015/04/01 15:00:00", 3, 425, 0, 104401, 104401, "2030/12/31 15:00:00", "いりや", "イリヤ", "【魔法】中衛で命を削り攻撃する「夜を統べる者」。\n自身のＨＰと引き換えに放つスキルは高い殲滅力を誇り\n範囲攻撃における威力では他の追随を許さない。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 2, 104501, 2.375, 0, 2, 104501, 450, "2015/04/01 15:00:00", 2, 130, 0, 104501, 104501, "2030/12/31 15:00:00", "くうか", "クウカ", "【物理】前衛で囮となり、攻撃を引き付ける暴走ドＭ娘。\nユニオンバーストにより、自身に魔法バリアを張り、\n敵の魔法攻撃を吸収してＨＰを回復する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 2, 104601, 2.25, 0, 2, 104601, 450, "2015/04/01 15:00:00", 2, 215, 0, 104601, 104601, "2030/12/31 15:00:00", "たまき", "タマキ", "【物理】前衛の、対魔法パーティが得意な気まぐれ猫娘。\n攻撃スキルは、「魔法攻撃力が一番高い敵」を狙い撃ち、\nダメージ、魔法攻撃力ダウン、ＴＰ吸収と畳み掛ける。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 5, 104701, 2.125, 0, 3, 104701, 450, "2015/04/01 15:00:00", 5, 135, 0, 104701, 104701, "2030/12/31 15:00:00", "じゅん", "ジュン", "【物理】最前衛で、完全防御スキルを操る騎士団長。\nユニオンバーストであらゆるダメージを吸収するバリアを\n展開できる。特に長期戦で圧倒的な防御性能を誇る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 6, 104801, 2.19, 0, 2, 104801, 450, "2015/04/01 15:00:00", 6, 420, 0, 104801, 104801, "2030/12/31 15:00:00", "みふゆ", "ミフユ", "【物理】中衛の、スタンと回復のスキルを持つ傭兵戦士。\n高い防御力と自己回復能力を併せ持つ事で、\n中衛の守りを堅固にし、パーティを底支えする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 5, 104901, 2.375, 0, 3, 104901, 450, "2015/04/01 15:00:00", 5, 285, 0, 104901, 104901, "2030/12/31 15:00:00", "しずる", "シズル", "【物理】前衛で、物理攻撃から味方を守る、お姉ちゃん。\nスキルで物理無効バリアを展開し、味方全員を守るほか、\n自身はさらに物理吸収バリアを纏い、敵前衛を討つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 7, 105001, 2.07, 0, 1, 105001, 450, "2015/04/01 15:00:00", 7, 760, 0, 105001, 105001, "2030/12/31 15:00:00", "みさき", "ミサキ", "【魔法】後衛から、範囲攻撃を行う、レディ・ウィッチ。\n複数回の範囲攻撃を放つスキルや、目の前の敵全てを\n攻撃するユニオンバーストを持ち、範囲殲滅に特化する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 1, 0, 5, 105101, 2.25, 0, 2, 105101, 450, "2015/04/01 15:00:00", 5, 565, 0, 105101, 105101, "2030/12/31 15:00:00", "みつき", "ミツキ", "【物理】中衛で、敵弱体化フィールドを操る隻眼の悪魔。\nフィールドの持続時間は短いが、範囲内の敵の\n物理防御力を大幅に減らす効果を持つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 4, 105201, 2.215, 0, 1, 105201, 450, "2015/04/01 15:00:00", 4, 105, 0, 105201, 105201, "2030/12/31 15:00:00", "りま", "リマ", "【物理】最前衛で味方を守る、恋する乙女。\nバトル開始後、しばらくしてから最前線まで\n突進してきて、敵の目の前に立ちはだかる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 4, 105301, 2.24, 0, 3, 105301, 450, "2015/04/01 15:00:00", 4, 410, 0, 105301, 105301, "2030/12/31 15:00:00", "もにか", "モニカ", "【物理】中衛で、味方の攻撃を鼓舞する、軍人娘。\n味方全体の物理、魔法攻撃力の大幅アップに加えて、\n攻撃速度もあげることで、戦闘の早期終結を実現する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 1, 0, 1, 105401, 2.42, 0, 2, 105401, 450, "2015/04/01 15:00:00", 1, 195, 0, 105401, 105401, "2030/12/31 15:00:00", "つむぎ", "ツムギ", "【物理】前衛で、敵の妨害に特化したテーラーガール。\n敵を強制的に移動させる事で陣形を崩し、さらに\n移動速度・行動速度を低下させ相手の反撃を遅らせる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 48, 105501, 2.34, 0, 1, 105501, 450, "2019/03/19 15:00:00", 48, 510, 0, 105501, 105501, "2030/12/31 15:00:00", "あゆみ", "アユミ", "【物理】中衛で、どたばたと敵をかく乱する、純情少女。\n敵をスタンさせるスキルのほか、ユニオンバーストでは\n暗闇と混乱を付与することで敵を邪魔することができる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 4, 105601, 1.965, 0, 3, 105601, 450, "2018/11/03 19:00:00", 4, 158, 0, 105601, 105601, "2030/12/31 15:00:00", "るか", "ルカ", "【物理】前衛で味方のため剣を振るう、いなせな姉御。\n相手へのカウンターも行う挑発や、敵の防御力を下げる\nユニオンバーストを持ち、攻防両面で実力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 4, 105701, 1.965, 0, 3, 105701, 450, "2015/04/01 15:00:00", 4, 245, 0, 105701, 170101, "2030/12/31 15:00:00", "じーた", "ジータ", "【物理】前衛で、先手必勝の大技を繰り出す騎空士。\nスキルによるＴＰ回復は、ユニオンバーストの発動を早め\nバトルの序盤から敵前衛に大ダメージを与える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 5, 105801, 2.25, 0, 1, 105801, 450, "2015/04/01 15:00:00", 5, 155, 0, 105801, 105801, "2030/12/31 15:00:00", "ぺこりーぬ", "ペコリーヌ", "【物理】前衛にて、全力で仲間を護る、腹ペコ剣士。\n高いＨＰに加えて、自分自身でもＨＰ回復できるため\nギリギリまで味方の壁役を引き受ける。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 6, 105901, 2.34, 0, 1, 105901, 450, "2015/04/01 15:00:00", 6, 500, 0, 105901, 105901, "2030/12/31 15:00:00", "こっころ", "コッコロ", "【物理】中衛で、補助と攻撃で皆を支える、導きの巫女。\nユニオンバーストは、味方全体の攻撃力アップに加えて\n自らも回復するため、継続的なサポートが可能となる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 7, 106001, 2.07, 0, 1, 106001, 450, "2015/04/01 15:00:00", 7, 750, 0, 106001, 106001, "2030/12/31 15:00:00", "きゃる", "キャル", "【魔法】後衛から、強力な全体攻撃を放つ、強気な猫娘。\nスキルで敵の物理、魔法両方の防御力を下げるため\n編成を選ばずにバトルを有利に進められる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 2, 106101, 1.8, 1, 3, 106101, 450, "2025/02/28 12:00:00", 2, 162, 0, 106101, 106101, "2025/03/03 18:59:59", "むいみ", "ムイミ", "【物理】前衛で、伝説の剣「天楼覇断剣」を振るう少女。\n小技を駆使する通常の戦闘スタイルから一転、ユニオン\nバースト後は「天楼覇断剣」を携え、敵を薙ぎ払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 8, 106301, 1.97, 0, 3, 106301, 450, "2015/04/01 15:00:00", 8, 625, 0, 106301, 106301, "2030/12/31 15:00:00", "ありさ", "アリサ", "【物理】後衛の、長期戦に優れた、森の守護者見習い。\nＴＰ回復によるユニオンバーストの速攻が可能。さらに\n使用後はスキル効果が上昇するため高い殲滅力を誇る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 4, 106401, 1.84, 0, 3, 106401, 450, "2021/02/15 23:00:00", 4, 368, 0, 106401, 106401, "2030/12/31 15:00:00", "しぇふぃ", "シェフィ", "【物理】中衛で氷雪を操り敵を凍滅する、氷竜の少女。\nユニオンバーストで獲得する【氷竜の印】を消費して、\nスキルの効果を増幅し、清廉な剣技で敵を切り伏せる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 1, 0, 1, 106501, 2.17, 0, 3, 106501, 450, "2019/11/15 15:00:00", 1, 168, 0, 106501, 106501, "2030/12/31 15:00:00", "かや", "カヤ", "【物理】前衛で、敵を殴り倒す喧嘩屋ドラゴン族。\nスキルで自身の物理攻撃力を高め、全身全霊を懸け放つ\n初弾のユニオンバーストは、超大な破壊力を誇る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 1, 0, 3, 106601, 1.55, 0, 3, 106601, 450, "2020/06/17 15:00:00", 3, 197, 0, 106601, 106601, "2030/12/31 15:00:00", "いのり", "イノリ", "【物理】前衛で範囲攻撃が得意な口達者のドラゴン娘。\nユニオンバーストで範囲内の敵をまとめて吹き飛ばし、\n陣形を乱したところで、敵を一気に焼き尽くす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 2, 0, 7, 106701, 2.27, 1, 3, 106701, 450, "2025/02/28 12:00:00", 21, 727, 0, 106701, 106701, "2025/03/03 18:59:59", "ほまれ", "ホマレ", "【魔法】後衛から無窮の魔力で全てを支配する龍の首領。\n障壁となる敵を龍眼で捉え、多彩な補助系スキルで敵の\n魔法攻撃からの耐性を弱め、雷霆の一撃で灰燼に帰す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 4, 106801, 1.55, 1, 3, 106801, 450, "2025/02/28 12:00:00", 4, 560, 0, 106801, 106801, "2025/03/03 18:59:59", "らびりすた", "ラビリスタ", "【物理】中衛で、万有を自在に創造する『迷宮女王』。\n七冠としての権能を解放するユニオンバーストで自身を\n極大に強化し比類ない攻撃能力で仇なす者を剿滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 45, 106901, 2.0, 1, 3, 106901, 450, "2022/11/15 15:00:00", 0, 660, 0, 106901, 106901, "2030/12/31 15:00:00", "かいざーいんさいと", "カイザーインサイト", "【物理】後衛で、天の星を瞳に宿し、覇道を進む皇。\n権能を解放し敵全体のバリアと全ステータスアップ効果を\n解除し、耐久力ダウンと複数の広範囲攻撃で敵を滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 0, 107001, 2.0, 1, 3, 107001, 450, "2025/02/28 12:00:00", 0, 660, 0, 107001, 107001, "2025/03/03 18:59:59", "ねねか", "ネネカ", "【魔法】後衛で、分身生成し敵を圧倒する『変貌大妃』。\n分身はネネカ自身と同じスキルをくり出し敵を攻撃する。\nその攻撃密度と制圧力の高さは他の追随を許さない。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 4, 107101, 2.0, 1, 3, 107101, 450, "2025/02/28 12:00:00", 10, 290, 0, 107101, 107101, "2025/03/03 18:59:59", "くりすてぃーな", "クリスティーナ", "【物理】前衛で、絶対的な制圧力を誇る『誓約女君』。\n攻防一体かつ超大な破壊力を誇るユニオンバーストと、\nその発動を加速するＴＰ上昇アップスキルを併せ持つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 5, 107501, 2.125, 1, 3, 107501, 450, "2024/06/21 12:00:00", 5, 235, 0, 107501, 107501, "2024/06/26 11:59:59", "ぺこりーぬ", "ペコリーヌ（サマー）", "【物理】前衛から強烈な範囲攻撃を放つ、渚のお姫様。\nかき氷を食べて物理攻撃力を一気に上昇させ、\nユニオンバーストを放つことで前方の敵を殲滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 6, 107601, 2.34, 1, 1, 107601, 450, "2020/06/19 15:00:00", 6, 535, 0, 107601, 107601, "2030/12/31 15:00:00", "こっころ", "コッコロ（サマー）", "【物理】中衛で、物理キャラの補助を行う癒しの巫女。\nＨＰが一番少ない味方単体への特大回復スキルを持ち、\n瀕死の味方ですら瞬時に治癒する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 2, 0, 7, 107701, 2.07, 1, 3, 107701, 450, "2024/06/21 12:00:00", 7, 775, 0, 107701, 107701, "2024/06/26 11:59:59", "すずめ", "スズメ（サマー）", "【魔法】後衛の防衛能力に秀でた、夏のドジっ娘メイド。\n味方全体に防御力アップと継続ＨＰ回復をかけ、\nさらに壁になるゴーレムを呼んで、戦線を支える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 7, 107801, 1.92, 1, 3, 107801, 450, "2024/06/21 12:00:00", 7, 780, 0, 107801, 107801, "2024/06/26 11:59:59", "きゃる", "キャル（サマー）", "【魔法】後衛から強力な単体攻撃を放つ真夏の猫娘。\nスキルで敵の魔法防御力を下げた上で、\n敵単体への魔法攻撃を繰り出し、大ダメージを与える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 2, 107901, 2.0, 1, 3, 107901, 450, "2024/06/21 12:00:00", 2, 225, 0, 107901, 107901, "2024/06/26 11:59:59", "たまき", "タマキ（サマー）", "【物理】前衛で連続攻撃を放つ、海辺の気まぐれ猫娘。\nランダムな相手への単体攻撃を計４度行う\nユニオンバーストは、対象がランダムだが威力は抜群。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 6, 108001, 2.19, 1, 1, 108001, 450, "2020/07/20 15:00:00", 6, 495, 0, 108001, 108001, "2030/12/31 15:00:00", "みふゆ", "ミフユ（サマー）", "【物理】中衛で、攻撃と補助の両方をこなす海の傭兵。\nユニオンバーストの効果で味方全体の物理攻撃力を高め、\n物理攻撃パーティの能力アップに一役買う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 3, 108101, 1.55, 1, 3, 108101, 450, "2024/10/24 12:00:00", 3, 440, 0, 108101, 108101, "2024/10/28 11:59:59", "しのぶ", "シノブ（ハロウィン）", "【物理】中衛で、弱った敵を刈り取る幻夜の霊能少女。\n弱った敵単体に大ダメージを与えるユニオンバーストは\n対象を撃破時さらに敵全体に追加ダメージを与える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 1, 108201, 2.42, 1, 1, 108201, 450, "2020/09/23 15:00:00", 1, 590, 0, 108201, 108201, "2030/12/31 15:00:00", "みやこ", "ミヤコ（ハロウィン）", "【物理】中衛から、敵陣の後方を狙い撃つ幽霊狼少女。\n前方の相手を無視して妨害スキルやユニオンバーストを\n敵後方に放ち、特に防御力が低いキャラの脅威になる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 7, 108301, 2.07, 1, 3, 108301, 450, "2024/10/24 12:00:00", 7, 815, 0, 108301, 108301, "2024/10/28 11:59:59", "みさき", "ミサキ（ハロウィン）", "【魔法】後衛で、妨害スキルを操るオシャレ魔法少女。\n相手を誘惑状態にするスキルや、複数の状態異常を\n同時に付与するユニオンバーストで相手をかく乱する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 2, 0, 7, 108401, 2.27, 1, 3, 108401, 450, "2024/12/22 12:00:00", 7, 770, 0, 108401, 108401, "2024/12/31 11:59:59", "ちか", "チカ（クリスマス）", "【魔法】後衛で、精霊を唱喚して戦う雪華の歌姫。\nユニオンバーストで呼び出す精霊たちはＨＰやＴＰを\n回復する能力を持ち、不利な戦況をも一変させる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 1, 0, 3, 108501, 1.55, 1, 1, 108501, 450, "2020/11/24 15:00:00", 3, 295, 0, 108501, 108501, "2030/12/31 15:00:00", "くるみ", "クルミ（クリスマス）", "【物理】前衛から、ベルの音色で味方を守る小さな女優。\n攻撃を受けるまで周囲にいる味方の防御力を大きく高める\nスキルを持つ。活用できれば無類の耐久力を誇る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 1, 0, 3, 108601, 1.425, 1, 3, 108601, 450, "2024/12/22 12:00:00", 3, 190, 0, 108601, 108601, "2024/12/31 11:59:59", "あやね", "アヤネ（クリスマス）", "【物理】前衛で、敵をなぎ倒していく聖夜のくまっ子。\nユニオンバーストはＨＰを消費するが威力は絶大。\nスキルで物理攻撃力を上げ、さらにダメージアップ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 1, 108701, 2.17, 1, 3, 108701, 450, "2025/01/15 15:00:00", 1, 170, 0, 108701, 108701, "2025/01/20 11:59:59", "ひより", "ヒヨリ（ニューイヤー）", "【物理】前衛で、物理火力を担う、振り袖姿の元気娘。\n物理攻撃キャラの攻撃力を上昇させるスキルを使う。\nさらに高い威力の単体ユニオンバーストの攻撃力は随一。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 2, 0, 7, 108801, 2.27, 1, 3, 108801, 450, "2025/01/15 15:00:00", 21, 745, 0, 108801, 108801, "2025/01/20 11:59:59", "ゆい", "ユイ（ニューイヤー）", "【魔法】後衛の、補助魔法に特化した、晴れ姿の癒し手。\n自身の攻撃力と引き換えに強力なバリアと継続回復状態を\n味方全体に付与する。絶大な防御能力は他を圧倒する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 4, 108901, 2.215, 1, 1, 108901, 450, "2020/12/22 15:00:00", 22, 153, 0, 108901, 108901, "2030/12/31 15:00:00", "れい", "レイ（ニューイヤー）", "【物理】前衛で、魔法攻撃から味方を守る、華麗な剣士。\nスキルで範囲内の味方に魔法無効バリアを展開し、\nさらにユニオンバーストで敵をスタンさせ攻撃を断つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 1, 0, 3, 109001, 1.9, 1, 1, 109001, 450, "2021/01/25 15:00:00", 3, 187, 0, 109001, 109001, "2030/12/31 15:00:00", "えりこ", "エリコ（バレンタイン）", "【物理】前衛で、ピンチをチャンスに変える愛の壊し屋。\nスキルはＨＰを消費し、自身の攻撃力を高める。また、\nユニオンバーストはＨＰが低いほど超絶威力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 5, 109101, 2.125, 1, 3, 109101, 450, "2025/02/20 12:00:00", 5, 385, 0, 109101, 109101, "2025/02/24 11:59:59", "しずる", "シズル（バレンタイン）", "【物理】中衛で皆を助けるパティシエールのお姉ちゃん。\n複数のパラメータが同時に上昇するフィールドを展開し、\nさらに効果時間中はスキル効果が大幅に強化される。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 2, 0, 7, 109201, 2.27, 0, 3, 109201, 450, "2019/03/31 12:00:00", 23, 630, 0, 109201, 109201, "2030/12/31 15:00:00", "あん", "アン", "【魔法】後衛で、英霊の召喚術を武器に戦うお姫様。\n味方に英霊の加護を付与するスキルによるサポーターと、\n高威力の魔法によるアタッカーの二つの役割を併せ持つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 109301, 2.27, 1, 1, 109301, 450, "2021/03/23 15:00:00", 23, 640, 0, 109301, 109301, "2030/12/31 15:00:00", "るぅ", "ルゥ", "【魔法】後衛で、オメメちゃんと戦う、ノーテンキ少女。\nスキルで呼び出すオメメちゃんは敵の攻撃を防ぐほか、\nその数が多いほどユニオンバーストの威力を増加させる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 1, 109401, 2.27, 0, 3, 109401, 450, "2019/04/19 15:00:00", 1, 525, 0, 109401, 109401, "2030/12/31 15:00:00", "ぐれあ", "グレア", "【魔法】中衛で、炎を操り敵を薙ぎ払う、竜人の少女。\n高い魔法攻撃力を生かした範囲攻撃を得意とする。\nまた、飛行能力を駆使して放つ突進攻撃も強力。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 109501, 2.066, 0, 3, 109501, 450, "2019/04/30 12:00:00", 2, 140, 0, 109501, 109501, "2030/12/31 15:00:00", "くうか", "クウカ（オーエド）", "【魔法】最前衛で、味方を鼓舞する着物姿のドＭ娘。\n味方の魔法攻撃力を高め、敵の魔法防御力を下げる事で、\n魔法パーティのサポート兼防御役として活躍する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 2, 109601, 2.0, 0, 3, 109601, 450, "2019/05/17 15:00:00", 2, 175, 0, 109601, 109601, "2030/12/31 15:00:00", "にのん", "ニノン（オーエド）", "【物理】最前衛で、忍法を駆使して敵と戦うクノイチ。\nユニオンバーストはクリティカル時のダメージを１．５倍\nにする特性を持ち、うまくかみ合った時の威力は抜群。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 1, 0, 13, 109701, 2.25, 1, 3, 109701, 450, "2024/09/15 15:00:00", 24, 540, 0, 109701, 109701, "2024/09/25 11:59:59", "れむ", "レム", "【物理】中衛で、鬼の力で敵を粉砕する、双子の妹。\n回復スキルの他にも、温度を操るスキルを得意とし、\n敵を凍結させた上でユニオンバーストで薙ぎ払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 1, 0, 9, 2, 0, 7, 109801, 2.5, 1, 1, 109801, 450, "2015/04/01 15:00:00", 24, 545, 0, 109801, 109801, "2030/12/31 15:00:00", "らむ", "ラム", "【魔法】中衛で、特殊能力で味方を補助する、双子の姉。\n相手の物理攻撃を見切って回避させる「千里眼」や、\n敵全体を束縛するユニオンバーストの補助は効果絶大。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 109901, 2.27, 1, 3, 109901, 450, "2024/09/15 15:00:00", 23, 725, 0, 109901, 109901, "2024/09/25 11:59:59", "えみりあ", "エミリア", "【魔法】後衛で、攻撃と回復をこなす、ハーフエルフ。\n高い魔力で攻撃魔法を繰り出して敵を蹴散らしつつ、\nユニオンバーストは瀕死の味方すらも瞬時に癒す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 1, 0, 8, 110001, 1.82, 1, 3, 110001, 450, "2024/06/21 12:00:00", 8, 705, 0, 110001, 110001, "2024/06/26 11:59:59", "すずな", "スズナ（サマー）", "【物理】後衛で高威力の弓技を放つ夏のカリスマモデル。\nバトル中に放つ「サマーランウェイ♪」でＴＰを全回復\nして、発動後のユニオンバーストがクリティカルになる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 5, 110101, 2.4, 1, 1, 110101, 450, "2021/05/18 15:00:00", 5, 715, 0, 110101, 110101, "2030/12/31 15:00:00", "いお", "イオ（サマー）", "【魔法】後衛で、敵の体力を奪い取る、夏の夜の先生。\nダメージを与えつつ味方を回復するスキルを複数持ち、\n攻守どちらの側でも高い能力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 0, 0, 7, 0, 0.0, 0, 1, 110201, 0, "2015/04/01 15:00:00", 7, 0, 0, 110201, 0, "2030/12/31 15:00:00", "みさき", "ミサキ（サマー）", "");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 4, 110301, 2.24, 1, 3, 110301, 450, "2024/06/21 12:00:00", 4, 585, 0, 110301, 110301, "2024/06/26 11:59:59", "されん", "サレン（サマー）", "【物理】中衛で、味方の速攻を支える海辺のサレンママ。\nバトル開始時に味方全体のＴＰを一気に溜めることで、\n迅速にユニオンバーストを発動させることを可能にする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 5, 110401, 2.125, 1, 3, 110401, 450, "2024/07/23 12:00:00", 5, 180, 0, 110401, 110401, "2024/07/31 11:59:59", "まこと", "マコト（サマー）", "【物理】前衛で攻撃対象が１つの敵に特化した海の狼娘。\n攻撃対象が１つの場合、スキルとユニオンバーストともに\n真価を発揮し他を圧倒する絶大な破壊力で敵をなぎ払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 1, 110501, 2.42, 1, 1, 110501, 450, "2021/06/18 15:00:00", 1, 425, 0, 110501, 110501, "2030/12/31 15:00:00", "かおり", "カオリ（サマー）", "【物理】中衛で、敵をまとめてなぎ倒す、真夏の南国娘。\n吹き飛ばし効果を持つスキルで敵をまとめ、攻撃対象が\n多い程、威力が増すユニオンバーストで敵をなぎ倒す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 2, 0, 7, 110601, 2.27, 1, 3, 110601, 450, "2024/07/23 12:00:00", 7, 792, 0, 110601, 110601, "2024/07/31 11:59:59", "まほ", "マホ（サマー）", "【魔法】後衛で、膨大な魔力を振るう、真夏のおとぎ姫。\n高火力で対象がランダムなスキルやユニオンバーストは、\n攻撃対象が１つの敵に対して絶大なダメージを叩き出す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 8, 110701, 1.82, 0, 3, 110701, 450, "2019/09/03 21:00:00", 8, 680, 0, 110701, 110701, "2030/12/31 15:00:00", "あおい", "アオイ（編入生）", "【物理】後衛で毒を操る、ぼっちエルフの編入生。\n敵が毒や猛毒になっていると強化される攻撃スキルと、\n敵の物理防御力を下げるスキルで味方をサポートする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 2, 110801, 2.125, 0, 3, 110801, 450, "2019/09/16 15:00:00", 2, 185, 0, 110801, 110801, "2030/12/31 15:00:00", "くろえ", "クロエ", "【物理】前衛で、手数で圧倒する、ダウナー系エルフ。\n攻撃するたびに相手に【畏縮】ステータスを追加し、\nスキルのダメージと物理防御力ダウンに磨きをかける。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 1, 110901, 2.295, 0, 3, 110901, 450, "2020/04/15 15:00:00", 1, 222, 0, 110901, 110901, "2030/12/31 15:00:00", "ちえる", "チエル", "【物理】前衛でパンチを放つちぇる盛りＭＡＸな女の子。\nクリティカルを出すたびに【ちぇる】を溜め、自己を強化\nし続け、後半になるほど敵をちぇるっとじぇのさいする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 2, 0, 7, 111001, 2.27, 0, 3, 111001, 450, "2020/03/31 12:00:00", 26, 807, 0, 111001, 111001, "2030/12/31 15:00:00", "ゆに", "ユニ", "【魔法】後衛で、味方の支援に特化した象牙の塔の才媛。\n自身の攻撃力は低いが、物理攻撃力の高い味方を強化し\n更に味方全体を魔法無効バリアで護りつつ勝利へ導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 2, 0, 7, 111101, 2.07, 1, 3, 111101, 450, "2024/10/24 12:00:00", 7, 820, 0, 111101, 111101, "2024/10/28 11:59:59", "きょうか", "キョウカ（ハロウィン）", "【魔法】後衛で味方の火力を高める、黒猫仮装の優等生。\n自分の魔法攻撃力に応じて効果が強化されるものと、\n効果が累積するもの等、多彩なスキルで攻撃力を高める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 1, 111201, 2.17, 1, 1, 111201, 450, "2021/08/16 15:00:00", 1, 212, 0, 111201, 111201, "2030/12/31 15:00:00", "みそぎ", "ミソギ（ハロウィン）", "【物理】前衛で範囲攻撃を放つ、カボチャ衣装の悪戯娘。敵に５カウントで爆発する爆弾を仕掛けたり、カボチャの手榴弾を投げつけたりと、様々な範囲攻撃で敵を弄ぶ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 5, 111301, 2.0, 0, 3, 111301, 450, "2019/10/16 15:00:00", 5, 365, 0, 111301, 111301, "2030/12/31 15:00:00", "みみ", "ミミ（ハロウィン）", "【物理】中衛の、攻撃に特化した、ぐるぐるうさぎっこ。\n行動頻度は少ないが、高威力の範囲攻撃を放ち、\n味方全体の物理攻撃力を大きく高めるスキルも効果抜群。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 111401, 1.87, 0, 3, 111401, 450, "2019/10/31 12:00:00", 25, 765, 0, 111401, 111401, "2030/12/31 15:00:00", "るな", "ルナ", "【魔法】後衛で、死霊術でおともだちを作る可憐な少女。\nルナがダメージを与える度に集まってくるおともだちを、\n各スキルで消費する。消費数に応じ強大な力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 4, 111501, 2.0, 1, 3, 111501, 450, "2024/12/22 12:00:00", 10, 265, 0, 111501, 111501, "2024/12/31 11:59:59", "くりすてぃーな", "クリスティーナ（クリスマス）", "【物理】前衛で戦いを愉しむ、聖夜の『誓約女君』。\nユニオンバーストを使用すると得られるコインの有無で、\n補助と攻撃の二役を切り替え、華麗に敵を斬り倒す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 4, 111601, 2.24, 1, 1, 111601, 450, "2021/10/19 15:00:00", 4, 418, 0, 111601, 111601, "2030/12/31 15:00:00", "のぞみ", "ノゾミ（クリスマス）", "【物理】中衛でパーティの守りを支えるサンタアイドル。\nスキルで物理攻撃を無効化し、後方の味方が多いほど\n物理防御力を高めるユニオンバーストで生存力を高める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 2, 0, 3, 111701, 1.3, 0, 3, 111701, 450, "2019/12/20 15:00:00", 3, 255, 0, 111701, 111701, "2030/12/31 15:00:00", "いりや", "イリヤ（クリスマス）", "【魔法】前衛で、血を対価に敵を攻撃する聖夜の吸血鬼。\n残りＨＰが低い程ダメージが上昇するスキルと、ＨＰを\n消費するスキルを組み合わせ、絶大な破壊力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 13, 1, 0, 4, 111801, 1.965, 1, 3, 111801, 450, "2025/01/15 15:00:00", 22, 248, 0, 111801, 111801, "2025/01/20 11:59:59", "ぺこりーぬ", "ペコリーヌ（ニューイヤー）", "【物理】前衛で元気いっぱいに新年を祝う腹ペコお姫様。\n物理攻撃力を高めるスキルで味方を強化し、ＨＰが多い\nほどダメージが上昇するユニオンバーストで敵を討つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 7, 111901, 1.87, 1, 3, 111901, 450, "2025/01/15 15:00:00", 23, 159, 0, 111901, 111901, "2025/01/20 11:59:59", "こっころ", "コッコロ（ニューイヤー）", "【物理】前衛で、味方の補助に秀でた、新風の巫女。\n物理クリティカル時のダメージアップのスキルで味方の\n殲滅力を高めつつ、挑発や継続回復で耐久面を支える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 2, 0, 7, 112001, 2.07, 1, 3, 112001, 450, "2025/01/15 15:00:00", 21, 690, 0, 112001, 112001, "2025/01/20 11:59:59", "きゃる", "キャル（ニューイヤー）", "【魔法】後衛で、攻防双方で活躍する晴れ着姿の猫娘。\n与ダメージに応じて魔法攻撃力がアップするユニオン\nバーストで、高いベースの攻撃力がさらに強大になる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 7, 112101, 2.27, 1, 1, 112101, 450, "2021/11/16 15:00:00", 21, 722, 0, 112101, 112101, "2030/12/31 15:00:00", "すずめ", "スズメ（ニューイヤー）", "【魔法】後衛で祈りによるサポートを行うドジっ娘巫女。\nスキルの魔法無効バリアで耐久力を高め、ユニオン\nバーストでＨＰ回復と行動速度アップで戦況を整える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 112201, 2.27, 0, 3, 112201, 450, "2020/01/31 12:00:00", 7, 730, 0, 112201, 112201, "2030/12/31 15:00:00", "かすみ", "カスミ（マジカル）", "【魔法】後衛の、妨害に特化した能力を持つ魔法探偵。\n相手のＴＰを大きく減少させるスキルや、遠くの敵を\n束縛するスキルを操り、相手の連携を大きく乱す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 8, 112301, 1.97, 1, 1, 112301, 450, "2021/12/16 15:00:00", 8, 712, 0, 112301, 112301, "2030/12/31 15:00:00", "しおり", "シオリ（マジカル）", "【物理】後衛で味方の火力の底上げを担う魔法狩人。\nユニオンバーストで味方全体の物理攻撃力を大きく高め\nスキルは範囲内の物理攻撃力を高め、輝矢の一撃を放つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 18, 1, 0, 4, 112401, 2.24, 1, 3, 112401, 450, "2021/03/08 12:00:00", 4, 370, 0, 112401, 112401, "2021/03/24 11:59:59", "うづき", "ウヅキ（デレマス）", "【物理】中衛で、味方を鼓舞して戦う、キュートな少女。\n味方をＴＰや攻撃力をアップさせるスキルでサポートし、\n自身も強力な範囲攻撃で敵をなぎ払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 18, 1, 0, 4, 112501, 2.215, 1, 3, 112501, 450, "2021/03/08 12:00:00", 4, 153, 0, 112501, 112501, "2021/03/24 11:59:59", "りんでれます", "リン（デレマス）", "【物理】前衛で、氷を創出し味方を護る、クールな少女。\n目の前の敵を束縛するスキルや挑発で行動を妨害し、\n自身にもバリアを展開し、鉄壁の防御能力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 1, 0, 18, 2, 0, 7, 112601, 2.27, 1, 1, 112601, 450, "2015/04/01 15:00:00", 7, 695, 0, 112601, 112601, "2030/12/31 15:00:00", "みお", "ミオ（デレマス）", "【魔法】後衛で攻防共に活躍するパッション溢れる少女。\nダメージを受けた味方中心に回復魔法で補助をし、\nさらに魔法攻撃を仕掛けて、敵を一網打尽にする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 6, 112701, 2.19, 0, 3, 112701, 450, "2020/04/30 12:00:00", 6, 422, 0, 112701, 112701, "2030/12/31 15:00:00", "りん", "リン（レンジャー）", "【物理】中衛から、敵の守りを貫くドングリレンジャー。\n戦闘開始と共に範囲内の敵の物理防御力を大幅に下げ、\n序盤から大ダメージを与えられる戦況を作り出す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 6, 112801, 2.19, 0, 3, 112801, 450, "2020/05/15 15:00:00", 6, 390, 0, 112801, 112801, "2030/12/31 15:00:00", "まひる", "マヒル（レンジャー）", "【物理】中衛で、多勢相手が十八番のまきばの戦士。\n相手を吹き飛ばすスキルを多用し敵の陣形を押し込む。\n範囲攻撃と混乱付与スキルを駆使し一気に殲滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 8, 112901, 1.97, 0, 3, 112901, 450, "2020/06/03 19:00:00", 8, 730, 0, 112901, 112901, "2030/12/31 15:00:00", "りの", "リノ（ワンダー）", "【物理】後衛で、希望の力で戦う、不思議の国の救世主。\nクリティカルすると効果アップのスキルや味方の数が多い\n程、威力がアップするユニオンバーストで敵を殲滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 6, 113001, 2.34, 1, 1, 113001, 450, "2020/05/31 12:00:00", 6, 508, 0, 113001, 113001, "2030/12/31 15:00:00", "あゆみ", "アユミ（ワンダー）", "【物理】中衛で、戦場の動向を自在に操る白兎の乙女。\n敵の行動速度をダウンさせ、味方の速度をアップさせる\nスキルと敵の時を止めるスキルで、常に味方を支える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 27, 113101, 1.3, 1, 3, 113101, 450, "2024/07/23 12:00:00", 27, 192, 0, 113101, 113101, "2024/07/31 11:59:59", "るか", "ルカ（サマー）", "【物理】前衛で、流麗な太刀捌きを魅せる若夏の姉御。\nダメージを与えた回数に応じて攻撃力が高まるスキルと、\n携えた二刀の幾刃もの斬撃で、眼前の敵を薙ぎ払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 5, 113201, 1.625, 1, 1, 113201, 450, "2022/04/18 15:00:00", 28, 256, 0, 113201, 113201, "2030/12/31 15:00:00", "あんな", "アンナ（サマー）", "【魔法】前衛で皆の力を借り受け力を振るう真夏の冥姫。\n味方全体の魔法攻撃力を代償に自身の魔法攻撃力を劇的に\n高め、熾烈な攻撃魔法を繰り出し大ダメージを与える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 113301, 2.07, 0, 3, 113301, 450, "2020/07/15 15:00:00", 7, 468, 0, 113301, 113301, "2030/12/31 15:00:00", "ななか", "ナナカ（サマー）", "【魔法】中衛の多勢相手の戦いが得意な真夏の魔法少女。\n敵の数に応じて効果が上昇する防御力ダウンのスキルや、\n多彩な範囲攻撃を操り、真夏の戦いをサイカワに制する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 113401, 1.92, 1, 3, 113401, 450, "2024/07/23 12:00:00", 29, 567, 0, 113401, 113401, "2024/07/31 11:59:59", "はつね", "ハツネ（サマー）", "【魔法】中衛から全体魔法攻撃を放つ超能力チアガール。\nユニオンバーストで敵全体に絶大な一撃を放ち、\nその後、超能力発動の反動で枕を抱えて夢の中へ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 113501, 2.07, 1, 1, 113501, 450, "2022/05/18 15:00:00", 7, 697, 0, 113501, 113501, "2030/12/31 15:00:00", "みさと", "ミサト（サマー）", "【魔法】後衛で、敵の脅威から生徒を守る夏女神な先生。\n味方の魔法防御力を高め、ＨＰを継続回復するスキルや、\nバリア展開のユニオンバーストで味方を堅固に守護する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 5, 113601, 2.0, 0, 3, 113601, 450, "2020/08/15 15:00:00", 5, 182, 0, 113601, 113601, "2030/12/31 15:00:00", "じゅん", "ジュン（サマー）", "【物理】前衛で、業火を従え悪を討つ真夏の騎士団長。\n敵単体にダメージを与えつつ、攻撃力を高めるユニオン\nバーストや、火傷を与えるスキルを操り敵を焼き尽くす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 113701, 1.92, 0, 3, 113701, 450, "2020/09/03 19:00:00", 7, 530, 0, 113701, 113701, "2030/12/31 15:00:00", "あかり", "アカリ（エンジェル）", "【魔法】中衛で、魔法攻撃をお見舞いする双子の妹天使。\n効果時間が短い分、絶大な効果の強化魔法で自身の攻撃力\nを大きく高め、威力抜群の単体魔法で敵を一掃する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 6, 113801, 2.34, 0, 3, 113801, 450, "2020/09/19 15:00:00", 6, 531, 0, 113801, 113801, "2030/12/31 15:00:00", "より", "ヨリ（エンジェル）", "【魔法】中衛で、補助魔法で味方を助ける双子の姉天使。\n防御力を大幅に高めつつ、対象の味方に敵の注意を引か\nせるなど、特殊で強力なスキルを駆使し敵を翻弄する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 2, 113901, 2.375, 1, 3, 113901, 450, "2024/10/24 12:00:00", 2, 152, 0, 113901, 113901, "2024/10/28 11:59:59", "つむぎ", "ツムギ（ハロウィン）", "【物理】前衛で敵の物理攻撃を妨害する吸血鬼テーラー。\n挑発と物理無効バリア、さらに最も物理攻撃力が高い敵を\n狙った妨害スキルによって、鉄壁の防御を織り上げる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 4, 114001, 1.965, 0, 3, 114001, 450, "2020/10/15 15:00:00", 4, 375, 0, 114001, 114001, "2030/12/31 15:00:00", "れい", "レイ（ハロウィン）", "【物理】中衛で、手負いの敵を死に誘う、幽雅の剣士。\nＨＰが50％未満の敵に対し放つと、威力が大アップする\n範囲攻撃を繰り出し負傷した敵の戦意を一気に叩き折る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 1, 114101, 2.295, 1, 1, 114101, 450, "2022/07/19 8:00:00", 1, 186, 0, 114101, 114101, "2030/12/31 15:00:00", "まつり", "マツリ（ハロウィン）", "【物理】前衛で鋭爪を突き立てるハロウィン騎士見習い。\nユニオンバーストの使用回数に応じて強化されるスキルで\n与えるダメージと行動速度をアップさせ、敵を斬り裂く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 4, 114201, 2.24, 0, 3, 114201, 450, "2020/10/31 12:00:00", 4, 528, 0, 114201, 114201, "2030/12/31 15:00:00", "もにか", "モニカ（マジカル）", "【物理】中衛で愛と希望の力を胸に指揮を執る魔法提督。\nユニオンバーストで敵単体の物理防御力を下げ、範囲内の\n味方の物理攻撃力アップスキルで、一気に戦況を決める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 4, 114301, 2.0, 0, 3, 114301, 450, "2020/11/21 15:00:00", 4, 402, 0, 114301, 114301, "2030/12/31 15:00:00", "とも", "トモ（マジカル）", "【物理】中衛で正義の力を魔法にのせて戦う魔法剣士。\n自身の物理攻撃力を魔法攻撃力に加える魔法少女の力と、\nたゆまぬ努力で獲得した剣技で、華麗な魔法攻撃を放つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 5, 114401, 2.0, 1, 3, 114401, 450, "2024/12/22 12:00:00", 5, 189, 0, 114401, 114401, "2024/12/31 11:59:59", "あきの", "アキノ（クリスマス）", "【物理】前衛で味方のサポートを力に戦う、聖夜の令嬢。\nステータスアップ効果を受けると増加する【聖夜の煌き】\nでスキルを大きく強化し、聖夜を彩る強烈な一撃を放つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 4, 114501, 2.09, 0, 3, 114501, 450, "2020/12/21 15:00:00", 4, 150, 0, 114501, 114501, "2030/12/31 15:00:00", "されん", "サレン（クリスマス）", "【物理】前衛で、光を操り味方を守る聖夜のサレンママ。\n敵の攻撃を防ぎ、ダメージを与えるユニオンバーストや、\nダメージを与えつつ回復するスキルで、攻守双方を担う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 5, 114601, 2.09, 1, 1, 114601, 450, "2022/09/17 15:00:00", 5, 408, 0, 114601, 114601, "2030/12/31 15:00:00", "ゆかり", "ユカリ（クリスマス）", "【物理】中衛で物理キャラを支援し戦う聖夜の元聖騎士。\n魔法攻撃力の高い敵単体のＴＰ減少や周囲の味方の魔法\n防御力アップスキルで、対魔法戦で無類の輝きを放つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 2, 114701, 2.0, 1, 1, 114701, 450, "2022/10/16 15:00:00", 31, 138, 0, 114701, 114701, "2030/12/31 15:00:00", "むいみ", "ムイミ（ニューイヤー）", "【物理】前衛で、新風纏う「天楼覇断剣」を振るう少女。\n皆より一足遅れて突撃し、敵の前衛を吹き飛ばした後、\n強化した範囲物理攻撃で、ダンゴになった敵を斬り払う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 2, 0, 7, 115001, 2.07, 1, 3, 115001, 450, "2025/01/15 15:00:00", 21, 562, 0, 115001, 115001, "2025/01/20 11:59:59", "ねねか", "ネネカ（ニューイヤー）", "【魔法】中衛で艶やかな晴れ着を身に纏う『変貌大妃』。\n味方の魔法攻撃力アップやＨＰ、ＴＰを回復させるスキル\nで攻守にわたってサポートし苛烈な戦況でも勝利に導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 2, 115501, 2.0, 1, 3, 115501, 450, "2025/02/20 12:00:00", 2, 533, 0, 115501, 115501, "2025/02/24 11:59:59", "こっころ", "コッコロ（儀装束）", "【物理】中衛で、尊き儀装束を纏うエルフの里の巫女。\n自分の攻撃力を強化し、さらにＴＰ回復で攻撃の手数を\n増やす。クリティカル時の猛攻で仲間を守る剣となる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 2, 0, 7, 115601, 1.97, 1, 1, 115601, 450, "2022/11/17 15:00:00", 7, 578, 0, 115601, 115601, "2030/12/31 15:00:00", "ゆい", "ユイ（儀装束）", "【魔法】中衛で精霊術を操り味方を支える儀装の魔法士。\n複数の物理攻撃力の強化スキルで味方を攻撃支援しつつ\n強力な回復スキルで、前線を整えバトルを掌握する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 115701, 2.27, 0, 3, 115701, 450, "2021/02/23 15:00:00", 7, 738, 0, 115701, 115701, "2030/12/31 15:00:00", "かすみ", "カスミ（サマー）", "【魔法】後衛で、敵の防御力を削ぎ取る渚の名探偵。\n攻撃力が高い敵に目標を定めて防御力を下げつつ、\n味方を強化するスキルで、どんな難事件も解決へと導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 116101, 4, 115801, 2.27, 1, 1, 115801, 450, "2022/12/19 15:00:00", 4, 173, 0, 115801, 115801, "2030/12/31 15:00:00", "りま", "リマ（シンデレラ）", "【物理】前衛の魔法の果実でお姫様姿になった恋乙女。\n獣の姿からユニオンバーストで一定時間、人の姿に変身し\n味方全体の攻撃力アップスキルで、火力の底上げをする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 5, 115901, 2.27, 0, 3, 115901, 450, "2021/03/03 19:00:00", 5, 166, 0, 115901, 115901, "2030/12/31 15:00:00", "まこと", "マコト（シンデレラ）", "【物理】前衛で、華装に身を包み流麗な剣舞を舞う狼娘。\n周囲の味方の数に応じて、スキルの威力と物理攻撃力\nアップのスキル効果が上がり、鼓舞激励の一斬を振るう。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 2, 0, 7, 116001, 2.27, 0, 3, 116001, 450, "2021/03/22 15:00:00", 7, 645, 0, 116001, 116001, "2030/12/31 15:00:00", "まほ", "マホ（シンデレラ）", "【魔法】後衛から、優雅に味方を支援するマホ先生。\n物理攻撃をする味方の攻撃力アップや、最遠の味方のＴＰ\nを回復するスキルで、しゃなりしゃなりと戦況を変える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 2, 116201, 2.125, 0, 3, 116201, 450, "2021/04/15 15:00:00", 2, 184, 0, 116201, 116201, "2030/12/31 15:00:00", "くろえ", "クロエ（聖学祭）", "【物理】前衛で疾風の如く駆け抜ける、ダウナー女子。\n特定回数ダメージを与えるたびＴＰを全回復するスキルと\n発動ごとに攻撃力アップのスキルで勝利のテープを掴む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 1, 116301, 2.295, 0, 3, 116301, 450, "2021/03/31 12:00:00", 32, 223, 0, 116301, 116301, "2030/12/31 15:00:00", "ちえる", "チエル（聖学祭）", "【物理】前衛で聖学祭を盛りアゲる、はぴちぇる系女子。\n攻撃ヒットのたびに【ちぇる】を貯め、威力が増すスキル\nと味方全体の物理攻撃力アップ効果のＵＢで勝利に導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 2, 0, 7, 116401, 2.27, 0, 3, 116401, 450, "2022/02/08 12:00:00", 39, 808, 0, 116401, 116401, "2030/12/31 15:00:00", "ゆに", "ユニ（聖学祭）", "【魔法】後衛から、仲間を啓蒙するユニちゃんズの才媛。\n自分以外の味方全員の物理・魔法両方の攻撃力アップと\nクリティカル時のダメージアップで皆を勝利へと導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 1, 0, 3, 116501, 2.295, 0, 3, 116501, 450, "2021/04/30 12:00:00", 3, 252, 0, 116501, 116501, "2030/12/31 15:00:00", "いのり", "イノリ（タイムトラベル）", "【物理】前衛で威勢よく、まくしたてる口撃系ドラゴン。\n全体物理攻撃力アップのＵＢは、味方の防御力を下げる\n代わりにＨＰが多いほど効果を高め、味方を勝利に導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 1, 0, 1, 116601, 2.295, 0, 3, 116601, 450, "2021/05/21 15:00:00", 1, 169, 0, 116601, 116601, "2030/12/31 15:00:00", "かや", "カヤ（タイムトラベル）", "【物理】前衛で味方を勝利にナビする喧嘩屋ドラゴン族。\nユニオンバーストで自身の攻撃速度を極大アップさせ、\n物理攻撃力アップスキルのシナジーで拳の雨を降らせる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 1, 0, 33, 116701, 1.82, 0, 3, 116701, 450, "2021/05/31 12:00:00", 33, 527, 0, 116701, 116701, "2030/12/31 15:00:00", "あおい", "アオイ（作業服）", "【物理】中衛で敵を妨害する、作業服姿のぼっちエルフ。\n友情の絆で結ばれた『マイフレンドくん』と放つＵＢは、\n敵の動きを阻み、物理防御力を劇的に下げる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 2, 116801, 2.295, 1, 1, 116801, 450, "2023/03/16 15:00:00", 2, 216, 0, 116801, 116801, "2030/12/31 15:00:00", "たまき", "タマキ（作業服）", "【物理】前衛でたい焼き器片手に悪を討つ売り子の猫娘。\n味方全体を物理攻撃力アップとＴＰ回復させ、ＵＢでさら\nに自分の攻撃力を特大強化し、驚異の威力を発揮する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 6, 116901, 2.065, 0, 3, 116901, 450, "2021/06/16 15:00:00", 6, 275, 0, 116901, 116901, "2030/12/31 15:00:00", "みふゆ", "ミフユ（作業服）", "【物理】前衛で、皆の盾となり進攻するバイトリーダー。\nバトル序盤は物理の攻防両方をスキルで強化。ＵＢ発動毎\nに自分のクリティカルをアップし猛烈な攻撃を繰り出す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 1, 0, 3, 117001, 1.55, 1, 3, 117001, 450, "2024/08/26 19:00:00", 3, 407, 0, 117001, 117001, "2024/08/31 11:59:59", "えりこ", "エリコ（サマー）", "【物理】中衛で、火花を纏い夕闇を彩る真夏の壊し屋。\n二つのスキルで物理防御力を下げ、ダメージ量に応じて\nクリティカル上昇が延長するＵＢで眼前の敵を粉砕する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 5, 117101, 2.0, 0, 3, 117101, 450, "2021/07/15 12:00:00", 5, 163, 0, 117101, 117101, "2030/12/31 15:00:00", "しずる", "シズル（サマー）", "【物理】前衛で、堅牢に仲間を守る海辺のお姉ちゃん。\n物理防御力が高い程、効果が高まる複数のスキルを巧みに\n操り防御と補助役をしつつ範囲攻撃で攻撃役をもこなす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 1, 0, 4, 117201, 2.09, 1, 3, 117201, 450, "2024/08/26 19:00:00", 4, 417, 0, 117201, 117201, "2024/08/31 11:59:59", "のぞみ", "ノゾミ（サマー）", "【物理】中衛でみんなを応援する真夏のトップアイドル。\nすべてのスキルで味方全体の物理攻撃をサポートし、\nバトル中盤から味方全体のＴＰ回復で、皆を勝利に導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 2, 0, 7, 117301, 2.07, 0, 3, 117301, 450, "2021/08/15 15:00:00", 7, 520, 0, 117301, 117301, "2030/12/31 15:00:00", "ちか", "チカ（サマー）", "【魔法】中衛で、守護の賛歌を捧げる夏海の歌姫。\n最序盤に味方の魔法防御を大幅に強化し、吸収バリアや\nＨＰ回復スキルで、如何なる猛攻からも味方を守る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 10, 1, 0, 1, 117401, 2.42, 1, 1, 117401, 450, "2023/05/16 15:00:00", 1, 355, 0, 117401, 117401, "2030/12/31 15:00:00", "つむぎ", "ツムギ（サマー）", "【物理】中衛で敵を引き寄せ陣形を崩す夕凪のテーラー。\n最も攻撃力の高い敵を、敵陣から自陣に引き寄せつつ\n束縛や行動速度ダウンを絡めたスキルで敵の自由を縛る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 11, 1, 0, 5, 117501, 2.4, 0, 3, 117501, 450, "2021/09/03 19:00:00", 5, 552, 0, 117501, 117501, "2030/12/31 15:00:00", "みつき", "ミツキ（オーエド）", "【物理】中衛で、戦況のすべてを見据える隻眼の闇医者。\nＨＰが低い敵を束縛し防御力ダウンスキルで仕掛けつつ\n呪詛の毎秒増加する継続ダメージで敵の命を刻々と蝕む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 117601, 2.0, 0, 3, 117601, 450, "2021/09/13 12:00:00", 7, 798, 0, 117601, 117601, "2030/12/31 15:00:00", "ゆき", "ユキ（オーエド）", "【魔法】後衛で、迷える皆を救う白衣を纏いし美の象徴。\n一番低い味方１キャラのＴＰを回復し、ＨＰ回復スキルで\n守り、魔法攻撃力アップのスキルで一気に攻め落とす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 1, 117701, 2.17, 1, 3, 117701, 450, "2024/10/24 12:00:00", 1, 156, 0, 117701, 117701, "2024/10/28 11:59:59", "かおり", "カオリ（ハロウィン）", "【物理】前衛の敵を追い詰める程、力を増す幽玄南国娘。\n敵の体力が少ない程、効果が高まる攻撃力アップスキルと\nダメージ量アップの攻撃スキルで、敵を逃さず仕留める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 36, 117801, 2.25, 1, 1, 117801, 450, "2023/07/18 15:00:00", 36, 518, 0, 117801, 117801, "2030/12/31 15:00:00", "にのん", "ニノン（ハロウィン）", "【物理】中衛で物理攻撃タイプの味方を強化するシノビ。\n味方の物理攻撃力アップや行動速度アップのスキル効果で\n味方全体の攻撃力を底上げし、勝利の笛を吹きならす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 1, 0, 8, 117901, 1.97, 0, 3, 117901, 450, "2021/10/15 15:00:00", 8, 761, 0, 117901, 117901, "2030/12/31 15:00:00", "すずな", "スズナ（ハロウィン）", "【物理】後衛でアタッカーを狙う小悪魔カリスマモデル。\n誘惑状態だと威力アップの攻撃スキルや、ＴＰを奪い取る\n妨害スキルを放ち、物理攻撃力が最も高い敵を狙い撃つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 35, 118001, 1.97, 1, 3, 118001, 450, "2024/09/25 12:00:00", 35, 751, 0, 118001, 118001, "2024/09/30 11:59:59", "くれじった", "クレジッタ", "【物理】後衛で、攻撃と敵の弱体化の二役で輝く女商人。\n生ける金貨が詰まった財布を投げつける不意打ちや、\n金貨の嵐を巻き起こす攻撃で、相手の防御を大きく崩す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 19, 2, 0, 7, 118101, 2.07, 1, 3, 118101, 450, "2025/02/28 12:00:00", 21, 782, 0, 118101, 118101, "2025/03/03 18:59:59", "らんふぁ", "ランファ", "【魔法】後衛で、美しい旋律を紡ぎ支援する幽寂の歌姫。\n味方の強化と敵の弱体化の両方面での補助を行い、さらに\n召喚した鉱石の飛竜との波状攻撃により敵を鎮圧する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 19, 2, 0, 0, 118201, 1.7, 1, 3, 118201, 450, "2025/02/28 12:00:00", 0, 730, 0, 118201, 118201, "2025/03/03 18:59:59", "みそら", "ミソラ", "【魔法】後衛で笑顔とともに破壊を振りまく溌溂少女★\n最大３体に着弾するスキルとＵＢは【PSI★チャージ】\n状態で魔防ダウン効果を追加し、邪魔者たちを殲滅する。");
