INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 37, 118501, 2.0, 1, 1, 118501, 450, "2021/10/19 12:00:00", 37, 787, 0, 118501, 118501, "2030/12/31 15:00:00", "かりん", "カリン", "【魔法】後衛で皆を支える、ギルド管理協会の職員さん。\n通常攻撃の代わりに味方の能力を高める魔法を掛け、\n味方の守護や敵への妨害など、完璧なサポートをこなす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 0, 118601, 2.0, 1, 3, 118601, 450, "2024/08/10 21:00:00", 0, 826, 0, 118601, 118601, "2030/12/31 15:00:00", "ねびあ", "ネビア", "【魔法】後衛を飛び出し悪戯の限りを尽くす幻惑の妖精。\n敵陣を勝手気ままに飛び回り、敵の攻撃を空振りさせて、\nＵＢで４種の妨害効果をランダムに与え大混乱に陥れる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 7, 119001, 2.27, 0, 3, 119001, 450, "2021/11/03 19:00:00", 7, 714, 0, 119001, 119001, "2030/12/31 15:00:00", "いお", "イオ（ノワール）", "【魔法】後衛で魔法アタッカーを妨害する闇のせんせー。\n最も魔法攻撃力が高い敵を、誘惑とＴＰダウンで妨害し\nＴＰ回復スキルで味方を援護することで戦況を支配する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 7, 119101, 2.545, 0, 3, 119101, 450, "2021/11/16 12:00:00", 26, 143, 0, 119101, 119101, "2030/12/31 15:00:00", "くうか", "クウカ（ノワール）", "【魔法】前衛で、猛攻をバリアで受けきる闇のドＭ娘。\n最も魔法攻撃力が高い味方の魔法攻撃力とクリティカルを\nアップさせ、魔法防御力ダウンスキルで敵を怯ませる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 5, 1, 0, 6, 119201, 2.19, 1, 1, 119201, 450, "2023/09/19 15:00:00", 6, 397, 0, 119201, 119201, "2030/12/31 15:00:00", "まひる", "マヒル（クリスマス）", "【物理】中衛で味方を支援し、守護する聖夜の牧場主。\n範囲内の敵をノックバックさせることで相手の隊列を崩し\n全体物理バリア展開と単体ＨＰ回復で激戦を勝利に導く。");
