INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 2, 1, 0, 4, 120801, 1.3, 1, 1, 120801, 450, "2023/10/16 15:00:00", 22, 193, 0, 120801, 120801, "2030/12/31 15:00:00", "るか", "ルカ（ニューイヤー）", "【物理】前衛で雪月花の舞を納める新春の姉御。\n攻撃を当てる度に増加する【剣の刻印】でＵＢとスキルの\n威力を上昇させ、目にも止まらぬ斬撃で敵を圧倒する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 2, 0, 3, 120901, 1.425, 1, 3, 120901, 450, "2025/01/15 15:00:00", 38, 424, 0, 120901, 120901, "2025/01/20 11:59:59", "いりや", "イリヤ（ニューイヤー）", "【魔法】中衛で自らの血を代償に力を振るう新春吸血鬼。\n自身のＨＰを消費して放つ強力なスキルと、敵からＴＰを\n吸収するスキルを自在に操り、目の前の敵を撃滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 5, 121001, 2.25, 1, 3, 121001, 450, "2025/02/20 12:00:00", 41, 164, 0, 121001, 121001, "2025/02/24 11:59:59", "ぺこりーぬ", "ペコリーヌ（オーバーロード）", "【物理】前衛で、仲間との絆を力に変え戦う腹ペコ王女。\n敵のＨＰ状態で永続の物理攻撃力上昇と、短時間の特大\n攻撃力上昇＆自己ＴＰ回復を使い分け戦況を一変させる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 7, 121101, 2.07, 1, 3, 121101, 450, "2025/02/20 12:00:00", 42, 588, 0, 121101, 121101, "2025/02/24 11:59:59", "きゃる", "キャル（オーバーロード）", "【魔法】中衛で仲間との絆を力に変え戦うツンデレ猫娘。\n敵を妨害するスキルと、ＵＢで敵全体を恐慌状態にし既に\n恐慌状態の敵のＴＰをダウンさせ、全戦場を掌握する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 6, 1, 0, 4, 121201, 1.55, 1, 1, 121201, 450, "2023/11/16 15:00:00", 40, 260, 0, 121201, 121201, "2030/12/31 15:00:00", "らびりすた", "ラビリスタ（オーバーロード）", "【物理】前衛で世界の理をすべて見つめる『迷宮女王』。\n味方を攻撃面で強化し、継続ＨＰ回復を全体に付与する\nスキルで戦線を整え、自身も圧倒的な力で敵を殲滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 3, 1, 0, 3, 121301, 1.9, 0, 3, 121301, 450, "2022/03/03 19:00:00", 3, 731, 0, 121301, 121301, "2030/12/31 15:00:00", "くるみ", "クルミ（ステージ）", "【物理】後衛で、皆のため意を決し奮起する小さな女優。\n残りＨＰが多いほど物理攻撃力がアップし、物理攻撃無効\nバリアのスキルやＨＰ・ＴＰ継続回復で味方を支援する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 15, 2, 0, 7, 121401, 2.27, 0, 3, 121401, 450, "2022/03/15 15:00:00", 7, 742, 0, 121401, 121401, "2030/12/31 15:00:00", "みさき", "ミサキ（ステージ）", "【魔法】後衛で後ろの味方を強化する魔法少女の看板娘。\n後衛の味方をバリアとＴＰ継続回復で支援し、自分の魔法\n攻撃力に応じ強化される攻撃力アップスキルで鼓舞する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 12, 1, 0, 6, 121501, 2.0, 1, 3, 121501, 450, "2024/04/12 12:00:00", 6, 590, 0, 121501, 121501, "2024/04/15 14:59:59", "あゆみ", "アユミ（怪盗）", "【物理】中衛で、闇夜に身を潜める神出鬼没の怪盗少女。\n敵の目の前から姿を消す隠密スキルや、遠くの敵に対する\nＴＰダウンスキルを駆使して、敵陣後方から瓦解させる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 17, 1, 0, 3, 121601, 1.425, 0, 3, 121601, 450, "2022/04/15 15:00:00", 3, 292, 0, 121601, 121601, "2030/12/31 15:00:00", "いのり", "イノリ（怪盗）", "【物理】前衛で闇に紛れ会心の一撃を放つ怪盗ドラゴン。\n隠密効果中、ダメージが大アップし必ずクリティカルする\nＵＢとスキルを駆使し安定したダメージを敵に叩き込む。");
