INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 14, 1, 0, 3, 122001, 1.425, 0, 3, 122001, 450, "2022/05/03 19:00:00", 3, 380, 0, 122001, 122001, "2030/12/31 15:00:00", "しのぶ", "シノブ（パイレーツ）", "【物理】中衛で、彷徨える魂を解き放つ海星の霊能少女。\n攻撃の際、付与する【霊錨の刻印】を駆使し、ダメージ量\nアップのスキルと防御力ダウンのＵＢで敵を浄滅へ導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 9, 1, 0, 35, 122101, 1.97, 1, 1, 122101, 450, "2024/04/18 15:00:00", 35, 731, 0, 122101, 122101, "2030/12/31 15:00:00", "あおい", "アオイ（キャンプ）", "【物理】後衛で百千の矢を放つ、ぼっちの山籠りエルフ。\n物理魔法無効バリアで耐え凌ぎ、敵全体への射撃と自分の\n間合いに入った敵への強力な弓雨の追撃で掃討する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 4, 122201, 1.84, 0, 3, 122201, 450, "2022/05/31 12:00:00", 4, 419, 0, 122201, 122201, "2030/12/31 15:00:00", "ゆかり", "ユカリ（キャンプ）", "【物理】中衛で最高の一杯を自然と共に愉しむ元聖騎士。\n【至福のひと時】が増えるほどに、味方の物理攻撃力や\nクリティカル時のダメージを強力に強化し、勝利を掴む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 43, 122301, 1.425, 0, 3, 122301, 450, "2022/06/15 15:00:00", 43, 564, 0, 122301, 122301, "2030/12/31 15:00:00", "うぁんぴぃ", "ヴァンピィ", "【魔法】中衛で、幼き竜と共に歩むさいきょーの吸血姫。\n味方全体の強化と、敵のＴＰを吸収するスキルで力を蓄え\nＵＢ発動後ベスと共闘し防御ダウンと強撃で攻勢に出る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 8, 1, 0, 1, 122401, 2.42, 1, 1, 122401, 450, "2024/06/17 15:00:00", 1, 264, 0, 122401, 122401, "2030/12/31 15:00:00", "ひより", "ヒヨリ（サマー）", "【物理】前衛で皆にエールを送り鼓舞する真夏の元気娘。\n味方のＨＰを継続回復し行動速度アップの他、味方の数に\n応じてクリティカル強化と攻撃力を引き上げ勝利に導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 4, 122501, 1.965, 1, 3, 122501, 450, "2024/08/26 19:00:00", 4, 250, 0, 122501, 122501, "2024/08/31 11:59:59", "れい", "レイ（サマー）", "【物理】前衛で水刃の加護を纏い水面を駆ける渚の剣士。\nクリティカル時のダメージを底上げしＵＢ発動の度、纏う\n【水刃の加護】で全スキルダメージを上げ、敵陣を穿つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 122601, 2.27, 0, 3, 122601, 450, "2022/07/15 15:00:00", 7, 554, 0, 122601, 122601, "2030/12/31 15:00:00", "ゆい", "ユイ（サマー）", "【魔法】中衛で、魔法キャラを補助する夜凪の魔法士。\n累積する攻撃強化スキルとＴＰ回復、物魔両面の無効\nバリアや継続ＨＰ回復で攻守共に味方を支え勝利へ導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 122701, 1.92, 0, 3, 122701, 450, "2022/08/15 15:00:00", 7, 812, 0, 122701, 122701, "2030/12/31 15:00:00", "きょうか", "キョウカ（サマー）", "【魔法】後衛で、難しい魔法の練習に励む海辺の優等生。\nランダムな敵に当たるスキルの威力は攻撃の際に付与する\n【バタフライシール】で増幅し、防御力もダウンさせる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 1, 122801, 2.295, 1, 1, 122801, 450, "2024/07/16 15:00:00", 1, 237, 0, 122801, 122801, "2030/12/31 15:00:00", "みそぎ", "ミソギ（サマー）", "【物理】前衛で敵後方に花火を仕掛ける夜涼みの悪戯娘。\n敵の最前衛をスタンで足止めしつつ、最後衛を中心とした\nＵＢで狭範囲の敵を引き寄せ、ＴＰダウンで攻勢を阻む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 5, 122901, 2.25, 1, 3, 122901, 450, "2024/08/26 19:00:00", 5, 361, 0, 122901, 122901, "2024/08/31 11:59:59", "みみ", "ミミ（サマー）", "【物理】中衛で巨大バルーンと跳ね回る夏色うさぎっこ。\nＵＢやスキルで全てのマルチターゲットに追加ダメージを\n与え、自己強化と防御力ダウンで強敵のブレイクを狙う。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 44, 123001, 2.27, 1, 3, 123001, 450, "2025/02/28 12:00:00", 44, 746, 0, 123001, 123001, "2025/03/03 18:59:59", "あめす", "アメス", "【魔法】後衛で、新たな姿で騎士の冒険を支える妖精。\nダメージカットや防御力アップ、無効バリアで味方を護り\nバリアを起点とする攻撃力とＴＰの支援で攻勢に転ずる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 123101, 1.795, 0, 3, 123101, 450, "2022/09/03 19:00:00", 7, 793, 0, 123101, 123101, "2030/12/31 15:00:00", "まほ", "マホ（エクスプローラー）", "【魔法】後衛でぬいぐるみ達と大冒険に挑むマホ隊員。\n味方に付与する【ふわふわウール】は被ダメージ時に消費\nされＴＰを回復し、一気呵成の反撃で活路を切り拓く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 3, 123201, 1.425, 0, 3, 123201, 450, "2022/09/15 15:00:00", 3, 256, 0, 123201, 123201, "2030/12/31 15:00:00", "あやね", "アヤネ（エクスプローラー）", "【物理】前衛で、相棒と困難を越え猛進するアヤネ隊員。\n群る敵を二つの範囲攻撃で一気に捉え、周囲の味方の物理\n攻撃力とクリティカルアップで冒険を阻む壁を打ち崩す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 1, 123301, 1.62, 1, 3, 123301, 450, "2024/09/25 12:00:00", 0, 350, 0, 123301, 123301, "2024/09/30 11:59:59", "ねあ", "ネア", "【物理】中衛で粘液を振りまくキイロいスライムギャル。\nスキルで敵全体の物理攻撃能力やＴＰ上昇を低下させ、\nＵＢでスライム化し全敵を攻撃しつつ無敵ボディを得る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 0, 0, 5, 0, 0.0, 0, 3, 123401, 0, "2015/04/01 15:00:00", 5, 0, 0, 123401, 0, "2030/12/31 15:00:00", "じゅん", "ジュン（キャンプ）", "");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 6, 123501, 2.34, 1, 1, 123501, 450, "2024/09/17 15:00:00", 6, 583, 0, 123501, 123501, "2030/12/31 15:00:00", "りん", "リン（ハロウィン）", "【物理】中衛で敵を悪戯で翻弄する恐怖のキョンシー娘。\n最も魔法攻撃力が高い敵に急襲を仕掛け、ＴＰ減少や魔法\n攻撃力ダウン、スタン等の妨害スキルで機先を制する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 4, 123601, 1.965, 1, 3, 123601, 450, "2024/10/24 12:00:00", 4, 262, 0, 123601, 123601, "2024/10/28 11:59:59", "とも", "トモ（ハロウィン）", "【物理】前衛で、同志と力を合わせ共に戦う夜廻り剣士。\n自分と物理攻撃力が最も高い味方１キャラの攻撃力を大幅\nに上昇させ、目にもとまらぬ剣技で不届き者を成敗する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 123701, 2.07, 0, 3, 123701, 450, "2022/10/15 15:00:00", 21, 741, 0, 123701, 123701, "2030/12/31 15:00:00", "ななか", "ナナカ（ハロウィン）", "【魔法】後衛で、オタ活に精を出すハロウィン魔法少女。\nＨＰ回復量ダウンで敵の戦略を崩し、ダメージフィールド\nと対象の数で火傷の威力を増す熱狂の炎が敵を包み込む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 1, 123801, 2.295, 1, 3, 123801, 450, "2024/11/26 12:00:00", 1, 238, 0, 123801, 123801, "2024/11/30 11:59:59", "くりすてぃーな", "クリスティーナ（ワイルド）", "【物理】前衛で、戦場を跳ね回るラビット姿の誓約女君。\n物理攻撃を全て回避するスキルと攻撃力の強化で敵陣に\n単身特攻をかけ、ＵＢの範囲攻撃で敵を内から薙ぎ倒す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 2, 123901, 2.125, 0, 3, 123901, 450, "2022/11/15 15:00:00", 2, 183, 0, 123901, 123901, "2030/12/31 15:00:00", "まつり", "マツリ（ワイルド）", "【物理】前衛で母製の鎧を纏い気合い上々の獣人族騎士。\n自分のＨＰが低いほど与えるダメージと無効バリアの耐久\n力を高め、防御力ダウンと永続攻撃アップで活路を拓く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 124001, 2.07, 1, 3, 124001, 450, "2024/12/22 12:00:00", 7, 438, 0, 124001, 124001, "2024/12/31 11:59:59", "あかり", "アカリ（クリスマス）", "【魔法】中衛で、聖夜の街に幸せを運ぶ双子の妹サンタ。\n自分の魔法攻撃力に応じて効果が上がる味方全体への攻撃\n力強化に加え、単体特大魔法攻撃で勝利の旋律を奏でる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 6, 124101, 1.965, 1, 1, 124101, 450, "2024/11/16 15:00:00", 6, 433, 0, 124101, 124101, "2030/12/31 15:00:00", "より", "ヨリ（クリスマス）", "【魔法】中衛で、光を灯し幸せを届ける双子の姉サンタ。\n最も近い１キャラの魔法攻撃力や行動速度アップ、ＴＰの\n回復に加え、全員を支援するスキルで聖夜の勝利を飾る。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 5, 124201, 2.07, 0, 3, 124201, 450, "2022/12/23 12:00:00", 5, 144, 0, 124201, 124201, "2030/12/31 15:00:00", "じゅん", "ジュン（クリスマス）", "【物理】前衛で、味方を庇護し祝福する聖白の騎士団長。\nスキルで後方の味方の攻撃能力を強化、ＴＰを回復させ、\n防御力ダウンと自分のＨＰを回復するＵＢで勝機を掴む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 1, 181001, 0, 0, 0, 0, 0, 0.0, 1, 3, 124301, 0, "2015/04/01 15:00:00", 0, 577, 0, 124301, 0, "2030/12/31 15:00:00", "あん", "アン（アン＆グレア）", "");
