INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 130801, 1.84, 0, 3, 130801, 450, "2024/05/15 15:00:00", 7, 558, 0, 130801, 130801, "2030/12/31 15:00:00", "すずめ", "スズメ（スプリング）", "【魔法】中衛で、春に彩られた花衣装のドジっ娘メイド。\n範囲内の敵にダメージを与えながら、多数の弱体化効果を\n持つスキルやＵＢを駆使して、穏やかに吹く勝利を掴む。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 130901, 2.09, 0, 3, 130901, 450, "2024/06/15 15:00:00", 7, 440, 0, 130901, 130901, "2030/12/31 15:00:00", "くるる", "クルル", "【魔法】中衛で、皆を夢の世界に誘う宝石兎族の支配人。\nステータスアップ効果を受けるたびに【かばばん君】を増\nやし、反射的に味方へ攻撃支援の恩返しをしてしまう。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 131001, 1.84, 1, 3, 131001, 450, "2024/12/09 12:00:00", 7, 702, 0, 131001, 131001, "2024/12/15 14:59:59", "まほ", "マホ（ドリームパーク）", "【魔法】後衛で、サーカスの舞台に輝くメルヘン少女。\n味方の攻防支援や敵の攻防弱体化の他【カーテンコール】\n付与時の追加ダメージスキルで、勝利のライトを照らす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 2, 131101, 2.09, 0, 3, 131101, 450, "2024/06/30 12:00:00", 52, 165, 0, 131101, 131101, "2030/12/31 15:00:00", "ねあ", "ネア（サマー）", "【物理】前衛でハグの機会を窺う水着のスライムギャル。\n攻撃してきた敵に【ネバネバ】を自動で付着させ、攻撃力\nダウンやダメージ、束縛など複数の効果を引き起こす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 35, 131201, 2.09, 1, 3, 131201, 450, "2025/01/09 12:00:00", 35, 753, 0, 131201, 131201, "2025/01/15 14:59:59", "みそら", "ミソラ（サマー）", "【魔法】後衛で夏の盛りにイイコトをする溌溂水着少女★\n【PSI★ストーム】をチャージし、連続攻撃で敵を殲滅し\nて、継続ＴＰ回復やバリアで抜け目なく味方を支援する。");
