INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 4, 131601, 1.92, 1, 3, 131601, 450, "2025/01/09 12:00:00", 4, 399, 0, 131601, 131601, "2025/01/15 14:59:59", "りり", "リリ（サマー）", "【物理】中衛で、夏の陽光のもと研鑽する妹系大和撫子。\n防御力アップに加えバリアやＨＰ回復で味方の守りを固め\nＴＰ回復や攻撃力ダウンをも巧みに操り、場を支配する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 44, 131701, 1.92, 1, 3, 131701, 450, "2025/02/10 12:00:00", 53, 743, 0, 131701, 131701, "2025/02/15 14:59:59", "あめす", "アメス（サマー）", "【魔法】後衛で、黄昏の水面に舞い降りる水着姿の妖精。\n行動速度アップやＴＰ支援をはじめとした多数のサポート\nを施しつつ、ＵＢの攻防弱体化で清爽に勝利へと導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 131801, 2.09, 0, 3, 131801, 450, "2024/08/15 15:00:00", 21, 709, 0, 131801, 131801, "2030/12/31 15:00:00", "ほまれ", "ホマレ（サマー）", "【魔法】後衛で夏の清涼に翼を休める水着姿の龍の首領。\n攻防弱体化に加え、ステータスダウン付与のたび増加する\n【日食】の数に応じたダメージで、抗う全てを灰に帰す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 1, 0, 0, 2, 0, 7, 131901, 2.09, 1, 1, 131901, 450, "2024/08/31 12:00:00", 23, 725, 0, 131901, 131901, "2030/12/31 15:00:00", "えみりあ", "エミリア（サマー）", "【魔法】後衛で、パックと共に戦う真夏のハーフエルフ。\n氷壁を生成するスキルや、強力なダメージと速度ダウンを\n付与するユニオンバーストで、敵の侵攻を食い止める。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 132001, 2.09, 1, 3, 132001, 450, "2024/09/13 12:00:00", 21, 775, 0, 132001, 132001, "2024/09/25 11:59:59", "えきどな", "エキドナ（サマー）", "【魔法】後衛で未知の世界との邂逅に胸躍る強欲の魔女。\n鮮烈な攻撃魔法をはじめ、味方への攻撃支援、敵の弱体化\nから自己蘇生まで、あらゆる魔法を振るい、欲を満たす。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 13, 132101, 2.09, 1, 3, 132101, 450, "2024/08/31 12:00:00", 24, 146, 0, 132101, 132101, "2024/09/13 11:59:59", "れむ", "レム（サマー）", "【物理】前衛で夏の日差しの中、余暇を楽しむ双子の妹。\nＵＢで鬼族の力を解放すると攻防両面に加えＨＰ吸収が\n上昇し、目に映る全てを【狂騒】のままに叩き伏せる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 132201, 1.84, 1, 3, 132201, 450, "2024/09/30 12:00:00", 7, 721, 0, 132201, 132201, "2024/10/10 11:59:59", "のぞみ", "ノゾミ（アルケミスト）", "【魔法】後衛で、希望を錬成するアルケミストアイドル。\n【錬金素材】から【パフューム】を創り、ダメージに加え\nＴＰ回復や攻撃支援能力を向上させて勝利の香りを運ぶ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 132801, 2.09, 1, 3, 132801, 450, "2024/11/30 12:00:00", 0, 750, 0, 132801, 132801, "2024/12/09 11:59:59", "らいらえる", "ライラエル（クリスマス）", "【魔法】後衛で、雪白の肌を黒衣に包んだ甘美なる姫君。\n味方のＨＰを代償に強力なバリアを展開しつつ、バリアを\n起点とした高出力の二連撃で、聖夜の大樹に灯を点す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 5, 132901, 2.09, 0, 3, 132901, 450, "2024/12/09 12:00:00", 46, 748, 0, 132901, 132901, "2030/12/31 15:00:00", "いお", "イオ（クリスマス）", "【魔法】後衛で、心地良い眠りの中に訪れる聖夜の悪魔。\n最も魔法攻撃力が高い味方に【悪魔の口づけ】を付与し、\n攻撃支援とＴＰ回復による付きっきりの手ほどきをする。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 0, 133001, 2.09, 1, 3, 133001, 450, "2025/02/28 12:00:00", 0, 234, 0, 133001, 133001, "2025/03/03 18:59:59", "ぐれいす", "グレイス", "【物理】前衛で、宙を舞うように戦う幽霊の守護騎士。\nＵＢで味方の攻撃と速度支援を行い、【レイスボディ】で\n敵の目を逃れ、不意を突く一撃で永遠の眠りにつかせる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 1, 0, 0, 1, 0, 4, 133201, 2.09, 1, 1, 133201, 450, "2024/12/31 12:00:00", 22, 296, 0, 133201, 133201, "2030/12/31 15:00:00", "もにか", "モニカ（ニューイヤー）", "【物理】前衛で、元日も元気にはしゃぎまわる軍人少女。\n与ダメージに応じて追加効果が強化されるＵＢと、発動ご\nとに成長する自己強化スキルで秩序を乱す者の目を回す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 4, 133501, 2.09, 1, 3, 133501, 450, "2025/01/03 19:00:00", 22, 718, 0, 133501, 133501, "2025/01/15 14:59:59", "されん", "サレン（ニューイヤー）", "【魔法】後衛で、傍らで心を重ねる振袖姿の救護院の主。\n味方へのＴＰ回復の支援をしつつ、味方がＵＢを発動する\nたびダメージが上がるスキルで、共に困難を乗り越える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 0, 133601, 2.09, 1, 3, 133601, 450, "2025/01/20 12:00:00", 0, 445, 0, 133601, 133601, "2025/01/31 11:59:59", "めどぅーさ", "メドゥーサ", "【物理】中衛で眷属と力を合わせて戦う誇り高き蛇乙女。\n【魔眼】の数に応じて効果量が上がるスキルのダメージと\n弱体化で範囲内の敵を追い詰め、鋭い瞳で石へと変える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 8, 133701, 2.09, 1, 3, 133701, 450, "2025/01/31 12:00:00", 0, 717, 0, 133701, 133701, "2025/02/10 11:59:59", "らびりすた", "ラビリスタ（アルファ）", "【魔法】後衛で、始原の冠装を使いこなす『迷宮女王』。\n水属性の味方のＴＰ上昇を増加する領域を作り、連射攻撃\nで敵の防御を撃ち抜くことで七冠の力を敵に知らしめる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 133801, 2.09, 1, 3, 133801, 450, "2025/02/20 12:00:00", 0, 759, 0, 133801, 133801, "2025/02/28 11:59:59", "ねねか", "ネネカ（アルファ）", "【魔法】後衛から、始原の姿で戦況を操る『変貌大妃』。\n召喚したグレゴールから攻撃支援とＴＰ回復を受け、互い\nの魔力を合わせた連携攻撃で、幽玄なる玉座に君臨する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 6, 133901, 2.09, 1, 3, 133901, 450, "2025/02/10 12:00:00", 0, 228, 0, 133901, 133901, "2025/02/20 11:59:59", "くりすてぃーな", "クリスティーナ（アルファ）", "【物理】前衛で、始原の冠装の力を振るう『誓約女君』。\n10行動目に攻撃力を大幅アップし、スキルに追撃を発生\nさせる【虚数】を得て隔絶した力で楯突く敵を抹消する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 0, 134001, 2.09, 1, 3, 134001, 450, "2025/02/28 12:00:00", 0, 746, 0, 134001, 134001, "2025/03/03 18:59:59", "ふぃお", "フィオ", "【魔法】後衛で、アストルムの冒険へと導くガイド妖精。\nバフとバリアで味方を護りバリアを起点とする攻撃支援、\n風属性の味方への行動速度とＴＰの支援で仲間を支える。");
