INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 2, 0, 7, 134401, 2.09, 0, 3, 134401, 450, "2025/03/15 15:00:00", 21, 773, 0, 134401, 134401, "2030/12/31 15:00:00", "ちか", "チカ（儀装束）", "【魔法】後衛で儀装束を纏い歌を紡ぎ伝えていく唱喚士。\n自分と最も近くの味方へ強力なバフとＴＰ回復を行うこと\nで攻撃を支援し、共に心を通わせ美しき旋律を奏でる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 0, 1, 0, 35, 134601, 2.09, 1, 3, 134601, 450, "2025/03/31 12:00:00", 35, 526, 0, 134601, 134601, "2025/04/08 11:59:59", "あおい", "アオイ（パイロット）", "【物理】中衛で巨大ロボを駆り戦うぼっちのパイロット。\n敵の弱体化や、味方へバリアを展開するスキルを持ち、\n鋼鉄の乙女から放つ光線はいかに堅固な守りも打ち破る。");
