INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 4, 1, 0, 34, 105701, 1.965, 0, 3, 170101, 450, "2015/04/01 15:00:00", 34, 245, 0, 170101, 170101, "2030/12/31 15:00:00", "じーた", "ジータ", "【物理】前衛で、先手必勝の大技を繰り出す騎空士。\nスキルによるＴＰ回復は、ユニオンバーストの発動を早め\nバトルの序盤から敵前衛に大ダメージを与える。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 0, 180101, 1.62, 1, 3, 180101, 450, "2025/02/28 12:00:00", 0, 199, 0, 180101, 180101, "2025/03/03 18:59:59", "ひより", "ヒヨリ（プリンセス）", "【物理】前衛で炎翼を背に敵を粉砕する、陽気な元気娘。\n戦いが続く程に自身の攻撃能力を強めるスキルを操り、\n高めた力で放つ超爆の一撃は、あらゆる敵を塵と化す。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 0, 180201, 1.92, 1, 3, 180201, 450, "2025/02/28 12:00:00", 0, 767, 0, 180201, 180201, "2025/03/03 18:59:59", "ゆい", "ユイ（プリンセス）", "【魔法】後衛から極大魔法で敵を滅する願いの魔法士。\nＴＰを回復しつつ攻撃力を累積アップする補助スキルで\n自身の力を高め、強力な範囲攻撃を放ち敵を一掃する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 7, 1, 0, 0, 180301, 1.965, 1, 3, 180301, 450, "2025/02/28 12:00:00", 0, 377, 0, 180301, 180301, "2025/03/03 18:59:59", "れい", "レイ（プリンセス）", "【物理】中衛で風刃を従え激戦を流麗に征く嵐翼の剣士。\n敵が多い程に効果が高まる自己強化スキルで、自身を奮い\n立たせ、強力な範囲攻撃スキルで一切の敵を鏖滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 180401, 1.965, 1, 3, 180401, 450, "2025/02/28 12:00:00", 0, 155, 0, 180401, 180401, "2025/03/03 18:59:59", "ぺこりーぬ", "ペコリーヌ（プリンセス）", "【物理】前衛で、攻防共に絶大な力を誇る腹ペコ王女。\nＨＰが多い時は爆発的な攻撃力で、いかなる敵をも屠り、\nＨＰが減ると自己回復能力を得て、鉄壁の護りを見せる。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 180501, 2.14, 1, 3, 180501, 450, "2025/02/28 12:00:00", 0, 555, 0, 180501, 180501, "2025/03/03 18:59:59", "こっころ", "コッコロ（プリンセス）", "【物理】中衛で、絶大な補助スキルを繰出す純白の巫女。\n味方の攻撃力とクリティカルダメージを強化しつつ、\n二つの全体回復スキルで味方を堅固に守護し勝利へ導く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 0, 180601, 1.92, 1, 3, 180601, 450, "2025/02/28 12:00:00", 0, 747, 0, 180601, 180601, "2025/03/03 18:59:59", "きゃる", "キャル（プリンセス）", "【魔法】後衛で、極大魔法を操り敵を討つ姫装の猫娘。\n回復や補助を兼ねた各攻撃スキルを駆使し戦況を整え、\n極大威力のユニオンバーストを撃ち込み敵を燼滅する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 180701, 1.75, 1, 3, 180701, 450, "2025/02/28 12:00:00", 0, 732, 0, 180701, 180701, "2025/03/03 18:59:59", "はつねしおり", "ハツネ＆シオリ", "【物理】後衛から、真の絆の力を発揮する仲良し姉妹。\n敵の数が増えるほど威力が高まる攻撃スキルと、周りの\n味方を強化するスキルを操り、立ち塞がる敵を一掃する。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 180801, 1.5, 1, 3, 180801, 450, "2025/02/28 12:00:00", 0, 465, 0, 180801, 180801, "2025/03/03 18:59:59", "みそぎみみきょうか", "ミソギ＆ミミ＆キョウカ", "【物理】中衛で敵単体に必殺の一撃を放つ仲良しトリオ。\n３人の力を合わせて放つ攻撃は、相手の弱点に合わせて\n物理から魔法攻撃に変化し、どんな強い敵も退治する！");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 180901, 2.09, 1, 3, 180901, 450, "2025/02/28 12:00:00", 0, 421, 0, 180901, 180901, "2025/03/03 18:59:59", "あきのされん", "アキノ＆サレン", "【物理】中衛で背を預けあう最高の好敵手で親友の二人。\n味方の物理攻撃をサポートしつつＨＰとＴＰを継続回復し\nＨＰが多いほど威力が増すＵＢで、共に勝利の道を征く。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 2, 0, 0, 181001, 2.09, 1, 3, 181001, 450, "2025/02/28 12:00:00", 0, 577, 0, 181001, 181001, "2025/03/03 18:59:59", "あんぐれあ", "アン＆グレア", "【魔法】中衛で、魔力共鳴し連撃を放つマナリアの双姫。\nステータスアップ効果を受けると増加する【友情の魔印】\nがスキルを強化し、波長を合わせた炎の魔槍で敵を討つ。");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 181101, 1.92, 1, 3, 181101, 450, "2025/02/28 12:00:00", 0, 551, 0, 181101, 181101, "2025/03/03 18:59:59", "しずるりの", "シズル＆リノ", "【物理】中衛でお揃い衣装の、真偽を超えた仲良し姉妹。\n攻守共に味方を支えるシズルと、クリティカル時に強力な\nダメージを与えるリノ、姉妹の絆で困難を跳ね除ける！");
INSERT INTO `v1_479e3c3fae60712a22965236dca94efb97b8cc21318b04309dd894100bb84b66` VALUES (0, 0, 0, 0, 1, 1, 0, 0, 181201, 1.92, 1, 3, 181201, 450, "2025/02/28 12:00:00", 0, 368, 0, 181201, 181201, "2025/03/03 18:59:59", "しぇふぃ", "シェフィ（プリンセス）", "【物理】中衛で新たな力と共に再廻する姫装の氷竜少女。\n環境効果【絶氷】でクリティカルダメージを高め、火力と\n手数を併せ持つ剣技で、再び共に歩むため道を切り開く。");
