INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES (0, 52018, 20006, 2, "ランファの世話焼き三昧肉じゃが", "次に振るダイスの目が必ず「3」になる。", "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 500, 10, 6, "ランファの世話焼き三昧肉じゃが", 1, 0, "1000マイルを獲得する。", 51018, 1, 1);
INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES (0, 52019, 20007, 2, "ワカナの節約腹八分目カレー", "次に振るダイスの目が必ず「8」になる。", "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 500, 0, 0, "ワカナの節約腹八分目カレー", 1, 0, "なし", 0, 1, 1);
