INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES (0, 52024, 2, 8, "アユミの隠密ガトーインビジブル", "RANK2のマイルショップを開く。", "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 500, 11, 3, "アユミの隠密\nガトーインビジブル", 3, 0, "次にダイスを振った際、3マス多く進める。", 51024, 0, 3);
