INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES ("次にダイスを振った際、1マス多く進める。", 1, 1, 52015, "シズルの愛情\n盛り盛りハンバーグ", "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 0, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 1, 11, 1, 500, 2, 51015, "シズルの愛情盛り盛りハンバーグ", 0);
INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES ("なし", 1, 3, 52016, "アオイの疾走\nサンドイッチ", "次に振るダイスの目が必ず「6」になる。", 1, 0, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 2, 0, 0, 500, 20005, 51016, "アオイの疾走サンドイッチ", 0);
INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES ("なし", 1, 3, 52017, "クウカの緊縛\n触手活け造り", "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 2, 1, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 2, 10, 5, 500, 1, 51017, "クウカの緊縛触手活け造り", 0);
INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES ("1000マイルを獲得する。", 1, 1, 52018, "ランファの世話焼き三昧肉じゃが", "次に振るダイスの目が必ず「3」になる。", 1, 0, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 2, 10, 6, 500, 20006, 51018, "ランファの世話焼き三昧肉じゃが", 0);
INSERT INTO `v1_4813be21beb54d8d7599c93204cf0e779ac9991acb7c519c682c9f1ee6793d82` VALUES ("なし", 1, 1, 52019, "ワカナの節約腹八分目カレー", "次に振るダイスの目が必ず「8」になる。", 1, 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 2, 0, 0, 500, 20007, 0, "ワカナの節約腹八分目カレー", 0);
