INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12525, 12488, 12469, 5, "", 1219, "銃で狙いを定めるための「照準器」の種類の一つ。いわゆる「ホログラム」の技術を使っている", 2, 5, 2, 5, "これ、何と言う？", 1, "ホロサイト", 5, 12452, 5, "ホロサイト");
