INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12475, 12523, 12540, 12501, 5, "", 3180, "「自撮り」とも呼ばれる、自分で自分を写真撮影する行為、英語での呼び方は？", 2, 5, 2, 5, "これ、何と言う？", 3, "セルフィー", 5, 12451, 5, "セルフィー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12515, 29577, 12508, 5, "", 3181, "子どもに人気の遊び道具。ストローの先にせっけん水をつけ、大きな球を吹き出して楽しむ", 1, 5, 2, 5, "これ、何と言う？", 3, "シャボン玉", 5, 12531, 6, "シャボン玉");
