INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12496, 12521, 12451, 12456, 5, "", 3185, "テレビ番組のジャンルの一つ。お笑いやトーク、ゲームなどの企画が多く、娯楽の性質が強い", 1, 5, 2, 5, "これ、何と言う？", 3, "バラエティ", 5, 12486, 5, "バラエティ番組");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12375, 12375, 12362, 4, "", 3186, "和式の庭園に置かれる、竹製の装置。水の重みで動作し、「カポーン」と風情のある音が鳴る", 2, 4, 2, 4, "これ、何と言う？", 3, "ししおどし", 4, 12393, 4, "ししおどし");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12360, 12403, 12362, 12377, 4, "", 3187, "目を細めてニコニコと笑う顔つきのこと。「七福神」のとある神様をほうふつとさせることから", 2, 4, 2, 4, "これ、何と言う？", 3, "えびすがお", 4, 12364, 4, "えびすがお");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12522, 12531, 12475, 5, "スリーセブン", 3188, "数字の「7」が三つ並んだもの。スロットマシーンでもおなじみで、非常に縁起が良いとされる", 1, 5, 2, 5, "これ、何と言う？", 3, "スリセブン", 5, 12502, 5, "スリーセブン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12518, 12450, 12501, 12475, 5, "", 3189, "", 3, 5, 2, 5, "これ、略さずに言うと？", 3, "ユアセルフ", 5, 12523, 5, "DIY\n＝ドゥ イット ユアセルフ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12372, 12375, 12435, 12421, 4, "", 3190, "寺や神社を参拝した証となる印を集めること。スタンプラリー感覚で楽しめるとして、人気上昇中", 3, 4, 2, 4, "これ、何と言う？", 3, "ごしゅいん", 4, 12356, 4, "ごしゅいん集め");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12461, 12523, 12458, 12524, 5, "", 3191, "ゲームで、自分が敵を倒した回数と、敵に倒された回数との比率。「キルレート」とはやや異なる", 2, 5, 2, 5, "これ、何と言う？", 3, "キルレシオ", 5, 12471, 5, "キルレシオ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12429, 12390, 12429, 12435, 4, "", 3192, "温泉の形式の一つで、屋外に開放されている風呂のこと。温泉の醍醐味の一つとして親しまれる", 1, 4, 2, 4, "これ、何と言う？", 3, "ろてんぶろ", 4, 12406, 4, "ろてんぶろ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12360, 12403, 12435, 12377, 4, "", 3193, "「七福神」のうち、漁業や商売を司るとされる神様。ニコニコ顔と、釣り竿と鯛を持った姿が有名", 2, 4, 2, 4, "これ、何と言う？", 3, "えびすてん", 4, 12390, 4, "えびすてん（ひらがなで）");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 69, 12473, 12484, 12509, 5, "", 3194, "デジタルゲームをスポーツとして捉える、近年普及した呼称", 1, 5, 2, 5, "これ、何と言う？", 3, "Eスポーツ", 2, 12540, 5, "Eスポーツ");
