INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12499, 12462, 12474, 12490, 5, "", 3197, "賭けごとなどで、初心者がやけに運の良いヒキを持つこと", 1, 5, 2, 5, "これ、何と言う？", 3, "ビギナーズ", 5, 12540, 5, "ビギナーズラック");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12358, 12425, 12369, 12379, 4, "", 3199, "「三千家」と呼ばれる茶道の三流派、「表千家」と「武者小路千家」ともう一つは？", 3, 4, 2, 4, "これ、何と言う？", 3, "うらせんけ", 4, 12435, 4, "うらせんけ");
