INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12531, 12525, 12481, 5, "アンチヒーロー", 3201, "ダークヒーローとも呼ばれるフィクションのキャラ像。非道徳的でありながら魅力的な要素も持つ", 3, 5, 2, 5, "これ、何と言う？", 3, "アンチヒロ", 5, 12498, 5, "アンチヒーロー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 35501, 12414, 12383, 39135, 4, "読まずに食べた", 3202, "有名な童謡の歌い出し。♪白ヤギさんからお手紙着いた　黒ヤギさんたら…", 1, 6, 2, 4, "この歌の歌詞は？", 3, "読ま食べた", 6, 12409, 4, "読まずに食べた");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12362, 12426, 12402, 4, "", 3204, "干潟で、潮が引いているあいだに貝類や生き物をとって遊ぶこと。春の訪れとともに楽しまれる", 1, 4, 2, 4, "これ、何と言う？", 3, "しおひがり", 4, 12364, 4, "しおひがり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12418, 12415, 12426, 12376, 4, "", 3205, "紅葉の時期に野山に出かけ風景を楽しむこと。何かを採取するわけではなく、あくまで見て楽しむ", 1, 4, 2, 4, "これ、何と言う？", 3, "もみじがり", 4, 12364, 4, "もみじがり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12405, 12367, 12385, 12399, 4, "", 3206, "「節分」で鬼に豆を投げるときに言う、お決まりのかけ声", 1, 4, 2, 4, "これ、何と言う？", 3, "ふくはうち", 4, 12358, 4, "鬼は外、ふくはうち");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12371, 12356, 12426, 12398, 4, "", 3207, "季節行事として5月5日前後に外に飾るもの。「♪屋根より高い～」という歌でもおなじみ", 1, 4, 2, 4, "これ、何と言う？", 3, "こいのぼり", 4, 12412, 4, "こいのぼり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12480, 12540, 12489, 12454, 5, "アングラ＝アンダーグラウンド", 3208, "", 1, 5, 2, 5, "これ、略さずに言うと？", 3, "ダーウンド", 5, 12531, 5, "アングラ＝アンダーグラウンド");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12531, 12488, 12512, 12510, 5, "", 3209, "古くから親しまれる大道芸で、壁や階段などが、あたかも本当に存在するかのように振るまう", 1, 5, 2, 5, "これ、何と言う？", 3, "ントマイム", 5, 12452, 5, "パントマイム");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12397, 12378, 28779, 12415, 6, "", 3210, "花火の一種。手には持たず、着火して地面に放つと火花を発しながら、ある動物のように動き回る", 2, 4, 2, 4, "これ、何と言う？", 3, "ねずみ花火", 4, 33457, 6, "ねずみ花火");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12531, 12486, 12480, 5, "", 3211, "音楽用語で、「歩くような速さで」を意味する言葉", 3, 5, 2, 5, "これ、何と言う？", 3, "アンダンテ", 5, 12531, 5, "アンダンテ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12458, 12540, 12531, 12471, 5, "", 3212, "ビジネス用語で、既に激しい競争状態にあるので新規参入には向いていない市場のこと", 3, 5, 2, 5, "これ、何と言う？", 3, "オーシャン", 5, 12515, 5, "レッドオーシャン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12489, 12531, 12522, 12471, 5, "", 3213, "イヤホンなどについての俗語。低音がドンドン、高音がシャリシャリと強調されて聴こえるもの", 3, 5, 2, 5, "これ、何と言う？", 3, "ドンシャリ", 5, 12515, 5, "ドンシャリ");
