INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 19977, 26522, 12375, 12362, 4, "", 5050, "魚の身をおろす方法の中で、最も基本的とされるもの。右身・左身・中骨の3つに切り離す", 2, 4, 2, 6, "これ、何と言う？", 5, "三枚おろし", 6, 12429, 4, "三枚おろし");
