INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12459, 12523, 12531, 12480, 5, "", 5056, "「スパイスの女王」「香りの王様」などの別名を持つ香辛料。カレーやチャイには欠かせない", 3, 5, 2, 5, "これ、何と言う？", 5, "カルダモン", 5, 12514, 5, "カルダモン");
