INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12508, 12479, 12523, 12491, 5, "", 5058, "近年シャンプーや化粧品によく見る「植物由来の」の意味の言葉。オーガニックとはやや異なる", 3, 5, 2, 5, "これ、何と言う？", 5, "ボタニカル", 5, 12459, 5, "ボタニカル");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12372, 12426, 12426, 12435, 4, "", 5059, "髪の毛を「２ミリメートル」ほどに短く丸刈りにした髪型", 2, 4, 2, 4, "これ、何と言う？", 5, "ごりんがり", 4, 12364, 4, "ごりんがり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12484, 12502, 12463, 12525, 5, "ツーブロック", 5060, "髪型の一種。髪の毛を伸ばしてある部分と、短く刈り上げた部分の２つにきっぱり分かれている", 2, 5, 2, 5, "これ、何と言う？", 5, "ツブロック", 5, 12483, 5, "ツーブロック");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12500, 12521, 12473, 12486, 5, "", 5061, "マット上で体幹を鍛え、身体の機能を整えるエクササイズ。ヨガと近い要素もあるが別物である", 3, 5, 2, 5, "これ、何と言う？", 5, "ピラティス", 5, 12451, 5, "ピラティス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12487, 12464, 12475, 12521, 5, "", 5062, "洋食のテクニック。肉を焼いた鍋底に焦げ付いた旨味をワインでこそぎ取り、味付けに利用する", 3, 5, 2, 5, "これ、何と言う？", 5, "デグラッセ", 5, 12483, 5, "デグラッセ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12464, 12523, 12531, 12479, 5, "", 5063, "和食に欠かせない「旨味」のうち、三大旨味成分と呼ばれるのは、イノシン酸・グアニル酸と何？", 3, 5, 2, 5, "これ、何と言う？", 5, "グルタミン", 5, 12511, 5, "グルタミン酸");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12459, 12501, 12531, 12455, 5, "", 5064, "一般にコーヒーを飲んで摂取することが多い、眠気覚ましの効果がある成分", 1, 5, 2, 5, "これ、何と言う？", 5, "カフェイン", 5, 12452, 5, "カフェイン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12497, 12523, 12531, 12513, 5, "", 5065, "「粉チーズ」と言うと、これを指すのが一般的。パスタなどの風味づけに使われることが多い", 2, 5, 2, 5, "これ、何と言う？", 5, "パルメザン", 5, 12470, 5, "パルメザンチーズ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12423, 12356, 12387, 4, "", 5066, "「甘味」なら「あまい」、「酸味」なら「すっぱい」。では「塩味」なら？", 1, 4, 2, 4, "これ、何と言う？", 5, "しょっぱい", 4, 12401, 4, "しょっぱい");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12450, 12458, 12472, 5, "", 5067, "スパイスの一つ。「かしょう」以外の読み方は？", 3, 5, 2, 5, "これ、何と読む？", 5, "ホアジャオ", 5, 12515, 5, "花椒＝ホアジャオ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12514, 12531, 12531, 12502, 5, "", 5068, "ケーキの種類の一つ。「栗（クリ）」のクリームを、しぼるように飾り付ける", 1, 5, 2, 5, "これ、何と言う？", 5, "モンブラン", 5, 12521, 5, "モンブラン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12475, 12511, 12523, 12480, 5, "", 5069, "ベッドの大きさの種類。「シングル」と「ダブル」の間の大きさは？", 2, 5, 2, 5, "これ、何と言う？", 5, "セミダブル", 5, 12502, 5, "セミダブル");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12524, 12514, 12489, 12493, 5, "", 5070, "レモン果汁に砂糖やシロップ、はちみつなどを加えて薄めた飲み物", 1, 5, 2, 5, "これ、何と言う？", 5, "レモネード", 5, 12540, 5, "レモネード");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12518, 12491, 12473, 12488, 5, "ユニットバス", 5071, "一般には、浴槽とトイレと洗面台が一緒になったタイプのバスルームのこと", 1, 5, 2, 5, "これ、何と言う？", 5, "ユニトバス", 5, 12496, 5, "ユニットバス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12496, 12523, 12540, 12467, 5, "", 5072, "住宅の設備の一つ。ベランダと混同されるが、上に屋根が無く解放されているという点で異なる", 3, 5, 2, 5, "これ、何と言う？", 5, "バルコニー", 5, 12491, 5, "バルコニー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12424, 12358, 12367, 12375, 4, "", 5073, "マグロなどの食用の魚介で、「天然」の反対をなんと言う？", 2, 4, 2, 4, "これ、何と言う？", 5, "ようしょく", 4, 12423, 4, "ようしょく");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12487, 12488, 12473, 12483, 5, "", 5074, "体内の老廃物や毒素を排出する健康法。食事やエクササイズ、マッサージなど、方法はさまざま", 2, 5, 2, 5, "これ、何と言う？", 5, "デトックス", 5, 12463, 5, "デトックス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12390, 12435, 12375, 12404, 4, "", 5075, "布団や洗濯物を直射日光の下で干すこと。紫外線の殺菌効果で雑菌の繁殖を抑えることができる", 2, 4, 2, 4, "これ、何と言う？", 5, "てんぴぼし", 4, 12412, 4, "てんぴぼし");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12371, 12435, 12367, 12395, 4, "", 5076, "糸□□□□□、玉□□□□□、刺身□□□□□。共通して当てはめられる食材は？", 3, 4, 2, 4, "これ、何と言う？", 5, "こんにゃく", 4, 12419, 4, "こんにゃく");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12513, 12479, 12463, 12508, 5, "メタボリック・シンドローム", 5077, "「メタボ」を略さずに言うと？", 2, 5, 2, 5, "これ、何と言う？", 5, "メタボリク", 5, 12522, 5, "メタボリック・シンドローム");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12502, 12540, 12473, 12465, 5, "", 5078, "結婚式で花嫁が女性ゲストに向けて花束を投げること", 1, 5, 2, 5, "これ、何と言う？", 5, "ブーケトス", 5, 12488, 5, "ブーケトス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12469, 12540, 12531, 12525, 5, "", 5079, "牛肉の部位の呼び方の一種。背中から腰にかけてを指す。ステーキなどで好まれる", 3, 5, 2, 5, "これ、何と言う？", 5, "サーロイン", 5, 12452, 5, "サーロイン");
