INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12463, 12458, 12451, 12522, 5, "", 5082, "「生活の質」のことを表した略語", 2, 5, 2, 5, "これ、何と言う？", 5, "クオリティ", 5, 12486, 5, "QOL＝クオリティオブライフ");
