INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12527, 12540, 12464, 12461, 5, "", 5144, "若者が、他国で働いて資金を稼ぎながら、そこの文化に触れ長期休暇を過ごすという、公の制度", 2, 5, 2, 5, "これ、何と言う？", 5, "ワーキング", 5, 12531, 5, "ワーキングホリデー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12481, 12531, 12458, 12472, 5, "", 5145, "", 1, 5, 2, 5, "これ、何と読む？", 5, "チンジャオ", 5, 12515, 5, "青椒肉絲＝チンジャオロース");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12474, 12464, 12491, 5, "シーズニング", 5146, "鉄のフライパンが錆びないように行う「空焼き」と「油ならし」の2工程を合わせてこう呼ぶ", 3, 5, 2, 5, "これ、何と言う？", 5, "シズニング", 5, 12531, 5, "シーズニング");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12452, 12525, 12467, 5, "", 5147, "", 1, 5, 2, 5, "これ、何と読む？", 5, "ホイコーロ", 5, 12540, 5, "回鍋肉＝ホイコーロ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12425, 12462, 12364, 5, "", 5148, "ネギの白い部分を、まるで髪の毛のようにごく細くせん切りにしたもの。和食の飾りに使われる", 2, 4, 2, 4, "これ、何と言う？", 5, "しらがネギ", 4, 12493, 5, "しらがネギ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12514, 12525, 12516, 12504, 5, "", 5149, "特別に栄養価が高いことから「野菜の王様」とも呼ばれる緑色の葉野菜", 3, 5, 2, 5, "これ、何と言う？", 5, "モロヘイヤ", 5, 12452, 5, "モロヘイヤ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12512, 12540, 12540, 5, "", 5150, "凍らせた果物や野菜などをミキサーにかけて作る飲み物で、朝食に好まれる", 1, 5, 2, 5, "これ、何と言う？", 5, "スムージー", 5, 12472, 5, "スムージー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12523, 12540, 12531, 12486, 5, "", 5151, "毎日の夜に行う、自分ならではの習慣のこと。近年ではタレントが動画に撮って発信するのが人気", 2, 5, 2, 5, "これ、何と言う？", 5, "ルーティン", 5, 12451, 5, "ナイトルーティン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12496, 12531, 12472, 12496, 5, "", 5152, "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 3, 5, 2, 5, "これ、何と読む？", 5, "バンバンジ", 5, 12531, 5, "棒棒鶏＝バンバンジー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12356, 12381, 12370, 12409, 4, "", 5153, "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 2, 4, 2, 4, "これ、何と言う？", 5, "いそべあげ", 4, 12354, 4, "いそべあげ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (5154, 12362, 12395, 12374, 12366, 4, "", 5154, "", 3, 4, 1, 4, "このパネルの「プリコネ読み」は？", 5, "おにぎりざ", 4, 12426, 4, "おにぎりざ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12480, 12540, 12531, 12472, 5, "", 5155, "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 1, 5, 2, 5, "これ、何と言う？", 5, "ダージリン", 5, 12522, 5, "ダージリン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12523, 12452, 12464, 5, "アールグレイ", 5156, "ポピュラーな紅茶の一種で、お菓子作りの材料にも好まれる。伯爵という意味の言葉が含まれる", 2, 5, 2, 5, "これ、何と言う？", 5, "アルグレイ", 5, 12524, 5, "アールグレイ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12472, 12531, 12540, 12472, 5, "", 5157, "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 1, 5, 2, 5, "これ、何と言う？", 5, "ジンジャー", 5, 12515, 5, "ジンジャーエール");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12456, 12531, 20418, 12466, 5, "", 5158, "家庭の支出額のうち、「食費」が占める割合のこと", 2, 5, 2, 5, "これ、何と言う？", 5, "エンゲル係", 5, 12523, 6, "エンゲル係数");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12503, 12522, 12356, 12531, 4, "", 5159, "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 2, 5, 2, 5, "これ、何と言う？", 5, "プリンたい", 5, 12383, 4, "プリンたい");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12365, 12396, 12405, 12393, 4, "", 5160, "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 1, 4, 2, 4, "これ、何と言う？", 5, "きぬどうふ", 4, 12358, 4, "きぬどうふ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12452, 12531, 12512, 12501, 5, "インフォームド・コンセント", 5161, "患者が医師から十分な説明を受け、合意のうえで医療行為を受けること", 3, 5, 2, 5, "これ、何と言う？", 5, "インフォム", 5, 12457, 5, "インフォームド・コンセント");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12384, 12435, 12426, 12375, 4, "", 5162, "元は「ヨガ」から生まれた習慣で、不要なものを捨て、物に執着せずに生きること", 2, 4, 2, 4, "これ、何と言う？", 5, "だんしゃり", 4, 12419, 4, "だんしゃり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12462, 12435, 12363, 4, "", 5163, "花粉症の原因として最も多いのは、どんな種類の花粉？", 2, 4, 2, 5, "これ、何と言う？", 5, "スギかふん", 5, 12405, 4, "スギかふん");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12524, 12473, 12483, 5, "", 5164, "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 2, 5, 2, 5, "これ、何と言う？", 5, "フレックス", 5, 12463, 5, "フレックスタイム制");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12365, 12421, 12435, 12358, 4, "", 5165, "アルコールをあまり毎日飲み過ぎず、肝臓を休める日を適度に設けることを推奨する言葉", 2, 4, 2, 4, "これ、何と言う？", 5, "きゅうかん", 4, 12363, 4, "きゅうかん日");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12510, 12520, 12474, 12493, 5, "", 5166, "卵・酢・油などから作られ、サラダなどにかけることが多い、定番の調味料", 1, 5, 2, 5, "これ、何と言う？", 5, "マヨネーズ", 5, 12540, 5, "マヨネーズ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12465, 12481, 12503, 12515, 5, "", 5167, "主にトマトなどから作られる、定番の赤い調味料。オムライスやフライドポテトなどによく合う", 1, 5, 2, 5, "これ、何と言う？", 5, "ケチャップ", 5, 12483, 5, "ケチャップ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12510, 12540, 12531, 12460, 5, "", 5168, "トーストなどに塗るのが定番の加工食品。バターの代替品として使われることも", 1, 5, 2, 5, "これ、何と言う？", 5, "マーガリン", 5, 12522, 5, "マーガリン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 30722, 31958, 37284, 22633, 6, "砂糖・塩・酢・醤油・味噌", 5169, "料理の「さしすせそ」、順番に何と言う？", 3, 6, 2, 6, "これ、何と言う？", 5, "砂糖塩酢醤", 6, 37218, 6, "砂糖・塩・酢・醤油・味噌");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12372, 12371, 12356, 12367, 4, "", 5170, "健康食品として知られるお米。米のほかに麦や粟など、合計5種類の穀物が合わさっている", 3, 4, 2, 4, "これ、何と言う？", 5, "ごこくまい", 4, 12414, 4, "ごこくまい");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12499, 12517, 12455, 12483, 5, "", 5171, "ホテルなどで多い、セルフサービス形式での食事。「バイキング」と混同されることが少なくない", 3, 5, 2, 5, "これ、何と言う？", 5, "ビュッフェ", 5, 12501, 5, "ビュッフェ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12383, 12363, 12417, 12398, 4, "", 5172, "トウガラシの代表的な品種。名前は、とある鳥のかぎづめに似ていることから", 2, 4, 2, 4, "これ、何と言う？", 5, "たかのつめ", 4, 12388, 4, "たかのつめ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12479, 12521, 12491, 12496, 5, "", 5173, "高級食材としてよく知られるカニ。魚のタラが獲れる場所に生息していることが由来", 1, 5, 2, 5, "これ、何と言う？", 5, "タラバガニ", 5, 12460, 5, "タラバガニ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12469, 12463, 12499, 12521, 5, "", 5174, "食用として、かき揚げなどで好まれる小さなエビ。透き通ったピンク色の身体をしている", 2, 5, 2, 5, "これ、何と言う？", 5, "サクラエビ", 5, 12456, 5, "サクラエビ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12373, 12435, 12401, 12365, 4, "さんきゅっぱ", 5175, "売り物によく設定される「398円」という値段を読みやすくした読み方", 1, 4, 2, 4, "これ、何と言う？", 5, "さんきゅぱ", 4, 12421, 4, "さんきゅっぱ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12363, 12356, 12425, 12400, 4, "", 5176, "ホタテなどの二枚貝で、円柱の形をした大きな可食部の通称", 1, 4, 2, 4, "これ、何と言う？", 5, "かいばしら", 4, 12375, 4, "かいばしら");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12449, 12488, 12540, 5, "", 5177, "結婚式で、ケーキ入刀のあとに新郎新婦がお互いにケーキを食べさせあう演出", 2, 5, 2, 5, "これ、何と言う？", 5, "ファースト", 5, 12473, 5, "ファーストバイト");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12356, 12429, 12375, 12394, 4, "", 5178, "結婚披露宴の途中で新郎新婦が離席し、衣装を変えること", 2, 4, 2, 4, "これ、何と言う？", 5, "いろなおし", 4, 12362, 4, "おいろなおし");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 21098, 23450, 12415, 12400, 4, "", 5179, "庭木を切って整えるためのはさみで、普通のはさみなら切れない太い枝なども切ることができる", 3, 4, 2, 6, "これ、何と言う？", 5, "剪定ばさみ", 6, 12373, 4, "剪定ばさみ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12463, 12540, 12464, 12522, 5, "", 5180, "訪問販売などで考える暇なく購入してしまったものを、一定期間中ならキャンセルできる制度", 2, 5, 2, 5, "これ、何と言う？", 5, "クーリング", 5, 12531, 5, "クーリング・オフ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12397, 12371, 12414, 12414, 4, "", 5181, "「猫にあげるような飯」のこと。削り節をかけるか、味噌汁をかけるか、定義は地域で分かれる", 2, 4, 2, 4, "これ、何と言う？", 5, "ねこまんま", 4, 12435, 4, "ねこまんま");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12362, 12367, 12531, 12471, 5, "", 5182, "分譲マンションで、価格が1億円を超えるような特に高級なものを指す俗語", 3, 5, 2, 4, "これ、何と言う？", 5, "おくション", 4, 12519, 5, "おくション");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12497, 12540, 12464, 12461, 5, "", 5183, "高速道路で、「SA」は「サービスエリア」。では「PA」は？", 1, 5, 2, 5, "これ、何と言う？", 5, "パーキング", 5, 12531, 5, "パーキングエリア");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 32048, 12510, 12519, 12483, 5, "", 5184, "「ほどよく筋肉があり、脂肪は少なく引き締まった体型」を指す俗語。対義語は「ゴリマッチョ」", 2, 5, 2, 5, "これ、何と言う？", 5, "細マッチョ", 6, 12481, 5, "細マッチョ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12495, 12531, 12464, 12496, 5, "", 5185, "定番の肉料理。ひき肉をメインに、卵やパン粉、野菜などと一緒にこねて焼いたもの", 1, 5, 2, 5, "これ、何と言う？", 5, "ハンバーグ", 5, 12540, 5, "ハンバーグ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12454, 12455, 12512, 12523, 5, "", 5186, "結婚式やパーティなどで、ゲストを迎え入れるために入り口に置かれる案内板のこと", 2, 5, 2, 5, "これ、何と言う？", 5, "ウェルカム", 5, 12459, 5, "ウェルカムボード");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12390, 12387, 12365, 12363, 4, "", 5187, "のり巻き寿司の一つで、マグロの赤身とわさびが巻かれたもの", 1, 4, 2, 4, "これ、何と言う？", 5, "てっかまき", 4, 12414, 4, "てっかまき");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12363, 12387, 12365, 12401, 4, "", 5188, "のり巻き寿司の一つで、きゅうりが巻かれたもの", 1, 4, 2, 4, "これ、何と言う？", 5, "かっぱまき", 4, 12414, 4, "かっぱまき");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12362, 12420, 12435, 12371, 4, "", 5189, "どんぶり物の料理の一つ。割下で甘く煮た鶏肉を、たまごでとじてご飯に乗せたもの", 1, 4, 2, 4, "これ、何と言う？", 5, "おやこどん", 4, 12393, 4, "おやこどん");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12385, 12425, 12375, 12375, 4, "", 5190, "寿司の一種で、握ったり巻いたりしたものではなく、具材をご飯の上に散らすように盛るのが特徴", 1, 4, 2, 4, "これ、何と言う？", 5, "ちらしずし", 4, 12378, 4, "ちらしずし");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12395, 12399, 12400, 12385, 4, "", 5191, "「そば」の分類の一つで、そば粉とうどん粉を２：８の配分で打ったそばのこと", 3, 4, 2, 4, "これ、何と言う？", 5, "にはちそば", 4, 12381, 4, "にはちそば");
