INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12388, 12385, 12378, 12405, 4, "", 6054, "人間の足の裏の、地面と接しないくぼんだ部分。人によっては無い場合もある", 1, 4, 2, 4, "これ、何と言う？", 6, "つちふまず", 4, 12414, 4, "つちふまず");
