INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6057, 12467, 12524, 12540, 12463, 5, "", 6057, "『ナナカ』の二つ名。さまざまな魔法を探し集めていることに由来する", 3, 5, 1, 5, "これ、何と読む？", 6, "コレクター", 5, 12479, 5, "蒐集家（＝コレクター）");
