INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12469, 12521, 12489, 12502, 5, "サラブレッド", 6060, "レースのために、長い年月をかけて改良された類の馬。家柄の良い人のたとえ表現でもおなじみ", 1, 5, 2, 5, "これ、何と言う？", 6, "サラブレド", 5, 12524, 5, "サラブレッド");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12490, 12493, 12462, 5, "", 6061, "キツネの一種。冷ややかで悟りを開いたような表情に見えることで近年人気を博している", 3, 5, 2, 5, "これ、何と言う？", 6, "スナギツネ", 5, 12484, 5, "チベットスナギツネ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12521, 12473, 12459, 5, "シーラカンス", 6062, "数億年も前から変わらない姿のまま現存しているため、「生きた化石」と呼ばれる深海魚", 2, 5, 2, 5, "これ、何と言う？", 6, "シラカンス", 5, 12531, 5, "シーラカンス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12504, 12521, 12473, 12463, 5, "", 6063, "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", 1, 5, 2, 5, "これ、何と言う？", 6, "ヘラクレス", 5, 12524, 5, "ヘラクレスオオカブト");
