INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 27875, 12365, 12429, 12412, 4, "", 6065, "目のすぐ下のあたりにあるほくろの通称", 1, 4, 2, 4, "これ、何と言う？", 6, "泣きぼくろ", 6, 12367, 4, "泣きぼくろ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12523, 12463, 12496, 5, "シルバーバック", 6066, "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 3, 5, 2, 5, "これ、何と言う？", 6, "シルババク", 5, 12496, 5, "シルバーバック");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12459, 12513, 12531, 12524, 5, "", 6067, "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 1, 5, 2, 5, "これ、何と言う？", 6, "カメレオン", 5, 12458, 5, "カメレオン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12458, 12491, 12510, 12516, 5, "", 6068, "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 1, 5, 2, 5, "これ、何と言う？", 6, "オニヤンマ", 5, 12531, 5, "オニヤンマ");
